public class d11 extends KeEntry{
	public d11() {
        set(4, new LiuReKe("乙亥日第一局　干上辰", "课体：伏吟，自任，斩关。", "课义：支夜三武，三勾可虑，末马遁辛，特来相苦。", "解曰：支乘三武，既防其盗干矣，干又乘三勾以克支，岂不可虑乎。末传巳乃驿马，旬遁乘辛，又来伤干，则所以苦我者，非一端而矣。", "断曰：自任之卦，占静则宜，占动则滞。甲而未圻，屈而未伸。又名斩关，有隐遁之形，无安贞之象。伏吟有马须言动，今末传之马，遁辛伤厂，又不利于动也。末助初财，却遁庚鬼又乘勾陈，必有争斗之事，或因财涉讼，致起祸端。", "天时：日上乘土神土将，阴而不雨。家宅：有一好子，未免劳碌。求名：不利。求财：防起争端。婚姻：暗昧不吉。胎产：女孕。疾病：初末传遁得庚辛二鬼，必系肝经受病，然人克病症，不久自痊。出行：青龙乘马，一路平安。行人：久于稽留，近日将至。出师：尚无吉兆。", "毕法云：支坟财并旅程稽。纂义云：自高自大，自逞自是，棱角太过，由此误事。勾留不快，屈而未伸。自家不是，反怨别人。秘要云：乙亥日见丑为财，上乘旬丁，夜乘白虎，必出入求财，或因妻妾而动。心印赋云：乙庚用起见天罡，地上同辰亦此方，预防争夺见乖张。"));
        set(3, new LiuReKe("乙亥日第二局　干上卯", "课体：元首，斩关，励德，闭口。", "课义：退入鬼藩，幸而空焉，病防再至，煞号销魂。", "解曰：初财两阴相夹，乙不敢取，次第退归于酉申，是入于鬼域也。幸申酉皆空，不能为害。倘遇出旬，鬼复填实，安知病愈者，不再发也。乙日金鬼本空，出旬则实，谓之销魂煞。遇年命有救，乃可无妨。", "断曰：旺禄临身，宜占食禄。凡百所占，咸宜守旧。但踏脚空亡，当奋力前进，方为有功。苟首鼠两端，即陷于空败之乡，祸且不测矣。", "天时：毕宿与水母皆空，龙神与六合相并，出旬大雨。家宅：卯与戌合，宜室宜家之象。仕宦：申酉之岁，或三秋之月，必能遂意。求财：戌上加阴，未免从惊危中得来。婚姻：干支相合，洵是佳妇佳儿。胎产：上强下弱，男妊产速。求谋：凡事俱有关隔，必待成后获利。疾病：课体关格，恐是隔食气阻之疾，或肝脾受病，速治可愈；若至出旬，病必有加。出行：支干相合，不能速行，出旬有实，冲卯乃行。行人：天罡加孟，尚未启行。出师：和解为上，防有损失。", "毕法云：旺禄临身徒妄作，鬼乘天乙乃神o，踏脚空亡进用宜。课经云：乙亥日戌加亥为用，为魁度天门。凡占皆被阻格。纂义云：占产在甲申旬中生，盗逃在甲申旬中获。"));
        set(2, new LiuReKe("乙亥日第三局　干上寅", "课体：遥克，蒿矢，u女，寡宿。", "课义：交车虽美，所畏蒿矢，熟视初传，全无威势。", "解曰：干上寅木与支神亥水作六合，支上酉金与干寄辰土作六合，交车相合，凡事皆美。所畏者蒿矢带金为有镞，力足以伤人。细观发用之酉，已作旬空，又谓之遗镞。矢既无镞，又何威势之足畏哉。三传乃三旬空亡，凡事散乱。", "断曰：遥克蒿矢，已属疏远。今又空亡，是毫无影响。喜既虚声，忧亦泡影。止宜交关财利，及合本经营，或有俾益。课体u女、寡宿，大不宜占婚姻。", "天时：日上昼乘朱雀，夜乘天空，毕宿落陷，晴而有风之象。家宅：室内空虚，必居嫠妇；财爻虽现，取之撄祸。功名：官星落空，填实可望。求财：财乘墓虎，不取免患。婚姻：支上乘空，占婚不吉。胎产：妊恐成女，母空儿实，孕凶产吉。求谋：凡占皆无倚靠。疾病：昼占是积块之疾，或肾水虚涸之症，虽不能即愈，亦不致大害。逃盗：难获。出行：从陆路去。行人：天罡加仲，尚在中途。出师：鬼空防诈，不可因粮于敌。", "毕法云：后合占婚岂用媒。秘要云：遥克、昴星、别责，遇发用空陷，将乘元武，定主失脱，此法极验。又课经云：乙亥日干上寅，支上酉，为交车脱，缘寅木脱亥水，酉金脱辰土。虽相交涉，主彼此各怀脱赚之意。"));
        set(1, new LiuReKe("乙亥日第四局　干上丑", "课体：重审，稼穑，芜淫，励德。", "课义：递互克伐，不相顾接，财化为鬼，求贵事捷。", "解曰：干上丑土克支，支上申金克干，不相顾接，而互相克伐也。三传皆土，生起支上之申金克日，是传财化鬼，财必难取，取必得祸。夫申为夜贵，又为官爻。而三传财神生之，故求贵之事甚捷也。如病则祷神，讼则求贵，或纳粟得官，或乞恩补授。总之利于有官，不利于平人。", "断曰：昼占为励德，利君子而不利小人，更不宜问父母之事，缘三传克父母爻也，幸赖支上申金，窃土气而生父母，又遇空亡，仍为不吉。此亦见救不救之例也。", "天时：三传全土，水母又空，虽则夜有青龙，或待出旬始雨。家宅：财旺生官，只宜出仕之家，常人占之，讼病相扰。功名：不无所费。求财：财多反亏。谒贵：贵人在牖，遇之极易。谋望：耗财无益。婚姻：昼占平平，夜占甚吉。胎产：下强上弱，妊必成女，交车相克，必有所伤。疾病：昼占乃肾水虚耗，或系积块之症。出行：课名游子，定有所往。行人：天罡加季，行者立至。出师：昼占凶，夜占吉。", "毕法云：传财化鬼财休觅，闭口卦体两般推。三车一览云：乙上丑，亥上申，不可执，为芜淫。但先有人相许，后却不顾耳。秘要云：传财化鬼，其祸必自宅中发出。唯行年本命上神，能制其鬼，庶无深害。"));
        set(0, new LiuReKe("乙亥日第五局　干上子", "课体：涉害，曲直，u女。", "课义：自墓传生，先迷后醒，昼占家宅，昏晦伶仃。", "解曰：未乃乙木之墓神，亥乃乙木之长生。初传未土，末传亥水，是自墓中传出长生也，占事先昏晦而后通明。未墓昼乘白虎克宅，墓主暗昧，虎主丧凶，自然宅内多昏晦伶仃之事矣，", "断曰：课乃见机，传曰曲直，宜尖上取尖，美中求美。倘或悠柔不决，必至曲屈难伸；又支干相害，不宜占交加之事。利于兄弟，而不利于妻孥。夜占帘幕临日　利占科第。天后阴神，乘虎亦防断弦", "天时：大罡指阳，水母空陷，木局上风，晴而有风。家宅：自墓传生，先凶后吉，日渐光明之象。仕宦：昼夜皆有帘幕，占试甚利。求财：不能如意　取之有祸。谒贵：事干两贵。婚姻：支干克害，占婚不吉。胎产：上强下弱，男妊；子母相害，难产。疾病：肾水不足之疾，或有痞积，病症克人，宜慎宜谨。出行：陆路可行。行人：天罡加孟，尚未启行。逃亡：难获。出师：昼夜皆凶。", "毕法云：贵虽坐狱宜临干，支乘墓虎有伏尸，干墓并关人宅废，彼此猜忌害相随，合中犯杀蜜中砒。纂义云：未为干墓，临支克支，上乘白虎，宅中必有伏尸为祸。课经云：亥年正月，亥将加卯为灾厄课，盖未为丧车，乘死气加亥，又为岁虎故也。"));
        set(11, new LiuReKe("乙亥日第六局　干上亥", "课体：重审，不备。", "课义：中丁初脱，空鬼居末，幸遇支生，死而后活。", "解曰：初传午火，为乙木之脱气。中传丑土，上乘遁丁。末传申金，乃日干之官鬼，空亡无气。则三传之中，无一佳境。而午又为乙木之死地，若非支神之亥水相生，安能死而复活乎。", "断曰：支干皆刑，又复相克，宾主不投，室家不睦。事起于女，患由于下。凡百所占，皆勿通利。末传空亡，不如退守为佳。乙亥日午加亥为用，为恩多怨深格，缘干生初传，初传生中传，中传生末传，是为恩也。末传克日干，是反成仇也。支加干生干，为自在格。", "天时：天罡指毕，课体阳不备，青龙乘丁，皆为雨象。家宅：宅来生人，主发财丁。仕宦：静守生气，恐防不久。求财：主得妇女之财。谒贵：贵人空陷，恐不能遇。婚姻：支来加干，女家俯就。胎产：二阳包阴，上弱下强，恐是女孕。疾病：金鬼克木，肝经之症，然已空陷，不药可愈。出行：昼占不利，夜占水路甚吉。行人：天罡加仲，尚在中途。出师：宜守生气，粮储足备。", "毕法云：闭口卦体两般推。课经云：乙亥日，午加亥为用，不利仕宦，惟利常人。盖末传之申金为官鬼，既被初传午火所伤，又被中传丑土所墓，复兼空陷，是申金全无气象矣，复何畏哉。"));
        set(10, new LiuReKe("乙亥日第七局　干上戌", "课体：反吟，元胎。", "课义：昼将凶恶，须防有失，动则双双，费用不值。", "解曰：巳为乙木之脱气，昼乘白虎，既极凶恶，夜乘元武，岂免遗失乎。传有两马，来往不已，是动则双双也。亥之生我也少，已之脱我也多，其费用岂小哉。", "断曰：反吟之卦，反覆呻吟，变迁不常，得失无定，主宅舍动移之事。巳乘白虎加亥，主小儿有咽喉之病，及木器橱柜锁有伤损，或车船有翻覆之患。支马临绝，行人即来。", "天时：久晴而始雨，雨而复晴，终是晴多雨少。家宅：来者思去，去者思归，夜占防盗，失不止一。仕宦：贵禄不见课传，官星与青龙又皆藏匿，占官不吉。求财：财爻临身，宜得文书狱卒之财。谒贵：无因致前，婚姻：男亦不贵，女亦欠佳。胎产：妊者防坠，产者防迟，下强上弱，产乃弄瓦。疾病：肺经有疾，或肝经受制，或轻或重，寒热往来，热多冷少。出行：陆路可行，水路不吉。行人：天罡加季，行人立至。出师：昼夜占之皆不吉，或损将士，或伤资财，或失盔甲。", "毕法云：虎乘遁鬼殃非浅(昼将)。秘要云：白虎加临旬内之遁鬼，其祸最深，纵空亡亦不能为救。照胆秘诀云：巳亥中间常缺欠，重求轻得报君知。"));
        set(9, new LiuReKe("乙亥日第八局　干上酉", "课体：重审，斩关，不备，励德。", "课义：空鬼干遇，支乘实墓，用鬼获财，迤逦克去。", "解曰：酉乃干鬼，旬空无畏。辰乃支墓，实克亥支，宅必昏晦矣。酉克初传寅木，寅克中传未土，未克末传子水，皆是递克之财，此所谓用鬼获财者也。", "断曰：重审之卦，利上不利下，支干自刑，三传受制，大约忧喜皆无实在也。三传全受夹克，寅受阴酉夹克，未受龙寅夹克，子受贵未夹克，不宜干贵求财，恐其致祸。辰就支上，谓避难逃生：", "天时：水运乎上，箕毕相会，天罡临亥，风雨大作。家宅：人宅自刑，门衰祚薄。仕宦：官禄两空，占官不吉。求财：昼占获财，夜占费力。谒贵：昼占必遇，夜占不吉。婚姻：六合事成，但恐不吉。胎产：下强上弱，占妊成女，支于带刑，产亦欠利。疾病：脾胃伤食所致，或痢疾之类，喜鬼空无害，但不能即愈。出行：陆路欠安，舟楫不吉。行人：天罡加孟，尚未启行。出师：昼占中平，夜占防诈。", "毕法云：避难逃生须弃旧，干支全伤防两损，宾主不投刑在上。要览云：乙亥日，干上酉，支上辰。干支上神作六合，但支上神为日之财，反生干上之鬼，有求荣反辱之象。乙日干上见六合乘酉，谓之对神隔将，玉成歌曰：对神隔将主人离，辰上逢之宅破期。"));
        set(8, new LiuReKe("乙亥日第九局　干上申", "课体：重审，曲直，u女，丧门。", "课义：官贵无补，夜禄乘虎，宅虽旺临，昼乘元武。", "解曰：申金为乙木之官星，夜贵又临其上，俱为空陷，是无补益也。卯为日禄，临于亥上，虽则旺相，然夜虎惊忧，昼元费耗，亦何益之有。酉为乙木之胎神，今作空亡，受下神巳火之克，为损孕格。占产当日便生，占孕恐致损堕。", "断曰：曲直之卦，事因同类，起厂女子，有孟济未济之象。申虽日鬼临身，缘是贵人，切勿作鬼祟目之。占病是神o为患，又谓之闲贵人，不宜占讼。", "天时：天罡指阳，日上皆空，曲直生火，大晴之象。家宅：旺禄临亥，家道必丰，但嫌元虎，不甚安宁。仕宦：官星与贵人皆空，求官不吉。求财：青龙与财父发用，急宜取之，迟则不得。谒贵：夜贵临干，夜占甚吉。婚姻：男嫌贵空，女嫌将凶，且恐不就。胎产：母实儿空，恐不成胎。疾病：肝脾之病，恐难痊好。出行：陆路空克，水路惊忧。行人：日马不见，天罡加孟，行者未归。出师：昼占无成，夜亦不吉。", "毕法云：权摄不正禄临支，贵虽坐狱宜临干，鬼乘天乙乃神o，二贵皆空虚喜期，干墓并关人宅废。指南：戊子二月亥将加未，占兵曰：游都居西南恋生，且离日辰远，贼符临干支，水陆必有伏兵。然乘死绝之气，且初传休囚夹克，末传建旺制初，是守坚敌弱，不能持久。至丑年会金破局，即难支矣。"));
        set(7, new LiuReKe("乙亥日第十局　干上未", "课体：重审，稼穑，飞魂，游子。", "课义：钱财遍地，得不偿费，两贵俱空，虎丁可畏。", "解曰：三传皆土，作乙木之财，财可谓旺矣。不知反生起支阴之巳火以脱干，是所得不偿所费也。申子为乙日之贵人，今皆空陷，是已蒙允许，而被人搀越，不过虚喜而已。至于末传，乃事之结果。而丑上遁丁，夜乘白虎，宁不可畏之甚乎。", "断曰：重审之卦，一日稼穑，有艰难之象。凡事逼迫，不能由己。占者克尽辛勤，方能遂意。华盖覆日，占行人则不归，占逃亡则自归。戌乘朱雀，当有恶犬吠人。", "天时：日已被墓，青龙升天，天罡指阴，必然大雨。家宅：寅为乙木之兄弟，居宅脱宅，恐被失脱。仕宦：二贵皆空，占官不吉。求财：传财太旺，反无所得。谒贵：恐不能遇。婚姻：日辰相克，占婚不吉。胎产：昼占犹吉，夜占难产。疾病：肝气受伤，或目痛腹疼。子孙乘虎，医人不见效。出行：昼占陆路可行，夜占皆所不吉。行人：课名游子，不能即返。出师：将不得力，夜占防败。", "毕法云：宾主不投刑在上，于墓并关人宅废；华盖覆日人昏晦，二贵皆空虚喜期。纂要云：乙亥日未加辰，秋占，与关神相并，主人口衰替。假若加临干支上，则为宅废也。倘墓神作生气，必作库务差遣。不求自得，勿作关墓视之。"));
        set(6, new LiuReKe("乙亥日第十一局　干上午", "课体：重审，间传，涉三渊，寡宿。", "课义：虎人朱雀，讼极凶恶，不论空亡，贵难倚托。", "解曰：毕法谓甲戌旬中，申加午发用者，为虎人朱雀。虽空不论，占讼最凶。夜贵空陷，坐于鬼方。昼贵入狱，皆难依托也。", "断曰：间传涉渊，有奔驰阻隔之象。出门无马，渡河无舟，必先难而后易，苦去而甘来也。夜占丁虎克宅，灾祸非小。或屋字坍塌，以致损人。或人口灾病，谓之蛇虎乘丁格。", "天时：天空临干，朱雀飞空，天罡指阳，必主大晴。家宅：丑丁克宅，极凶之象。仕宦：日德与贵人皆空，占官禄不利。求财：两贵皆空，唯财独存，求之可得。谒贵：昼占夜卜，皆不能遇。婚姻：支干相害，两皆不吉。胎产：午为子息，胎必成，男产不甚吉。疾病：气促伤残，日德发用作空；占尊长病难痊。出行：陆路防诈，水路甚凶。行人：天罡加孟，且无日马，行者未归。出师：有失众之象。", "毕法云：喜惧空亡乃妙机，两贵受克难干贵，彼此猜忌害相随。课经云：干上之午，昼乘天空，日干生亡神，上神生天将，名为脱上逢脱。凡占俱无中生有，尽被脱耗，又三传皆是贵人，谓之贵多不贵。凡占权摄不一。反无依倚。如用夜贵，乃名咄目煞。若贵人咄目专视，反坐罪也。纂要云：两蛇夹墓，病有积块难愈。"));
        set(5, new LiuReKe("乙亥日第十二局　干上巳", "课体：元首，连茹，三奇。", "课义：拱虚一位，所欠财利，木命属龙，方始遂意。", "解曰：此拱虚格也。日干为辰，日支为亥，干上乘巳，支上乘子，是子巳夹拱丑寅卯辰四神在内。而三传尚无辰字，是辰乃所虚之一位也。辰乃乙木之财，所欠者非财利而何。倘占人本命属辰，则财利全美，方为称意也。", "断曰：元首之卦，罗网高张，以尊制卑之象。事多顺利，起于男子。白虎乘巳脱干，又遁旬鬼，恐被子孙脱漏之事。辰阴夜虎乘丁克宅，宅中惊恐。", "天时：水下火上，恐不能雨。家宅：日辰相克，阴神相害，不安之象。仕宦；贵人临支，日禄临传，春时占此，功名必得。求财：财爻虽发传，蛇虎难当，得财亦险。谒贵：贵人临宅，不招自至。婚姻：佳儿佳妇，匹配相宜。胎产：上强下弱，二阴包阳，产必生男。疾病：以病克人，占病不吉。出行：水陆俱佳，龙马临干，出行最吉。行人：天罡加仲，已在中途。出师：凡百惊疑，昼夜不利。", "毕法云：所谋多拙遭罗网。中黄经云：乙亥日巳加辰，子加亥，为天罗地网，盖干支各乘羊刃。如网罗兜裹，不宜妄动。或年命冲破，乃为破罗破网。纂要云：乙日胎神在酉，上乘后合。十月占为私孕格，否，则婢妾有孕。"));
    }
};
