public class d13 extends KeEntry{
	public d13() {
        set(7, new LiuReKe("丁丑日第一局　干上未", "课体：自信，稼穑，游子，三奇。", "课义：身动则徙，彼投于己，两丁拱墓，静中动矣。", "解曰：伏吟见丁而动，今干即是丁，为身自动，而徙干支，支神发用，还传至干，为彼投于己也。干丁也，支即旬丁，亦丁也。中传戌为日墓，凡占传墓事止。今初末两丁夹之，是于静之中有动象矣。", "断曰：此伏吟自信之卦，本象潜藏伏匿，止而不发。况纯土稼穑，为艰难之兆，宜静以应之。然日辰逢丁，干支上神，两两冲动，稼穑化为游子。若三九月天马并临，则有必动之势。丑未作年命者，占试必中。盖丑中有斗，未中有鬼，二字合为魁也。", "天时：阴多雨少。田禾：可得丰收，终防虚耗。家宅：不宜动土，恐致口舌。婚姻：主田家女，或库吏家女，入赘吉。胎产：不佳。功名：若屯田版筑等官，必得异擢，余不利。求财：无得且有耗折。投谒：不值。疾病：主脾泄积聚，难愈。失脱：难追。出行：主有私孟事，或往妇女家。行人：飘流未归，或有私恋。争讼：主争夺田产。兵战：吉，尤利屯田。", "毕法云：费有余而得不足，彼求我事支传干。课经云：交车冲，不论亲疏，先合后离。指窍云：凡逆传四季者，春占越库，散财不以其道；夏占传魁，委用不得其人；秋占杀墓，势将兴而将起；冬占伏阴，机渐收而渐藏。"));
        set(6, new LiuReKe("丁丑日第二局　干上午", "课体：重审，退茹。", "课义：昼禄虎边，遂谒三传，随鬼归墓，终受淹缠。", "解曰：午为丁禄临日，然昼占乘白虎，不可守也。遂动而投于三传，初传子鬼，中传亥鬼，而人在末传之戌墓，始终受淹缠昏滞之咎而已。子鬼坐丑方受克，似不能为害。然丁课坐未，与之冲动，亦未见安宁也。", "断曰：此重审之卦，逆传连茹，利为主而后动。长上有厄，事从内起，咎由妇人，往多不顺，详审后行则吉。在君子则有不终家食之象，然志孟扶摇，身仍淹滞。在常人则为破产、涉讼之象。大约半生磨折，始就安逸也。百凡有始鲜终。", "天时：多雨久阴。家宅：人旺不宜买奴婢，主时有官非盗失。婚姻：主有官讼，久始得合。胎产：主女，即产。功名：必历两官，后见蹭蹬。求财：有损无益。疾病：难安，亦不大凶。失脱：夜占反受贼累，为官所诈。出行：不利，未远即返。行人：即至。争讼：昼占宜求贵助，夜占不利。兵战：宜用权变，恐不果断。", "毕法云：鬼乘天乙乃神o，将逢内战所谋危。袖中金云：重阴安嘉遁之贞，宁甘没齿。古鉴：午将加未占前程曰：丁德在午，亥为上德，子为紫微，丑为北斗，主有阴德洪大。传遁乙丙丁三奇，贵人在中，午禄扶身乘虎，行年催官，子蛇前引，必得高第，小则郎官，大则侍从，未易量也。俱验。"));
        set(5, new LiuReKe("丁丑日第三局　干上巳", "课体：重审，间传，时遁。", "课义：遍地贵人，利见大人，支干互绝，六位阴纯。", "解曰：亥乃昼贵，酉乃夜贵，见于三课四课，又在初传中传，遍地皆是。利于谒见，不利于倚托，贵多则不专也。火绝于亥，土绝于巳。今支干互绝，宜于两相更代之事。又三传间递，纯是六阴地位，宜于私谋阴于，不利公事也。", "断曰：此重审之卦，退茹间传，合时遁格。用起孟神，传仲及季，以阴加阴，有朋从阴谋之事，起于下人女子。若循理而行，则应牝马之贞　黄裳之吉。否则忧疑患难，自临绝地矣。中末空亡，凡事有始无终，吉凶皆虚而不实。", "天时：多阴晦，亦无雨。家宅：宜与人互相兑换而居，夜占朱雀内战，防口舌火烛。婚姻：不成。胎产：土女胎，或婢妾生子。功名：现任主多差委，权摄考试，恐荐而不售。求财：八月可得。疾病：主犯寒邪，口轻夜重。失脱：主女子盗窃，深藏难觅。出行：谒贵多遇而无力。行人：占尊长即归。争讼：主为交易田产，或阴私之事，各投势要，终归解散。兵战：宜用间谍，临时权变。", "毕法云：昼夜贵加求两贵，课传皆贵转无依，不行传者考初时。课经云：亥酉未时遁格，盖酉为太阴，未中丁马玉女，利隐遁潜形。自亥传未，如人入幽暗求隐，有遁身之象、君子吉而小人凶也。纂义云：夜占朱雀内战，家有口舌火烛。"));
        set(4, new LiuReKe("丁丑日第四局　干上辰", "课体：昴星，掩目，虎视。", "课义：递相暗昧，各甘其晦，魁罡乘丁，如骑虎背。", "解曰：支墓覆干，干墓覆支，是为递相暗昧。乃干复自坐于戌，支复自坐于辰，又为各甘其晦也。故毕法有天网恢恢之喻。魁罡主动，而丁又变动之神，今魁乘旬丁，罡乘课丁，其动凶猛，如骑虎背也。", "断曰：此昴星之卦，名冬蛇掩目，亦名虎视。魁罡系于丑未，人宅俯仰皆在墓中，否塞甚矣。倘更日月宿临四仲，则为天地二烦。夜占则又蛇虎纵横，尤为凶象。占者当如蛰虫坏户，退藏于密，极意提防，庶可免咎。", "天时：主有雷电，昼占雨足，夜占风多。家宅：夜占有大惊恐事，或多怪梦。婚姻：不宜，若成必夫妇不睦。胎产：昼占得男吉，夜占立产，有虚惊。功名：中有危险，兢慎终吉，午生人尤佳。求财：计谋多左。疾病：暴病无事，久疾凶，主惊疾。失脱：难获。出行：利赴任，余有悔。行人：淹滞。争讼：不利。兵战：昼占不宁，夜占获金宝之兆。", "毕法云：干支乘墓各昏迷，人宅坐墓甘招晦，虎视逢虎力难施。课经云：丁丑日辰加未，夜将白虎乘墓，临身入传，卦体虎视，如逢三虎，有力难施，惊危不免。括囊赋云：昴星归去而防险。"));
        set(3, new LiuReKe("丁丑日第五局　干上卯", "课体：元首，从革。", "课义：小利先施，大财必归，传空将土，重重取妻。", "解曰：干，自己也；支，他人也。酉本我财，然是空财。今加丑上，是我以小利施于彼。遂致巳加于酉，合成三传金局，皆为我利矣。初传乘空，中传脱气，末传旬空，而得夜将纯土生之，皆化为实，重重皆我妻财也，", "断曰：此元首之卦，合从革之局，事多顺利，起于男子，有正大光明之象。但三合退行，必有阻隔。卯酉临于干支，体同龙战，不免疑忌乖离。贵人立卯，阴小有灾。初末皆空，中间耗窃，枉用心术工夫，始终虚而不实。", "天时：气燥，有云少雨。家宅：宜进田财，多蓄奴婢。婚姻：再娶吉，然防拂逆翁姑。胎产：防不实。功名：尊显者吉，微员不利。求财：随地有财，却不得实。疾病：主父母灾，腹痛、血痢，暴病即解。失脱：捕风捉影。出行：宜避患远行，不利吊丧问疾。行人：占奴仆、文书，速到。争讼：乍讼难解，久讼即和。兵战：步步谨慎，始无挫折。", "毕法云：人宅皆死各衰羸，合中犯煞蜜中砒。课经云：范蠡为越王占产，申加子，巳为用，旺火克下死金，上强下弱，决生男。指南：正月亥将加卯，占升迁，三传空脱，太岁龙神落陷，互乘死气，春得金局，官难满任，明年宜请告。不听，果被劾罢。"));
        set(2, new LiuReKe("丁丑日第六局　干上寅", "课体：重审，四绝，斫轮。", "课义：木坐空申，斫朽木轮，事虽败坏，再造出旬。", "解曰：申金旬空，卯木坐于其上，虽为斫轮，不免朽木难雕矣。丁火败于卯，而脱气于戌。巳又支之破碎，事必败坏。然出旬则申金填实，再造可成也。按甲辰旬，卯落旬空，方为朽木。今申空当为无斧，亦作朽木难雕。", "断曰：此重审之卦，格合斫轮。乙庚作合成器，占主官荣爵显。惜申值旬空，则为斧斤未利，亦同朽木之论，必须待时改图，不当率意任行也。子午卯酉，皆临绝地，但宜了结旧事，不利谋为。昼占卯用，为勾陈内战，夜为天空内战。", "天时：久雨即晴，久晴有雨。家宅：不富，主有造作之事。婚姻：夜占吉，主吉期改卜。胎产：胎神临绝，防损。功名：良材未遇，改图有成。求财：未得，宜田产加绝。疾病：暴病吉，久病凶，年命寅酉尤忌。失脱：夜占难获。出行：有阻。行人：占弟兄即至。争讼：得贵力调解。兵战：事多虚声，防失众。", "毕法云：所谋多拙遭罗网，空上逢空事莫追。课经云：干上乘支之网，支上乘干之罗，我孟网罗他，他已网罗我。申遇空亡是为破罗。袖中金云：凡卯木坐空，亦谓之朽木难雕。宜弃业别作生理。订讹云：传见本日墓神，名曰旧轮，再斫来意，主退官失职，再谋兴复。"));
        set(1, new LiuReKe("丁丑日第七局　干上丑", "课体：反吟，无亲，并栏射。", "课义：动意先有，主客相就，契义难忘，欢欣如旧。", "解曰：丁主动。干，丁也；支，亦丁也。干支相加，两丁交互，是意已先动，两丁迭为宾主以相就也。亥中有壬，初传亥与口乾丁壬相合，而中末丑未，复往来稠密，町谓契义难忘矣。中传未之课丁，与末传丑之旬丁又成两丁相通，其欢忻之情，宜如旧而不改矣。德神发用，能解百凶。", "断曰：此反吟无亲之卦，为井栏斜射之格。占主一身两用，旁求易就，盲道难容。凡事速成，亦复易破。静则宜，动则扰也。昼夜贵人，皆克其所临之神，亦主贵人嗔怪。", "天时：求雨即得，占晴未晴。家宅：宅吉人和，但时有移动。婚姻：始见反覆，终得成就。胎产：主胎不宁，昼占吉，功名：大利，多历旧任，同官极相得。求财：转展不一，后有厚蓄。疾病：虽甚无防，须防反覆。失脱：寻得，逃亡自归。出行：多遇亲故。行人：即归。争讼：贵人不悦，变作不常。兵战：昼吉夜凶，交锋不一。", "毕法云：传墓入墓分憎爱。贵人蹉跌事参差。纂要云：丁丑日无克。丑宫癸水，遥射巳宫丙火，以巳上亥神为用，如傍井倚栏，斜冲射之，不出井外也。亥是官星德神，不宜值中末之墓，有官人占之不利。如寻常人，却喜鬼入墓也；心印赋云：登明忽来传上居，干求贵位必悬鱼。"));
        set(0, new LiuReKe("丁丑日第八局　干上子", "课体：重审，铸印，乘轩。", "课义：彼此不足，然后和睦，虽尔貌恭，终赖其仆。", "解曰：未为子害，丑为午害，日与辰先自有不足之处，然后未合于午，丑合于子，交相和睦也。然子午丑未，又实相冲，其为和睦，貌恭而已。午火能生支丑，而子水仍克干丁，惟赖中传戌土遥制。戌为奴仆，是赖奴仆之力以救患也。", "断曰：此得重审之卦，格合铸印，又得乘轩。君子大亨，常人不吉，事主迟钝晚成。又：一下用事，两上无权，事由阴小，不利长上，凡百宜循理而行。中传两蛇夹墓，若更夏日占之，则为火气太旺，致印模有损，反非佳兆。", "天时：旱热少雨。家宅：宅利，家长不宁，赖有干仆。婚姻：门第皆贵者吉，常人不利。胎产：生女必贵，防有损。功名：应举必捷，谒选必得，官必升擢，摄篆得实授。求财：仕者财旺，经营者难。投谒：和好。疾病：多凶少吉。失脱：终得逃亡，可获。出行：上官赴任为宜。行人：占同辈即至。争讼：为人所欺，防被囚禁。兵战：昼凶夜吉，宜慎重。", "毕法云：权摄不正禄临支。指南：癸酉二月亥将加午，有被劾者占回奏吉凶。曰：天空发用，必面君奏对。铸印乘轩，必迁官转职。虽四课上下冲害，支阴制禄，止于罚俸耳。交仲秋天诏临龙神日禄，午居丑上，必节钺斗生之分矣，果验。"));
        set(11, new LiuReKe("丁丑日第九局　干上亥", "课体：重审，从革，蓦越。", "课义：传生亥鬼，夜将无畏，两贵虽伤，空财生起。", "解曰：三传合成金局，生起干上亥鬼，亦可畏矣。喜夜将纯土，足以制之，庶无大害。昼贵临未，执拗受克。夜贵临巳，空亡被伤，不可干也。初传旬空，中传坐空，末传临空，财局已亡。而夜将生起，为复得之兆。", "断曰：此重审之卦，局合从革，酉加巳为愁课。课象本是和合，而日辰内外交冲，变为激拨。又且两贵受制，三传俱空，事起蓦然，有声无实。若安静守拙，反得自然快乐。倘强图非分，徒费奔波，终归无益。", "天时：孟雨还晴。家宅：宅旺发财，主人不宁。婚姻：后有，不吉。胎产：卯日生男，防不实。功名：惟宜纳财得官，或承袭固有之职，余无成。求财：可获珍宝，失而复得。投谒：不利见贵人。疾病：欠宁。失脱：或藏土中获之。出行：利西方。行人：在外占得，为已归；家中占之，为未动。争讼：遭官怒，然易解散。兵战：无利，防众心有变，或敌将窥营。", "毕法云：传财化鬼财休觅，两贵受克难干贵，鬼乘天乙乃神o。课经云：三传皆财，取之即生起干鬼，此如刀上之蜜，不可舐也。惟昼占宜以财告贵，乞恩泽而得补授。心印赋云：酉加巳上用朱禽，干贵求财有信音。"));
        set(10, new LiuReKe("丁丑日第十局　干上戌", "课体：昴星，掩目，虎视。", "课义：干支乘墓，昼占蛇虎，两贵皆空，略无少补。", "解曰：干支皆为墓神所覆，而昼占更逢蛇虎，不止昏晦而已也。夜贵旬空，昼贵又坐空，孟藉贵人之力，岂有所益哉。", "断曰：此冬蛇掩目之卦，昼占又为虎视逢虎，提防暗算，惊恐非常。倘更遇日月宿加四仲，便为天地二烦，其祸尤烈。幸初传午乘龙神发用，为日禄支德。虽传入墓，中反得末虎冲散，以凶散凶。凡事谨慎，虽有忧患，不为大害。", "天时：主风雨雷电，兼有虹霓。家宅：主昏暗不吉，昼占多怪梦惊恐，夜占宜防奸私。婚姻：不吉。胎产：占产即下，占胎防败损。功名：蹭蹬难成。求财：难得。投谒：不遇。疾病：在外凶，在家虽凶无害。失脱，难寻。出行：有惊恐。行人：即至。争讼：彼此俱损。兵战：不利。坟墓：西南有惊。", "毕法云：两贵皆空虚喜期，虎视逢虎力难施；人宅坐墓甘招晦，干支乘墓各昏迷。课经云：昴星传见蛇虎甚凶，惟午加卯为明堂，主万事昌隆。盖午为离明，卯为天驷、房心明堂之宿，遇凶成吉。又未加辰，丑加戌，为干支互坐丘墓，不宜两相投奔，必是愚蠢之人也。心镜云：用起昴星为虎视，秋分在酉知生死；女人淫u问何因，此地名为难禁止。"));
        set(9, new LiuReKe("丁丑日第十一局　干上酉", "课体：重审，间传，凝阴，孤寡。", "课义：身临空死，求财不喜，丁马俱逢，两贵徒尔。", "解曰：酉为丁之财神，又为丁之死方。今值旬空，而临于日干之上，是财在目前，既如空花，亦如鸩毒，不可求也。中传支马，末传旬丁，坐之必有动意。奈两贵皆空，虽入课传，亦徒然耳。干支上皆乘死神，万事不可轻动。", "断曰：此重审之卦，顺进间传为凝阴之格。起于阴小，不利长上。事多悖逆，不利先动。况一空再空，毫无捉摸，惟僧道九流遇此无防。士俗占之，皆主乖暌悔吝，凡百无成也。仲秋酉金秉令，庶为有益。", "天时：凝阴不解。家宅：主幽暗不明，人宅俱不吉。婚姻：不吉，亦不成。胎产：防不实。功名：屡谋不利，八月占得吉。求财：捕风捉影。投谒：不遇。疾病：暴病吉，久病凶，主伤寒症。失脱：不还，逃亡自返。出行：既出必复返。行人：占奴仆即至。争讼：惊恐，两造阴谋俱不成。兵战：昼占凶，夜占吉。", "毕法云：昼夜贵加求两贵，人宅皆死各衰羸。心印赋云：凝阴有履霜坚冰之象，事主幽暗不明。古鉴：己酉年午将加辰占前程，曰：太岁作朱雀，亥鬼临之，丁火受绝，主上书言事而被贬窜。酉为宠妾，亥贵加之，反克其妻。卯为日母，被制于酉。本宫见巳，会起酉丑，是宠妾而弃妻离母也，遭贬宜哉。"));
        set(8, new LiuReKe("丁丑日第十二局　干上申", "课体：重审，进茹，流金。", "课义：守之乏财，进步陷没，墓神助财，折本再发。", "解曰：干上空财不可守也。进而取酉，不特旬空，又为死地，能无陷没乎。喜末得戌墓，生起初中申酉之财。已失者可以复得。是动而谋为，反折其本。静而安守，反获再发也。", "断曰：此重审之卦，顺进连茹。卦体虽顺，卦德则逆。况三传及日课，阴阳俱落空陷，几如梦幻泡影。若安分守己，听其自然，见忧不忧，闻喜不喜，反可坐而获福。倘妄有作为，则不特如捕风捉月，亦且财耗人殃。三秋占之稍吉。", "天时：秋有雨，余占少雨。家宅：不发丁，家有孤独人。婚姻：无成。胎产：占胎防不实，占产立下。功名：难就。求财：经营折耗，坐守反有。投谒：昼占所投人，为事不安，夜占不遇。疾病：自愈，久病凶。失脱：出本家即难获，逃亡捉归。出行：无利而归。行人：占家长即至，余秋月至。争讼：主争空财，可不成讼。兵战：不遇敌，防失众。", "毕法云：空空如也事休迫，进茹空亡宜退步，所谋多拙逢罗网。袖中金云：顺茹申酉戌为流金，似霜桥起马。指南：庚寅七月午将加巳，占行人曰：进茹逢空，元武劫杀入辰之阴阳，主当地交界，有兵戈盗贼：宅中眷属，退居山水之间。九月节后，子丑日方到，后果如期，以避贼迟发也。"));
    }
};
