public class d14 extends KeEntry{
	public d14() {
        set(5, new LiuReKe("戊寅日第一局　干上巳", "课体：伏吟，自任，元胎。", "课义：连名状论，台阁上言，我求于彼，顺受和焉。", "解曰：初克中，中克末，末克干，三传递克，平日所为凶横，被人连名攻讦也。仕途则防台阁上言，宜自检束。自干传支，为我有求于彼，虽彼递相克制而来，我惟顺以受之。末助初生，终归于和而后已焉。", "断曰：此自任之卦，课传皆孟，格合元胎，有屈而不伸，伏而未发之象。事多暗昧，守正而静则亨，妄为而动则滞。又三传自内向外，迤逦递克，直至于干，其势可畏。喜中传折腰，末助初生，祸或中止，而反得阴助也。", "天时：昼占无雨，夜占有雨而尚远。家宅：昼占有火烛惊怪，夜占安稳。婚姻：夜占可成，防女不永。胎产：昼占有惊，夜占生男。功名：宜荫职，余防被劾。求财：艰难，夜占可得。投谒：半途而废。疾病：猝病凶，久病无妨。失脱：家内人盗窃，逃者隐乡邻。出行：夜占及申年月宜。行人：中路被阻，出旬始至。争讼：始干众怒，终得解。兵战：不利，宜静以待动。", "毕法云：我求彼事干传支，三传互克众人欺。秘要云：寅能助初生干，亦能克干，是两面刀。指南：癸酉六月未时，占官曰：斗罡居支前，传课刑克，蛇鬼入宅，龙神伤岁，当防弹章，人秋果被劾解任。"));
        set(4, new LiuReKe("戊寅日第二局　干上辰", "课体：知一，退茹，斩关，重阴，三奇。", "课义：三奇六仪，凡谋可施，稍嫌幽暗，斩关昼驰。", "解曰：三传四课，凡历戌亥子丑寅卯辰，旬首是戌，既为旬仪矣。而亥子丑遁为天上乙丙丁三奇，戌寅辰又遁为地上甲戊庚三奇，是三奇六仪，全入课传。凡有谋为，可以任其施展矣。稍嫌墓神覆日，不免幽暗不明耳。然斩关之课，昼占喜天乙神光乘丁而临天梁，天后掌华盖掩形，太阴主潜藏隐匿，可以疾驰万里，尚何阻隔之有哉：", "断曰：此知一之卦，连茹退传，格合重阴，事起同类。狐疑不定，六合并临，天关天后，传归元武，有奸私暗昧之象。干支墓覆，人宅俱昏，惟冬月占之，百凡顺利。", "天时：阴雨多。家宅：主昏暗，有逃亡者。婚姻：美中不足。胎产：胎气旺，或系私孕。功名：主有奇遇。求财：有获，防被盗窃。疾病：得之色孟，留连未愈。失脱：宜寻。出行：利避难速行。行人：关津有阻。争讼：有解。兵战：昼夜不宁。", "毕法云：后合占婚岂用媒，魁度天门关隔定。课经云：干为夫，支为妻，岂宜上乘六合，以应私情。曾门定章云：戊己之辰可逃亡，再得天上三奇乙丙丁者，追之不得。照胆秘诀云：除定开危卯未方，龙常阴合可逃藏。"));
        set(3, new LiuReKe("戊寅日第三局　干上卯", "课体：重审，间传，极阴，三奇，龙战，励德。", "课义：我就他侮，他中我计，天道好还，子卯狠戾。", "解曰：以干就支，戊受寅克，诚为所侮矣。殊不知戊课在巳，巳火反脱寅木之气，岂非中计而不觉乎。子卯固无礼相刑，寅来刑巳，丑亦去刑戌。天道好还，狠戾还得狠戾之报也。干支互坐墓上，必是愚人，主彼此各招昏晦。余传内虽有所喜之神，或年命上神克去，乃是心多懒退，不肯向前。", "断曰：此重审之卦，间传退逆，格合极阴。上下不顺，宾主失和，修德则升，悖理则斥。若年命更在卯酉，为龙战之象，主身心疑惑，进退乖离。惟旬奇玉堂发用，足以灾致福。", "天时：阴晦寒冱。家宅：人口不安，宜祈土神。婚姻：主为养媳，或夫不爱。胎产：昼占生男。功名：占弟兄，主有奇遇，显官更有升擢，卑秩不利。求财：得之不光明，终被耗脱。疾病：不妨。失脱：难得，逃亡自归。出行：不利。行人：有阻。争讼：主为田土、文书遭贵怒，后有解。兵战：不利。", "毕法云：宾主不投刑在上，人宅坐墓甘招晦。课经云：干上卯，昼将朱雀作日鬼，名雀鬼格。在朝防弹章，且不宜上书言事，反受黜责。心印赋云：朱雀克日事纷纭，望事求财总不成。玉成歌云：卯乘前二招唇吻，天空立用事无凭。"));
        set(2, new LiuReKe("戊寅日第四局　干上寅", "课体：元首，元胎，不备，乱首。", "课义：两位空申，岂胜四寅，守动皆祸，昼夜贵嗔。", "解曰：支临干而克干，乃是上门乱首，所恃者申金为救耳。然寅聚四鬼为朋，虽有两申　却值旬空，以两位之空神，岂能胜四寅之实鬼耶。守之固坐受其殃，动则又逢初传之克，皆不免于祸患也。昼夜贵人临于罡魁，谓之入狱，干之必生嗔怒。", "断曰：此元首之卦。四孟出传，格合元胎，合中有破，日遭辰克，有俯就他人之象。", "天时：先风后雨，冬占有雪霰。家宅：宅旺人弱。婚姻：夜占町用，主赘婿他家。胎产：平安，昼占生男。功名：申年月占者有成。求财：有得，不能安享。投谒：始虽交欢，终必吴越。疾病：主惊恐　老人、小儿甚凶。失脱：难得，逃亡不返。出行：不果，出亦不利。行人：未动身，申年月占在道。争讼：中有人两地指唆。兵战：昼占不利，夜占吉。", "毕法云：合中犯煞蜜中砒。吴越春秋：吴王孟赦越王，越王喜，范蠡占曰：戊，囚日也，功曹为蛇临戊，青龙在胜光临酉，死气也。时克其日，用又助之，天网四张，万物尽伤，王何喜焉。子胥果谏，复囚石室。精蕴：寅加巳，占婚曰：四课虽是互合，却不喜末传之申作天后来克日上之寅。寅鬼，夫也。青龙亦为夫，受后之克，夫妻不睦之象，且阳不备，必然克夫。"));
        set(1, new LiuReKe("戊寅日第五局　干上丑", "课体：重审，炎上，u女，斩关。", "课义：虎鬼夜潜，斩关昼占，我旺彼败，屋破人添。", "解曰：斗魁加支上发用，是为斩关。但寅鬼夜乘白虎，其象凶险，不如昼占也。火局生干脱支，若分人我，即为我旺而彼败。若论家常，则为人多而宅隘也。占者得此，切不可移居宽广，反生灾悔。", "断曰：此重审之卦，局成炎上，格合斩关。事多不顺，起于阴小，虚而不实。先喜后嗔，先合后散。利于行动，不利安居。六合并临于戌，而加鬼门，主有奸丑不明之事：丑戌相刑，彼此恃势，传虽三合，亦似笑中之刀，蜜中之砒而已。", "天时：久旱，密云有电，昼占可雨，夜占发风。家宅：人多宅少，防奴婢奸逃之事。婚姻：不可用。胎产：生女难产。功名：晚得，须人帮扶，亦防人暗损。求财：有阻隔。投谒：面和心背，恩中有怨。疾病：易瘥，防后再发。失脱：主奴婢窃逃，可获。出行：利避患潜踪。行人：即至。争讼：同伙参商，利于见官剖雪。兵战：昼占不吉，利于攻。坟墓：主墓中生白蚁。", "毕法云：合中犯煞蜜中砒，后合占婚岂用媒。课经：壬午年，正月初八日，亥将卯时占守城，曰：火局生身，但嫌干上丑为合中犯煞，须至癸未日，冲散丑刑，则援兵至而贼退矣。果子十三日援兵至，十五日贼移营去。"));
        set(0, new LiuReKe("戊寅日第六局　干上子", "课体：重审，四绝。", "课义：交互逢败，夏夜火怪，鬼虽是寅，熟视何碍。", "解曰：土败于酉，木败于子。日辰上交互相加，为我孟败他，他先败我。不宜讦人阴事，反被牵扯己之旧过，同时获罪也。夏占火鬼在酉，临支克宅，夜乘朱雀，当防火怪也。寅虽日鬼，然熟视盘中，下有未仇，上有酉难，彼自顾不暇，何能有碍于我哉。", "断曰：此重审之卦，两上不胜一下，事多不顺，起于阴小。又五行皆临绝地，干支互乘败神，凡事小心详慎，自能转祸为福也。昼占蛇入水，主虽有惊忧，后即消散。", "天时：有雨，雨中时见日出。家宅：主屋舍颓坏，夏夜防火烛。婚姻：昼占男家不孟，夜占女家不允。胎产：胎神临绝地，防损，昼占主男。功名：先否后泰，不利西方。求财：夜占吉。疾病：主气血衰败。失脱：昼占即获。出行：防有阻滞。行人：占有职人即归。争讼：主为下人奸私，或反被告讦。兵战：昼占防小挫，夜占大利。", "毕法云：干支皆败势倾颓。课经神将论云：蛇发用在干，曰坠水，主惊疑怪梦，凶灾不成。又云：子乘龙合，女占必受皇恩。子午乘龙，妻妾怀孕。又云：朱雀在酉日夜噪，主官灾起。心印赋云：子居巳位卯居申，结绝旧事最为真。"));
        set(11, new LiuReKe("戊寅日第七局　干上亥", "课体：无依，元胎，度厄。", "课义：彼此合忌，交淡如水，生克既无，如斯而已。", "解曰：干支上阴阳神，交互作六害，又两两作六合。似乎有利有害，然动而三传，往来空陷，方且无生无克，何有于害，何有于合。君子处此。惟有淡交如水，使牛克合害，皆归空虚，福亦不生，祸亦不成。如斯而已，岂可轻动哉。", "断曰：此反吟无依之卦，四孟课传，三下贼上，事主远而暗昧，反覆不定。亲者疏，合者散，迟疑隐伏。又罡魁易位，四月占之，为天罡课。万物被伤，小则阻滞，大则灾殃。幸来去俱空，有菩提无树、明镜非台之象，吉凶皆不实也，惟利九流僧道。", "天时：雨孟作而还止，出旬沾足。家宅：主有空巷，往来不宁。婚姻：不成，申年月昼占成。胎产：昼占惊恐，夜占女胎吉。功名：秋试有望，余无成。求财：宜结绝田产旧事，或借本营生。疾病：不利占妻妾。失脱：勿寻。出行：空劳往返。行人：在道未至。争讼：先见纷纭，后即解散。兵战：无成功。", "毕法云：彼此猜忌害相随，干支值绝凡谋决，来去俱空岂动移。秘要云：昼占三重白虎作长生，乃不幸中之幸。夜占三重青龙作日鬼，乃幸中之不幸也。袖中金云：反吟四孟，为绝元胎。"));
        set(10, new LiuReKe("戊寅日第八局　干上戌", "课体：知一，斩关，铸印，蓦越。", "课义：禄神及德，戌墓子克，夜贵闭口，作墓临宅。", "解曰：巳乃日之德禄，今居中传，却被初传子水克之。末传戌土墓之，则德禄俱废矣。未加寅夜占，为贵人临宅。然未乃旬中之尾，名为闭口，有求于贵，必不见诺。况未又为木墓，今覆宅上，必主晦暗而不明也。", "断曰：此知一之卦，干支上见刑害，事多起于同类。自知自见，不觉其为寇仇也。斗魁临日，格合斩关，忧疑险阻，必得与贵和同，方能免咎。虽名铸印，然已临水乡，火不胜水，又无印模，又名铸印不成。", "天时：雨多晴少。家宅：主形势低暗，灶被水坏。婚姻：吉，可用，夜占尤胜。胎产：主女，占胎不安，占产即下。功名：主得印绶，但有阻隔。求财：虽有得，即遇耗失。投谒：不合。疾病：主不能言语、饮食。失脱：即在家中，人不肯言。出行：马在贵前，同贵人行，吉，尤利避祸。行人：占尊长即至。争讼：事起蓦然，不利告贵。兵战：昼占有惊，夜占吉。", "毕法云：一旬周遍始终宜。课经云：子年戌月寅日卯将戌时，子为太岁，又为日财，德合加未为用。初传青龙，末传六合，为时泰课，占者万事亨利。照胆秘诀云：除定开危卯未方，龙常阴合可逃藏。"));
        set(9, new LiuReKe("戊寅日第九局　干上酉", "课体：昴星，虎视，三光，转蓬，励德。", "课义：首末皆空，刃虎居中，两贵勿恃，终守困穷。", "解曰：酉既旬空，丑亦坐空，占事必无终始也。午为羊刃，昼占乘虎，居中独存，动必遭其祸也。昼贵临酉空脱，夜贵临卯被克，皆不足恃也。三传无益，两贵难依，计惟守空败之酉金，终身于困穷而已矣。", "断曰：此昴星虎视之卦，主举动稽滞，多历惊危，难进易退，虚耗百出。又：转蓬为惶惑不定之象。三传阴甚，暗昧昏沉；幸而初末俱空，吉凶皆归无实。丑为玉堂旬奇，贵临卯酉，格合励德。君子俯仰无愧，自能避灾获福。", "天时：多雨多风，久阴。家宅：主宅旧人贫，防有败子。婚姻：不成。胎产：胎坐长生，昼占即产，夜占生女。功名：艰苦难得。求财：如捕风捉影。投谒：不遇，即遇无益。疾病：甚见危险。失脱：主在北方，难获。出行：孟行还止，既行复返。行人：未归。争讼：惊忧易散，被系即解。兵战：昼占吉，夜占不利。", "毕法云：人宅受脱俱招盗，虎视逢虎力难施。课经：六月午将寅时，虽是昴星虎视，即为三光。夏占得丑贵人临之，戊课在巳，是日有气。寅支传午，又是辰有气。既光其身，又光其宅，又光其动作，三者皆有光华，故名三光。占者万事吉昌，不劳费力也。"));
        set(8, new LiuReKe("戊寅日第十局　干上申", "课体：重审，元胎，不备。", "课义：虚生天干，中传财散，祸乃自招，夜鬼凶悍。", "解曰：申为戊土长生，临干发用，岂不为美，然值旬空则为虚生矣。亥为戊土之财，初传相生而出，岂不顺利。然坐空乡，则为财散矣。所存者，末传寅为日鬼，夜占乘虎，尤极凶悍，胡可近哉。而戊课之巳，自往加寅，以招其祸，于人乎何尤。干乘支马，支乘干禄，而寅虎临于亥地，是谓鬼自就生，不来侵犯也。", "断曰：此重审之卦。一下克上，事多未顺，必从内起，利后动不利轻举。又名生胎，凡事有鼎新之象。惜初中空陷，吉凶皆虚耳。若于七月得之，申金填实，好事即能真确矣。盖禄马俱临，时逢旺气，虽旬空无害。", "天时：秋占有雨，余占无雨有风。家宅：利出外，若在家不免虚耗。婚姻：七月昼占吉，余恐不成。胎产：胎神临败地，宜防损漏。功名：主有虚声荐引，或无实际。求财：虚费唇舌，或关田土，终防耗散。疾病：火症或头目之症，或得之女色，其像甚凶。失脱：难寻，或投势家，吏不敢擒。出行：主孟东行，不能离家。行人：占家长当日至，余在道。争讼：事无实迹，不利到官。兵战：防虚诈。", "毕法云：权摄不正禄临支，富贵干支逢禄马。纂要云：白虎名催官符，占赴任必速。又干上申昼乘青龙。若九月占，作月内之生气，虽目下未见峥嵘，却徐徐发福，惜值旬空耳。"));
        set(7, new LiuReKe("戊寅日第十一局　干上未", "课体：重审，间传，斩关，登三天，狡童。", "课义：自墓传生，终不能宁，递相挽抑，两贵皆丁。", "解曰：初传日墓，末传长生，自墓传生，先迷后醒也：而申值旬空，徒然受墓逢刃，终成虚语，岂能宁处乎。干上乘支墓，支上乘干墓，互相牵挽掩抑，以就昏昧：丑贵旬丁，未贵亦藏课丁，主贵人动变。惟贵登天门，仕宦占之最吉。", "断曰：此重审之卦，斗罡加辰发用，课名斩关，亦名登三天。事起阴小，利于后动，所往阻隔，不得遂意而行。况末传空脱，为孟登高而不能之象。夜占用起六合，终于天后，主阴私不正，君子所羞。惟罡塞鬼户，利阴谋私祷，避难逃灾。然利大人大事，不利常人小用也。秋占吉。", "天时：久旱得雨。家宅：主人昏宅暗，尤宜谨慎闺门。婚姻：夜占不宜。胎产：防因病损胎，七月昼占得男。功名：年命在丑，七月昼占大利。求财：无益。疾病：风瘫之症，虽危有解。失脱：主奴仆幼辈所窃，不可获。出行：利上任及逃避患难。行人：在路。争讼：防事情转大。兵战：昼夜皆吉。", "毕法云：罡塞鬼户任谋为，干支乘墓各昏迷，后合占婚岂用媒。课经云：贵登天门，神藏煞没；丑加亥者为的，四孟月尤佳，缘四维为月将也，神将论云：六合在辰曰违礼。天后在申曰奸私，起六合，谓之狡童。"));
        set(6, new LiuReKe("戊寅日第十二局　干上午", "课体：重审，进茹。", "课义：身宅罗网，守之自旺，动作他谋，许多恶况。", "解曰：干上午，支上卯，皆前一位神，名天罗地网。占得此者，兜裹身宅，不得亨快也。支上乘旺，止利静守在家。若妄有动作，则变为罗网　缠其身宅。初入墓中，继逢绝地，终见羊刃，凶咎百出矣。如得行年本命，冲破罗网　始见亨通。卯乘朱雀加支，天罡蓦越发用。主口舌文书，事起忽然，进凶退吉。", "断曰：此重审之卦，顺进连茹，名为升阶，有观光上国之象。凡占守旧则吉，积善余庆，主有人来相助，始虽阻碍，终得其援引卫护之力，巳午岁春夏逢之大利。", "天时：初见兴云，旋复晴皎。家宅：不吉，然宜守旧。婚姻：主仓猝而合，后防惧内。胎产：主胎气旺，昼占生男。功名：安命自来，求进反退。求财：无得。疾病：不妨。失脱：主有窝者，难获。出行：有阻，虽远必返。行人：忽然到家。争讼：终不明剖，不如和息。兵战：进中有退之象。", "毕法云：所谋多拙遭罗网，干墓并关人宅废。古鉴：一助教四十六岁，申将未时，占前程。曰：日上天罗、羊刃，支阴发用，传归日上，昼将青龙，当赴任矣。行年到亥，见子即为地网，必阻外艰。卯六数，辰五数，巳四数，此十五年，合至监司之职。自后入午天罗，必见谪降。二十三年，大运终矣，皆验。"));
    }
};
