public class d15 extends KeEntry{
	public d15() {
        set(7, new LiuReKe("己卯日第一局　干上未", "课体：伏吟，自信，三交。", "课义：卯鬼交逢，昼合夜龙，随时闭口，灾祸犹攻。", "解曰：卯为日鬼交逢，课传重叠五见，又况昼将六合，夜将青龙，亦皆木类，助鬼可知。未为旬尾，而临日干，是为闭口，当随时缄默，犹且不免昼虎夜蛇之凶。若使轻举妄动，则灾祸之攻，必不免矣。", "断曰：此伏吟自信之卦，三传四仲，格合三交，事主藏匿。静中求劳，动中得滞，有屈而不伸，前不能退，后不能遂之象。又支墓覆干，主自被昏迷。六合并临于卯，主阴私不明。若戍月占此，名曰天烦，多凶少吉。", "天时：先雨后晴。家宅：宅旺，人口未宁。婚姻：和美，但恐受制于内。胎产：不实或主聋哑。功名：可得，但宜静矣，动则有咎，夜占试必中。求财：当藉贵人之力。投谒：不遇，虽遇，两情不吐。疾病：主僵伏喑□，或不进饮食，作福吉。失脱：不出家中，逃者难获。出行：不能动身，行亦复返。行人：未至或先得音信。争讼：不利见官，和息为吉。兵战：昼占得金宝、美丽；夜占大胜，得货物图书，忌暗中侵袭。", "毕法云：鬼临三四讼灾随，丧吊全逢挂缟衣。神将论云：雀乘午火居南方，四刑冲煞，当主火烛，幸伏吟神煞不动耳。玉成歌云：伏吟举动心无遂，三交凶吉皆因内。"));
        set(6, new LiuReKe("己卯日第二局　干上午", "课体：重审，退茹，三奇，励德，蓦越。", "课义：遁传三奇，所卜皆宜，动用既可，退守无亏。", "解曰：三传旬遁得乙亥、丙子、丁丑，为天上三奇。日月星精，珠联璧合，占者得之，无往不宜也。动而三传，既得子亥之财，退守日上，又有旺禄之生。进退动静，无所不可也。丑为玉堂，子为明堂，亥为绛宫。", "断曰：此重审之卦，顺进连茹。凡事当周详审顾，循理而行，退中有进，孟行不行，孟止不止。事防再举，节外生枝。上神生日，传见三奇，逢凶不凶，遇吉愈吉。仲夏得此，年命又临旺神，必有奇福蓦然而至。惜其丑午穿心，子午冲击，未免美中不足。", "天时：风雨调和，岁时丰穰。家宅：宅吉人安，主有果木隙地。婚姻：吉，防翁姑不喜，昼占为佳。胎产；安。功名：夏占及年命生日者，必得显达。求财：遂意。投谒：有意外之遇。疾病：主气于呕恶心，愈防再发。失脱：难获，知者不肯言。出行：宜南方及夏日，必得称心。行人：速至。争讼：主为口舌虚诈，宜和解。兵战：昼夜皆利，益多损少。", "毕法云：尊祟传内遇三奇，旺禄临身徒妄作。课经云：酉加旬首戌上，昼乘元武为闭口卦。袖中金云：丑子亥入墓，有收藏之态，仕进无心。心印赋云：日上神与命上神相生合，喜福来亲。又云：子丑相加事必成，更逢吉将转欢欣。"));
        set(5, new LiuReKe("己卯日第三局　干上巳", "课体：涉害，间传，极阴，九丑，龙战。", "课义：身马宅丁，不容少停，元脱空败，阴极阳生。", "解曰：身乘卯马，宅乘旬丁，马动丁随，决主于动，不容少停也。末传酉乘元武，盗脱日干之气，又系旬空，又系土败之地，至此极矣。物极而变，或者阴尽阳生之候乎。此课因未加酉，故有此说，余不然也。", "断曰：此涉害之卦，格合极阴。又己卯日，丑加辰发用，是名九丑。戊己乃北辰下降之日。卯为阳盛阴绝之辰，刑杀不正，万物纽结，上下，事多凶咎。况涉害主久历艰辛，龙战主人心疑惑，极阴进寸退尺，动有乖离也。所喜上神生日，乃有人上门助我，不劳我力。然终嫌末传不实，有始鲜终耳。", "天时：久阴霖雨。家宅：主人多屋窄，幽暗不明。婚姻：相宜，然美中不足。胎产：主女胎，难产。功名：年命在巳者吉。求财：有得。疾病：主腹痛惊危，然不妨。失脱：难获。出行：不利。行人：有信，丑巳日至。争讼：难解，至达省部。兵战：防有变。", "毕法云：眷属丰盈居狭宅，龙加生气吉迟迟。课经云：昼将六月占，青龙乘生干之神，又作月内之生气。若君子孟施惠于人，未尝启齿，缓缓作福。观月经云：四辰连五日，九丑主恶声，大吉将加仲，大小两时并，三年与三月，不出大凶生。"));
        set(4, new LiuReKe("己卯日第四局　干上辰", "课体：弹射，三交，励德，二烦。", "课义：守辰厄塞，动值遥克，中末皆空，解忧释惑。", "解曰：辰乃水土之墓，守之则为墓所覆，厄塞而不通。动而逢子，为弹射之财，又遭贵人、勾陈两土夹克，虽有财而不为己用也，遂投中未。两传俱空，只宜解散忧疑。若占好事，是谓捕风捉影　凡百无成。", "断曰：此弹射之卦，三传皆仲，格合三交，事主动摇，人情倒置。利客不利主，利先不利后。况午加酉为死交，凡事失节阻碍，不能成合。辰阳与日干相竞，凶重有力。幸传空为遗镞失弹，祸福俱无成也。又日月宿加四仲，斗罡系丑未，行年立卯酉，名天地二烦，为咎不小。", "天时：求雨不雨，求晴不晴。家宅：身宅不安，宜谢工神。婚姻：刁；吉。胎产：防不实，兼防产难。功名：显者升迁，小吏迪否。求财：用尽机心，终归无益。疾病：春夏无事，秋冬及久病大凶。失脱：难寻。出行：不宜。行人：不来。争讼：解散。兵战：昼占开地千里，夜占士卒折伤。", "毕法云：夫妇芜淫各有私，彼此猜忌害相随。课经：三月十五口酉将子时，男命行年在子，日宿酉并临子，罡系未，为天烦格。女命行年在午，月宿卯并临午，罡系未，为地烦格，：德气在内，刑气在外，天地翻覆，莫大忧烦也。"));
        set(3, new LiuReKe("己卯日第五局　干上卯", "课体：涉害，曲直，u女，不备，魄化，飞魂。", "课义：上门见制，循环不已，昼贵休干，夜亦如是。", "解曰：支临干而克干，乃是上门乱首。日干见制，尤为不堪。三传亥卯未不离四课，是谓循环不已，事主牵连不断也。昼贵子临辰上，既入于狱。夜贵申加子上，又陷于空。虽曰昼夜贵加，恐干之者，徒劳无益耳。", "断曰：此涉害之卦，合曲直之局。事主疑难，留滞。孟行不行，先直后曲，多算则胜，少算则败。幸己日根固，春占甚宜也。四课不备，又传逢后合，必有阴私不明。辰来克日，上门乱首，亦象邪慝犯上，倘逢冬夏仲月，虎入行年，则为飞魂魄化，尤见凶咎，占者慎之。", "天时：多风或阴雨。家宅：春吉，主多树木，秋不利，防有奸私不明之事。婚姻：不宜。胎产：胎神坐墓，防有损。功名：可望显达，春占尤吉。求财：甚微，防因财生咎。疾病：主疫气，夜占虎临干鬼甚凶。失脱：难获。出行：投人吉，远行有阻。行人：即归。争讼：主为争财，牵连难结。兵战：夜占不吉。", "毕法云：后合占婚岂用媒，虎临干鬼凶速速。袖中金云：飞魂丧魄，二者聚散去来之神。若于日辰行年上见者，更值白虎入传，不见生气，健者忧衰，病者忧死。订讹云：己日根固，木得土为根也。若以日干官鬼论，大约君子吉，小人凶。"));
        set(2, new LiuReKe("己卯日第六局　干上寅", "课体：重审　斩关。", "课义：生气居中，子戌不容，身边官鬼，于火有功。", "解曰：巳火能生己土，今在中传，为初传之戌所墓，又为末传之子所克，是前后皆不相容也。寅虽日鬼，今临身上，反能克开戌墓，耗脱子气，使巳得安然，致生日之功，是巳之有功于己，非以寅之有功于巳耶。", "断曰：此重审之卦，事多不由己愿。中见更改，遍历艰辛，方可遂意。又斗魁加辰发用，格合斩关，利于避难奔逃，非安居之象。寅为日鬼，又为日德而生福神，可善可恶，介乎两端。处正则获福，妄动则招损。朱雀并临宅上发用，恐招文书口舌。幸入墓无冲，可以安处。若仕人却喜冲动也。", "天时：久不见日，亦有风雨。家宅：主人口不和，兼防火烛。婚姻：先难后喜。胎产：胎神临绝，子妻冲克，婢孕防损。功名：有成。求财：有得，交易亦成。疾病：主上逆呕吐，手足不举，大象无妨。失脱：宜寻戌月，擒盗必获。出行：利避患。行人：申日来。争讼：主经三四处官司。兵战：申严号令，无有不克。", "毕法云：巳坐戌，旦乘白虎作墓，主父母墓中生白蚁，如在堂主病灾。神将论云：朱雀南方，文书可防，辰戌投网，乖错遗亡。百炼金云：重审利主后举亨，用起朱雀士卒惊；朱空太阴日辰土，彼此欺诈机最深。"));
        set(1, new LiuReKe("己卯日第七局　干上丑", "课体：反吟，无依，三交，九丑。", "课义：俱鬼俱空，见凶不凶，夜忧酉鬼，火怪虚逢。", "解曰：卯乃干鬼，酉乃支鬼，今酉值旬空，而卯复坐空。虽俱可畏，而却俱消散。乍见为凶，实不成凶也。酉夜乘蛇临宅克宅，夏占更为火鬼，宜防火灾。然既落空亡，不过为火怪虚惊而已，曷足畏哉。", "断曰：此反吟无依之卦，卯酉相加发用，主事不和。亲情反覆，近者必远，合者应分。又格合三交，课名九丑。前不能进，后无所依，万物纽结，凡谋不利。元合蛇虎入传，主有盗贼阴私，惊疑病讼。反幸三传俱空，虚声无实。", "天时：忽阴忽晴，昼占密云不雨，夜占闪电大风。家宅：主门户动摇，更改修换。婚姻：不成。胎产：不实。功名：得而复失。求财：虚费奔波。投谒：反覆不遇。疾病：暴病惊忧，久病不吉。失脱：耗失无存，逃者难获。出行：孟行频止，卒不果行。行人：未动身。争讼：始见惊疑，终归解散，囚系者，即得释。兵战：交锋不成，虚声相恐，防失众。", "毕法云：后合占婚岂用媒，来去俱空岂动移。课经云：酉夜乘蛇克宅，夏占主遭天火，宜以井底泥涂灶禳之。指窍云：卯酉为分，气分则异，异则争，定主分争之事。卯为外门，主生气。酉为内门，主杀气。"));
        set(0, new LiuReKe("己卯日第八局　干上子", "课体：知一，铸印，乘轩，励德。", "课义：身宅俱贵，夜费昼惠，末来助初，卯鬼无畏。", "解曰：昼贵临身，夜贵临宅，是身宅俱贵也。然夜贵在支，为鬼有损于家，不如昼贵之日财，于身有实惠耳。初传巳火生干，末传卯木助之，卯虽日鬼，助生克墓，殊为有益，何足畏哉。", "断曰：此知一之卦，事起同类，恩中有害，贵于和衷安分，方获吉庆。传见铸印乘轩，仕宦得之，官禄通显，常人反为灾咎，且主每事迟钝。传墓入墓，夜贵克宅，昼贵与己课作六害。凡占必尊卑相轧，邪正同处，以致人口灾患。", "天时：晴多雨少，时有风。家宅：不利，主家庭神位不肃。婚姻：相宜，恐不成。胎产：安。功名：朱雀克贵，占试不宜，占有印官员吉。求财：守己财，有生发。疾病：昼占父母沉重，夜占凡病见凶。失脱：勿追，恐反有伤损。出行：宜上任。行人：归。争讼：不利，求贵宜和。兵战：昼占不畏，夜防失物。", "毕法云：末助初传三等论。古鉴：酉命生人，午将加丑占前程，曰：子加未为鼠，忌羊头。戌加巳为蛇，惊犬吠。妻制父母，身克子息，宅贵空亡，神像不整。铸印而元武破范，白虎损模。乘轩而虎金伤卯，谓之堕轩。心术不正，好谈人短，功名必无成。又占雪曰：申为水母，正在当权。火伏水腾，申子会水，加临日辰；又三传顺布，末传卯作白虎，夜半作雪　当有七寸，果然。"));
        set(11, new LiuReKe("己卯日第九局　干上亥", "课体：涉害，比用，曲直，乱首，不备。", "课义：灾祸难遏，夜虎居末，自招其咎，何由免脱。", "解曰：亥财临于日上，不为我用，反与未卯合而为鬼。灾祸之生，难以止遏也。夜占白虎乘末传卯鬼，为害滋甚。良由已去加支，自取乱首，非关他人之过，其咎何由脱免也哉。涉害亥历戌土一重，未历卯乙二重，又未在仲，亥在季，故应用未。", "断曰：此涉害之卦，局成曲直。始难终易，事贵和同。四课不备，三传循环，遇屯宜止。又日往加辰被克，乃不自尊重，致取其辱。财近身而传化鬼，亦防贪财致悔。", "天时：多风少雨。家宅：主上下不和，宜谨慎门户。婚姻：不宜，防夫受制。胎产：胎神坐长生，宜胎不宜产。功名：吉利，宜纳粟求官，改易姓名。求财：防因财致祸。疾病：天后受克临死乡，不利占女病。失脱：盗神生鬼，或致成讼。出行：求官吉，宜春月。行人：即至。争讼：主为妻财，或奸私不明之事，防枷扭。兵战：昼占大胜，夜占无威，军中不宁。", "毕法云：后合占婚岂用媒，二贵皆空虚喜期。课经云：干支自作三合，三传不出四课，名回环格。占凶凶成，占吉吉就。心镜云：乱首下欺上，若更将得卯酉，神得后合，主男女讹杂。再值魁罡勾空，至有不可名言之事。"));
        set(10, new LiuReKe("己卯日第十局　干上戌", "课体：重审，三交，蓦越。", "课义：交车颇美，脱空初值，元卯末逢，穿窬防备。", "解曰：卯与戌合，午与未合，干支上下，交车相合，大为美事。但初传之酉，既落旬空，又脱日气，值此只宜解释忧疑耳。凡所谋为，皆不利也。太冲类为贼人，狡猾不正。又乘元武，居末传作鬼，当提防穿窬之辈。", "断曰：此重审之卦，孤辰发用，占骨肉至亲不利，占身主见孤独，别离乡井，自立门户。惟恐宜于僧道九流，及解仇释怨，消忧散祸。又格合三交，利隐匿避灾。占者当固穷守分，大辨若讷，大巧若拙，方能免咎。", "天时：重阴无雨。家宅：虚耗不足，子加酉乘贵人，主尊长有灾，或是妇女奸私口舌。婚姻：不宜亦不成。胎产：占胎不实，占产即生。功名：主改任。求财：耗散。投谒：一片虚情，全无实际。疾病：新病即愈，久病凶。失脱，不获。出行：防损失。行人：望久不至，后忽然至。争讼：难中生易，宜解怨释结。兵战：军政更张，防失众心。", "毕法云：权摄不正禄临支，交车相合交关利。课经云：交车六合，传见三交，凡交关用事，必有奸私，或相交涉二三事。指窍云：人只知旬空为十干不到处，不知惟虚能起化，此正天之中也，故曰天中煞。"));
        set(9, new LiuReKe("己卯日第十一局　干上酉", "课体：遥克，弹射，间传，纯阴，溟蒙。", "课义：弓在弹亡，夜失须防，六阴全备，两贵渺茫。", "解曰：日克遥神，是名弹射。然日上酉既空脱，初传亥又坐空，岂非弹丸已失，虚张空乎。空财夜乘元武，须防遗失也。丑卯巳未酉亥六阴全备，不见一阳，昏昧太甚。昼贵子加戌。入狱受制，夜贵申加午，空亡被克，皆渺茫不足恃也。", "断曰：此遥克弹射之卦，日之阴阳自相战，多主外事。虚声相射，不得实用，耗费百出，凡事难成。若遇非常惊恐，却反消散，不足为患。吉不成吉，凶不成凶也。课传纯阴，阴极者阳自生，知机静守，待时而动，斯为得之。", "天时：占雨还晴，占晴久阴。家宅：幽暗不明，防阴私走失。婚姻：不成，成亦不利。胎产：有虚惊，却易产。功名：不利，位高者危。求财：虚耗不足。投谒：干贵无益。疾病：日马乘丧车临病，占病大凶。失脱：不获。出行：仲秋吉，余不宜。行人：必至。争讼：易为解散，有前程者不利。出兵：防诈变，不吉。", "毕法云：六阴相继尽昏迷，两贵受克难干贵，人宅受脱俱招盗。课经云：溟蒙之格，利阴谋私干，不利于公。兼天将蛇元后合，干支皆乘盗气，用神坐空，费力不可言也，，指掌赋云：从魁若乘武合，妻必怀娠；太乙若逢白虎，家多疾病；财遇天中煞，产业倾颓。"));
        set(8, new LiuReKe("己卯日第十二局　干上申", "课体：重审，进茹，斩关。", "课义：虚贵干遇，宅乘日墓，拱三欠一，惟禄难顾。", "解曰：申乘夜贵临干，恰是旬空，遇而不遇也。辰为干墓临宅，宅必昏迷也。干上申，支上辰，内拱巳午，欠一未字。午为日禄，进不得未，虚拱于前，则禄不足顾矣。三传自墓传生，为先迷后醒之象。", "断曰：此重审卦，顺进连茹，进中有退，节外生枝，迟则阻滞，急则顺利也。墓神发用，常人安静则吉，有官者须努力向前，庶可得禄。斗罡加辰发用，又勾陈并临，犹人遇凶神，重土闭塞。天关难度，喜贵登天门，利于隐避，宜更新外出也。", "天时：日多云，夜多露。家宅：宅卑暗，防时有斗殴之事。婚姻：可成。胎产：胎旺，防不足月。功名：努力可得，初见蹭蹬。求财：主有恩赐钱帛，或防弟兄争夺为患。投谒：勤求有益。疾病：淹缠，不妨。失脱：主在道路，或系奴仆远遁。出行：虽有阻隔，忧疑亦吉。行人：可至。争讼：主争田斗杀，防问罪。兵战：利客不利主，进退疑贰，稍伤士卒，不为凶。", "毕法云：所谋多拙遭罗网，干墓并关人宅废。占验：寅将加丑占官职，曰：申作长生，贵人生己土，当有尊长提携。又秋金司令，当作刑官。行年白虎乘丁，其势必动。午为禄神临马，朱雀并之，临巳为炉冶，当有炉冶执事。悉验。"));
    }
};
