public class d16 extends KeEntry{
	public d16() {
        set(8, new LiuReKe("庚辰日第一局　干上申", "课体：伏吟，自任，元胎。", "课义：空禄宜舍，去乘财马，官鬼长生，须分真假。", "解曰：申为日禄，既系旬空，又乘昼虎，不宜守也。舍而之中传，既为日财，又为辰马，足可乘矣。末传巳本长生，然夜乘朱雀，为克我之将，是长生而官鬼也。必昼乘勾陈，实有以生我者，方是长生。中有真假之分，可不辨乎。", "断曰：此伏吟自任之卦，三传皆孟。利上不利下，利远不利近。伏匿不动，触则成咎。占者守正修德则亨。孤辰发用，多虚少实，改动为宜。虎旺金乡，勾陈捧印，蛇生角，考试仕宦皆吉。", "天时：有孟雨之状，得风辄散。家宅：安静，昼占防虚惊，夜占防妇女口舌。婚姻：不宜。胎产：占胎，日上神乘虎克胎神，防堕，占产即生。功名：申月巳将占者荣贵，余不过虚喜。求财：所得细微。疾病：不妨，老人及小儿忌。失脱：难寻。出行：有阻。行人：近者即至，远者回轮。争讼：惊忧易解，患难易消。兵战：有更变失众之象。", "毕法云：宾主不投刑在上，后合占婚岂用媒。课经云：丑是旬丁，因父母之墓田而凶动。古鉴：丁巳生人，戊申年子将占武职曰：武职主兵，金旺为宜。今天地盘之申俱空，太常不上传，武五位而禄虚，非吉占也。幸寅上青龙入庙，巳上朱雀为本命长生、官星学堂，改文必显，果以己酉庚戌联捷。"));
        set(7, new LiuReKe("庚辰日第二局　干上未", "课体：元首，退茹，联芳。", "课义：始贪货财，丁马随来，详分昼夜，冬雀火灾。", "解曰：卯为日之财，临宅发用，似乎可取，故人必贪之。然中传寅为日马，末传丑为旬丁。方孟取财，而丁马随至，则宜相机而生变动。若株守初财，必无利也。卯乘朱雀临宅克宅，冬月火鬼在卯，昼占主有火灾也。", "断曰：此元首之卦，占事多顺，忧喜皆实。连茹逆传，退中有进，和平安静，自当化难为祥。用神与支作六害，又生鬼克日。凡得利之地，须防暗侵常存，退步为佳。", "天时：风多雨少。家宅：防有口舌文书之事，或奴仆逃走，冬月防火灾。婚姻：不利，或因妻而致讼。胎产：主有私娠，或涉口舌。功名：宜损财得官，或藉妻家之力。求财：有得，防致祸。疾病：主长上有灾。失脱：难获，主捕役受贿。出行：昼占为贵人差使。行人：在道。争讼：为财起见，或至破家。兵战：互有胜负。坟墓：末传丑是旬丁，必因父母之墓田而凶动。", "毕法云：金日逢丁凶祸动。课经云：冬占火鬼克宅，末传又乘丁克日，必遭天火，宜以井底泥涂灶禳之。神应经云：丑是旬丁，旺相为田，囚死为墓。指掌赋云：逆传连茹，卯寅丑联芳悔吝，须知否极泰来。"));
        set(6, new LiuReKe("庚辰日第三局　干上午", "课体：涉害，见机，间传，顾祖。", "课义：彼此不和，寅来教唆，尊求卑下，莫被财魔。", "解曰：干被午克，支被寅克，彼此皆受克制，不能和美。而寅为日财，乃助初克日，有如教唆词讼之人，若取此财，必生灾悔矣。自干传支，是谓以尊求卑，谦下乃宜也。末传寅财来冲庚位，当视如魔怪，不可为其所惑。", "断曰：此见机之卦，事多阻滞，虚声无实，吉不成吉，凶不成凶。改变动移，贵乎得中守正。又贵人立卯酉，大位者占之，必主升迁荣庆，常人则身宅不安也。", "天时：占晴未晴，占雨未雨，必待出旬。家宅：不安，宜谢土神，或有孕女借歇。婚姻：不宜。胎产：占胎安，占产不宜。功名：官禄空亡不利，惟五月昼占吉，可得科名。求财：防因财而致祸。投谒：主宾客会集。疾病：月内见凶。失脱：宜寻，逃亡自归。出行：不利。行人：即至。争讼：主有教唆之人，或为吏曹，或为道士，或有胡须人，或姓从木旁人，或属虎人。兵战：昼占大胜，夜占众心不宁。", "毕法云：干支全伤防两损，我求彼事干传支。精蕴云：末虽助初而坐空乡，无力克干，为抱鸡不斗之象。又午辰寅为顾祖格，盖午为寅之子孙，寅乃午之长生，自午传寅有复旧庐之象。凡谋皆吉，惟庚日占病凶也。"));
        set(5, new LiuReKe("庚辰日第四局　干上巳", "课体：元首，元胎。", "课义：夜巳鬼名，昼巳长生，亥分生克，能败能成。", "解曰：巳也，昼占乘勾陈辰土，土能生金，则为长生矣。夜占乘朱雀午火，火能克金，则为日鬼矣。亥水居末，若径冲克巳火，则巳火不能伤干，而为救神。若作三传递生，则亥生寅，寅又生巳，反为助初而克干，是成败皆在亥也。", "断曰：此元首之卦，三传俱孟，亦名元胎。顺利之中，多有隐伏。干支阴阳，俱带刑煞，切不可妄动招悔。喜巳为长生，寅为驿马，三传递生，官临日上发用，又且勾陈捧印，蛇化龙。仕宦占之，更逢旺相，显达何疑，常占不利。", "天时：久旱得阴，出旬有雨；久雨得止，出旬大晴。家宅：宅吉人不旺，防有奴仆盗窃。婚姻：和合。胎产：胎神临败地，防损。功名：四七月占，大吉。求财：昼占不吉，夜占利。疾病：沉重缠绵。失脱：可获，逃者自归。出行：改期上官者吉。行人：在道，戌日至。争讼：防有刑罪，和解则吉。兵战：昼占防折伤士卒，夜占军容不整。坟墓：元武临墓，防水侵或贼损。", "毕法云：三传递生人荐举。纂要云：夜占朱雀作日鬼，加干为雀鬼格。朝官防弹章，不利上书言事，临年命可用。神将论云：勾陈在巳，金生火旺，故主迁拜。蛇在寅，旺则生角成龙，衰则失时，反为蜥蜴。"));
        set(4, new LiuReKe("庚辰日第五局　干上辰", "课体：重审，润下，不备，斩关。", "课义：彼求于己，似乎不美，事到周全，致成迤逦。", "解曰：自支上神传至干上神，为彼求于己。辰既克子，子亦脱庚，似乎不美也。然及其周全合局之后，则末生中，中生初，初复受生干日干，致成迤逦递生。传课回环，绝无间杂，谓之不美，不可得矣。", "断曰：此重审之卦，合润下之局。占者当详审而后行，流动而不息，以应其象。三传不出四课，主谋望必成。亲情和合，所谓三传皆子孙，不求财而财自至也。凡三合成局，事主牵连，终见耗脱。中传空陷，亦名折腰格也。", "天时：雨多，草木盛长。家宅：虚耗，主弟兄少，树木多。婚姻：夜为子孙，占吉可用，主女家微贱。胎产：胎神临墓，主产迟。功名：主有人推荐，却见虚耗。求财：得不偿费。投谒：和合而有不足。疾病：子蛇主妇人灾哭，暴病即愈，申酉月占子孙病即愈。失脱：盗神临空地，盗去可以复还。出行：有虚惊。行人：申戌日来。争讼：和解。兵战：昼占有惊，夜占大胜。", "毕法云：三传递生人举荐，万事喜忻三六合。课经云：未加亥为贵登天门，神藏煞没。又支加干而生干，名自在格。订讹云：水性就下，吉凶事皆下人当之。三传喜顺，元武并临，定主盗脱。"));
        set(3, new LiuReKe("庚辰日第六局　干上卯", "课体：涉害，绝嗣，蓦越。", "课义：四课犯上，祭祀绝享，中丑遁丁，动之灾障。", "解曰：四下贼上，为绝嗣卦，故曰祭祀绝享。守其干上固有之财，犹之可也。若动而之传，初逢午鬼，显而易知。中传之丑，虽曰生干，却是干墓，又旬遁为丁，亦作日鬼。末传空陷，德禄无气，重重灾障，何可当哉。", "断曰：此涉害之卦。课经订讹谓四下犯上，被上夺禄，应名无禄。占主孤独失业，小人无礼，横灾暗祸，事起蓦然。利主不利客，宜后不宜先，静而必动，守正则吉。", "天时：风雷电闪，孟雨还止。家宅：午乘虎加亥，防火灾；亥乘朱加辰，主小儿哭泣。婚姻：合而不合。胎产：亥日生，防损堕。功名：始利终钝。求财：宜守己财，妄求反失。投谒：见喜，有相助之意。疾病：主血妄行，或寒热，或痨瘵危笃。失脱：难寻，主妇女拐逃。出行：不利远行。行人：亥日至，或当日至。争讼：主横逆之事，易结。兵战：不利，防中变。", "毕法云：金日逢丁凶祸动，胎财生气妻怀孕，胎财死气损胎推。订讹云：此课神将凶，骨肉分离。神将吉，来意主分财异居。曾门云：此占利居家，不利为客，宜训教子弟。指掌赋云：初生中，中生末，名遗失而事久陵夷。凡发用之气要聚于干上，初生中末，则益我之气薄矣。"));
        set(2, new LiuReKe("庚辰日第七局　干上寅", "课体：反吟，无依，元胎。", "课义：动意虽切，满目空绝，夜逢五虎，惊不可说。", "解曰：遍地禄马，往来冲激，动意可云切矣。然申乃旬空，寅临绝地，虽孟动而不可得也。申本虎也，夜占白虎在寅。三传及日上阴阳之神，共为五虎，逢此岂非至惊至危乎。幸其俱空，或虚惊多而实祸少耳。", "断曰：此反吟无依之卦，三传皆孟，四孟临绝，为绝元胎之格。凡事无成，况中传旬空，初末坐空，日上两神元不空者，营营扰扰，真如捕风捞月。惟遇难逢凶，虽惊危之至，却得消解，反为福也，宜静以待动。", "天时：久雨难晴，久旱未雨。家宅：贫耗劳苦，防婢仆奸私逃盗之事。婚姻：不宜。胎产：防损。功名：难成。求财：徒费奔波，反多折耗。投谒：十五一遇。疾病：夜占极凶，暴病瘥，久病危。失脱：主宅内人盗，反覆不一次，失散难追。出行：防惊恐失脱，往而复返。行人：在路，或别有所往。争讼：不利，若在刑狱，却得即释。兵战：彼此虚作声势，若被围困，即解。坟葬：屡迁不宁，防水防贼。", "毕法云：后合占婚岂用媒，昼夜贵加求两贵，来去俱空岂动移。袖中金云：寅申乘龙，隔墙有祸。指掌赋云：年临孤寡，自甘半世孤灯；日遇空亡，多主首阳饿死。"));
        set(1, new LiuReKe("庚辰日第八局　干上丑", "课体：重审，蓦越，引从。", "课义：己财先费，方获厚利，初引末从，旬丁昼贵。", "解曰：寅为日之财，因坐空乡，故为财费。三传日克初财，迤逦克去，无非财也。财复得财，非厚利而何。干上丑为旬奇，得初传寅为前引，末传子为后从，丑旬遁为丁鬼。昼为旦贵临身，夜为帘幕临干，仕宦者利有攸往矣。", "断曰：此重审之卦，前引后从，日干拱贵，乃升迁吉兆也。虽墓神覆日，中传入墓，而丑未相冲，两墓俱开矣。所惜者用神坐空，则前引无力，未免虚声，难期实效耳。又未为干奇，寅为支仪，酉月占百事顺利。", "天时：晴雨调和，而百谷少实。家宅：人安吉，颇虚耗。婚姻：妻财坐空不宜。胎产：安，主婢孕，昼占生女。功名：八月占科第，升迁吉，余占虚声无实。求财：小财不去，大财不来。疾病：暴病吉，久病凶。失脱：难获。出行：另改日期。行人：来迟，音信浮沉。争讼：消解。兵战：更变不一，秋占利。", "毕法云：前后引从升迁吉。课经云：庚辰日干上丑，十月占为凶怪格。谓月厌大煞，天日飞廉，丁神墓神俱临也。若更临年命，主极怪极凶。订讹云：初末传拱干，主得人提携成合。两贵拱干，主官职升擢。"));
        set(0, new LiuReKe("庚辰日第九局　干上子", "课体：元首，润下，不备，励德，闭口。", "课义：课传循环，脱空在关，无心俯就，两贵难扳。", "解曰：三传不离乎四课，循环而不穷也。事主牵连不断，初末水土皆脱，中申又值旬空。三传虽曰递生，实无所益。俯就干支，支既不甘受脱，我亦何甘受元武之耗乎。两贵临门，昼贵脱败，夜贵被克，俱不足扳也。", "断曰：此元首之卦，合润下之局，事虽顺而迟缓，亦主动而丛杂。三传盗泄旺气，又兼空脱逢空，虽见有人相助，究归虚耗。譬如人肉，充皮润而骨髓已空，徒有其形，未免痿废矣。秋冬稍吉，亦主先难后易。", "天时：雾露连朝，不成甘雨。家宅：门户冷落，人口衰残。婚姻：不利。胎产：胎安难产，主生女。功名：虽虚声相引，毫无实际。求财：百般耗折，或为子孙蠹耗。投谒：见贵无益。疾病：上下俱脱，必主吐泻虚损。失脱：难获。出行：多费。行人：申日至。争讼：孟解不解。兵战：昼占凶，宜止息，夜占胜。", "毕法云：避难逃生须弃旧，脱上逢脱防虚诈。古鉴：癸亥生人，戊申六月午将占身位，曰：满盘盗气，一生受子孙之累。初任巡辖，次任巡检，水陆三任，水边屯住。宅上蛇，十二年主怪出，寄居避之。申死在子，子九申七，寿六十三。其人前后十男　一女，磨折破家，无一长大者。历任皆验。"));
        set(11, new LiuReKe("庚辰日第十局　干上亥", "课体：弹射，元胎。", "课义：夜贵闭口，马负财走，幸而逢生，空亡随后。", "解曰：夜贵旬尾临辰，是为闭口。贵人不发言，干支何益。寅乃日之财，马负之而发用，宜远动求财。若株守则财反走失也。中传逢日干长生，诚为有幸。奈末传申值旬空，如有缺陷，随后而至，求事之成难矣。", "断曰：此弹射之课，日上两课自战，作事无力。多主外来，不干于内，未可出尖。末见空亡，是为失丸遗镞。主蓦然有灾，虚惊无实。又申加巳为生胎，名为忧课。主狐疑犹豫，有始鲜终，吉凶皆不成也。", "天时：有风，见虹无雨。家宅：主衣冠门第，秋占有喜。婚姻：七月昼占吉，余不利。胎产：卯临败地，防损。功名：申年月吉，余美中不足。求财：宜远求速取。投谒：不见礼遇。疾病：暴病愈，久病凶。失脱：急追，逃亡自归。出行：有惊险。行人：在道将至。争讼：利客不利主。兵战：不吉。", "毕法云：贵虽坐狱宜临干。纂义：辰戌日为贵人入宅。古鉴：己未生人，子将占应试，曰：夜贵在宅，自身难显。日上子息，来传我气，子得中第。勾陈滞神，身当患痢不入场。寅妻乘白虎加盗气上，十月防炊臼之梦。五旬后当得官，但未为闭口，巳临绝地。得官后一二年，当办终事矣，俱验。"));
        set(10, new LiuReKe("庚辰日第十一局　干上戌", "课体：涉害，间传，斩关，涉三渊。", "课义：初中泛浮，两虎堪忧，乘生坐克，乐里成愁。", "解曰：初传申为旬空，中传戌为坐空。虽有德禄旬仪之美，泛泛浮浮，与我何涉。申，虎也。又乘昼虎，巳不堪其忧。况重以戌乘两武，子为脱乡乎。然且忧乐相半，何也？盖庚上得戌生乐矣，而下被午克。辰上得午生乐矣，而下被寅克。乐一边即愁一边也。", "断曰：此涉害之卦，又为涉三渊之格。历遍艰辛，终防侵害。凡百求谋，劳而无得。惟秋占为吉，乃自立之象。贵登天门，罡塞鬼户。六神藏，四煞没，斩关得断，华盖隐形。利逃灾避难，不利追捕也。", "天时：大风忽止，阴雨连绵。家宅：宅旺人隆，不利举动，暗中有损。婚姻：不成，主女有病。胎产：占胎不安，占产即下。功名：无成。求财：不得。疾病：禄空乘虎主绝食，危甚。失脱：不获，逃亡，不可踪迹。出行：利避难远去。行人：即来。争讼：速解。兵战：待时而动。", "毕法云：虎乘遁鬼殃非浅，罡塞鬼户任谋为，互生俱生凡事益。袖中金云：魁罡临日辰，传有虎阴申酉，为斩关得断，逃者永不获矣。指掌赋云：先生后克，乐极生悲，即毕法所云乐里生忧格也。"));
        set(9, new LiuReKe("庚辰日第十二局　干上酉", "课体：蒿矢，进茹，丽明。", "课义：支干交会，拱贵在内，蒿矢带金，申空无畏。", "解曰：巳与申合，酉与辰合，干支上神，与干支交车作六合也。巳酉拱夹三传，而初传末传，又拱中传夜贵在内，凡事易成。蒿矢得申金，是为有镞。又乘昼虎，势若可畏。然值旬空，则为遗镞，不过虚惊而已。", "断曰：此蒿矢之卦，顺进连茹，进中有退。事主动摇，人情颠倒，吉凶倚伏。又魁罡临于二八之门，为天网四张，万物被伤之象。喜末传空陷，始虽惊恐，终得无事。利主不利客，利小不利大，忧在西南，喜在西北。", "天时：忽晴忽雨。家宅：主有孝服，阴人离散，或有孕妇寄宿。婚姻：再议，媒妁虚诳。胎产：胎神临绝地，防伤。功名：主有官无禄，七月占吉。求财：申为旬外甲财，主为同伙弟兄脱耗，改谋可得。疾病：凶，宜祈神作福。失脱：难见。出行：宜往西北，亦防耗脱。行人：即来。争讼：主起于口舌，宜和。兵战：夜占不利，昼占胜。", "毕法云：所谋多拙遭罗网。心镜云：遥神克日名蒿矢，虽然射我不足畏，家有宾来不可留，每忧口舌西南至。袖中金云：顺连茹午未申为丽明，威权独盛。指掌赋云，顺连茹空，名曰声传空谷，退吉而进则不宜。"));
    }
};
