public class d18 extends KeEntry{
	public d18() {
        set(11, new LiuReKe("壬午日第一局　干上亥", "课体：伏吟，自任，杜传。", "课义：禄上空常，财禄可伤，倘若他逐，暗伤不睦。", "解曰：旺禄临身发用，昼乘天空，夜乘太常，禄被天将所伤，且又带刑，甚觉难守。未免就中传之财，不知午火又临于劫财之上，是财复受伤，故曰财禄可伤。财禄虽伤，尚宜谨守，终有所益。若或弃此而改图，必就末传之子水。子乃网罗羊刃，又是败气劫财，冲午克午，暗中相伤，岂有一点和气乎。", "断曰：伏吟自任，刚暴自用，任其所刑，必成过愆。今者自刑，乃为杜传，有杜塞不通之象。", "天时：天罡居于本位，水火相参，忽雨复晴，既晴复雨之象。家宅：宅乘自刑，必有所伤，复乘后合，阴私难免。功名：日禄与日德发用，初必得意，后不称心。求财：有得有失。婚姻：男不佳，女不吉。胎产：七月占胎，必然妻孕，难免刑伤。疾病：恐患不语、呻吟之症。出行：网罗居末，又无丁马，不动之象。行人：本家暂出，可归。兵战：昼占欺诈，夜占稍可。", "毕法云：胎财生气妻怀孕(七月占)，胎财死气损胎推(正月占)。占验：十月寅将加寅占升转，曰：现在食禄，又兼太常，主有兼职，却未升转。幸得监司恳告解任，盖子午乃官贵往来之所，亦监司之家。月建前神，亦为监司，必恳于此解任也，果然。"));
        set(10, new LiuReKe("壬午日第二局　干上戌", "课体：元首，退茹，六仪，斩关。", "课义：关隔频频，虎戌临身，退求生意，空亡后蹲。", "解曰：戌加亥发用，为魁度天门，凡占皆主阻隔。戌土作白虎临干克干，诸事皆极凶险，急切不能逃避。未免退归申酉金地以求生，又逢空陷。真是前不能进，后不能生，进退无门之象也。幸第四课辰蛇一冲，凶危可散。", "断曰：六仪斩关，本为行动之吉象。今逢关隔退茹，则寸步难行。且虎作明鬼，昼夜随身而不舍，危亦甚矣。倘逢申酉月将，与申酉月建，始不言凶。", "天时：课名关隔，晴不见日，阴不见雨。家宅：昼占为贵人临宅，家有贵人则吉，无贵人则凶。功名：催官使者发传，官必速得，后恐不吉。求财：财爻不见，龙常皆空，财不能得。婚姻：昼夜皆乘白虎，男必凶暴；昼占女贵，夜占女凶。胎产：日上乘阳，上强下弱，产必生男。疾病：必是肺虚肾竭之疾，恐难脱体。出行：马临空陷，出旬始行。行人：天罡加巳，尚未启行。兵战：昼夜皆凶。", "毕法云：魁度天门关隔定，干乘墓虎毋占病，不行传者考初时，虎临干鬼凶速速，催官使者赴官期。精蕴云：戌加亥，旦墓皆乘白虎，占病多是隔食隔气，或是邪祟，下之为佳。诸占皆不脱关隔二字，犹之乎驾舟楫而登剑阁，御辎重而过江津也。"));
        set(9, new LiuReKe("壬午日第三局　干上酉", "课体：元首，斩关，间传。", "课义：阳拱阳神，两贵为邻，辰酉合定，酒色败身。", "解曰：支神为宅，宅之左右为邻。壬日以巳卯为贵人，今巳卯居于辰之左右，而支神阴阳，得寅辰午三阳间拱昼夜贵人在内，岂非阳拱阳神，两贵为邻乎。酉临亥上，得水为酒，又为女子。辰即勾陈，乃私孟之神。辰酉相合，自然恋酒贪色矣。何以言败？盖辰为水墓，酉为败地故也。", "断曰：课名斩关，发用天梁，利遁逃，宜隐逸，乃行旅之象。此课干支自刑，未免伤身，又自相合，且后合重重，必有阴私，祸非外来，必由自致。", "天时：天罡指午，风伯发用，有风无雨。家宅：支于自刑，全无和气。功名：末传昼夜乘龙虎，占官可得。求财：财爻不见，青龙作鬼，取之甚难。婚姻：男不佳，女不吉。胎产：子母相合，又且自刑，三传逆布，占产不吉。疾病：昼占病凶，夜占病减。出行：水陆路俱不佳。行人：天罡加仲，行者半途。兵战：初传克末传，利客不利主。", "毕法云：空上逢空事莫追，宾主不投刑在上。指掌赋云：寅子戌为冥阴格，盖寅为日出之方，子戌阴气盛旺，自寅传戌有阳退入阴之象。凡占自明入暗，犹防暗损，占官最凶。"));
        set(8, new LiuReKe("壬午日第四局　干上申", "课体：元首，元胎。", "课义：虚生难靠，往临脱耗，天将宜详，旦贵勿告。", "解曰：申金本生壬水，临于甚善，但逢旬空，是以不足相靠。初传往临，既值空乡，又为破碎。再投中传，复为脱气，是往临其脱耗也。末投壬禄，既被空勾土将相克，又与支上卯木并力脱干，而旦占则贵人又坐空亡，告之何益乎。", "断曰：元首元胎，事方基始。发用旬空，凡占不实。或因妻失财，或因子致脱。相合者空亡，相刑者实在，好事去而恶事存也。", "天时：水母已空，天罡指未，有风无雨。家宅：人空宅伤，虽贵何益。功名：官爻不见，贵空禄克，占官不吉。求财：财已落空，安能有得。婚姻：男空女刑，占皆不吉。胎产：子空母刑，孕产皆凶。疾病：人空病实，赖末有救。出行：水陆路俱防伤损。行人：马空不至。出师：昼吉夜凶。", "毕法云：费有余而得不足，苦去甘来乐里悲。古鉴：己酉二月戌将加丑，占幼童病，曰：初传巳火，末传亥水，主上热下冷。中传寅木，主风气击搏。巳火伤金，又主下部不通。因食醐冷物，致伤肺金，遂成下胚上喘之症。申金本可生壬水，奈遇旬空，谓之见生不生。申七亥四，恐二十八日不能过也。悉验。"));
        set(7, new LiuReKe("壬午日第五局　干上未", "课体：重审，六仪，炎上，励德。", "课义：交互传合，递互相脱，财化成鬼，凶化难遏。", "解曰：寅与亥合，午与未合，卯与戌合，三传作三合。中传又与干合，是为交互传合也。寅木脱干，未土脱支，初传脱支，末传脱干，是为递互相脱也。三传作火局，本为日干之财，奈生干上未土，反来克干，是财已化鬼。其所化者甚凶而难遏，故曰凶化难遏也。", "断曰：重审炎仁，事属于逆。占主文书，或言炉冶。有合有散，有喜有嗔，课名闭口，用宜缄默。又系交车脱合，其中各怀欺骗，乃笑里藏刀之象也。", "天时：三传火局，生土克水，天罡指阳，晴而不雨。家宅：干支互生，人宅两旺，彼此相滋之象。功名：财旺生官，占官必得。求财：财已化鬼，未必能得。婚姻：彼此相合，必缔朱陈，阴阳相刑，刑伤必有。胎产：传逢三合，产必迟延。疾病：病症克人，三合六合，病难脱体。出行：合而又合，驿马不见，难于启行。行人：有所贪恋，不能即归。兵战：宜夜不宜昼。", "毕法云：万事喜忻三六合，合中犯杀蜜中砒，交车相合交关利。课经云：冬月丑将加巳，日辰旺气遇三合六合，交车合者，为和美课。最宜交关财利，主人情和悦，全无障碍。有人相助，虽有鬼杀，亦不能阻也。"));
        set(6, new LiuReKe("壬午日第六局　干上午", "课体：重审，不备，赘婿。", "课义：支财就日，夜元空失，熟视其中，占尊不吉。", "解曰：午乃支财，临于日上。夜占乘元，不免空失矣。熟视末传之申金，乃日子之长生父母。昼乘元武，夜乘白虎。初传午火克之，中传丑土墓之，又复空陷。若占尊长，岂云吉乎。", "断曰：课体不备，事物不全，初传午火，下被亥克，上被元后所克，谓之夹克，乃是财不由己。虽则不能由己，却又推之不去，终不相离。缘四课不离于三传，三传不出于四课故也。支加干被克，为赘婿课，有田宅相连事。", "天时：水母已空，又被午克丑墓，恐不能雨。家宅：勾朱脱宅，与日上相害，时有口舌争竟之患。功名：官星临宅，卯命人占，功名可得。求财：财虽随己，不为己用。婚姻；支干相害，两皆不吉。胎产：子母相害，午受夹克，儿必不活。疾病：肾气虚脱，恐不能起。出行：陆路甚凶，水路亦险。行人：马已落空，行人迟滞。兵战：昼夜俱不甚吉。", "毕法云：初遭夹克不由己、彼此猜忌害相随。秘要云：壬癸日申加丑，夜将上乘白虎，此乃父母爻上乘虎坐墓，父母墓中必有白蚁。若昼将上乘元武，必墓中有水，父母在堂患病，有不起之象。"));
        set(5, new LiuReKe("壬午日第七局　干上巳", "课体：反吟，比用，三交，度厄，励德。", "课义：三财被贼，二贵受克，昼旺虎临，左右不得。", "解曰：干上巳火之财，为亥水所贼。初末两传之午火，为子水所贼，是三财被贼也。卯贵临于酉地，巳贵落于亥宫，皆受下神克贼，是二贵受克也。子为日旺，昼乘白虎，又系羊刃网罗，故左右皆不能得也。于日午发用，必占妻孕，七月尤的。", "断曰：反吟课体，南北相易，东西互居，事多反覆。占必多疑，遇凶翻吉，得喜乃忧。非一定之体，塞翁得失之象也。", "天时：晴日必雨，雨日必晴。家宅：干支相克，上下不和，焉能致福。功名：龙常与官星俱不见，又且两贵受克，占官不吉。求财：满目皆财，上下相制，皆非已有。婚姻：占男昼吉，占女皆凶。胎产：占胎孟坠，占产生男。疾病：支水克干火，昼占甚凶。出行：来者思去，去者思归，丁马不见，终无实济。行人：虽无丁马，去者必归。逃亡：宜问亲友。兵战：昼夜皆有不吉。", "毕法云：两贵受克难干贵，受虎克神为病症。课经云：壬午日德禄皆系于亥，巳为壬水之绝地，今亥加已上，为德丧禄绝，诸占不吉。又云：占病宜视日干之禄神，为禄粮神，日干之食神，为运粮神，不宜受克与落空，如占久病，必绝食而死。括囊赋云：蛇巳午而怪梦。又云：元武乘午兮职转。"));
        set(4, new LiuReKe("壬午日第八局　干上辰", "课体：知一，斩关，不备。", "课义：彼己俱伤，卑下不甘，未免一动，盗失多端。", "解曰：壬水日干，被上神辰土所克，午火支辰，亦被亥水所克。干支皆被上神所伤，故曰彼己俱伤。日为尊长，辰为卑幼，日去临支克支。而支之阴神，遂往加干而克之，是卑幼不甘也。壬干不免一动，去就初传之辰土，乃是鬼墓。再投中传，又是败气旬空。更投末传寅木，又是脱气，复乘蛇武落陷，更多耗散，是盗失者非一端矣。", "断曰：知一斩关，乃行旅之顺境。又曰不备，事必不全。祸生干内，灾及阴小。三传中末皆空，不得已临支取财，亦为避难逃生格也。", "天时：天罡指亥，墓神覆日，宜乎有雨，然毕宿空亡，终不过阴而已。家宅：尊长相欺，卑幼有愆，彼亦受克，患亦难释。功名：墓神覆日，贵人不见，虽有官星，恐亦不吉。求财：财爻与青龙不见，难必其有。婚姻：男不佳，女不吉。胎产：占逢七月，妻必有妊，干支皆刑，产不甚吉。疾病：墓神覆日，病必难痊。出行：陆路不吉，水路稍可。行人：天罡加孟，尚未起程。兵战：利主不利客。", "毕法云：权摄不正禄临支，不行传者考初时。越绝书吴王召公孙圣，占得亥将加午，圣曰：干上辰克干，支上亥克支，求救于寅，寅败于酉，命属上天，不能逃亡，非但自伤，且伤吴王。"));
        set(3, new LiuReKe("壬午日第九局　干上卯", "课体：重审，曲直，斩关。", "课义：人少宅宽，虽费成欢，三传俱脱，逃避尤安。", "解曰：三传未亥卯木局，脱干之壬水，去生支辰之午火，乃人少宅宽也。既而卯与戌合，寅与亥合，交互相合，是虽有所费，而彼此却成欢悦之象。再壬被三传所脱，未免就初传之未土，而与丁火相合。乃避难逃生，而亦可以相安也。", "断曰：重审曲直，事属逆征，外观有余，内观不足之象。始屈终伸，利用舟车，春占最吉。", "天时：天罡指阳，木局脱水，不雨而风。家宅：屋宅极多，更兼华美，但恐人丁欠旺。功名：支上戌土官星，昼夜各乘龙虎，似有可为，奈木局全去伤官，占恐不吉。求财：己财难守，何瑕他求。婚姻：朱陈可结，占婿极佳。胎产：子恋母腹，占产最迟。疾病：夜占肾虚，三传自墓传生，不久自愈。出行：一路坦然，恐难离别。行人：天罡加孟，尚未启行。兵战：利主不利客。", "毕法云：万事喜欣三六合，屋宅宽广致人衰。观月经云：三六相呼见喜欣，纵然带恶不成嗔。夫带恶者，乃金日得寅午戌，土日得亥卯未，木日得巳酉丑，火日得申子辰，纵然克干，亦不能为祸，主有人在中相助。精蕴云：壬午三传脱气。如夜占，皆是勾贵常土神，并来伤干，却赖三传木局克去，乃应脱气为救之格。"));
        set(2, new LiuReKe("壬午日第十局　干上寅", "课体：重审，三交，二烦，寡宿。", "课义：交互生意，终是不美，昼号三交，夜乃损己。", "解曰：干上寅生支，支上酉生干，交互生意，而寅又脱壬，酉乃午火死地，终是不美。课号三交，将乘勾虎，主杀伤争斗丧孝之患。午加酉为交，酉加午为破，主交事不成。夜将空元，而贵人临子受刑，须防损己失财也。", "断曰：此课发用空破，中传劫盗，末传脱气，始终如此。孟求事成，不亦难乎。春占酉将，课名二烦。", "天时：天罡指阴，毕宿空破发用，阴而不雨之象。家宅：逢破逢败，萧条之象。功名：贵人破刑，官禄不见，恐不能得。求财：财爻青龙不见，难必其有。婚姻：干上六害，支上相破，占婚不吉。胎产：下强上弱，子息属阴，产女之象。疾病：酒色虚脱之症，延及肺经，大补下元方愈。出行：辰上克日，出行不吉。行人：天罡加季，行人即归。兵战：昼占有畏，夜占敌诈。", "毕法云：互生俱生凡事益。课经云：六壬日，干上寅木作壬水之脱气。昼占天将乘元武，为脱盗格，凡占皆主盗脱失物。又指窍云：四仲顺加子午卯酉。春占陷阱，如鸟投笼。夏占正烦，若牛受刃。秋曰失友，状若离散而复合。冬月出渐，名曰阴极而阳生。"));
        set(1, new LiuReKe("壬午日第十一局　干上丑", "课体：重审，间传，励德，九丑。", "课义：丁与壬午，求谒为良，虎入朱雀，遇殃不殃。", "解曰：丁丑临壬，官鬼之财动矣，故利于干求官贵也。申为白虎，午为朱雀，以申加午，是为虎入朱雀。幸申乃旬空，戌鬼落陷，故凶不成凶也。虎人朱雀，占讼不论　空亡最凶。", "断曰：重审间传，发用空陷，凡百所占，尽是虚声。若占凶事，大可化小，小可化无。若占吉事，成少败多。中传之官星，又复不实。只余末传之子水，又作劫财。昼夜上乘元白，恐不免有所失脱也。五行生处见青龙，财帛亦能如意。", "天时：天罡指阳，水母落空，恐不能雨。家宅：日本临支作空亡，尊长防失，且宅上尽空，家徒壁立。功名：官爻空陷，占官不吉。求财：丑中有丁火，乃壬水之财，丑为官鬼，临于日上，主得官长之财帛。婚姻：男实女空，多是虚语。胎产：下强上弱，产必生女，母空儿实，产必易生。疾病：肺金受病，尚可医治，若逢久病，恐防不吉。出行：陆路相宜，水路空虚，驿马填实，必定启行。行人：驿马落空，当从水路而来，或子丑日至。兵战：敌虚可攻，昼获财宝，夜获大胜。", "毕法云：水日逢丁财动之，喜惧空亡乃妙机。课经云：六壬日，丑加亥，八月昼占，日鬼作死气，乘太常，加临于日干之上，必主内外有孝服也。指掌赋云：传送亡会青龙，子孙财损。"));
        set(0, new LiuReKe("壬午日第十二局　干上子", "课体：元首，进茹，芜淫，三奇。", "课义：交互凌虐，盗脱不祥，刃乘元虎，脱虚相伤。", "解曰：干上神子水克午支，支上神未土克壬干，交互凌虐也。初传丁财，似乎可以远动而求之，中末则又脱气。孟守干上旺子，乃是羊刃网罗，昼夜天将复乘元虎，其脱耗相伤，岂复能免哉。", "断曰：进茹网罗，只宜守己，不宜动谋。且干支互克，彼此相害，和气全无，不宜占交加之事。更申为日本，上乘龙合而作空亡。父母分上，亦止有虚喜而已。", "天时：水母已空，初传克水，水下火上，占天不雨。家宅：人宅相害，又系芜淫，占宅不吉。功名：官爻与日上相合，似乎有成，然被寅木所伤，日禄又空，恐无实际。求财：水逢丁，占财自有，但为木伤，不能全美。婚姻：彼此互伤，各有所私，占婚最忌。胎产：支干相害，复又相克，子母皆伤。疾病：脾胃之病，刃乘罗网，幸赖中末脱刃制鬼，终必有救。出行：罗网在前，马又空陷，支干相害，必不能行。行人：天罡加仲，中途有羁，行人迟滞。兵战：昼夜平平，未见全胜。", "毕法云：所谋多拙遭罗网，夫妇芜淫各有私，彼此猜忌害相随。课经云：壬午日干上子，不可执为芜淫。但先许而后不相顾，彼此无情耳。干支上子未作六害，亦主各相猜忌。"));
    }
};
