public class d2 extends KeEntry{
	public d2() {
        set(5, new LiuReKe("丙寅日第一局　干上巳", "课体：伏吟，元胎，自任，临官，励德。", "课义：干传入支，行人远归，禄财生聚，所卜皆宜。", "解曰：初从干上，末归支上，乃我托人干事也。支马在申，传归支上，行人虽远，必至。禄马长生，与财相聚于三传，所求无不遂意。但课体刑冲，善始保终为吉。", "断曰：此自任之卦，又名元胎励德。占事静则宜，动则滞。事主藏匿，屈而不伸，君子守正修德则亨。求官见贵，喜逢禄马，但不脱刑冲，美中未美也。凡占遇励德之课，当思致君泽民之意，自然有泰无否。春夏吉，秋平，冬忌。年命在戌亥者，不利。", "天时：主先晴后雨，见虹电，刮风。家宅：人财兴旺，不利六畜。婚姻：难成；昼占子多，夜占女性悍。胎产：易产，胎防犯聋哑。疾病：主脉紧滴，寒热，不进饮食，然末传生干，易愈。求财：未称意。失物：不出其家。捕获：不出邑里，动中得获。出行：不果，在路安。行人：远行回轮，近行将至，应在亥戌日。争讼：和息则吉，不然招刑险。征伐：防诈，又不动之象。", "毕法云：我求彼事干传支(有礼下求人之意，旺相吉；凡伏吟，惟六丙日吉，缘初传为德禄，中传为财，末传为长生也，各忌空亡)。风后元经：丙辛日，寅为游都敌主帅，冲为鲁都敌偏帅。若临于日辰之上，敌帅当日即至。玉成歌云：伏吟举动心无遂，刚主行人到户庭。"));
        set(4, new LiuReKe("丙寅日第二局　干上辰", "课体：知一，斩关，退茹，天网。", "课义：畏空官鬼，病防再至，子丑夜常，牛女合位。", "解曰：亥为丙之官，子为丙之鬼。亥既旬空而临地盘子位，又日贵夜贵皆落空处，占病官鬼为主，故畏其落空，防其再发也。凡已了之事占此，俱恐复生事端，举病占以例其余耳。子与丑合，而夜占乘太常，是为牛女合位，占婚宜之。拔茅连茹，事多蓦地，三传退神，先难后易。", "断曰：此卦官贵财元皆空，求官见贵，皆枉劳心力。喜干奇支仪临口，旬奇临辰，旬仪发用，能逢凶化吉。阳日比阳，主事起同类，或自外来，大约宜改图去就。", "天时：昼占孟雨还晴，夜占密云不雨。家宅：空阔难居，多口舌，防火灾。婚姻：昼占不成，夜占易合，不烦媒妁。胎产：是男，难产，产或不育。疾病：暴病醮神即愈，久病医药无功。求财：微未艰难。出行：宜迟，宜从陆。行人：近出即到，远者亥日到。盗贼：脱逃拒捕，出旬必获。", "毕法云：屋宅宽广致人衰(支阴发用，为家鬼生支克干，惟宜兑卖宅舍，以钱预备灾患之费)。二贵皆空虚喜期(旦暮贵人皆值空亡，如干谒贵人，已蒙许允，后被他人搀越，或有人报喜，且勿信，或同姓名人，终非我喜，诚为虚喜而已，反有所费也)。"));
        set(3, new LiuReKe("丙寅日第三局　干上卯", "课体：重审，间传，龙战，隔角。", "课义：虽生难恃，子卯无礼，反为败神，切休恋贵。", "解曰：木虽生火，而丙火败于卯，何足恃也。卯来刑子，子息不律，无尊卑之礼也。两贵皆空，不宜求贵。干支皆败，不宜讼争。极阴之课，课体冥蒙，合中有破，百事逢之不利。", "断曰：此重审之卦，又名龙战。主身心迷惑，进少退多，必当详审而折衷于理，庶免咎悔。四时占之，皆有始无终。昼占多诈，夜占贵人登于天门，却无阻滞。若占人年命在子，两贵拱之，必有贵人虚声相助。", "天时：久阴，多雨有风。家宅：损人口；昼占有火烛惊怪，夜占有喜事。婚姻：不成，成亦有刑克。胎产：生女，难产有惊。疾病：病淹缠，医不效，昼占尤为危急。求财：费力难得，得之亦不利己。失盗：为内贼，匿不出，又为捕人纵盗。出行：宜缓，速动防水火灾。行人：迟归，先得信。争讼：此不胜，彼破财。兵战：防虚诈设伏，宜坚壁清野待之。", "毕法云：干支皆败势倾颓(干支上皆逢败气，占身气血衰败，占宅屋舍倾颓，尤不可告诉阴私，必至牵连己过，同时败露)。宾主不投刑在上(干上卯，支上子，干支上神各为无礼之刑；主交涉，各有异心，讼必两败)。风后元经：支上神生日上神，又两神相刑，探报半虚半实。昼占虚诈不实，归于两军和解。指掌赋云：大吉小吉作勾陈，斗争田地。"));
        set(2, new LiuReKe("丙寅日第四局　干上寅", "课体：蒿矢，元胎，龙德，不备。", "课义：执弓忘矢，事声迤逦，舍此危疑，彼来生己。", "解曰：神遥克日，发用旬空，是犹执弓而亡矢也。末传迤逦冲起空亥克干，乃声势危疑而已，不足畏也。中传之财，切不可取。喜支来生干，静守为吉。", "断曰：此蒿矢之卦，一名元胎，以四孟发传也。而用神空亡，事主动摇，人情倒置。干谒占之，主客虽和，阴有侵损。人自外至，不可容纳。须防口舌官事，西南而至。老人小儿占之，反为人吉之兆。亥年月时，占事有成。", "天时：久雨必晴，久晴虽有雨，不大。家宅：利老幼口，阴人不和。婚姻：夜占吉，可用，犹宜迟归。胎产：防小产，产母安。疾病：病虽小而难痊，妇人啾唧，老人小儿即愈。求财：无利，遇利且勿取。失盗：贼在西南，赃不获。出行：东方吉。行人：在路上，有虚惊。争讼：纷扰忧疑，终归解散，不能为害。征伐：开地千里，战胜之兆；然用神落空，宜持重观变，方不失众；正五九月尤不可轻动，当行反间，亦可得敌臣之功。", "毕法云：彼求我事支传干，互生俱生凡事益；鬼乘天乙乃神o，龙加生气吉迟迟。课经云，此俱生格，乃干上神生干，支上神生支。又皆值长生，彼此和顺，宜合本营生。又支加干上而生干，名自在格。"));
        set(1, new LiuReKe("丙寅日第五局　干上丑", "课体：重审，炎上，斩关，狡童。", "课义：自墓传生，先迷后醒，全无和气，上刑下刑。", "解曰：初传戌为火墓，末传寅为长生，此先迷而后醒也。事防再举，病防再发。寅刑丙上课巳，而丑刑支上神戌，日辰上下相刑，何和气之有哉。丑为甲子旬奇，今临课首，占官当居一品之尊。贵人廊庙常人得此，犹可散凶。", "断曰：此卦象火旺太过，有燎原之势。明事反成暗事，图谋枉劳不遂。卯生人占之，遇贵登天门，权要无比。犹嫌性太刚躁，须容忍乃无伤也。余占多虚少实。", "天时：早热，时有雾及闪电，有雨亦微。田禾：乾旱，低处有收。家宅：防火烛；昼占有奴婢欺主作奸。功名：可得显名，虚而不实。求财：未遂。婚姻：自相得，不用媒；又云，起天后，终六合，名为u女格，最忌。胎产：防不育；如系狎邪私孕，反可育。疾病：为热，为闭结，新病作福，保之可愈广久病危，即愈亦防再发。遗失：奴婢所偷窃。出行：宜东方，宜从陆，下人投势利北。行人：干墓临支发用，若系奴仆幼辈，出旬即到。争讼：宜得有力调解。征伐：将尊功高，然须慎重，勿食饵兵，方解忧释难。", "毕法云：尊崇传内遇三奇，先难后易墓先施；干墓并关人宅废，合中犯煞蜜中砒。袖中金：魁临支，中传白虎，为斩关得断，利于逃亡。观月经：甲子于甲戌，大吉两旬奇；万事皆和合，千灾速解离。"));
        set(0, new LiuReKe("丙寅日第六局　干上子", "课体：知一，四绝，度厄。", "课义：彼己灾殄，三传外战，干支坐墓，事终难辨。", "解曰：干遭水克，支受金伤，己与彼俱灾也。三传皆地盘受克，为外战不和之象，且递克至于干神，须防人来凌己。巳坐戌上，寅坐未上，则为自临墓库，甘于昏晦，是非何辨。干上旬首，支上旬尾，初传生末传以生干，而支神生干神以发用，是谓引鬼为生，顺正无咎，妄求有悔。", "断曰：知一之卦，求官见贵吉，但惊疑不定，人情失和。所喜辰为干奇支仪，临干支阴。子为旬仪，临于干上，能转祸为福，化难成恩也。", "天时：多雨少晴。家宅，人宅俱坐墓，必近有坟堂庙宇；酉加寅，主尊长有灾。科名：可获高第。婚姻：夫妇和合，恐家中尊卑不喜。求财：大有得，恐因财生恼。疾病：幼者不吉，祟惊宜禳，老年人虽病易痊。投谒：主宾集会猝然，有暗昧之惊。失脱：或是金物，或坠水中，宜寻。出行：宜缓。行人：迟至。争讼：事起同类，宜和。兵战：主将坐谋，当以奇兵远出取胜。仕宦：防被台省参劾。", "毕法云：一旬周遍始终宜，人宅坐墓甘招晦，支干全伤防两损。观月经：三上来临下，小者必低蕤(鬼发初传，而中子孙，末父母，小口不利)。袖中金：六甲旬首见于初传，是为仪神发用。末传得吉将，始终喜庆之卦也。"));
        set(11, new LiuReKe("丙寅日第七局　干上亥", "课体：无依，元胎，比用，察微。", "课义：交互虽密，合之却益，德禄鬼财，并无实迹。", "解曰：四课两互，六合相交，有德应之，成事却有益也。德禄鬼财，往来不定，吉凶皆不足言也。鬼陷空亡，不能为害，惟鬼加日辰三传，而年命无制伏者凶。", "断曰：此无依之课，十二神各易本位，无所凭藉。来者思去，去者复来。况干上阳神阴神俱值空亡，凡动无益。但当镇之以静，行之以礼。三四九十月，得此稍吉，吉中终藏凶象。", "天时：晴雨不常。仕宦：防遭弹劾，若上书献策，或受斥责。家宅：主移徙改造，又居左右近通衢。婚姻：两家有是非，媒妁奔走。胎产：防堕。求财：得而复失，亦多争端。疾病：寒热往来，鬼空可即愈，亦防复发。失盗：贼难获。出行：近行即反，远行正五九月不利。行人：于路不安，或有阻隔。争讼：变诈百出，多费解释则易。兵战：宜休甲息马。", "毕法云：支干值绝凡谋决，鬼乘天乙乃神o，支干全伤防两损，彼此猜忌害相随。心镜云：无依是反吟，逃者远追寻；合者应分散，安巢别改林；守官须易位，结友也分襟。袖中金云：刑冲破害，事带两端。指要云：天地乖隔，南北相违之象。动则宜，静则扰，主两事，用神旺相始宜。又云：反覆不定，病主两症。一名结绝格，若占得昼贵，尤宜告诸贵人结绝凶事。"));
        set(10, new LiuReKe("丙寅日第八局　干上戌", "课体：比用，乘轩，铸印，察微。", "课义：两蛇夹墓，家遭咒诅，熟视三传，略无好处。", "解曰：墓来覆于而两蛇夹之，晦而且凶也。天空加巳，主奴婢于灶卜咒诅家长，或灶有损破。三传课灯空脱，何好处之有。天空乘巳被克，方可言灶破，遇戌墓方可言奴婢咒诅。", "断曰：此卦宅暗人昏，善恶混处，诈变不一。幸传入空亡，不过虚惊而已。求官见贵，摇摇不定。须宁心忍耐，见怪不怪。广积阴德，暗中自得神明之佑，久而凶消吉至。", "天时：阴晦霾雾，久得清明。家宅：奴婢咒诅，灶自破坏；又主宅近庙墓，窗户幽暗。婚姻：夫妇和合，家道有成。胎产：是女，恐临盆不育。功名：蹭蹬未遂。求财：得而复失。交易：和合而未成。投谒：虚费跋涉。疾病：主上逆呕吐，宜服丸方。逃亡：自归。捕捉：难获。出行：不得抽身。行人：即至。争讼：多诈。坟葬：兆吉，宜子孙，可发贵。", "毕法云：两蛇夹墓凶难免，支干乘墓自昏迷。课经云：墓覆日辰，人宅昏沉，如在云雾中行，凡占皆不亨快。古鉴：戊申六月丙寅，未加寅占身位。支干带墓鬼发传，传归日上。六月死神在戌，两蛇夹之，带迷惑煞，主伏尸为殃。鬼入宅阴，归于日上，渐次侵人。中传虽有生气，受死气之克化鬼，戌年戌月未时死矣，后皆应。先因造宅筑基，掘去古坟十余也。"));
        set(9, new LiuReKe("丙寅日第九局　干上酉", "课体：重审，从革，地网，龙德。", "课义：守死赔钱，夜将助焉，所得一贯，家费万千。", "解曰：酉虽为丙之财，而丙火死于酉，况夜将皆土，脱干之气，为耗无穷。三传虽会成财局，难供宅上午火之费，是所人者一贯，而所出者万千也。", "断曰：此卦合中有破，笑中有刃　先天后革，变金成铁。事防再举，病防再发。大忌命上乘水，乃为自招其殃。求官见贵，多不喜悦。凡百谋为，成而有变。惟文明之象，足以应之。可得去旧生新，以合从革之义。", "天时：多晴少雨。家宅：人口不宁，兄弟不和。婚姻：合中有破。胎产：午加寅，主正妻有孕。科名：文字改变从新，可得高捷。求财：得少去多，且有口舌。疾病：隔塞中风，或禁口痢，或伤筋骨，皆有凶象。失脱：有见贼人，却不肯说。出行：不利西南，防破财。行人：书信浮沉，盘费不继。争讼：不得伸理，须防人暗害。兵战：宜偃旗息鼓，屯积粮草。", "毕法云：两贵受克难干贵，传财太旺反财亏；宾主不投刑在上，合中犯煞蜜中砒。袖中金云：旬尾加旬首为闭口，凡占多主秘密，不能测其机关，告贵不允，枉狱不伸。观月经云：一下贼其上，顺行忧浅，逆行忧深。入墓有害，传生可避。"));
        set(8, new LiuReKe("丙寅日第十局　干上申", "课体：重审，进退，不备。", "课义：递相荐引，夜无凭准，屈尊就卑，所谋和允。", "解曰：三传递生，必有荐引之喜。但中末空陷，未免徒有其名。夜将凶多，尤虞阻滞，其无凭准宜矣。干就支生，占者凡事心存谦抑，则所谋可成，所求可允。", "断曰：此卦重审元胎，干乘支马，支乘干禄，富贵兆也。申为财，加巳为用。中传官鬼，三传递生日干。昼占六合为用，贵人登天门，主谋为利禄，人财纳官，或得上人荐举，终履高位。夜占财被蛇伤，况遁出壬癸，中末空亡，恐因贪召祸。", "天时：多小雨，有电。家宅：多隙地，有藩篱，人口安吉。婚姻：先刑后合，媒妁玉成。胎产：是男，易产，天后落空，恐或不育。功名：昼占得京职，须有嘱托；夜占有悔。求财：有关节货赂。疾病：从色孟得之，支生干，可愈。失脱：昼占不寻自来，夜占不可获。出行：从西绕北，至东大吉，夜占路中防失财物。行人：若系幼辈即到。争讼：须用财贿关节，夜占不胜。兵战：宜设备，利为主。", "毕法云：权摄不正禄临支，三传递生人荐举，日辰禄马财官动，富贵干支逢禄马，用破身心无所归。袖中金云：申加巳，名生胎，为忧课，主事生新，心忧身喜。课经云：三传递生，日辰生旺，主人亨利，时运开通，故名亨通。若值空亡破刑，凡事亦难就矣。"));
        set(7, new LiuReKe("丙寅日第十一局　干上未", "课体：重审，间传，斩关，励德。", "课义：壬申乘马，见财难舍，得来何用，添修屋瓦。", "解曰：申财遁为壬鬼克日，如此得财，可谓从危险中出矣。既在眼前，谁能舍之。然丙得申财，旋为支上辰土脱去，支为宅舍，非添修屋瓦而何。", "断曰：此卦辰午申三传，名为登三天。惟利君子，不利小人。贵者将迁，下吏罢退，庶人居宅不安。又主进中有退，凡事必费力而成。人有私谋暗祷，喜逢此占。", "天时：雨迟不大，旋霁有晚霞。家宅：费修理，宜开西方门。婚姻：不宜。胎产：应得女，易产，有虚惊。功名：正途得超擢，异路防蹉跌。求财：费力，得不偿失。疾病：见凶，宅神为祟。捕捉：贼匿私门难缉，或远去不可追踪。出行：利速行，宜西方。行人：将到，于关津处为轩盖所阻。争讼：须越诉省部得伸，又两造势卑者遭谴。兵战：宜暗设伏兵，昼占不利，夜占大胜。", "毕法云：昼夜贵加求两贵，罡塞鬼户任谋为；龙加生气吉迟迟，鬼乘天乙乃神o。袖中金云：天罡临支发用，传有白虎，为斩关得断，更带血支羊刃，必伤人而走。指南载：戊寅三月丙寅日，戌加申占请告。决其不允，更主升迁。盖因官登三天，传将进引，又龙神乘相气，太岁加行年，而生青龙日干也。后果屡疏不允，旋历显任。"));
        set(6, new LiuReKe("丙寅日第十二局　干上午", "课体：重审，进茹，六化，龃龉。", "课义：第四课用，末与日共，格号朝元，百发百中。", "解曰：支阴发用，末传又归干上，乃朝元格也。事有终始，发无不中。干支皆乘旺神，事可坐谋而得。又第四课发用，课名蓦越，事出蓦越而成，又为过去之事。课用年命空亡，或为人代占。", "断曰：此卦乃朝日格。昼占青龙在末传，夜占青龙在初传。事主成合，不求自至，无心中得。但传进贵逆，事虽顺速，境有迟阻。遇空亡，反可消灾避祸也。", "天时：时旱多晴，时涝多雨。家宅：宅旺人强，不宜兴作改造。婚姻：另议。胎产：是女，若已四胎后，当连得男矣。科名：得熟题佳作，可望连飞，但喜中逢破，须防贴出磨勘之类。求财：或自至，有心求之不得。疾病：淹缠不伤身，忌轻换医药。逃亡：虽远，不缉自归。出行：宜多约伴，主速归。行人：占日已启行。争讼：牵连未结。兵战：昼占防有敌急攻急围，夜占必胜，终归降顺。", "毕法云：所谋多拙遭罗网，互旺皆旺坐谋宜。课经云：丑加子为腹胎格，盖子为日胎，丑为腹也，值此来意必占妻孕。又云：主客、父子、夫妇皆极兴旺，不利谋动，惟宜坐待；或已遭丧失，孟复旧业，得此极佳。倘外求远动，则变为罗网，缠绕身宅，反为灾祸。订讹云：连珠课，主吉凶重叠不已，进连珠事顺。"));
    }
};
