public class d20 extends KeEntry{
	public d20() {
        set(2, new LiuReKe("甲申日第一局　干上寅", "课体：伏吟，自任，元胎。", "课义：行人到户，来必欺侮，昼禄遁庚，末逢众虎。", "解曰：伏吟驿马发用，行人即归。支神阴阳皆金，干神阴阳皆木。自干传支，动必受其欺侮也。寅为日禄，旬遁庚鬼。夜犹得青龙相助，昼则蛇火脱气，必成灾咎。寅禄既不可守，中传巳又脱日，孟投末传，遂逢众虎。盖申固虎也，又乘昼虎，支神阴阳，众虎为群，胡可当哉。", "断曰：此自任之卦，课传皆孟，名曰元胎，乃隐伏未发之象，静以待时则吉。发用禄马俱全，蛇生角，勾陈捧印，官上催官。仕宦占之，显达可必，尤喜见水，若年命逢申者不利。", "天时：风多雨少，夜占有雨。家宅：干支互绝，宜与人兑换房屋。婚姻：夜占可用，但防刑克。胎产：胎旺或主婢孕。功名：夜占龙马驾德禄，科名大利，惟申巳午命者不足。求财：难得。投谒：利见贵求名。疾病：主见血光甚凶，幸有解神，不妨。失脱：近处寻获。出行：宜远行。行人：即至。争讼：不利，宜静以待动。兵战：昼占不宁，夜占大胜。", "毕法云：宾主不投刑在上，我求彼事干传支，信任丁马须言动。袖中金云：元胎见禄马龙雀，大利占试。如常人见勾陈白虎，必有讼事或病人也。亥月占主胎产，行年见龙生男，见后生女。惊虎伤勾有厄也。"));
        set(1, new LiuReKe("甲申日第二局　干上丑", "课体：知一，退茹，重阴。", "课义：递相蒙蔽，两贵恃势，生计虽荣，子息废弃。", "解曰：干墓复支，支墓复干，彼此递相蒙蔽也。昼贵在干，夜贵在支，而丑与未为恃势之刑，两贵亦不相能也。子亥，水也。戌乘双武亦水也。全水生干，生计可谓荣茂矣。然制火太甚，为日干之子孙者，不几废弃乎。", "断曰：此知一之卦，连茹逆传，退中有进，恩中生害。事起同类，和衷则吉，疑贰则凶。后阴元武，一片奸私。子未丑午，四神交害，人旺宅衰，主宾不合。百凡谨慎防闲，免受暗中侵损也。阴贵相助，秋冬占之大吉。", "天时：淫雨连旬不止。家宅：不和，防损小口，夜占天空，主屋上兽头落。婚姻：不宜。胎产：胎安产易，防不育。功名：有人暗中默助。求财：得后亦防耗失。疾病：夜占主鬼为殃，可禳，若占子孙病凶。失脱：宜寻。出行：迟疑不决。行人：迟归。争讼：可解，防暗害。兵战：少利，秋冬吉。", "毕法云：昼夜贵加求两贵，干支乘墓各昏迷。课经云：干支见昼夜贵人，事须两贵周全，方得成合。袖中金云：子亥戌为重阴，安嘉遁之贞，宁甘没齿。指掌赋云：三传纯父母，勿虑身而身自安。毕法云：六爻现卦防其克，此为父母现卦，子孙忧也。"));
        set(0, new LiuReKe("甲申日第三局　干上子", "课体：涉害，间传，顾祖，励德。", "课义：宅内脱空，嗣息飘蓬，庚赖此制，然后尊崇。", "解曰：午为甲之子，今居宅上，脱干之气，又值旬空，既脱且空，嗣自有飘蓬之象矣。寅旬遁为庚，是谓禄旁有鬼，禄不可就，全赖午火克制庚金。然后禄为我享，而成其尊崇也。五月占之，月建填实，方符此沦。", "断曰：此涉害见机之卦，凡事留连迟滞，进退不定，格名顾祖，有复旧之象。贵临卯酉，大吏升迁，微员降斥，亦主阴小有灾。凡百谋望，宜进不宜退，退则遇空也。", "天时：求晴未晴，求雨戌日可得。家宅：主屋舍倾颓，家业衰替，宜复旧庐，春夜占防火灾。婚姻：成合美中不足。胎产：占胎吉，占产不宜。功名：仲夏占吉，余恐虚声无实。求财：来处不正，防人窃耗。疾病：主气血衰败，新病不妨，久病凶。失脱：不获，防再失。出行：宜慎，恐遇小人。行人：在途，出旬必至。争讼：惊忧反覆，后有解。兵战：有失众之象，宜慎。", "毕法云：干支皆败势倾颓，脚踏空亡进用宜。指南：未将射覆，袁天罡断法。青龙子孙主文书，午为易气，当是易经。又用东方朔断法，本课三传纯阳，取遁甲丁卯，仰视得丑，阴神是亥，取亥发用。三传亥酉未，亥主图籍，又为日干文书。未数八，亥数四，发之果易书一册，八十四页。"));
        set(11, new LiuReKe("甲申日第四局　干上亥", "课体：元首，元胎。", "课义：自支传干，彼来相向，丁马交横，闭口为上。", "解曰：初传支上神，末传干上神，自支传干，为彼来求我之事也。寅为驿马，亥遁旬丁，交横而至，势处极动，不容少停矣。旬尾加支发用，为闭口之卦，必须谨言，乃为上策。", "断曰：此元首之卦，课传皆孟，格合元胎。以尊制卑，凡事顺利，起于男子。然君子占之，则合元首之象，而正大光明。小人占之，财合元胎之象，而暗昧隐伏。仕宦则有勾陈捧印，蛇生角之祥。常人则有争斗官非，火烛惊怪之咎。又支干各乘长生，而互相脱盗，为乐里生忧格。", "天时：忽晴忽雨。家宅：主有争夺田房，口舌文书之事。婚姻：不宜。胎产：主喑□，胎神临败脱，防损堕。功名：考试得科第，仕宦得迁擢，惟忌申害年命。求财：轻微。疾病：主心腹咽喉，或中风不语。失脱：难得，见者亦不肯说。出行：不利。行人：即至。争讼：防刑责，忌多言讦人阴事。兵战：互有胜负。坟葬：吉，树木茂盛，然防伏根侵圹，又两空夹墓，防有孔穴。", "毕法云：上下俱合两心齐，彼此猜忌害相随，闭口卦体两般推。袖中金云：旬尾加旬首发用，为闭口卦。凡占多主秘密，不能测其机关。上乘朱雀，占讼，枉者不伸。指掌赋云：上下六合，主客和同，上下刑害，冤仇相见。"));
        set(10, new LiuReKe("甲申日第五局　干上戌", "课体：涉害，炎上，狡童，斩关。", "课义：魁罡并临，中末空沉，若当寅月，虎马庚侵。", "解曰：斗魁临日，大罡临辰，动作非常也：戌加干发用，名曰斩关：中传午值旬空，末传寅坐空地，沉沦无气矣：寅为日马，旬遁庚鬼，夜乘白虎克日，其势凶恶。若非空陷，则侵害堆免。可不畏哉、", "断曰：此涉害见机之卦，合炎上之局。占主顾盼迟疑，多虚少实，九合后散，遇明成暗。又木日盗气，戊加寅为以墓临生，掩其虚灵之体。罡加日建，得驿马贞神。更值年命，仕人得美差。常人不利。", "天时：云合无雨，日孟现而不现，风孟起而不起。家宅：不安，防阴小有越理之事。婚姻：不宜。胎产：主婢妾孕，或不实。功名：未得遂意。求财：可得，宜与下隶私谋，但不由己用。投谒：未遇。疾病：主热病，或在心及妇人血病。失脱：主奴婢窃逃。出行：利于避难。行人：立至。争讼：利见官，必能辨雪。兵战：不利。坟葬：不吉，主坟墓破坏。", "毕法云：初遭夹克不由己，不行传者考初时。课经云：寅遁旬庚日鬼，而乘夜虎遥伤日子，至凶至危，纵空亡不能解救。袖中金云：天后厌翳，六合私门，用起六合，终于天后，曰狡童。"));
        set(9, new LiuReKe("甲申日第六局　干上酉", "课体：知一，罗网，铸印。", "课义：交互乘旺，各临墓上，动遭罗网，中传懊丧。", "解曰：木旺于卯，金旺于酉。今干上乘酉，支上乘卯，为干支交互乘旺也。然寅坐未亡，申坐丑上，又为干支各坐墓库，自招昏滞；若静以守之，则为旺来就我，倘妄相投奔，则旺变为罗网矣。中传巳火制鬼，却被戌墓子克，与我全无情况也。", "断曰：此知一之卦，事起同类，进退狐疑，恩中有害，合中有离。虽巳戌相加，亦有铸印之象。然戌下巳上，金火反位，子又临巳灭火，不得以正格论。", "天时：云蒸电掣，必雨。家宅：兴旺中防晦滞灾咎。婚姻：和合中有刑破。胎产：胎神作鬼临绝，不吉。功名：必得，三月占更吉，但非正印官。求财：可得。投谒：美中有诈。疾病：有解。失脱：主在亲邻处。出行：不得出，出亦不利。行人：占幼辈至。争讼：难结。兵战：吉。", "毕法云：互旺皆旺坐谋宜，人宅坐墓甘招晦。古鉴：一县令已未生，戊申年申将，占身命，得比用。日上乘破碎雀鬼，官位不明。初传戌财，并临六合，主私门受财。阴神化罗网，酉鬼伤身，明年八月事发遭勘。寅为功曹坐墓，当谪为曹属。行年在巳，子克戌墓，巳为旬尾阳极，庚戌年当得暴疾，闭口而终矣。前后俱验。"));
        set(8, new LiuReKe("甲申日第七局　干上申", "课体：反吟，元胎，乱首。", "课义：缘不自尊，凶犹可言，若逢夜卜，八虎攒吞。", "解曰：干往加支，却被支克，不自尊重，以取乱首之侮。支亦来加干克干，并见上门乱首之咎，其像甚凶，然犹可言也。若夜占则凶又甚焉。寅坐申上，申坐寅上，传课寅申，共为八虎。往来冲突，孰能御之。静以观变，尚且至惊至危。况往而自取，能免吞噬之患耶。", "断曰：此反吟无依之卦，又名乱首。干支互加，而日干独被支克，无往而非横逆也。况干支皆临绝地，禄马俱尽，何适而可。昼占尚有龙神解救，夜占极凶。", "天时：狂风骤雨，时作时止。家宅：人口不安。婚姻：昼占反覆，夜占不宜。胎产：胎神遇胎，又坐旺地，主有双孕，夜占即产。功名：宜引退。求财：无得有失。疾病：昼占可愈，夜占大凶。失脱：虽有窝藏，必致败露。出行：不利。行人：在路，昼占主有信息文字远来。争讼：凶，防遭刑险。兵战：不利，春占稍吉。", "毕法云：权摄不正禄临支，干支值绝凡谋决，昼夜贵加求两贵。课经云：申加甲为明鬼。寅加支，遁旬庚为暗鬼。又绝神作鬼，止宜结绝凶事，释解官讼。观月经云：乱首卦名白虹贯日，或主他姓乱宗。若系本支，亦主卑幼无礼。若又上下和顺，必遭官司。"));
        set(7, new LiuReKe("甲申日第八局　干上未", "课体：比用，铸印。", "课义：干支墓临，夜讼难胜，人渐通泰，宅渐隆兴。", "解曰：未为木墓，丑为金墓，干支皆为墓覆，既各昏迷。而夜占初传子未相穿，未贵人也，贵人相害，讼能直乎。若占人宅，却又是两贵临于身宅，人必通泰，宅必兴隆，渐者墓固迟滞。又两贵皆空，尚能虚喜；出旬以后，渐登亨衢也。", "断曰：此知一之卦，干支皆为墓覆。凡事暗滞难通，喜墓神逢空，反见暗里生明，难中化福。然发用无力，凡所谋为，亦虚而不实。惟九流僧道孤独之人，乃为大吉也。", "天时：占晴不久，占雨必须出旬。家宅：利改造发贵。婚姻：迟合。胎产：主女孕。功名：得贵人虚声相引。求财：主有私门贿赂，未即入手。疾病：久病不宜。失脱：武上神克勾上神，主吏不能捕。出行：改期。行人：立至。争讼：有解。兵战：防失众。坟葬：发贵，但丑上午相穿，防兽穴，或水穿激。", "毕法云：干支乘墓各昏迷，害贵讼直遭曲断。古鉴：一广文辛未生，戊申年亥时占前程，曰：干支皆墓，用神害贵，前程迟滞。巳为闭口，主无子。今年有灾，防为医误，庚申禄尽矣。果验。又：同此占家宅，曰：灶下有坟坑，移灶则声影绝。于第三间暖阁掘下五尺，必有老人葬此，适去则安矣。试之果然。"));
        set(6, new LiuReKe("甲申日第九局　干上午", "课体：元首，润下，励德，闭口，蓦越。", "课义：人盛宅隘，我成他败，干上脱空，三传可解。", "解曰：申子辰合为水局，以生干而脱支。论人宅，则为人盛宅隘。论彼我，则为我成彼败也。凡父母财官，空即无力。独我生之神，而值空陷，是为空脱，盗我之气愈甚。今幸三传水局，足以克制午火，而解空脱之患也。", "断曰：此元首之卦，合润下之局。凡事顺利，起于男子。以上制下，正大光明。宜先不宜后，宜动不宜静。天罡临支阴发用，上蓦然有变，继则浮游不安。惟智者乐水，足以当之。盖以元首之尊，而兼有润下之象。宜布德施惠，膏泽及人，斯协其吉。", "天时：多雨露。家宅：主人多屋窄，沿河处防贼入。婚姻：不宜。胎产：主生女，防有胎病难产。功名：有人举荐，秋冬占之吉，主河防水利等职。求财：得之不以其正，还防脱耗。投谒：合中有嫌。疾病：主泄泻虚脱，天罡作墓，大象甚凶。失脱：不出宅中而难获。出行：防有失。行人：先有书信。争讼：主为盗贼失脱，难解。兵战：水师吉，宜罢兵讲好。坟葬：主有水患贼耗，不宜于祖墓上葬妻妾。", "毕法云：人宅皆死各衰羸，合中犯煞蜜中砒，三传递生人举荐。观月经云：三传俱是水，润下水因由，忽然盗贼起，元武倍添愁。"));
        set(5, new LiuReKe("甲申日第十局　干上巳", "课体：重审，元胎。", "课义：冲克生扶，贵怒畏初，马载夜虎，非细驰驱。", "解曰：巳亥寅申相冲，申寅亥巳递克。然四孟长生，交作六合，是传课干支，互相冲克，又互相生合也。两贵入狱，干之必怒。初申绝鬼，深为可畏。末寅遁庚，马载夜虎，其为变动岂小哉。", "断曰：此重审之卦，事多不顺，起于阴小。又格合元胎，主狐疑不决。青龙折角，不免刑冲。当详审后行，勿妄动取咎。", "天时：有雨，但防风雹。家宅：防口舌争斗，兼有脱耗。婚姻：不宜，惟昼占可用。胎产：胎神临败地，防损。功名：有成，防阻碍。求财：防因财致讼。疾病：闭口临身，主中风不语，饮食不进。失脱：难寻。出行：防惊恐，投人不利。行人：在路。争讼：大有惊忧，终得解散。兵战：吉而未纯。", "毕法云：虎乘遁鬼殃非浅，彼此猜忌害相随。古鉴：一县丞申将占官职，曰：初传申官临巳，巳为双鱼，主兼职。中亥长生临宅，上官承顾。末寅身临亥，名达天庭。亥为学堂，有绛帐之荣。果以权县印得荐引，见补上宫教授。指南：巳将占院试，曰：月建旬首发用，龙雀乘旺，德禄乘马，干支六合，首荐无疑。又辛未年戌将占升迁，曰：龙常并见，城吏全逢。青龙内战，奇遇超擢。朱雀生日，公卿交誉。驿马德禄，俱入天门，非天官而何。然贵覆地网，主自孟引退，悉验。"));
        set(4, new LiuReKe("甲申日第十一局　干上辰", "课体：涉害，斩关，间传，登天。", "课义：支干魁罡，中末空亡，移远就近，岁月时详。", "解曰：魁罡临于日辰，动用非细。六合并于日上，乃真斩关卦也。况辰午申为登三天之格，事情远大非常。惜中末空亡，名为动中不动，事无究竟。止宜移远就近，解散凶咎而已。如初见太岁，中末见月建日辰，亦名移远就近，将缓为急。或占时发用，亦名动中不动，远求无功，反在近处也。", "断曰：此涉害斩关之卦，非安居之象。主忧疑进退，多历艰辛，初遭夹克，事不由己。日上私门，多是阴谋私祷，奈中末皆空，凶固不成，吉亦不就。", "天时：孟晴还阴，孟雨还止，大风忽起。家宅：不宁，虽凶有解。婚姻：不宜，亦不成。胎产：胎防伤损，产有惊恐。功名：百计营求，迄无成就。求财：利私谋纳贿，然到底成空。疾病：危险，暴病即愈。失脱：急寻可得。出行：远行不到，还作近游。行人：远者未发，近者即至。争讼：惊忧终解。兵战：小利，未足恃，须出敌不意，方获大胜。坟葬：防破坏。", "毕法云：支坟财并旅程稽，罡塞鬼户任谋为，不行传者考初时。课经云：龙登天则雨，官登天则迁，所忌空脱，惟争讼疾病，反喜空亡也。指掌赋云：空亡乃耗散之神，中为折腰，末为刖足。辰戌网罗之煞，在日毁卵，在辰覆巢。"));
        set(3, new LiuReKe("甲申日第十二局　干上卯", "课体：重审，进茹，罗网，升阶。", "课义：人宅皆旺，动遭罗网，夜占脱甚，互坐墓上。", "解曰：木旺于卯　金旺于酉，干支皆乘旺神。守之为贵，若妄动，定遭罗网缠绕，身宅变为羊刃，反生灾咎也。中末脱干之气，夜占则加以雀蛇火将，其脱弥甚矣。寅坐金墓之上，申坐木墓之上，干支互墓，各招昏晦，不宜两相投奔也。", "断曰：此重审之卦，顺进连茹，顺中有逆，进中有退，静宜动滞，有始无终。若已见蹉跌，而图复旧业，大为吉兆。如本安处，忽求分外，反生灾悔。又互坐丘墓，如愚蠢之人，两相投奔，适取昏迷，占者尤当猛自警省。", "天时：云多雨少。家宅：不昌，又主情愿借屋与人作践。婚姻：两旺可合，但恐有伤。胎产：胎临绝地，防不育。功名：有始鲜终。求财：不由己力，终不可得。疾病：或如狂，主得之饮食，禁忌即愈。失脱：急寻，迟则难觅。出行：宜近处，远行不利。行人：未有期。争讼：主为口舌文书，进凶退吉。兵战：利主不利客，忧患消解。坟葬：不吉，不能发贵。", "毕法云：互旺俱旺坐谋宜，所谋多拙遭罗网，人宅坐墓甘招晦。订讹云：前位神覆盖遮隔，不得出头，遇丁马更凶，得年命冲破为有救。指掌赋云：午正阳，有泰阶之象，从辰巳升之，岂非观光乎。但传空曰声传空谷，退吉，而进则不宜。"));
    }
};
