public class d23 extends KeEntry{
	public d23() {
        set(7, new LiuReKe("丁亥日第一局　干上未", "课体：伏吟，杜传。", "课义：身宅皆丁，岂容少停，递相吞啖，惊怪交并。", "解曰：本身是丁，宅亥遁丁，是身宅皆丁也。丁为动神，四课三传俱是丁神，变动不已极乎。土既克水，水复克火，递相吞啖，惊恐怪异之事不一而足矣。阴日伏吟，支上发用，奈亥为自刑，复归于干。未又刑末传之丑，刑冲交并，全无一息静机，是伏吟主静，而此课则全是动象也。但中传未空，谓之折腰，凡事必有间断作辍。", "断曰：自信之课，柔而专用。而传复杜塞，必须中道改图。占者遇未年未月，或月将是未，庶几作事，可有十得四五之望。", "天时：占晴则晴，占雨则雨。家宅：凡事不宁，只家守旧。功名：丁神兄弟太旺，无望。求财：数中并无一点财气。谒贵：无益。婚姻：门楣相当，礼仪俱盛，但联姻之后，不无小有言语之伤。胎产：女喜，未月末日产。疾病：水能克火，心与小肠受病，日医出现，在邻近求医治之则吉。求谋：不能如意。出行：不能起程，犹恐半途而反。逃亡：在近处觅。行人：未至。兵战：彼此相持，往来互伤。", "毕法云：鬼乘天乙乃神o。三车一览云：丁亥伏吟，自具丁马。占行人必中路被阻，缘中传空亡，不能驯至末传，是以进退两难。余占必先允许，后无实惠。"));
        set(6, new LiuReKe("丁亥日第二局　干上午", "课体：元首，退茹，斩关。", "课义：昼虎乘禄，初戌墓宅，弃此得财，死病为厄。", "解曰：午为丁禄，昼占乘虎，危而不可守也。戌为日墓，乘后覆日，晦而不可居也。然戌以上克下，发用而为初传矣。果能舍此而寻中末之财，则酉与申，皆可为我用也。然丁火病于申，死于酉，其祸与戌相等，则灾厄之来，谅不免矣。旺禄临身，艰难中尚可进步。但午为旬空，禄不可恃，三传又入于病死墓之乡。诸占不吉，惟马月马年填实，则福禄又可绵长。", "断曰：天鬼临亥，号为关格。百凡谋为出入，多有阻隔之象。", "天时：有雨。家宅：禄空财绝，毫无气焰。功名：不利。求财：艰难得之，终不旺相。谒贵：不能遇，且无力。婚姻：女子虽美而悍，不成为妙。胎产：受孕不实，临产不易。疾病：脾胃之病，反覆淹缠，恐防不救。官讼：彼来克我，防欺诈诳诱之局。出行：防有病患，志气昏沉。行人：心亦孟归，复因财别图，而往他处。兵战：彼兵无粮，我兵又不利于速战，且静以待之。坟葬：穴中防有水，宜葬女人。", "毕法云：干墓并关人宅废，后合占婚岂用媒，魁度天门关隔定。古鉴：戌将加亥占产，曰：占时与日比，用神克下，当生男。亥为头，戌为足，恐有倒生之象。末传蛇对冲卯，卯上寅作白虎，为血忌，卯日当生矣，果验。"));
        set(5, new LiuReKe("丁亥日第三局　干上巳", "课体：弹射，间传。", "课义：用遥传陷，自支归干，破碎临支，钱财不完。", "解曰：酉为弹射，而发用遥克，业已无力矣。况中传未为旬空，末传巳为陷空，从何处求益乎。酉为破碎，金煞来临宅上，钱财耗散，有不可胜言者矣。巳酉丑方成金局，今中传以未居之，谓之虚一待用。若年命上神临丑，或岁月是丑，则冲未而会成金局，则为我用矣。况巳火生未土，未土生酉金，三传递生，财气未始不旺，所嫌者支乘破碎耳。秋占为吉。", "断曰：弹射之课，为力已微。间传而退，有山遥水隔之象。自支传归干上，名曰朝元，事皆人求于我。", "天时：主晴。家宅：壮基之课，人宅利益，财来财去，亦得亦失。功名：不显。求财：阴亲贵人之财，求之可得。谒贵：夜占得贵人力。婚姻：可成。胎产：孕女，产易，产时有西南方阴人至。疾病：病轻，易治。求谋：凡事可成。出行：马临干头，支又冲之，必定起程。行人：马临末传，不数日即到，但恐到后，另有他行。兵战：我去生他，他复传我，两家讲信，修睦为上。", "毕法云：夫妇芜淫各有私，不行传者考初时。指掌赋云：未加酉为继母。中黄经云：马见巳亥在路歧。指窍云：酉未巳为励明，出入从其所便。"));
        set(4, new LiuReKe("丁亥日第四局　干上辰", "课体：元首，元胎，斩关。", "课义：昼名斩关，干上天罡，闭口突出，大利逃亡。", "解曰：天罡加干，昼占青龙临之，所谓真斩关也。癸巳发用为闭口，若能谨慎语言，出其不意而突然以出，则利有攸往，谁能追而捕之哉。巳为闭口，亦为驿马。而末传又乘了神，故与动相宜。若冬春占此，官文两旺，举子必然登第，仕宦必得高迁。但干受辰脱，上乘青龙，不免欢喜中费财耳，", "断曰；元首之课，贵神驿马，与官星交动。若更并年月吉将，其福未有艾也。", "天时：风雨交加。家宅：六亲皆好，事业可图，三传递生，得人钦敬。功名：癸遁巳上，为闭口官星，不宜多事，交至中末，则大发矣。求财：财临支上，取之尚难。谒贵：得贵人欢心。婚姻：恐女不正。胎产：孕男且贵，产时亦易。求谋：诸事可成。疾病：当有肺病，医治可愈。争讼；无事。出行：昼占宜由陆路，夜占宜由水路。行人：马恋生，不能动身。盗逃：不得。兵战：有解甲息兵之状。", "毕法云：鬼乘天乙乃神o。指南：壬午九月辰将加未，午命人占功名，曰；贵德宫星临年，月将青龙居干，且羊角相加(谓辰加未，辰有角星，未为羊)，应未年甲榜。岁居干后，日生青龙，先京职而后外任。但身禄不得地，岁君临嗔怒之所，恐为国家起见，不保其官。"));
        set(3, new LiuReKe("丁亥日第五局　干上卯", "课体：涉害，曲直，不备。", "课义：人得传力，将传废宅，自取其祸，半危半吉。", "解曰：木局生干，人得传力也。木局脱支，宅不因之而废乎。况天将俱属土，能克亥水，未临支上，又复克之，宅之废也宜矣。然此家宅之祸，由丁自取也。盖丁课在未，自上支门克支，支遂投卯而成木局，生身脱宅。其生也，其吉也。其脱也，其危也。两两相半，吉凶平分矣。此为回环格，乃三传俱在四课之中，又为干支相会，非不备课也。占凶凶成，占吉吉就。未与卯俱上克下也，涉害取用。未与卯俱四克，但未为孟上神，而又先见，故此课亦有用未发用者。", "断曰：涉害之课，其机最深，类成木局，凡事丛杂，又名回环。止宜守旧则吉，不可妄动。", "天时：木局主风。家宅：眷属丰盈居狭宅，宜增修以廓充之。功名：官文两旺，惟不利土年月时。求财：卯上有辛财，出旬可得。谒贵：昼占得贵人之力。婚姻：天后财神俱不出现，不必成。胎产：女胎坚固，而产则难。疾病：中风之症，有余之病，宜消道清理以治之。求谋：凡事可成。出行：贪恋家室，未必能行。失物：不远。兵战：彼此交通和好，大有轻裘缓带之风。坟葬：支来生干，葬之大吉。", "毕法云：眷属丰盈居狭宅。指掌赋云：常贵共入官乡，当朝执政。心印赋云：未卯亥为正阳，遵发生之意。"));
        set(2, new LiuReKe("丁亥日第六局　干上寅", "课体：重审，四绝。", "课义：交关且尔，妻财废弛，禄既空亡，守之何裨。", "解曰：午与未合，寅与亥合，交关相合，非不美也。然寅上遁庚，为丁之妻财，今陷旬空，则废弛而无用矣。午为日禄，又系空亡，守之究何益哉。中传丑为干之脱气，末传申为干之病乡，皆不可投。又申为丁财，既被初克，又被中墓，全无气象。惟干上寅遁庚财，却又陷空，故不利于求财。虎乘午为用，谓之白虎内战，防有火灾。", "断曰：四绝之课，最宜结绝旧事。禄神落空，即居显位，亦不免衿肘之患。惟年月太阳填实，方能丰富。", "天时：昼占主风，夜占主雨。家宅：人宅虽安，虚名虚利。功名：出旬填实，便有所望。求财：财气甚微。婚姻：女占男吉，男卜女不吉。胎产：孕女，产不得顺。疾病：白虎内战，恐防增病，急求医治。出行：交车合，恐不能起程。行人：自至。兵战：无约而请和者，谋也，宜防虚诈。", "毕法云：权摄不正禄临支，前后引从升迁吉，将逢内占所谋危。精蕴云：初传午居干后，末传申居干前，亦为前后引从，主有升迁之象。"));
        set(1, new LiuReKe("丁亥日第七局　干上丑", "课体：反吟。", "课义：改变双双，丁马俱张，论讼难诉，闭口为良。", "解曰：巳为双女，亥为双鱼，凡事主有重叠。且巳为驿马，亥为丁神，往来交错于三传之中，主事有变动，祸福皆不单行也。贵人作鬼来克，论讼难以分诉。发用闭口，惟谨尔出话，以远祸患，何用多其词说为哉。干上丑，昼占上乘太阴，乃日干生其上神，上神又生天将，名脱上逢脱，多虚诈不实。", "断曰：反吟之课，反覆变迁，事皆未定。巳亥相加，重求轻得之象。", "天时：主晴。家宅：比劫既多，财难得聚。功名：日德作官，将乘贵人，朱雀丁神值之，不日升迁。求财：不得。谒贵：必两三次，方能谒见。婚姻：昼占女子肥陋，夜占能饮。胎产：孕女不实。疾病：头目之疾，或患寒热劳病，大宜补火。出行：意在必行，从水路去。行人：不论远近，指日可归。逃盗：难捕。兵战：以主胜客，拓地千里。", "毕法云：鬼乘天乙乃神o。指南：寅将加申占回奏，曰：龙神克战，课将反吟，官难满任。巳为驿马，上乘皇诏，主一任未了，二任又临，其降调乎。精蕴：占雨曰：丑为雨师，未为风伯，上乘常雀是无雨矣。三传水火往来，乃阴晴不定之象。火在南为离，水在北为坎。诀曰：坎离交变，日出淋漓。"));
        set(0, new LiuReKe("丁亥日第八局　干上子", "课体：重审，铸印，斩关。", "课义：壬辰及子，支干双水，引从虽远，虎墓宜视。", "解曰：干乘子水，支神遁壬亦水，两水克干，丁火安能当其灭没哉。初传巳，末传卯，引从支上辰在内，主迁修家宅之喜。但辰土临亥为拱墓，而白虎凶神乘之，大宜详审，不可轻动也。赖中传戌蛇冲辰虎，方不为害。占人年命在戌上，谓之破墓冲虎。或居巳上，二戌冲辰，众凶皆散也。", "断曰：铸印之课，大宜求官见贵，君子占则吉。子水克丁，辰土克亥。若占官讼，必两败俱伤，所谓彼此全伤者也。", "天时：大风无雨。家宅：身宅俱伤，惟贵家则吉，亦防孝服。功名：大吉。求财：可得，不宜取之太急。婚姻：女甚凶狠，成之不吉。胎产：主得贵子。谒贵：未必得见。疾病：肾水大亏，恐不能治。出行：墓神临马，不能动身。行人：即归。失脱：可以寻见。兵战：以主胜客，有万里封侯之象。", "毕法云：支乘墓鬼有伏尸，彼此全伤防两损，制鬼之位乃良医。占验：九月辰将加亥占病曰：课得铸印，占病不吉。两蛇夹墓，白虎入宅。支墓临支克支，三日内必死。课经：未年二月，戌将加巳，亥命占功名，曰：岁月日命马俱在巳，发用又遇天魁太常，为官爵课，无官得官，有官进爵。"));
        set(11, new LiuReKe("丁亥日第九局　干上亥", "课体：重审，曲直，不备，回环。", "课义：人丰宅隘，后失倚赖，三传皆空，昼将驵侩。", "解曰：支来克干，为上门乱首。但亥卯未合为木局，生干脱宅，是人丰宅隘也。殊不知未亥皆空，而亥乃昼贵，既陷空地，后必失其倚赖矣。昼将皆土，又来脱干，特假三传之生，以暗肆其欺罔，非驵侩而何。卯为于之父母，乘太常临支，宅中必有婚礼之喜。或开设布帛酒食店肆，后必长进。回环之格，三传不离四课，凡事可成，惟不宜解释忧疑。亥加未，酉加巳，两贵立受克之方，难以干贵。", "断曰：三传曲直逢空，有材大而不中绳墨之象。事从内起，祸由妇人，止宜守旧，不宜动作谋为。", "天时：有雷无雨。家宅：平常，守旧则吉。功名：有文无官，冬季占则吉。求财：卯遁辛财，取之自获。谒贵：两贵受克，难于干贵。婚姻：女益男家，或系旧亲，成之吉。胎产：女胎不育。疾病：肝病，宜祈祷。求谋：止宜文书尊长之事。出行：未必遂意。行人：卯为门户，末传临之，行人至门。兵战；中止之象。", "毕法云：鬼乘天乙乃神o，两贵受克难干贵，眷属丰盈居狭宅。指掌赋云：未亥卯为从吉，待时而动。又云：大吉小吉作勾陈，斗争田地。"));
        set(10, new LiuReKe("丁亥日第十局　干上戌", "课体：昴星，斩关，炎上。", "课义：四虎来A，宜守术业，火局临木，受用不乏。", "解曰：两寅乘虎，共四虎也，凡占至惊至危。戌墓乘蛇后临干，凶而不可守也。初传午禄，虽为旬空，赖龙合木将生之。又龙合为术业，故为可守。火局临于木上，木能生火，其生自不能已矣。且火能生干上神之土，而土生寅遁之庚，支财受用，岂得空乏哉。", "断曰：虎视之课，发用无力，百凡未得称心。华盖覆日，又日生上神，主虚费百出，昏沉不乐。孤辰发传，主人有别离。", "天时：主晴。家宅：昴星逢蛇虎，必多怪异虚惊。功名：龙化为蛇，日渐萧索。求财：寅上遁庚，虽得亦惊危之财。谒贵：无力。婚姻：女淫而恶，不可成。胎产：男孕，产时恐有惊危。疾病：年命遇辰，尚可望生，余难救。出行：干支俱生上神，防有失脱。行人：中传空亡，半途有阻。争讼：以凶制凶，反可为福。盗逃：中途匿藏。兵战：彼此相持，兵连祸结。", "毕法云：避难逃生须弃旧，虎视逢虎力难施，华盖覆日人昏晦。秘要云：干上墓不可守，遂投初禄，却值旬空。弃禄再归干墓，终不可受。又投末传长生，奈逢白虎，止宜困守宅中，受惊危之长生耳，是谓避难逃生。"));
        set(9, new LiuReKe("丁亥日第十一局　干上酉", "课体：重审，间传，极阴。", "课义：自用传辰，礼下于人，财为破碎，妻婢伤身。", "解曰：干传干支，盖我有求于彼，自不得不礼下于人也。干上发用两酉，妻婢之象，乘破碎金煞，且火死于酉，绝于亥。丑加亥为遗泄，必因妻婢以丧败其身也，占者可以知所戒矣。占人年命在申，贵人拱定，必得两贵成就其事。日上见财，主妻不安，财不聚，末助初财，占婚尤的。丑加亥，夜乘太常，凡有求望，必涉水过桥。", "断曰：重审之卦，贵人差迭，凡事少喜多嗔。三传又名极阴，而逢间传，进中有阻，事体不甚明白。", "天时：主晴。家宅：间传凝阴，昏滞酒色，若年命上乘阳明之神，便可转凶为吉。功名：功名顺利，初时不无所费。求财：宜得贵人之财，但有分耗之者。谒贵：宜专心归一，莫使尖担两头脱。婚姻：可成，美而且贤，且日后家道隆盛。胎产：女孕，亦易产。求谋：百事可成。疾病：泄泻虚痨之症，求西北方医之则吉。出行：水陆皆通，前途无碍。行人：即归。逃亡：自归。兵战：无意于争，终归和解。", "毕法云：昼夜贵加求两贵，我求彼事干传支，鬼乘天乙乃神o。古鉴：亥生人，五月申将加午占前程，曰：破碎加干，必因费财得官。九丑在宅，主淫乱。在本命，主贪色。末传又见丑作阴加在官星。太阴者，阴晦也，当因色而死。"));
        set(8, new LiuReKe("丁亥日第十二局　干上申", "课体：重审，连茹。", "课义：三传财喜，人宅化鬼，仔细推详，病死墓矣。", "解曰：申酉戌全是西方金气，为丁之财，诚可喜也。但生宅上亥子之水，化鬼克干，则美者翻不美矣。况丁火病于申，死于酉，墓于戌，财之险危，孰有过于此哉。官爻临支，若以财告贵，可以捐纳得官，占升迁亦吉。顺连茹申酉戌曰流金，如霜桥走马，凡事俱要小心谨慎。", "断曰：重审之卦，连茹而进，有进易退难之象。事防再举，病防再发。支上神作鬼，凡有忧患，必然发自宅中。", "天时：主晴。家宅：财旺生官，当有纳粟奏名之喜。功名：名利双全，有求必得。求财：春夏占之，必有所获。谒贵：夜占得贵人力。婚姻：昼占大吉。胎产：女喜，恐难产。疾病：干支夹定三传，恐有益胀关隔之症；秋冬占之更不利。求谋：皆吉。出行：陆路可行，水路恐有风涛之险。行人：先有信，即日可至。逃亡：不能远去，宜速捕之。兵战：客来生主，可得敌人之粮。", "毕法云：后合占婚岂用媒，所谋多拙遭罗网，全财病体难担荷。指南：七月巳将加辰占逮问，曰：发用皇诏坐空，蛇虎二墓加临卯酉，此为墓门开，必至重叠死丧。又传将劫杀，丁火病死，墓绝俱见，全无救解矣。果廷杖死三人。"));
    }
};
