public class d25 extends KeEntry{
	public d25() {
        set(7, new LiuReKe("己丑日第一局　干上未", "课体：伏吟，自信，稼穑。", "课义：支传干上，彼求己向，可畏之因，昼夜天将。", "解曰：初传支上，末传干上，自支传干，必是人来向我委托，吉凶皆成也。三传比劫，无非干支同类，大有损于妻财。况昼夜天将，非蛇即白虎，皆能生灾作祸。太阴并戌曰被察，亦主怪异蔽匿，或小人诬谮，皆可畏也。", "断曰：此伏吟自信之卦，伏而未发，屈而未伸，静则安，动则滞。自信其柔，以与世接。身孟动而不得，家孟静而不宁。况稼穑卦本艰难，又逢土日，沉滞弥甚。凡事逼迫，不由自己。末传旬空，百事无就。", "天时：霾雾湿蒸，后见风雨寒冱。家宅：不宁，多见惊恐。婚姻：不用。胎产：防聋哑不实，若临产则立下。功名：淹滞无成。求财：反有损失。投谒：不相合。疾病：主在脾，久病不利，暴病无畏。失脱：宜急寻不远，逃者或匿邻家。出行：不得出，出亦即返。行人：立至，否则在彼处未动。争讼：主争田土事，有人唆弄激拨，后必解。兵战：有惊恐，众心疑畏不宁。坟葬：不安。", "毕法云：彼求我事支传干，宾主不投刑在上。课经云：卦无丁马，而占人年命上乘魁罡，及在亥者亦主动。指掌赋云：逆传四季丑戌未辰。春越库，散财不以其道。夏转魁，委任不得其人。秋煞墓，势将兴而将起。冬伏阴，机渐收而渐藏。"));
        set(6, new LiuReKe("己丑日第二局　干上午", "课体：重审，退茹，励德。", "课义：因贵失禄，丁马相逐，文书不利，婚姻急速。", "解曰：贵人发用，为日之财，而冲克午禄。得初传之贵，则失干上之禄也。中传亥遁旬丁，又为支马，相逐而来，为远动求财之象。午乘真朱，其属为文书，今旬空禄失，则不利于文书矣。子加于丑，夜乘太常，牛女相合，吉礼可以速行也。", "断曰：此重审之卦，连茹逆传，事多不顺。退中有进，宜详审后行，不可肆意妄动。上神生日，凡百皆宜。子丑相加，人情必合。惜午禄空亡，又遭初克，未免虚喜虚声，美中不足耳。仲夏月遇之，所谋皆能如意。", "天时：求雨即得，求晴难晴。家宅：和合，主有喜庆之事，防遗失文书。婚姻：必成，且见和美。胎产：恐系虚喜，占产立下。功名：虚名，未得实际。求财：当得贵人扶助，或远行求之。疾病：主虚，不进饮食，迟愈。失脱：宜寻。出行：远行有财喜。行人：即至。争讼：主争财，有解，原告多费。兵战：吉利。", "毕法云：空上逢空事莫追。课经云：干支上下相合，为和美课。又合神发用，占人年命，俱乘吉将，为合欢课。但干支上下，又交车作六害，主彼此相谋，不宜交关用事。指掌赋云：子亥戌为重阴，安嘉遁之贞，宁甘没齿。心印赋云：子丑相加事必成，更逢吉将转欢欣。"));
        set(5, new LiuReKe("己丑日第三局　干上巳", "课体：重审，间传，时遁。", "课义：宅用马丁，迁移难任，临事闭口，课传五阴。", "解曰：亥乃丁马，临支发用，其宅必主迁徙移动。巳乃旬尾，临干坐空，遇事且宜闭口深藏也。四课巳卯亥酉，三传亥酉未，共见五阴。若占人行年本命，更值丑字，亦可谓六阴相继尽昏迷矣。", "断曰：此重审之卦，逆退间传，事由内起，多关妇人，尊上无势，有不顺之情。后武同行，有不正之象。虽上神生日，而惜坐空乡。纯阴用事，主客不和。身空暗而莫诉，宅动摇而不宁，纵曰利于求财，亦复蹉跎难遂。", "天时：重阴久雨，罕见日色。家宅：主北向幽暗，防有奸私。婚姻：难成，亦不宜。胎产：胎神坐长生，胎安产不吉。功名：出自私门，或由财贿。求财：主因妻财动，或得之暧昧。疾病：主阴症，或不语，反覆难愈。失脱：宜寻。出行：宜，且止。行人：即来。争讼：主为奸私之事，或因妻财致讼，宜和。兵战：不利。", "毕法云：龙加生气吉迟迟。课经云：干上巳，昼将六月占，青龙乘生干之神，又作月内之生气，主徐徐发福。订讹云：亥酉未为时遁格，盖酉为太阴，未中遁丁为玉女，利隐遁潜形。自亥传未，如人入幽暗潜身，有识时肥遁之象。君子占之则吉，小人反为凶也。"));
        set(4, new LiuReKe("己丑日第四局　干上辰", "课体：昴星，掩目，天烦。", "课义：叠值魁罡，远涉他乡，贵财发用，无礼须防。", "解曰：天罡河魁，既临日辰之上，又入中末两传，可谓层见叠出矣。魁罡为变动之神，定主身宅不宁，他乡远涉也。子为日财发用，昼乘贵人，必得贵人以财相助。但子加卯上，虽曰励德，亦须防无礼之刑也。", "断曰：此昴星之卦，亦名冬蛇掩目。阴性从地，其气下沉，伏而视之，潜藏则吉，躁动则凶。占者事必循理，提防暗昧为佳。干支上魁罡冲击，其象主动。但缘墓覆昏沉，又柔日昴星，伏匿万状，终不能动耳。", "天时：有雨。家宅：不宁，防有奸私口舌。婚姻：不吉。胎产：防不实。功名：可因财而得，却防暗损。求财：或因势要，或由田土，皆可得。投谒：有门路，可获利而返。疾病：主脾疾，烦懑阻隔。失脱：宜寻。逃亡：恐投势家。出行：百端阻滞，不能动身。行人：未归。争讼：或争财，或争田土，久而不和。兵战：昼占得利，夜占防伤损。", "毕法云：宾主不投刑在上。课经云：魁罡加日辰，犹人遇凶神。重土闭塞，必须斩关而出。未为玉女能获身，子为华盖能掩形，太阴地户主潜藏，天乙神光能庇佑。但冬蛇伏匿，中传坐空，不能动也。"));
        set(3, new LiuReKe("己丑日第五局　干上卯", "课体：涉害，从革，蓦越。", "课义：传入宅上，俱为脱诳，身既乘鬼，赖此保障。", "解曰：卯为日鬼临日，夜乘六合尤甚。支上乘酉，三传会成金局，又归宅上。众金来盗土气，其为期诳，不可言也。然终赖此，得以制卯鬼之恶，除日干之灾，反为身之保障，虽被脱诳，亦所甘矣。", "断曰：此涉害之卦，合从革之局，事端两歧。多历难辛，萌芽孟就，又被阻隔。静而惶惑，动则乖张。喜卯来见酉，虽脱干而能制鬼。合中不足，冲反有功。失在始而得在终，难于前而易于后，革故鼎新，因盗获福。", "天时：始患多风，继得雨润。家宅：虽多耗费，却无灾咎，主出名医。婚姻：忌。胎产：胎神坐墓，空上逢空，防不实。功名：官坐空墓，为子孙耗脱之象。求财：初见耗折，后少得利。疾病：主在肺，或筋骨，虽虚不防；家有幼辈，善能治疗。失脱：难寻。出行：多费而有阻。行人：即归。争讼：耗财而终得解散。兵战：吉凶相半。坟葬：昼占兆吉。", "毕法云：虎临干鬼凶速速，制鬼之位乃良医。秘要云：凡喜见官鬼者，惟妻占夫，及有官人，不宜鬼空及被制，余皆以鬼为凶，喜有制也。指掌赋云：巳丑酉为反射，怀杀伐以酬恩，日生三传，财原必耗。"));
        set(2, new LiuReKe("己丑日第六局　干上寅", "课体：重审，斫轮，四绝，罗网。", "课义：寅卯两木，灾祸相逐，夜贵入宅，祛殃降福。", "解曰：寅加日上，卯发初传，两木并来克身，灾祸相逐，宜不可逃矣。喜夜贵在申，来临丑宅，于以制寅卯而扶己土。殃不由是而祛，福不由是而降乎。鬼助末传生干，是谓引鬼为生，亦主先凶后吉。昼占帘幕临支，士人得之亦利。", "断曰：重审之卦，格合斫轮，主先历艰难，后得成就。卯为车轮，加申为斧斤斫削。更喜戌工为印，白虎催官，土日得之，亦名流转。仕宦极为佳兆，常人不宜。", "天时：昼占主雨，夜占主风。家宅：必有科第，门列旗杆。婚姻：未成。胎产：不宜。功名：大吉，宜正取之。求财：不得。投谒：未和。疾病：大忌，夜虎乘卯鬼，为棺椁之象。失脱：远去。出行：赴官吉利。行人：在道，或遇丧车，或遇轩冕同行。争讼：大有忧患。兵战：宜慎。坟葬：父爻乘昼虎坐墓，主父母墓中生白蚁。", "毕法云：制鬼之位乃良医，常问不应逢吉象。指南：庚午年寅将，一县令占人觐考选。课得斫轮，日上天吏官德，坐于空墓，阴神酉制之，必有明暗相攻，不得翰林铨部。卯官乘元武发用，中传朱雀主言路，末传白虎主风力，必得垣中。历迁都宪，巡抚东方。宅上子孙乘帘幕贵人，又作长生学堂，当有贵子，由科甲入翰林也，后皆如言。"));
        set(1, new LiuReKe("己丑日第七局　干上丑", "课体：反吟，无亲，淫佚。", "课义：往来相会，丁马并至，动可取财，守之无利。", "解曰：干支上下，不出丑未二字，往来相会。亥独发用，既为日财，又为丁马。动而之传，财可得也。若但守干支，丑未内外反覆，动而不动，行而不行，终无所利矣。", "断曰：此反吟无亲之卦，取丑中癸水，遥射巳宫丙火，以巳上亥神为用。如傍井倚栏，斜冲射之，不出井外。主上下暌隔，彼此忧疑。直道难容，旁求易就。一身两用，动则相宜，静则反扰。末传坐空，实事消磨，虚惊亦散。", "天时：多雨，时止时作。家宅：不宁，或有嫁娶喜事，或防男女奸私。婚姻：往来和合，恐非正礼。功名：屡劳心力，恐终未遂。求财：宜动以取之，或因妻财而有动。疾病：得之财色，反覆不一，暴病有解，久病不吉。失脱：勿寻，逃亡勿追。出行：傍徨不定，终不成行。行人：将近复远。争讼：反覆，终得解散。兵战：无利。", "毕法云：后合占婚岂用媒，来去俱空岂动移。指要云：井栏射取传，占事难成易破，虽遇吉神，亦是半遂。又云：反覆不定，病主两症。中黄经云：六合从来福遂佳，乘于巳亥远天涯。神将论云：登明乘六合为小儿，木到亥上方生故也。乘蛇主哀哭。"));
        set(0, new LiuReKe("己丑日第八局　干上子", "课体：知一，铸印，励德。", "课义：先冲后击，交后有益，末助初生，如砒在蜜。", "解曰：子午丑未，干支上下交冲。而子未午丑，干支又各自相害，为先冲后击之象。午与未合而生日，子与丑合而为财，交车相合，交关利益也。末传卯助初巳而生干，如蜜之甘；然卯又径来克干，如砒之毒，其生克倚伏如此。", "断曰：此比用知一之卦，事有两端。察其同异，舍远就近，舍疏就亲。信里生疑，恩中起怨。格名铸印，兼号乘轩。常人得之不宜，仕宦见之大吉。但驿马六合，惜坐空乡，犹非真体。仲夏月占，方合格也。", "天时：子亥坐空，每作风雨之势而不成。家宅：仕宦而出，宅空吉利，常占有灾，妨损妻财。婚姻：不利。胎产：胎不实，产即下。功名：大吉之兆，嫌贵人禄马俱空，必五六月占始佳。求财：所得轻微。投谒：好处生嗔。疾病：凶，尤忌占妻病。失脱：难得。出行：宜上官之任，余无利。行人：必归。争讼：难和，容忍为上。兵战：不利，先难后易。", "毕法云：权摄不正禄临支，彼此猜忌害相随。课经云：昼贵临身，而朱雀乘戌克之，为贵人忌惮格，告贵无益。袖中金云：铸印主官禄迁擢之象。不见太阴天马，即非真体。常人反生灾，且为事迟钝。"));
        set(11, new LiuReKe("己丑日第九局　干上亥", "课体：涉害，从革。", "课义：丁马载财，传局生来，启齿便得，闭口无灾。", "解曰：亥为日财，丁马载之，来加日上，而三传会成金局生之。酉类为口窍唇舌，故一启齿，便可得生财之益也。末巳为旬尾，昼虎夜武而临宅上，闭口谨言，乃免灾咎。", "断曰：此涉害之卦，合从革之局，事多险阻，心费机关。孟上神发用，主狐疑反覆。有气则革而增进，无气则革而退失。酉加巳为愁课。又将得后武，盗其金气，名从革不革，得六合亦主孟动未能。", "天时：多燥风，有雨不大。家宅：虚耗，又凶将闭口加宅，主有灾患，不能明言。婚姻：不宜用。胎产：胎坐长生，胎安产凶。功名：不利，勿入私门，徒有耗失。求财：先费后偿，宜索债负。疾病：主在肺及筋骨虚弱，不妨。失脱：主为喜事中失脱，或随身婢仆。出行：有惊多费。行人：即至。争讼：惊忧百出，宜用贿央情。兵战：不利。", "毕法云：昼夜贵加求两贵。课经云：金局须脱干气，而生起日财，名为取还魂债。古鉴：寅将占失物。酉发用，其类为妇人，又为金钗之属。加巳乃长生之地，不落空亡，又是三合金局，其物不失，现在炉灶灰中。寻之果获。指掌赋云：酉丑巳为献刃，远近俱被其伤。"));
        set(10, new LiuReKe("己丑日第十局　干上戌", "课体：昴星，二烦，励德。", "课义：三奇昴星，昼盗兼刑，魁罡并现，动不容停。", "解曰：昴星俯视，初传得午。中末干支上神，己日元遁，得甲戌、戊辰、庚午，合地上三奇甲戊庚之局。昼占用神，空上逢空。中戌乘雀加日，必见盗脱。戌刑日干，丑又刑戌，辰午亦皆自刑也。课传魁罡叠见，动作非常，不容自己。", "断曰：此冬蛇掩目之卦，暗昧不明，进退无据。虽午加卯为明堂，主万事昌隆，遇凶成吉。奈午值旬空，孤辰发用，凡占多虚少实，止利僧道九流。又地上三奇，用空而奇精有损，其福顿减。又为天地二烦，却反喜用空而仲神无气，其祸亦散也。", "天时：求晴得阴，求雨得风。家宅：主有田土斗争之事，正五九月防有火惊。婚姻：不宜，亦不成。胎产：恐胎临死败，不吉。功名：虽有意外之遇，却无实际，且防同辈暗损。求财：劳多获少。疾病：主脾病虚热，暴病不妨，久病凶。失脱：难寻。出行：有忧难，难中有喜。行人：先有信至。争讼：公事昭彰，不利私暗，主争田土屋宅。兵战：不利。坟葬：主坟前多空地，或见惊怪，或起争端。", "毕法云：尊崇传内遇三奇，空上逢空事莫追。袖中金云：卦如冬蛇蛰藏，掩目不动。事多隐伏于内，犹豫惊恐。指掌赋云：昴星如虎对立视，俯仰以卜远近之忧危。言杀气至酉而盛，阴阳无克，乃从至阴处，讨出消息来也。"));
        set(9, new LiuReKe("己丑日第十一局　干上酉", "课体：元首，间传，迎阳。", "课义：败中藏慝，卯木入宅，外勾里连，两贵烦剧。", "解曰：己土败于酉，酉临干上，又旬遁为乙鬼，至恶也。辛卯入宅发用，且脱且克。又为日鬼，自宅中而出，是谓外勾里连。昼贵入狱，夜贵受克，两贵烦剧，干之必怒也。", "断曰：此元首之卦，以尊制卑。事由男子，正大光明，利于先举。但外来克内，干上又逢脱败，主客互相冲击，其象不和，必循理而行，斯无悔咎。", "天时：先见风及雷电，雨迟至。家宅：人宅俱耗，防子孙脱漏之事。婚姻：不宜。胎产：胎坐日墓，不安。功名：可得，防以私门致败。求财：主为病讼所耗。投谒：不和，防见怒于贵人。疾病：有祟，宜破财。失脱：不获。出行：未利，反利阴谋逃窜。行人：尚远。争讼：防有指唆，内外勾连，不宜托势。兵战：昼占不利，夜占大胜。", "毕法云：两贵受克难干贵，罡塞鬼户任谋为。指掌赋云：卯巳未为迎阳，鸣高冈之鸾凤，占事宜速就，稍迟则无气矣。指南：甲申年五月午时，日旁大晕围绕，众以为瑞，因就午将正时，占得此卦。发用元武，克干克支，夫干为天位，而乘败气。支为社稷，而见死神。岁君临灭没之方，贵人入囚辱之地，何瑞之有，果然。"));
        set(8, new LiuReKe("己丑日第十二局　干上申", "课体：元首，进茹，罗网，正和。", "课义：三传克日，夜贵临吉，或蒙神佑，利卜官秩。", "解曰：寅卯辰亦号木局，三传皆为日鬼，其势不可当矣。喜夜贵在申，金可制木，来加干上，足以拒鬼而护干，不啻蒙吉神之佑也。常占得此，为遇患有救。惟仕宦者占之，为德高官显，生贵临身，吉无不利也。", "断曰：此元首之卦，顺进连茹。三传皆官，贵常临日。日德在宅，发为用神。主忠孝积善之家，有甲第显荣之庆。春占尤利也。常人防官非口舌，或病患相侵。虽有救神，亦宜谨慎。", "天时：屡有风患，得雨而解。家宅：朱雀克宅，防火灾，或防病讼。婚姻：后克龙，干上克支上，主不成，成亦不利。胎产：丑加子为腹胎格。功名：占试得中，居官宜慎。求财：劳动而所得微细。疾病：家鬼作祟，宜作福祷神。失脱：或因喜事宴会，或主婢仆偷窃。出行：宜谒贵。行人：将至，有阻。争讼：必藉人调解，可以无患。兵战：宜慎。", "毕法云：所谋多拙遭罗网，鬼临三四讼灾随，众鬼虽彰全不畏。课经云：支上有鬼，引入鬼乡，为家鬼取家人，赖干上食神为救。然必先有惊危，后稍无畏。如食神是贵，必得贵人力也。指掌赋云：寅卯辰为正和，展经略而沐浴恩光。"));
    }
};
