public class d26 extends KeEntry{
	public d26() {
        set(8, new LiuReKe("庚寅日第一局　干上申", "课体：伏吟，自任，元胎，六仪。", "课义：禄马随身，两虎并临，居官极品，俗庶灾身。", "解曰：申为日之德禄，又乘驿马临身发用。中传蛇生角，末传勾陈捧印，仕宦占之，大为吉兆。申本虎也，以居官言之，二虎入庙，为威权显赫之象，必登极品。俗庶不足以当此，必有讼狱之灾矣。", "断曰：此伏吟自任之卦，课传皆孟，格合元胎。伏而未发，隐而未明。静则相宜，动则反滞。君子守正修德，则应禄马俱临之吉，旬仪发用之祥。富贵显庸，亨通无比。若常人无德，反主生灾作祸。", "天时：风雨时至。家宅：干支互绝，宜与人兑换屋宇。婚姻：昼占不宜，夜占可用。胎产：春占安，夏占防损。功名：文主高第显荣，武主功成衣锦，躁妄者不吉。求财：大有所得，防为病讼所耗，或为兄弟分夺。疾病：昼占大凶，久病及老人小儿尤忌。失脱：可得，逃亡必获。出行：如意。行人：先有信，在路将至。争讼：不吉，静以待动者得胜。兵战：昼夜不宁。", "毕法云：任信丁马须言动，宾主不投刑在上。课经云：伏吟交车冲，不论亲疏，主先合后离。父子、夫妇、主客、兄弟皆然。袖中金云：寅日用申巳，凡占主刑中之合，薄有所就，出行则可。"));
        set(7, new LiuReKe("庚寅日第二局　干上未", "课体：知一，退茹。", "课义：夜贵墓身，昼贵墓庭，三传虽水，两丙一丁。", "解曰：未临夜贵，本辰墓也，今覆日上。丑临昼贵，本日墓也，今覆辰上。交互相墓，两贵蹉跌矣。三传子亥戌，亦名水局。水虽能制日鬼，而亦脱日气。况旬遁丙子丙戊丁亥，两丙一丁，皆为暗鬼。若嫌日上空生，弃之而妄动，必逢灾咎也。", "断曰：此知一之卦，脱干生支，恩中有害，合中有冲。二下贼上，皆在宅内。主妻财争讼，咒诅不宁。又：三辰逆退连茹，节外生枝，先退后进。喜干奇临干，旬奇发用，亦合联珠之吉，可阴消祸患，意外获福。", "天时：久阴多雨。家宅；主人昏晦不通，宅亦废损虚耗，或被子孙耗费生祸；又丑加寅，夜将天空，主屋上兽头落。功名：士有奇遇，官有异政超擢。求财：所得轻微。投谒：昼占和美，夜占不合。疾病：大吉加寅，干支互墓，昏滞不吉。失脱：或在邻近，或子孙所窃。出行：多有耗费，兼防暗损。行人：迟滞。争讼：夜占防直遭曲断。兵战：不宁。", "毕法云：空上逢空事莫追，昼夜贵加求两贵，干支乘墓各昏迷。观月经云：四立干上神，分明末日临，此名天祸卦，乖角竟相侵。如立夏日是庚，前一日是巳，巳乃木之末日，巳课在未，庚日占事，干上得小吉，是末日临也。"));
        set(6, new LiuReKe("庚寅日第三局　干上午", "课体：涉害，间传，顾祖，励德。", "课义：虚忧不实，就生无力，倘聚钱财，祸患尤剧。", "解曰：午为日鬼，临干发用，大为可忧。幸值旬空，则虚而不实。弃而就中传辰土之生，似若可依。然亦陷空鬼之乡，不能助我为力。由是而之末传，势必取寅之财矣。而昼乘蛇火，为害非浅，可不慎哉。", "断曰：此涉害之卦，多历艰辛。每逢忧患，进退维谷。末助初而克干，主有人百端侵损。幸孤辰发用，为我难者，有心无力，虽见惊忧，旋归乌有也。贵临卯酉，大位升迁，微员斥退之象。", "天时：占晴不久，占雨难得。家宅：人宅互脱，又皆临败地，必见衰替。婚姻：不吉。胎产：胎神坐长生，占胎安，产不利。功名：仲夏昼占，赴任荣显。求财：迟得，防因财生灾。疾病：干支互乘死神，大凶。失脱：即在宅内。出行：不宜。行人：未至。争讼：不宜告人奸，必连累同罪。兵战：宜慎。", "毕法云：制鬼之位乃良医，干支皆败势倾颓。课经云：末虽助初，初却空亡，不能克日，末徒作仇怨，名抱难不斗格。指南：戊辰年子将代占枚卜。朝官占得顾祖格，多不满任。又初中空陷，龙化为蛇，龙神克其德禄，不但不能大拜，定以强进获咎。后果有一宗伯，枚卜不就，继被察处回籍。"));
        set(5, new LiuReKe("庚寅日第四局　干上巳", "课体：元首，元胎。", "课义：干传干支，我就他宜，亥虽解祸，生祸由斯。", "解曰：初传从干上发用，末传归干支上，自我就彼，为我求于彼之事也。巳为日鬼，亥水克之，洵足解一时之祸。然亥生寅，寅转生巳，迤逦递生，还为日害，是则生祸亦由斯也。", "断曰：此元首之卦，课传皆孟。巳加申上，名病元胎。虽事出顺利，以尊制卑，然必正大光明，方得动而无咎。苟或暗昧，则应四病之象。伏滞不通，触则成祸。又三传递生日鬼，勾陈捧印，蛇化龙，仕宦则宜，常占不吉。", "天时：孟雨还晴。家宅：主家有孕妇，或阴人离散不和。婚姻：未遂意。胎产：胎神临败，防有损堕。功名：大利，主得人推荐，迁官捧印。求财：有得，亦防耗费。疾病：遇医，防复发。失脱：不宜追。出行：求官利，余不宜。行人：即至。争讼：宜和，或因幼辈起祸。兵战：居安虑危，不可轻易。", "毕法云：制鬼之位乃良医，彼此猜忌害相随。课经云：变克翻为两面刀。又干支皆乘长生，后却互脱，为乐里生忧。神应经云：末传亥遁丁鬼，或因子息而凶动。古鉴：辰将占家宅，曰：巳火克申，功曹乘蛇刑巳克申。亥为日子，必因子息吏人耗业。金生干巳，却来亥上脱气。寅生干亥，却来巳上脱气，主先兴后替。亥四寅七，十一年后见也，俱验。"));
        set(4, new LiuReKe("庚寅日第五局　干上辰", "课体：涉害，炎上，斩关，狡童。", "课义：始课可畏，动用非细，中末既陷，守为上计。", "解曰：天罡临日，河魁临辰，而复发用，旬遁丙鬼，其为动作非细矣。六合并临鬼户，其为阴谋方始，甚可畏也。幸中末俱落空陷，卒归消散。然所动谋，亦迄无成，不如守干上辰土之生，为计之得耳。子涉五土，戌涉四木，故又有以子为用者。", "断曰：此涉害之卦，合炎上之局。事当见机而进，知难而退。三传从末递生，以至支上而生干，主得人多助，百事可成。但火局以虚而明，戌加寅为墓临生，占事明者反为暗昧。又中末皆空，多不成实。惟宜解散忧惊而已。", "天时：沉阴蔽日，得风雷而散。家宅：斩关非安居之象，狡童防男女之私。婚姻：不宜。胎产：胎坐空墓，占胎主私孕；占产即下，有惊恐。功名：虽有虚声相助，迄无成就。求财：虚耗。疾病：木侮脾经，不吉。失脱：难获。出行：宜避难远引，然不能动身。行人：有阻。争讼：主为奸盗之事，旋归解散。兵战：未利。", "毕法云：三传递生人举荐，后合占婚岂用谋，不行传者考初时。课经云：夜贵登天门，神藏煞没，四孟月占尤的，缘四维为月将也。斩关主动，而中末空亡，反不能动。订讹云：午加戌，主失马入墓故也。戌为狱神传墓，主有狱讼事。秋夏占为恃势，庚辛日名带煞。"));
        set(3, new LiuReKe("庚寅日第六局　干上卯", "课体：知一，斫轮，罗网。", "课义：丙戌丙子，中传逢巳，君子宜占，俗庶有悔。", "解曰：戌旬遁为丙，而子元遁为丙，两丙夹中传之巳，无非日之官鬼矣。又卯加申上，格合斫轮。仕宦占之，宜有迁除之吉。而世俗众庶遇此，不免病讼之凶也。", "断曰：此比用知一之卦，既为互旺，亦号交罗。吉人则符其吉兆，而有轩车印绶之来。凶人则应其凶征，而有羊刃网罗之患。二爻皆动，事出两歧，合外有冲，恩中生害；大都宜舍远就近，舍疏就亲，先历艰难，后得成就也。", "天时：有风有雨而易晴。家宅：夏占夜雀乘酉，火鬼加宅克宅，防火灾及官非，宜以井泥涂灶禳之。婚姻：不谐。胎产：胎神临绝，十月大忌，占产即生。功名：求官大吉，科场谨慎题目。求财：大有获。投谒：见贵吉。疾病：肺病，不吉。失脱：或在邻家，逃亡不远。出行：上官之任吉。行人：在道，迟至。争讼：宜和，两贵皆害，恐遭曲断。兵战：利主不利客。", "毕法云：互旺皆旺坐谋宜，人宅坐墓甘招晦，所谋多拙逢罗网，胎财死气损胎推。课经卦象云：木欲成器，须假金斫。孕病凶险，财喜欢跃。禄位加增，官职超擢。戌印常绶，遇之尤乐。袖中金云：占人有除授营运之事，金日曰成器。若秋三月庚辛日得之，将乘阴虎，则名伤斧，以金太过故也。"));
        set(2, new LiuReKe("庚寅日第七局　干上寅", "课体：反吟，元胎，赘婿。", "课义：他来我家，我去投他，满目财禄，夜占不佳。", "解曰：寅临申上，他来我家也。申临寅上，我去投他也。干支互临，彼此易地，三传四课，不出寅申两字。申为日禄，寅为日财，财禄满目，有何不美。然以夜将言之，则申蛇寅虎，亦复满目纵横，不佳之甚也。", "断曰：此反吟无依之卦，像合元胎，亦名赘婿。来往无端，反覆不定。又主狐疑隐伏，尊卑无纪，彼此不和。君子守正则亨，小人妄动则厉。虽曰禄马同乡，有富贵吉昌之兆。然俱临绝地，仍见凶征，不可不惧也。", "天时：阴晴不定，昼雨夜风。家宅：宜与人兑换屋宇而居。婚姻：昼占可用，夜占不宜，主男就女赘，或妇人携子改嫁。胎产：主双孕。功名：宜署摄印务，交代职任，余占不吉。求财：宜结绝交易之事。疾病：主非一症，禄神投绝，主绝食；又人入鬼门，申生人占之必死。失脱：远寻。出行：不利。行人：即至。争讼：先动者胜。兵战：主有杀伤，客反为主。", "毕法云：干支值绝凡谋决，权摄不正禄临支。观月经云：孟知赘婿卦，将身就妻家，辰往临其日，被克更无差。订讹云：中末见救神，或年命得神吉，为赘婿当权，可任意所为。若乘旺相，作勾虎，又名残下，甚不利卑小也。"));
        set(1, new LiuReKe("庚寅日第八局　干上丑", "课体：知一，铸印，蓦越。", "课义：干支乘墓，昼夜贵遇，二丙遁官，与巳同聚。", "解曰：干乘金墓，支乘木墓。虽主人昏宅暗，而丑为昼贵，未为夜贵，两贵交临，又人宅之福也。中传巳火固为日官，而子元遁丙，戌旬遁丙，并巳而三，遂成纯官铸印之局。仕宦则为休征，常人则为咎应矣。", "断曰：此知一之卦，异处见同，恩中有害。干支皆遭墓覆，彼此昏暗。宅上重空，用神无力。虽有铸印之祥，及两贵之吉，亦多虚而少实也。", "天时：忽然有雨，旋见日出，后成阴恕＜艺：门户虚耗，或将改换。婚姻：六月夜占可用。胎产：易产。功名：必有贵力异遇，虚而不实。求财：难得。疾病：不吉。失脱：未获，逃亡不远。出行：未决。行人：道远失期。争讼：各投势要，宜和。兵战：昼占宜慎，夜占胜。坟葬：防有水穴。", "毕法云：昼夜贵加求两贵，干支乘墓各昏迷。古鉴：乙酉年寅将占家宅，曰：宅上空亡，是出于旧宅。移南就北，初传子在空上，亦是暂住未有定论。巳加子，庚金生干巳。巳为店业，太阴为老妇人，是从内戚买得此基，当开店发迹，巳年方得改造。末传生干，得尊长主之。戌为干仆，六合并临，交易成也。其人果以旧宅丁众，谋迁外居，一一皆验。"));
        set(0, new LiuReKe("庚寅日第九局　干上子", "课体：元首，润下，励德，闭口。", "课义：人衰宅盛，我羸彼旺，官鬼居家，全不相向。", "解曰：三传水局，脱干而生支。占人宅，则为人衰而宅旺。占彼我，则为我弱而彼强也。午乃日之官鬼，不临身而临宅，既值空亡，又受水局所制，全无相向之意。以仕宦言，则官不能为福。以俗庶言，则鬼不能为祸也。", "断曰：此元首之卦，合润下之局。事多顺利，动而不息，合而有功。静守安分则亨，正大光明则吉。但人宅俱脱，又乘死神，水局脱干，空亡入宅。主谋望无成，动关人众，虚诈百出，耗费不赀。日上阴神发用，元武并临，与我合者，即来损我，其慎防之。", "天时：苦雨连绵。家宅：主系官宦旧家，人口衰弱，虚费无已。婚姻：女家忌惮不允。胎产：胎安，产不利。功名：五月占有望，但防人脱骗。求财：费多得少。疾病：乘死气凶，幸鬼空，暴病愈，久病不吉。失脱：防不一次。出行：防同伴虚诈。行人：舟行阻滞。争讼：宜止息，勿听唆者，徒被脱诈。兵战：不利火攻，亦主求和怀诈，慎防则吉。", "毕法云：脱上逢脱防虚诈，人宅皆死各衰羸，合中犯煞蜜中砒。订讹云：润下主浮游不安，元后并定主盗。又闭口元武，乘地盘为阳神，可以捕女。天盘为阴神，可以捕男。邵公云：一传与支干上神作刑冲破害者，名三合犯煞，笑里藏刀。"));
        set(11, new LiuReKe("庚寅日第十局　干上亥", "课体：重审，元胎，六仪。", "课义：害变为合，丁马相杂，贵蹉履狱，夜财虎踏。", "解曰：申亥巳寅，先作六害。而寅亥巳申，继变六合，凡事先阻后成也。申马亥丁，课传叠见，其为动变，不容己也。昼贵居夜，夜贵居昼，已为蹉跌，而且入狱，不可干也。末传寅财，夜占白虎守之，不可取也。", "断曰：重审之卦，格合元胎，旬仪发用，禄马聚之，大为吉兆。惜干支皆乘脱气，必致耗费。合中有害，亦审机而动。", "天时：忽然而雨，忽然而晴。家宅：宅吉人安。婚姻：昼占吉。胎产：安宁。功名：有成，然须多费。求财：宜合本营生。投谒：于贵无益。疾病：久病难治，老幼更忌。失脱：宜寻，逃者自归。出行：昼占吉。行人：即归。争讼：因财而起，宜和。兵战：昼夜宜防，秋冬吉。", "毕法云：交车相合交关利，彼此猜忌害相随。古鉴：癸卯生人，戊申年辰将占家宅，曰：干支皆脱，凶神会集。初传蛇扰禄马，欠人钱物。中传临日盗气，末财白虎夹克，主家业渐替。四年而减半，七年消尽，且丧妻也。又庚戌生人占病曰：日上见亥，金去生水，主多冷涎。寅支生巳，心绪不宁。上下俱脱，必见吐泻遗精。申蛇寅虎，寒热往来，课传全是凶神，五十六日难过。巳亥四，寅申七，相因得二十八，两处皆见，故倍数也，皆验。"));
        set(10, new LiuReKe("庚寅日第十一局　干上戌", "课体：涉害，间传，斩关，登三天。", "课义：全值魁罡，身动非常，动亦无用，中末空亡。", "解曰：魁罡临于日辰，更见天罡发用，主有非常之举动。但中值旬空，末又马临空地，虽动无益，必有变更。吉不成吉，凶不成凶也。", "断曰：此涉害之卦，合登天之格。天罡临辰发用，贵登天门，罡塞鬼户，六神藏，四煞没，为真斩关格。最宜避难潜形，不利追亡捕盗。凡有谋为，俱防阻隔。中末空亡无力，在吉事则为破散，在凶事则为消释也。", "天时：将雨而云散，出旬方有雨。家宅：不安，防奸私逃走之事。婚姻：不用。胎产：防损孕伤胎。功名：有声无实。求财：徒劳心力。疾病：始见危险，旋有解救。失脱：难寻，逃者踪迹不定。出行：有阻宜避，难潜藏。行人：立归。争讼：宜解散。兵战：虚张声势。坟葬：防墓有破环。", "毕法云：后合占婚岂用媒，罡塞鬼户任谋为。课经云：戌加旬首，并乘元武为闭口。又六神藏者，蛇临子为坠水，朱雀临癸为投江，勾陈临卯为入狱，天空临巽名被剥，白虎临午为烧身，元武临坤为折足。四煞没者，辰戌丑未四墓煞，临乾坤艮巽，陷于四维也。照胆秘诀云：除定开危卯未方，龙常阴合可逃藏。"));
        set(9, new LiuReKe("庚寅日第十二局　干上酉", "课体：重审，进茹，升阶，蓦越。", "课义：人宅俱旺，动遭罗网，干支坐墓，情怀不爽。", "解曰：干支俱临旺神，人宅亨泰，安享为宜。若不守旧待时，希心妄动，即遭罗网矣。苟能坐待，末助初生，自当亨吉。但交互坐墓，所谓甘遭昏晦者也。三传俱火，脱支伤干，仕宦占之，最为吉兆，惟不利于常人。", "断曰：此重审之卦，顺进连茹，进中有退，事不利顺。惟宜安分守正，则人宅兴隆，无心中得外来之助。", "天时：晴多雨少。家宅：人安宅旺，不宜妄有迁动。婚姻：龙神落空，且受后克，不成，成亦不永。胎产：胎安而产难。功名：可得，后少结局。求财：宜坐拥厚赀，妄求反有灾。投谒：两贵俱合下神，必相款洽。疾病：反因养生致疾，后得痊可。失脱：主奴婢所窃，可获。出行：不宜妄动。行人：忽然而至。争讼：财势相当，终归我胜。兵战：宜坚壁不动，先动者败。", "毕法云：互旺俱旺坐谋宜，人宅坐墓甘招晦。指掌赋云：辰巳午为升阶，亲观光于上国。顺连茹空，名曰声传空谷。退吉而进则不宜。神将论云：天罡发用主旧事，乘六合主宰杀。又：六合在天为光禄大夫，主婚姻嘉会，在辰曰违礼，亦曰持巾。"));
    }
};
