public class d29 extends KeEntry{
	public d29() {
        set(1, new LiuReKe("癸巳日第一局　干上丑", "课体：伏吟，励德，稼穑。", "课义：五鬼相攻，闭口免凶，昼夜戌虎，病讼重逢。", "解曰：土为日鬼，课与传共布五土，并攻癸水，何可当也。巳为闭口，缄默不言，庶可以免凶脱祸。河魁作鬼，昼夜乘虎，病讼不免重叠而见也。", "断曰：自信之卦，伏匿不动，惟三九月戌为天马，方有动象。或逢亥上年命，亦可言动。仕宦逢之，催官符至，上任极速，常人则不利也。所喜末传空亡，无足深患，又课名稼穑，凡事有艰难之象。", "天时：三传纯土，无雨。家宅：贵德临宅，财气重重，富而且贵之象。仕宦：官星临身，中传催官符至，得之甚速。求财：财已充庭，不求自至。婚姻：占男中平，占女差吉。胎产：男喜易产。疾病：肾水涸竭之症，或齿痛呕血，出旬不治。出行：陆路不佳，水路可行。行人：近者立归，远者难定。遗失：不出本家。逃亡：在西方寻。兵战：昼夜将凶，未见其利。", "毕法云：虎临干鬼凶速速，闭口卦体两般推。课经云：辰戌丑未为三传，自相冲刑，可以凶制凶，是谓贼捉贼。又内有四金字，可以制化凶鬼，翻不为害。玉成歌云：三刑为鬼入家破。又云：伏吟举动心无遂。指掌赋云：三传纯官鬼，而兄弟成灾。"));
        set(0, new LiuReKe("癸巳日第二局　干上子", "课体：元首，退茹，解离。", "课义：递相克贼，禄被元食，难进难退，课名迫逼。", "解曰：干上子水，克支神之巳火。支上辰土，克日干之癸水，是递相克贼也。子水为日干之禄，夜占被武侵蚀，则禄不可守矣。进而投于三传，初中均是脱气。又进而至于末传，更遭鬼克，乃退而复守虚枵之禄，终无所益，难以聊生。前既不可进，后又不可退，课名迫逼，不亦宜乎。", "断曰：退茹之卦，宜退不宜进。况旺禄临身，尤宜守旧。又交互克伐，课名解离，夫妇恐有乖睽，人情亦防叵测。", "天时：天罡指阴，自然有雨。家宅：人少宅宽，宦家安吉。仕宦：旺禄临身，与末传之官星相合，功名可望。求财：只宜谨守己财，不宜他求妄想。婚姻：占男则吉，占女不佳。胎产：男喜，母有惊恐。疾病：防遗漏风瘫之症，贵医可治。出行：陆路甚佳，水路不吉。行人：天罡加孟，尚未启行。兵战：昼占则凶，夜占则吉。坟葬：防损人口。", "毕法云：夫妇芜淫各有私，旺禄临身徒妄作。课经云：辰加巳，昼夜皆乘蛇，地盘之巳亦蛇，名两蛇夹墓。占病必有积块在腹，恐致不救。或行年本命是辰，死期尤速。若年命居亥加戌，用虎冲蛇，庶得少延耳。"));
        set(11, new LiuReKe("癸巳日第三局　干上亥", "课体：重审，间传，极阴。", "课义：初鬼末败，丁马独在，千里之行，获财十倍。", "解曰：初传之丑土，乃是日鬼。末传之酉金，又是水之败气，俱无所用矣。唯存中传之亥水，上乘旬丁，且系日马。丁见则财动，马见则远行。虽去而之千里，有不获财十倍乎。", "断曰：极阴之课，间隔而退，有抑塞不舒之象。初传鬼坐克地，末传酉坐脱乡，惟取亥上暗财而已。然家居亦不利也。宅有卯贵脱干，防有贵人脱赚，或施舍神佛功德。", "天时：毕宿临于末传，亥水加于日上，浓阴密雨之象。家宅：夜占贵人生宅，主有荐拔之事。求财：水日逢丁，外财大获。仕宦：昼夜贵加，必主逢贵，然系脱气，不无所失。婚姻：干上克支，支上脱干，未见甚美。胎产：极阴之课，妊是女胎，产亦不吉。疾病：胸胁多风，或肾水不足，然鬼坐克乡，不久自愈。出行：从陆路而去，必获财归。行人：天罡加仲，尚在中途。兵战：课名极阴，未见其利。", "毕法云：水日逢丁财动之，闭口卦体两般推(夜占)，昼夜贵加求两贵。精蕴云：卯为夜贵，加临巳上，两贵相加于极阴之课。惟宜两处暗求，却得贵人周济。课经云：倘立秋前一日是壬，为火绝之日，壬寄在亥，癸日占得丑上见亥，为天祸课。"));
        set(10, new LiuReKe("癸巳日第四局　干上戌", "课体：元首，稼穑，闭口。", "课义：干及三传，凶祸难言，宅中寅木，可敌仇冤。", "解曰：昼占白虎乘戌，临干克干，已见凶征。三传又皆凶鬼，其病讼之患，自当不一而足。幸赖支上之寅木，可以敌鬼，不为凶咎，当有同居之人，力为解救也。", "断曰：元首之课，虽曰顺征，但干支交互相脱，有彼此怀诈之象。所喜中末传空，先凶后吉。夜占申乘白虎，冲支上寅木之蛇，宅中当有怪异不宁，然亦虚惊而已。", "天时：箕宿临支，风而不雨。家宅：家有子弟，力能御侮，宅赖以安。仕宦：催官符发用，得官甚速。求财：夜占财可得。婚姻：支干相克，两各平平。胎产：男喜，产易。疾病：白虎临戌，病乃干表，或眼目疼痛之症，末空白愈。出行：水陆二路，俱未见利。行人：天罡加季，可以立至。兵战：众鬼全消，出兵大利。", "毕法云：虎临干鬼凶速速，不行传者考初时，众鬼虽彰全不畏。观月经曰：此课白虎若带死神死气，谓之魄化，又名旱苗无雨。如正月癸巳日亥将寅时，戌加癸发用，白虎乘死气来克癸水。课传又见天罗地网，末传复为日干之正墓，将乘蛇。占若得此，必主有死亡之事。"));
        set(9, new LiuReKe("癸巳日第五局　干上酉", "课体：元首，从革。", "课义：人就财乡，传课循环，昼将土神，忧变怡颜。", "解曰：日干为人，而癸寄于丑，丑即人也。支神之巳，乃是癸水之财，今以丑干加巳，是人就财乡也。四课不离于三传，三传不出乎四课，谓之循环格。昼将皆土伤干，宜乎可畏。幸三传之金，窃土生身，是忧变怡颜矣。", "断曰：从革之课，段炼相从。占主变动，有革故鼎新之象。但干乘败气，又为破碎，所谓破败神者是也。或家有不肖子弟，以酒色败家者。又三合金局，干上见酉为自刑，名金刚格。缘巳刑申，丑刑戌，惟酉不能刑，故自刑其西方也。有气则革而进，无气则革而退。", "天时：毕宿发用，必雨。家宅：支阴乘破败之神，宅必难守。仕宦：最吉。求财：家财不得。婚姻：课曰从革，最不相宜。胎产：课传皆阴，妊乃成女。疾病：病在肺经，或筋骨脾胃膀胱，并肾虚之症。出行：水陆二路，皆不云吉。行人：天罡加孟，尚未起身。兵战：昼占吉，夜占凶。", "毕法云：三传递生人举荐。课经云：此课丛杂不一，伙众共谋，或两处托人干事。凡谋必遂，大都三合成局，是无己之象。一事方去，一事又来。若遇吉将，必有先容引进之人。惟恐不利占解散等事。"));
        set(8, new LiuReKe("癸巳日第六局　干上申", "课体：重审，斫轮。", "课义：交朋合美，财神已矣，生虎鬼龙，一悲一喜。", "解曰：支上之子，与干丑相合。干上之申，与支巳相合，上下交合，朋情自然和美。但巳为癸水之财，既被旺禄所克，又被戌虎所墓，则此财止宜弃之而已。如夜将申金，本能生我，而乘白虎；戌土本作日鬼，而乘青龙，悲中有喜，喜中有悲，非一悲一喜而何。", "断曰：斫轮之卦，大利占官。但昼占贵人入狱，不宜妄干当事，所谓咄目煞者是也。旺禄临支克支，宅中不甚安妥。若有喜事破耗则吉。", "天时：水母临干，青龙临支，天罡指毕，必然有雨。家宅：青龙与六合，昼夜加干支上，必多喜合之事。仕宦：课名铸印，占官最吉。求财：财爻虽见，恐取之生患。婚姻：交车相合，秦晋可谐。胎产：私胎生男，产期尚远。疾病：伤风肾竭之症，喜得贵医治之，不日可愈。出行：水陆俱佳，但行人恋宅，行期迟滞。行人：天罡加仲，尚在中途。兵战：美中不足，更宜慎之。坟葬：龙神甚旺，发财发贵。", "毕法云：权摄不正禄临支，课传俱贵转无依，昼夜贵加求两贵。课经云：卯加申发用，夜占必因贵人而作内乱，缘贵人内战故也。心印赋云：子来加巳为极阳，戊癸合时大吉昌。"));
        set(7, new LiuReKe("癸巳日第七局　干上未", "课体：反吟，励德。", "课义：皆马皆丁，不容少停，贵虽难靠，财却丰盈。", "解曰：亥为丁神，又为驿马。未中亦有丁寄，是支干三传皆逢丁马，至动至速，岂容少停乎。昼贵巳火临于亥地，受水所克，夜贵卯木临于酉方，受金所克，是皆不可倚靠者也。巳为财神，丁亦财神，则遍地皆财，非丰盈而何。", "断曰：无依之课，支干各自相克，彼此皆防有损。贵登天门，宜动达天庭，上章奏事。又雀鬼加干，构讼最忌，惟占功名考试最吉。凡事人情不定，亦有重求轻得之象。", "天时：课传皆阴，水运乎上，火居于下，晴而复雨，雨而复晴。家宅：丁马相加，恐非安居之象。仕宦：贵登天门，占官甚利。求财：满目皆财，何患不得。婚姻：支干各自相伤，男女总非吉象。胎产：课传皆阴，女胎，产速，须防堕胎。疾病：有头风颠斜之疾，或寒热往来，须防反覆。出行：丁马甚多，来去甚速。行人：天罡加季，丁马重重，行者立至。兵战：情态多变，出兵宜慎。", "毕法云：两贵受克难干贵，干支全伤防两损。订讹云：午马胎财，夜占乘元，占孕者必系私胎。神将论云：夜占太常加干，二月占防有孝服。三车一览云：水日反吟，动获资金。中黄经云：马见巳亥在路歧。"));
        set(6, new LiuReKe("癸巳日第八局　干上午", "课体：重审，斩关，孤辰。", "课义：夜占被盗，钱财累耗，名值元辰，俱怀凶暴。", "解曰：发用财空，夜将乘元，上下夹克，午宫又被亥克，回归又受癸克，是钱财被盗而累耗也。阴日支后一辰名元辰，今末传之辰，既脱干上之午，复冲支上之戌，是见伤于元辰，而俱怀凶暴之情矣。", "断曰：斩关之课，中乘丁马，利于远行。但孤辰发用乘元，不免走失之患，亦主孤茕。惟青龙带官临宅，宜于仕宦。", "天时：午火受克，水运乎上，出旬必雨。家宅：昼夜皆乘青龙，宅中必多喜庆之事。仕宦：官星作墓，不甚通利。求财：财爻落空，恐不能得。婚姻：课名孤辰，占婚不吉。胎产：二阳包阴，产必成女。疾病：腹痛脾泄，肾水不足。出行：陆路不吉，水路可行。行人：天罡加孟，尚未起行。兵战：昼夜皆不利。", "毕法云：水日逢丁财动之，空上逢空事莫追。邵公占验，戊申五月申将卯时，一妇人己卯生占身命，曰：为不正妇人，遇饥则气攻胸臆，以致眼目昏暗。子不多存，足生小趾。后有血瘿之疾，破溃而死，必因养蚕而致。盖午空为不正阴人，又为目，被水所克，故主昏暗。本命禄空，故主饥。戌为足，加巳乘龙，为添足之象，故有小趾。亥为血海，乘不坚之辰土。天后厌秽，癸见厌秽，则成血瘿而死也。"));
        set(5, new LiuReKe("癸巳日第九局　干上巳", "课体：涉害，从革，不备，赘婿。", "课义：财来就我，循环相锁，将克传生，众口传播。", "解曰：巳为日财，加于干上，且三传自支传干，是财来就我也。巳为旬尾，循环相锁，若求解释忧疑，非所占矣。土将克身，似乎可忧。然生起传金，反来育身，是忧变为喜。且自末传之巳火，迤逦生干。仕宦得此，必有众口推荐之者。", "断曰：从革之体，有革故鼎新之象。三传火土生金，金来生水，有官有印，可为要地枢机。但才乘官贵，必当献纳殷勤，方为得力。且合中犯杀，亦防有笑里藏刀之人。支乘破败，不能安享。又名赘婿，进退不能自由。", "天时：毕宿发用，全金生水，必有大雨。家宅：败神临宅，不无耗散之事。仕宦：昼占贵人临身，又系三传递生，占官最吉。求财：财来就人，坐享其利。婚姻：三合相连，有必成之势。胎产：课传无阳，妊乃成女，但课皆合，产必迟延。疾病：咳嗽劳伤，肾水涸竭，医不得力，难以速瘥。出行：陆路甚好，然传逢三合，行期必愆。行人：天罡加仲，尚在中途。兵战：先凶后吉，防敌有诈。", "毕法云：合中犯杀蜜中砒，三传递生人举荐。纂要云：财神闭口，占病皆凶。辨惑云：午加寅上为胎坐长生，不利占产，大宜占孕。指掌赋云：酉丑巳为献刃，远近皆受其伤。"));
        set(4, new LiuReKe("癸巳日第十局　干上辰", "课体：重审，元胎，六仪，斩关。", "课义：墓克其身，倚赖于寅，不甘之者，初支两申。", "解曰：墓神覆日克日，赖末传寅木克制辰土为救。奈初传与支上之两申，情有不甘，反克寅木，不能制辰。所谓遇救不救，灾须自受也。", "断曰：元胎之卦，宜新不宜旧。但干乘墓克，支乘长生六合，有自己昏迷他人逸乐之象。且六合发用内战，事虽垂成，亦防破败。三传乘后合元武，占婚者先有私情。", "天时：墓神覆日，恐不能晴。家宅：青龙相合，宅多喜庆。求财：宅中自有。婚姻：可成。胎产：女胎，产顺。疾病：肾虚发昏，速医尚效。出行：水路甚佳。行人：可以立至。兵战：美中不足。坟葬：吉，阡有益。", "毕法云：后合占婚岂用谋。古鉴：寅将亥时占婚，曰：巳申寅亥相合，其婚必成。申为长生学堂，其妇必轻盈清白，善能书算。亥中有丁，婚后当丁母艰。子息乘于丁上，便当有子。三传无空，享福绵远，果验。邵公占验：戌将未时占宅，曰：墓作天后主迟钝，天后为滞神，见墓愈滞。喜旬首引出长生，乘合制元，且首尾相见，主进人口及怀妊之事。又寅为风，武为水，主子息得风湿之症。又行年在身，主内争讼，必有道土讲和也，皆验。"));
        set(3, new LiuReKe("癸巳日第十一局　干上卯", "课体：遥克，蒿矢，励德，寡宿，入冥。", "课义：源消根断，凡谋锢模迤逦脱耗，源入幽暗。", "解曰：干支自下生上，皆是脱气发用，又是旬空，非流消其源，枝断其根乎。凡事虚耗，皆不归实。三传又迤逦相脱，自昼传夜，五阴相继，非渐入幽暗而何。", "断曰：蒿矢之卦，祸福俱轻，又逢空脱，全无踪影。吉事未吉，凶事不凶。或为子孙耗费，或为卑下脱骗。若占病体，皆因不摄以致危笃。占此者惟僧道相宜。", "天时：朱雀发用，天罡指阳，恐不能雨。家宅：支干合乘脱气，耗费不已之象。仕宦：贵人临干，官星发用，功名可望，但多耗费之事。求财：水日逢丁，有财可得。婚姻：课名寡宿，占婚不吉。胎产：课传无阳，妊乃成女，母空儿实，产必易速。疾病：翻胃伤食吐逆之疾，或肾虚膀胱受伤，喜鬼空陷，不药可愈。出行：水陆二路，俱不甚佳。行人：天罡加孟，尚未起行。兵战：有名无实，未见其利。", "毕法云：昼夜贵加求两贵，罡塞鬼户任谋为。指掌赋云：未酉亥为入冥，主心劳而日拙。玉成歌云：水乘火将皆惊恐，勾朱同传主讼伤。心印赋云：子临四季虎来并，小口年中灾病频。纂要云：辰加寅为罡塞鬼户，不论在传与不在传，能使众鬼不敢窥觑。"));
        set(2, new LiuReKe("癸巳日第十二局　干上寅", "课体：遥克，蒿矢，连茹，寡宿。", "课义：蒿矢空亡，前路嘉祥，寅元午空，失脱先防。", "解曰：未作蒿矢，却喜旬空，不足畏也。进而至于前路，则遇申酉生气，不既嘉祥乎。但干上寅木，乘元脱气，支上午财，又遇旬空，其失脱之虞，所当预防矣。", "断曰：蒿矢之卦，与前课相似。空亡克日，事主多端。大约连茹而进，进则有益，退则无功，纵有龃龉，必当勉力精进。", "天时：毕宿居未，终必有雨。家宅：妻财空陷，占宅不吉。仕宦：官爻空陷，占官欠吉。婚姻：妻爻空陷，不吉。疾病：肾虚之症，不药可愈。胎产：胎神落空，恐是虚喜。行人：天罡加仲，尚在中途。兵战：先凶后吉。", "毕法云：所谋多拙逢罗网。古鉴：寅将丑时占财，曰：午为妻财，是问妻家觅财，许而未得，以旬空故也。寅为癸息，癸生干申，以生子而得是财，后遭婢仆之患，随得随失，空酉与破碎并临故也。行年酉见戌为六害，白虎与血支相加，恐遭官讼，几不免刑。幸作三合，终能解散耳，俱验。邵公占验：戌加酉占宅曰：晚年宠妾，财物被盗，盖末传酉为妾作破碎故也。又行年亥作太常，主有外服。天后乘脱气，主儿妇因病小产，应在四月后，果皆验。"));
    }
};
