public class d3 extends KeEntry{
	public d3() {
        set(7, new LiuReKe("丁卯日第一局　干上未", "课体：伏吟，三交，龙战，奇化。", "课义：三重卯丁，子水居中，动用非细，鬼贼须逢。", "解曰：丁乃变动之神。此课三丁叠见而三传刑冲，所动必惊人之事也。但鬼贼在中，为害亦小。盖子卯相刑，而午被子克。三传凶神相为制伏，所闻忧患，皆属虚声，若昼占末传乘虎禄，亦惊忧难守也。此卦不可便言伏匿不动，支神传首，皆旬内丁神，必静中求动，不可不知。", "断曰：此卦亦名三交，有进退不能之象。又名龙战，主身心疑惑，进少退多。占事静宜动滞，凡百谋为，皆不能远，两头干事难成。春吉冬平，夏秋忌。", "天时：久晴夜占有微雨。家宅：不宁，小口有凶灾。婚姻：两无益，不成。胎产：防胎伤腹中，久不出。功名：宜安卑处贱，躁进则凶。求财：无益。投谒：虽不藏匿，必在他处方见。疾病：胎孕聋哑，或不语呻吟，淹滞日久。失脱：不远，可获。出行：屡改日期，终不成行，行必有惊恐。行人：速至。争讼：主为田宅，防有刑责。兵战：不利，宜固守。", "毕法云：信任丁马须言动。观月经云：忽然值恶将，破散别离情；刚日刑中正，柔日宅内惊。袖中金云：用支无德，刑中有害；破中有合，凶中有吉；祸福倚伏，不可执一。"));
        set(6, new LiuReKe("丁卯日第二局　干上午", "课体：重审，退茹，夜衰，三奇。", "课义：禄在虎旁，勿恋寅乡，食一粒粟，失半年粮。", "解曰：午禄乘虎不可守也。若恋寅木之生己，则引入初传脱乡，贼必相机而起矣，其耗费岂小哉。所谓贪一粒之粟，而反失半年之粮也。", "断曰：此重审退茹之卦，凡百谋为，多有阻滞，守旧则吉。占主下不顺上，事从内起，或缘女人，兵讼后者胜。贵顺吉，贵逆凶。墓绝传生旺吉，生旺传墓绝凶。初克末吉，末克初凶。全要末传得吉将，天月德等，可化凶为吉也。", "天时：有雨，昼占雨难得。家宅：宅近坟庙河池，常有孤寡老阴人是非。婚姻：不成。胎产：宅神冲动胎神，防不实。科名：有奇遇，而抗罂眨防错误贴出。求财：财不顺理，得亦即耗。投谒：两贵皆空，所至不利，或有意外遭逢。疾病：迟愈，昼占见寒热凶。失脱：难寻。出行：不宜。行人：留滞旅舍，或有恙。争讼：彼此不利，宜和息。兵战：宜集众筹画，亥年月小利。", "毕法云：日乘旺禄休狂妄，鬼乘天乙乃神o，二贵皆空虚喜期。袖中金云：退茹三奇丑子亥，为入墓卦，有收藏之象，仕进无心。课经订讹云：甲子旬用丑为三奇，盖丑为玉堂，鸡鸣于丑而日精备。占主凡事逢凶化吉，不忌刑杀；士有奇遇，官有超擢，兵以奇胜，若奇精有损，福亦减半。"));
        set(5, new LiuReKe("丁卯日第三局　干上巳", "课体：涉害，间传，察微，极阴。", "课义：传课俱阴，事转沉沦，递相脱赚，勿依贵人。", "解曰：极阴之课，昏迷太甚，事转沉沦之谓也。巳火脱卯，而丑土脱丁，干支神互相脱赚，得于此而失于彼也。又昼贵受克，而夜贵受脱，贵人方自不安，焉能复为我干事乎。且二贵皆空，虚喜而已。经曰：极阴之卦丑亥酉，百事逢之悉皆丑，信哉。", "断曰：此卦为俱比于阴，而用涉害，事有两而取一。历诸艰阻而后已，乃苦尽甜来之兆。宜晦迹韬光，不宜侥幸求益。目前未遂，终须勤而有得。", "天时：密云不雨，有风。家宅：主向北房不聚财，难发贵。婚姻：有阻。胎产：迟滞而安。功名：先费机关，后得称意。求财：急取则得，缓即消散。投谒：相见和悦，而少实际。疾病：主久病，虚脱。失脱：失物可寻，逃人难获。出行：路有险阻。行人：跋涉不还。争讼：彼此脱赚，忧疑百端，控省部，方得伸理。兵战：渡水穿林，士马劳倦。坟葬：墓神克日干纳音，不可用。", "毕法云：二贵皆空虚喜期，鬼乘天乙乃神o。课经云：正月丁卯日，亥将丑时，二下贼上。若先取涉害，不取仲神，则丑加卯前行，只历辰中乙木一重，归本家丑位。亥加子前行，历辰戊未己戌土五重，归本家亥位。亥涉害深，当取亥加丑为用，三传亥酉未。"));
        set(4, new LiuReKe("丁卯日第四局　干上辰", "课体：遥克，二烦，天网，三交。", "课义：禄乘元虎，重遭鬼苦，中间闭口，尚忧门户。", "解曰：初鬼缠，中财闭口，末禄虎元，皆无足取。幸虽遭初传子鬼相克，赖系蒿矢之微，干上辰土又能制之。所忧者，子卯相刑，门户之间，有不利耳。", "断曰：此卦占者，多因内事，或来外侮。如有凶势，不久自休，忧喜皆未实也。事远难就，后动方宜。喜旬仪发用，二贵顺行，纵有恶煞，不能为害。久之渐生吉庆，和气盈门，刑伤解散矣。", "天时：阴噎(按：阴有风)变幻，有雨不沾。家宅：主尊卑大小不睦，宜小心门户。婚姻：不宜，合则有损。胎产：有虚惊。功名：有进步，禄未厚。求财：艰难得之，亦或贵人默助。疾病：主脏腑有灾，虚劳惊恐，遇良医速愈。失脱：可寻。出行：不利远行，兼忌正二月。行人：即回，路有惊恐。争讼：宜解，防遭责罚。兵战：冬占利出兵。", "毕法云：彼此猜忌害相随。袖中金云：蒿矢第三课为用，乃辰之阳神自战。两阳相克，重而有力，不可先动。指窍云：三交家隐奸私客，不是逃亡即避。曾门云：三交德气在内，刑气在外，利于居家，不宜远行。凡百举事，喜者反怒，解者复结。课经订讹云：第三四课发用为远射，凶势渐小矣。"));
        set(3, new LiuReKe("丁卯日第五局　干上卯", "课体：元首，曲直，四墓，复生，不备。", "课义：传课循环，事在关栏，木局休恃，昼将宜看。", "解曰：四课三传，不离亥卯未三字，是循环不已也。主事即多端，不出关栏也。木局生干，似乎可恃。而昼将纯上，又来脱干，是得失相半，不能全美也。卯虽生干而亦败其干，亥虽生支而奈坐旬空，利宅不利人，利彼不利己。", "断曰：此卦一上克下，三传木局，名曰曲直。今卯加未为先直后曲。譬如美材逢春畅茂，惜渐有朽蠹之忧。惟德足以合之，斯成乔荫，不致始菀终枯。丁日枝枯者，火脱木也，此就局言。若以日干言，则局于干又为父母矣。大凡君子吉，小人凶。", "天时：多风少雨。家宅：宜增造。婚姻：彼此华贵，本属旧戚，秋冬及夜占不利。胎产：生男，夜占不宁。功名：贵显，外任宜东方。科第，春试利。求财：易得，昼占脱气不利。疾病：主风症、肝气，乍病易愈　久病难愈。失脱：在西南方，茂林木器中。出行：往东方得利。行人：即回，传信不实。争讼：宜得贵人调解，祸患不成。兵战：利主，先者胜，又见罢兵之象。", "毕法云：互生俱生凡事益，鬼乘天乙乃神o。观月经云：利用为船车，修造觅材植，百事悉亨通，春占必好极。指掌赋云：三传纯父母，不求安而身自安。又云：见生不生，不如无生。"));
        set(2, new LiuReKe("丁卯日第六局　干上寅", "课体：重审，地网，天狱，传墓。", "课义：干上长生，守则无穷，动逢鬼墓，命忌巳宫。", "解曰：寅乃丁火长生，能守则终身用之不尽。弃而不守，动逢鬼墓矣。若其人本命在巳，巳(按：当为丙)中有丁(按：当为丙)，则既被戌墓，又逢子克，难保其延长也。毕法云：龙加生气吉迟迟。谓青龙乘生干之神，又作月内之生气，虽目下未见峥嵘，却能徐徐发福。若三月夜将，占得此课便是。", "断曰：此卦若炉冶既成，谋孟铸印，卯不入传，则为模损，勿用。占者当思改革，或安心守旧，自有生意。若更妄动，虽费尽工夫，不免化金成铁。", "天时：昼占有雨，即晴；夜占久晴后雨，有闪电。家宅：主隳废或借与人作践。婚姻：主和合，又主私合，不烦媒妁。胎产：胎气不宁，夜占难产。功名：贵顺可得，亦未通达。求财：得之甚难，耗于妇人。疾病：主颠狂，新病即愈，久病见危，宜用符药厌祷。失脱：系奴婢所窃，未离家。出行：近行，宜屡迁其地，远行宜止。行人：干墓临支，立至。争讼：主息而复起。兵战：主失众不利。", "毕法云：后合占婚岂用媒，干墓并关人宅废，将逢内战所谋危。课经云：春丑、夏辰、秋未、冬戌为关神。如日干之墓作关神发用临干，则应宅废人衰。如丁卯日冬占，戌加卯发用是也。"));
        set(1, new LiuReKe("丁卯日第七局　干上丑", "课体：反吟，三交，二烦，斫轮。", "课义：满地皆丁，岂容少停，夏昼火厄，贵财夜迎。", "解曰：课传四卯皆丁，其动非细。况兼反吟冲动，岂容少停也。夏火旺之时，昼占酉为火鬼，乘朱雀以克宅，主有火灾。夜占则酉为天乙，定得贵人之财也。又支干上神，互克干支，亦名芜淫。今干克支上神，为真解离卦，或夫妇行年，又值此者尤的。支上癸酉，为财遁鬼格，又为财作闭口格。", "断曰：此无依之卦，亦曰斫轮。传见卯酉，和而未和，反覆蹉跎，君子贞吉。若勉强而成，毕竟损多益少。故合者则为任重致远，有迁除之喜，否则虽动无益，有疑莫决矣。惟利反覆旧事，或改换门户，以应其兆。", "天时：忽阴忽晴，多早。家宅：主门户破坏，夏占防火灾，又主夫妻反目，阴人不宁。婚姻：有刑克，不宜。胎产：防堕或病，哑。功名：大利，昼占主考试之官，夜占奉朝命召对，宜迟发。求财：财出贵人，难于启齿。疾病：反覆未宁，或患口痢，或咽喉肿塞，或痰厥，不纳饮食。失脱：远去，难寻。出行：宜从陆，利西方。行人：在路，复展转他处。争讼：闭门结讼，各投势要。兵战：互有伤损，坚守为宜。", "毕法云：夫妇芜淫各有私。课经云：火鬼乘蛇雀克宅，主家中火灾。火鬼者，春午、夏酉、秋子、冬卯是也。如夏占此课，酉加卯昼将，乘雀克宅，须以井底泥涂灶禳之。"));
        set(0, new LiuReKe("丁卯日第八局　干上子", "课体：重审，铸印，乘轩。", "课义：彼己皆凶，空墓居中；末丁初马，事无定踪。", "解曰：干被子克，支被申克，彼此皆凶也。戌为空墓，而居中传，主事体中间蒙蔽不明。传墓非吉象，逢干墓，事即当止。而初末马丁动摇，驰驱殆无宁刻。若仕途得此，必得荣差也。", "断曰：此重审之卦，亦名铸印。巳为炉，戌为印，卯为印模。戌卯空亡，则为破印损模，或难迁擢。人宅俱被克制，子加未，举事无终始。申加卯，为竞强争进，主有侵损，喜得铸印乘轩之格，可以远患就福也。", "天时：或有雷，或有虹霓，雨少。家宅：人口小损，屋破坏，主远出。婚姻：就婚贵家吉，娶即非正。胎产：末传刑胎神，防堕。功名：掇巍科，应显辟，尤利署印，独不利家。求财：宜远投人，重求轻得。疾病：有祟，昼占甚危。失脱：自还。出行：仕宦最吉，余主避灾。行人：必归，不能如期。争讼：患难忧疑之事，和好而止。兵战：罢兵息甲，信使往来，讲和之象。", "毕法云：支干全伤防两损，后合占婚岂用媒。课经云：丁卯日，申加卯，昼将上乘六合，为六片板格。缘六合乘申临卯，为尸入棺。申者，身也。卯者，棺也。占病不宜，又当察其类神而言之。占妻病必死，盖妻财入棺故也。"));
        set(11, new LiuReKe("丁卯日第九局　干上亥", "课体：重审，曲直，不备，回环。", "课义：昼将脱丁，传却生身，好恶中半，遁循贵嗔。", "解曰：昼将皆土，脱干之气。三传皆木，又生丁火。一生一脱，可谓好恶各半矣。课传不离亥卯未三者，是循环无端也。昼夜两贵，皆受下贼，见必嗔矣。以回环格言之，此系重审，非涉害也。占凶凶成，占吉吉就，又名盘珠。", "断曰：此卦宅暗生空，大体有冥蒙锢蔽之象，凡事主勾留不遂。中传亥值旬空，是为栋梁之材。其中朽腐，不堪任重，良工袖手。若舍而他求，可获顺利。春占、夜占吉。若占病，亥虽日鬼，空不足畏。未受支克，亦不能救。主医虽识症，实无成功。", "天时：有风，出旬乃雨。家宅：宅近庙墓，多树木，主贫窘。婚姻：不成。胎产：主生女，难产。功名：目下难成。求财：外求无得，作家有益。干谒：不遇，遇必遭主人之怒。疾病：为虚脱或目疾，求得良医，医自有恙不至。失脱：难获，在林内或人入箱柜。出行：未能离家。行人：占久出者即至，占方出者仍回。争讼：昼占为田土，夜占为口舌，防有枷扭之灾。兵占：利为主，宜后动。", "毕法云：两贵受克难干贵，制鬼之位乃良医，鬼乘天乙乃神o。袖中金云：木以水为根，丁日枝枯。课经订讹云：自上传下则曲，亥加未是也。卯加亥，先曲后直，始难终易，风传事多不实。"));
        set(10, new LiuReKe("丁卯日第十局　干上戌", "课体：重审，三交，六仪，地烦。", "课义：交车眷恋，递相推荐，自己昏迷，权摄可羡。", "解曰：午未卯戌，交相眷恋，自初传递生，直至于干，为有人荐举之象。戌墓覆日，自己昏迷也。旺禄临支，权摄可羡也。正月初五六七，二月初三四五，三月初一二三，四月初一，遇此日皆为地烦卦。", "断曰：此卦三传递生，主人亨利，时运开通。惜日遭墓覆，主昏晦而未明。人宅受脱，亦虚耗而不实。又三交事体勾连，二烦触处荆棘，俱宜慎之为吉。中末临旬仪支仪，主渐获后福。", "天时：久阴，亦时有雨，得雷而晴。家宅：家有阴私不明之事，兼防盗窃财物，女口有灾。婚姻：易合而不宜。胎产：易产，防产后血脱。功名：被荐，而自失机会，署印袭职则吉。求财：得即耗散，大约耗于筑坟造屋之类。疾病：或血症，或产症，皆系虚脱，宜补。失脱：不宜根寻，寻之反损。出行：利投亲赴荐，防遗忘物件。行人：丑寅日到。争讼：得吉者有人暗助，凶者防徒流之罪。兵战：有来使，多虚少实，始终不一。", "毕法云：权摄不正禄临支，三传递生人举荐，人宅受脱俱招盗。课经云：午加酉为死交，酉加午为破交，反吟为反目交，皆不能成合之象。课无阴合，则名三交不交，或年月日时皆仲，则名三交不解，二者祸甚于交也。若气旺将吉，又名乘轩，占官大贵。"));
        set(9, new LiuReKe("丁卯日第十一局　干上酉", "课体：重审，间传，励德，龙战。", "课义：昼夜贵聚，事无凭据，本身力弱，占病深虑。", "解曰：夜贵被克，昼贵空亡，虽入用传，不得其力。况贵多则事不归一，贵人不救病。丁火死于酉，本身力弱，所以深可虑也。末助初财，遍地贵人，反无依据。酉财昼雀夹克，中末空亡，用破传空，身心不利。如用夜贵，名咄目煞，乃贵人咄目专视，反坐罪也。", "断曰：此极阴之卦，事见冥蒙。酉为闭口，又遭夹克，心迹难明，告贵不允。又卦名龙战，事主进退逡巡，疑惑不定。若积德之家，自致安宁。倘生妄求，必成灾悔。", "天时：久阴。家宅：虚耗，主出孤寡善人，或欺诳不实之人。婚姻：不宜。胎产：胎动不安，生女，防哑。功名：已仕转动，未仕蹭蹬。求财：不由自己，任人予夺。投谒：先天后违，主客异志。疾病：主气病，或呕吐咽塞，或在心肺，乍病愈，久病危。失脱：逃人自归，失物贼系妇人，见者不肯言。出行：遇贵无益。行人：春秋占必来，冬夏占不至。争讼：利后动，终解释。兵战：昼占虚诈，夜占有功，虎头蛇尾。", "毕法云：课传俱贵反无依，昼夜贵加求两贵，鬼乘天乙乃神o，用破身心无所归。订讹云：六阴朦昧，似涉重渊，公凶私利，病患缠延。课经云：酉临于为亚魁星。又云：酉加丁干，谓之财作闭口。"));
        set(8, new LiuReKe("丁卯日第十二局　干上申", "课体：见机，顺茹，斩关，龙战。", "课义：夜占财退，必因同类，病者腰疼，昼贵勿恃。", "解曰：日财申也，夜占乘蛇，且三传与日同类，有兄弟分争之象，财之所以退也。辰为腰，临卯被克，而乘白虎，虎专主筋骨，故腰痛为害也。此夹定三传格，谓日课未在午前，日支卯在辰后，夹定三传，凡事进退，皆不由人。", "断曰：此龙战斩关之卦，前程有阻，心多疑贰，孟进不能，孟退不得。君子占之，获福重叠，顺茹之象不虚也。寅午之岁，及酉月酉时占俱不利。又干支皆乘前一辰，网罗身宅，岂能亨快。止利守己，则为干支乘旺。倘若妄动，变为网罗，又作羊刃。", "天时：夜占有雨，昼占雷电无雨，后久晴。家宅：室窄人多，主有伏尸。婚姻：吉，但宜迟合。胎产：未实，又主连胎。功名：先难后易。求财：有得，见妻成灾。疾病：主腰痛，先重后轻，书符合药，厌祷最宜。失脱：在家左右前后，不漏。捕捉：昼占贼远去，不可获；若及之，必伤人而走。出行：正宜更新，外出主投人得所。行人：未还。争讼：宜和。兵战：主敌人求和之象。", "毕法云：所谋多拙遭罗网。照胆诀云：除定开危卯未方，龙阴后合可藏。课经云：丑加子为腹胎格，盖丑为腹也。"));
    }
};
