public class d30 extends KeEntry{
	public d30() {
        set(2, new LiuReKe("甲午日第一局　干上寅", "课体：伏吟，自任，元胎。", "课义：不肯守禄，去临空谷，马载虎鬼，末后相逐。", "解曰：寅乃甲禄，临身发用，宜相守而勿失矣。乃舍之而前，投于中传之巳，则为空陷之乡。又进而逢末传之申，既为日鬼，又乘昼虎。因马而动，灾咎难乎免矣。何如守德禄扶身之吉，足以自安耶。", "断曰：自任之卦，格合元胎，有伏而未发，屈而未伸之象。末传见马，主静中有动。但中落空陷，名为折腰。凡百谋为，必遭中阻。金鬼乘虎，名催官符。仕宦甚速，常人不宜。又支脱干气，乘蛇可畏。然能制官，尚可免祸。惟不利动谋也。", "天时：有风，阴晴不定。家宅：宜静不宜动。婚姻：夜占可合，亦防中阻。胎产：十月占主私胎，或婢妾孕。功名：四月占吉，余防有阻，占官主改任。求财：守分自足，妄求有损。疾病：肝家有火，昼占见凶有解。失脱：不出家中，逃亡不远。出行：不宜，上任者有阻。行人：中途有阻，巳月占即至。争讼：宜解，不解有凶。兵战：昼占不利，夜占吉。", "毕法云：信任丁马须言动，旺禄临身徒妄作，虎临干鬼凶速速。袖中金云：六甲伏吟，有禄有马。三传先刑后冲，破中有合，凶中有吉。祸福倚伏，不可一概而推。神将论：蛇生角在寅，勾陈捧印在巳，白虎衔牒在申。"));
        set(1, new LiuReKe("甲午日第二局　干上丑", "课体：知一，退茹，三奇。", "课义：退值生方，进遇空亡，夜逢庚虎，美里成殃。", "解曰：寅临卯上，进则遇辰，为空亡之地。退而历丑至子，乃木之生方也。生我发用，又中传长生。末传财神三合，可不谓美乎。但夜占初传子，旬遁为庚鬼而乘白虎，暗中克日，反生灾患耳。", "断曰：知一之卦，交车六害，主恩中有害，合中有离。三传亦名润下，生干克支，恐子孙不利，凡事宜退不宜进。三奇生干发用，与干神上下六合。昼占吉，夜占惟利仕宦，余占凶。", "天时：雨泽沾足。家宅：防争斗口舌，及奴婢奸私，宜迁改。婚姻：不宜。胎产：安。功名：士有奇遇，或得人暗助，或祖父袭荫。求财：迟得。疾病：夜占极凶，喜奇神可解。失脱：寻得。出行：宜赴官，余不利。行人：先有信至。争讼：不负。兵战：未得利。坟葬：恐是旧坑，朝山亦空，不吉。", "毕法云：虎乘遁鬼殃非浅，六阳数足须公用。课经云：传生干克支，占人旺而无正屋可居，为人旺弃宅格。指南：壬午年七月，客来坐定未言，袖占之曰：贵人临身，必科第中人，非田姓即王姓。贵被干克，岁破发用，课传退茹，必有获罪朝廷之事。喜初中后阴为恩泽，无大咎也。果荆州知府王姓，甲戌进土，以失城守逮问，后得脱免。"));
        set(0, new LiuReKe("甲午日第三局　干上子", "课体：涉害，间传，斩关，励德。", "课义：夜子昼申，总是凶神，六阳数足，革故鼎新。", "解曰：子以遁鬼，乘白虎临干。申以日鬼，乘白虎入传。夜占则畏子，昼占则畏申，总以白虎凶将故也。四课三传，皆居六阳之位，为六阳数足。凡占最宜公干，不利私谋。自夜传出于昼，为由暗而明，革故鼎新之象也。", "断曰：涉害之卦，主先难后易。三传间退，兼被初传戌财引入中末鬼脱。名悖戾之格，亦名倒拔蛇，凡事艰辛。生气坐干，奇神坐日，最能转祸为祥。末助初财，亦有旁人协助。贵临卯酉，显者有喜，阴小有灾。", "天时：先雨后晴。家宅：主虚耗，丑加卯，夜乘天空，主门户动土，身宅不安，宜谢土神。婚姻：不正。胎产：胎安，产不利。功名：主以财得爵，大位升迁，下位被斥。求财：得之暗昧，不可告人，防因财致悔，为商折本稽程。疾病：得之房孟，甚险，有解。失脱：或妇人，或小儿所盗。出行：赴官吉，防耗脱。行人：将至。争讼：不利见官，宜和解。兵战：忌用。", "毕法云：六阳数足须公用，支坟财墓旅程稽；虎临干鬼凶速速，后合占婚岂用媒。指掌赋云：戌申午曰悖戾，有追悔之心。盖戌午火局中间一申，一申反成克象矣，故曰悖戾。神将论云：元武在戌曰遭囚，与财星并，主亡失稽滞，少成多败。"));
        set(11, new LiuReKe("甲午日第四局　干上亥", "课体：遥克，蒿矢，元胎。", "课义：申为矢箭，射中非善，马载马鬼，昼难脱患。", "解曰：蒿矢本无力，得申金发用，便为矢有金镞。若被射中，非吉祥之事。况申金又为日鬼，而兼驿马，鬼则患深，马则动速。夜占犹可，昼占白虎乘之，其凶更甚。虽有善避者，岂能脱然免于患耶。", "断曰：蒿矢之卦，祸福俱轻。然合病胎之格，忧疑莫决。虎临干鬼发用，至为凶险。幸是折腰，吉凶皆半途消散，求官者反嫌其空陷也。若四季占，亥水无气，却喜申鬼生亥，乃不幸中之幸。否则虚心坐待，长生在干，亦可窃金育日也。", "天时：雨后生寒，日孟出而不出，大风之象。家宅：卯加午为闭口，主有喑者。虎乘申，主家有病人未愈。宅上朱勾，主有文书勾连之事，宜慎。婚姻：虽见和合，有阻；胎产：安吉，防生哑儿。功名：巳年月大吉，合催官捧印，化龙之兆。求财：惊患多而获利细。疾病：主咽喉症，巳酉丑月占，为脓血之灾。失脱：元武临贵，勾陈落空，不能捕获。出行：出必旋返。行人：中路有阻。争讼：利主不利客　托贵无力。兵战：宜慎。", "毕法云：宾主不投刑在上，闭口卦体两般推。课经云：亥加寅，夜将太常，乘日之长生，来人必占婚姻，或有恩赐服食等事。神将论云：太冲乘勾空为沙门，用日主其僧多不洁少实。又天后加申，甲日为湖池。"));
        set(10, new LiuReKe("甲午日第五局　干上戌", "课体：重审，炎上，斩关，狡童。", "课义：人衰宅旺，他忤我向，讼宜顺他，病虚脱丧。", "解曰：三传火局，脱干比支，是为人衰宅旺也。以彼此言之，他来脱我，我反生他，岂非他怀相损之意，我致相向之情乎。讼而得此，应在支之受生，为屈意而顺彼。病而得此，应在干之被脱，为虚弱而可虞矣。", "断曰：重审之卦，局合炎上。事多不顺，明中有暗，循理则吉，犯上则凶。三传四课，不出寅午戌三方，回环无端，主凡事孟成不成，孟结不结。人益我损，虚耗无穷。况狡童有不正之嫌，斩关非安居之象，可不慎哉。", "天时：时旱得雨，兼有风雷。家宅：人口衰弱，防奴婢逃走。婚姻：不用。胎产：难产，母强子弱。功名：有禄之官，主非正印。求财：宜索债负，不宜商贩；主为治宅多费，或为人所脱耗。疾病：主虚损，或火上炎。失脱：或因家中喜事，或系奴婢奸私。出行：利避难奔逃，余防耗失。行人：午丙日来。争讼：有解。兵战：利私度关隘，宜息兵解和。坟墓：丙龙坤向则吉，可以发丁。", "毕法云：权摄不正禄临支，后合占婚岂用媒；初遭夹克不由己，支坟财墓旅程稽。三车一览云：三传全脱，反生起干上财神，是为取还魂债。又于加支，求宅必得。但被支脱，虽目下强得其屋，后无益也。"));
        set(9, new LiuReKe("甲午日第六局　干上酉", "课体：元首，四绝。", "课义：元财在中，水生坐空，丁神作鬼，凶动重逢。", "解曰：辰为日财，今元武并临中传之辰，又属旬空，岂无耗失。末传长生，又投空墓，何能为益哉。惟存酉鬼乘丁神，而临日发用，鬼足致凶。丁又善动，日上初传，重重相遇，可勿畏哉。", "断曰：元首之卦，事由男子，利于先举。日坐墓上，切勿冒昧投入，必遭蒙蔽。官鬼乘丁发用，支上丑土助克。若非仕宦，必有家鬼弄家人。辰酉六合，三传自刑，卒难称意。", "天时：虽阴不能成雨。家宅：宅吉，宜发贵，然防有灾。婚姻：难成易破。胎产：酉临鬼户，防鬼胎，占产立下。功名：可得，主有改变或暗害。求财：防生灾祸，到底成空。疾病：大凶，谓酉鬼独发，申在墓外，又为鬼呼也。失脱：难寻。出行：不利，亦不果。行人：丁神独发，人宅即到。争讼：先发者胜，终解。兵战：宜慎。", "毕法云：众鬼虽彰全不畏。不行传者考初时。课经云：雀作日鬼加干，为雀鬼格。又干支互克上神，为真解离卦。指掌赋云：日辰加临卯酉，离者合而合者离。精蕴：丑将占行人，天盘寅为行年贵人，逆转到地盘寅位，从卯门上过，卯上见戌，戌中辛克日及行年。而寅上又被酉克，当以不归断之。"));
        set(2, new LiuReKe("甲午日第七局　干上寅", "课体：反吟，无依，涉害，元胎。", "课义：支干俱伤，又坐克方，舍就不可，彼此匪良。", "解曰：甲被申克，午被子克，彼此俱伤也。寅又坐申上，午又坐子上，俱坐克方也。在此在彼，无一善地；或就或舍，无一可安。易所谓不能退，不能遂者也。", "断曰：无依之卦，反覆不宁，多涉艰辛，人情不协。三传禄马俱动，似可有为。然皆临于绝地，长生又坐空方，干支上下皆克，惟杜门静守，庶免灾生。勉强营求，大非吉兆。若秋占，子为火鬼伤支，更防火烛之灾。", "天时：风雨无常，时作时止。家宅：人衰宅废，宜与人兑换结绝。婚姻：昼占可成，然多反覆。胎产：不安。功名：换职易位。求财：徒劳无得。投谒：不合。疾病：主非一症，时愈时发。失脱：难寻，逃亡远去。出行：既出，旋归。行人：孟近还远。争讼：彼此俱伤，不如止息。兵战：不利。", "毕法云：支干全伤防两损，昼夜贵加求两贵。课经云：申加甲，庚加支，为明暗二鬼。又阳日反吟，为德丧禄绝。反吟四孟，为四绝元胎。袖中金云：反吟阴阳，各易其位。天地乖隔，南北暌违。子午乘蛇，官病灾凶。寅申乘龙，隔墙有祸。指掌赋云：寅功曹，主木器文书。申传送，主行程消息。传送上会青龙，子孙财损。"));
        set(7, new LiuReKe("甲午日第八局　干上未", "课体：知一，三奇，铸印，引从。", "课义：交互彼己，两皆不利，夜害贵人，讼遭官制。", "解曰：午未寅亥，干支上下交车相合。然未墓覆日，亥水克辰，彼此两无所利也。夜贵临身，而初传之子害之，讼虽理直，必遭曲断，况不直乎。", "断曰：知一之卦，恩中有害，合中有离。日遭墓覆，辰被水克。贵人为用神所穿，格虽铸印，遇空不成也。幸奇神独发，支上神得初引末从，天狱解散，为逢凶化吉之象。", "天时：有雨而不沾足。家宅：支上引从，主迁修屋宅。婚姻：孟合还离。胎产：胎防不实，产即下。功名：主有遇合，而无实际。求财：始虽有利，终归虚耗。投谒：外合而情不亲。疾病：暴病吉，久病凶。失脱：难寻。出行：不果。行人：先得空信。争讼：防曲断有屈。兵战：小心则吉。", "毕法云：前后引从升迁吉，害贵讼直遭曲断。指掌赋云：三奇发用，疑惑解而喜气生；引从日辰，家必兴而人必旺。占验：壬午年十二月一司马占出师。曰：初传岁破内战，卯命上龙马克下，功名非久远之象。必因宰执不悦，自孟请退。来年秋初，当去任矣。且斗系日本，墓贵临干，为仰丘俯仇。干墓支绝，种种不佳。幸奇神天赦发用，朱雀皇诏作恩，可得温旨归里也，后果如所占。"));
        set(6, new LiuReKe("甲午日第九局　干上午", "课体：元首，炎上，励德，u女。", "课义：循环脱耗，秋冬急暴，身位频灾，家庭累盗。", "解曰：三传会火，循环脱干，耗费甚矣。昼午夜寅，皆乘白虎。春夏木旺火相，犹之可也。秋冬遇之，即为饿鬼饿虎，为恶尤暴。身既受灾，而支亦墓脱，必频频招盗也。", "断曰：元首之卦，局合炎上，事多利顺，像亦光明。但脱上逢脱，虚诈多端。幸脱气生戌，可取还魂之债。然励德则阴小有灾，u女则奸私可丑，占者尚其慎诸。", "天时：晴多雨少。家宅：防有耗损，及婢仆奸私。婚姻：不正，大忌。胎产：胎安，产不利。功名：无益。求财：不利交易，得不偿费，止宜索取偿负。疾病：主虚症、火症。失脱：屡见难获。出行：宜投亲访故。行人：迟至。争讼：彼此多耗，彼来求和。兵战：多耗军储，宜慎重。", "毕法云：脱上逢脱防虚诈，后合占婚岂用媒。课经云：支加干为宅来就人，买屋不费力。古鉴：辛酉生人，己酉年寅将占谒县令，曰：三合盗气，支又来脱干，远涉徒费。u女淫荡，末财坐墓，纵有厚赠，旋以狎邪耗尽。行年蛇衔剑，贵人在后，被克而怒，必主后来嫌怨不足。其人曰：彼有札召我，何为不去。及至，初相见，款洽厚赠。寻因遇友引入妓家，遂倾所有。令知之，果见诮让，情礼顿疏，潦倒而归。"));
        set(5, new LiuReKe("甲午日第十局　干上巳", "课体：知一，元胎。", "课义：脱空破碎，丁鬼宅内，初马鬼驰，生禄后泰。", "解曰：巳乃旬空，又为破碎煞，今临干上，脱干之气，不可守也。酉为日鬼，又遁旬丁，今临支上，不可就也。遂投初传，则逢鬼乘驿马。幸坐空方，不为大害。进而中末，始遇亥之长生，寅之德禄，盖至是而后泰然也。", "断曰：知一之卦，长生元胎，凡事先难后易。巳虽空破，却能制鬼。遂使三传递生，助起末传德禄，故巳火不作空论，实为救神。但贵人皆受囚辱，贵不治事，不可干谒妄动。", "天时：求晴未晴，求雨未雨。家宅：不宁，防有争斗口舌，火烛虚惊，人亦不旺。婚姻：昼占可合，然龙神坐空，主男家改悔。胎产：胎神临空败，防损。功名：官爻落空，主迟得，或有改调。求财：未见利益。投谒：贵人不悦。疾病：暴病易愈，久病不吉。失脱：元武克勾，难获。出行：须改期。行人：寅日至。争讼：不利投托势要，宜解。兵战：先难后易，宜慎。", "毕法云：贵人蹉跌事参差。纂要云：干上巳，昼占乘太常，干生上神，上神又生天将。凡占尽被脱耗，多虚诈不实，又白虎入丧车。惟申加巳发用为的，占病可畏。汇函云：甲日申为青龙，主财帛出其道路，或主远信寄物。又申作龙，加孟为银匠、铁匠、僧人。"));
        set(4, new LiuReKe("甲午日第十一局　干上辰", "课体：涉害，间传，斩关，登三天，狡童。", "课义：空财动取，马载鬼虎，贵坐天门，罡塞鬼户。", "解曰：辰为日财，既值旬空，又被夹克。虽临干发用，然动而取之，无所得也。中传午脱干气，末传日鬼，昼乘白虎，加以驿马载之而行，其动必速也。虽贵登天门，罡塞鬼户，仕宦最为吉兆。常人终畏虎鬼，不宜冒昧前行。", "断曰：涉害之卦，间传顺进，号登三天，多历艰危，全无凭藉。又斗罡加日发用，格合斩关，龙合入传，神藏煞没，宜逃灾避难，默干阴谋。又孤辰发用，惟利僧道。自干传支，当虚己就人，然亦有声无实也。", "天时：孟雨不雨。家宅：主人出外，兼防阴私逃失之事。婚姻：不宜，亦不成。胎产：主逾期，主难产。功名：先难后易，无中生有。求财：主非光明之财，且归乌有。疾病：肝经受伤，久病及小儿病凶，药宜泻肺。失脱：难寻，逃亡不获。出行：利逃灾避难，然不果行。行人：失约未来。争讼：必得于远方，然消解不成讼。兵战：季春吉，余不利。", "毕法云：我求彼事干传支，虎临干鬼凶速速，罡塞鬼户任谋为。类神云：六合与天后同入传，谓之狡童卦。占人多不正，所为奸弊，兼防有失。六合临辰主宰杀，又曰违礼，又曰持巾。括囊赋云：内出外而己求。阳人阴而彼索。"));
        set(3, new LiuReKe("甲午日第十二局　干上卯", "课体：重审，进茹，孤辰。", "课义：支干拱传，空脱双全，孟进不进，耗盗忧煎。", "解曰：干上卯，支上未，拱夹三传辰巳午在内，联珠合璧，凡事可成。但初传空财，引入中末，空脱破碎，毫无利益耳。辰未皆为墓神，又皆为日财。今未覆支而辰发用，所谓支坟财并也。虽号进茹，而难于进，进则逢空被脱，能免忧煎乎。", "断曰：重审之卦，顺进连茹，一下贼上，多逆少顺。龙战主乎狐疑，孤辰不免茕独。若有侥幸之得，必有无意之失。网刃坐于干支，虎鬼临于墓上，进而得尺，不如退而守寸。如在外经商，必然折本伤财，致有羁旅之叹。", "天时：求雨无雨，求晴难晴。家宅：主虚耗，兼防口舌。婚姻：不成。胎产：占胎不宜，占产立下。功名：无成，夜占贵临宅上，墓神未中藏申，官主门荫或诰封。求财：中末助初财，似可有得，然初空，屡有改变。疾病：墓临宅，未中申为家鬼，名曰墓呼；占病凶，宜祷家神保佑。失脱：主奴婢盗窃，夜占勾临日克元，可获。出行：主蓦然远出，孤客傍徨。行人：稽迟在彼。争讼：主以下抗上，不利，宜解。兵战：宜慎。", "毕法云：进茹空亡宜退步，所谋多拙遭罗网。纂要云：三传皆是空亡，便宜退步。庶甲木不为火炉，奈支干前后夹定空脱，尽被耗散，无有穷期。如遇丑为年命，即当退就禄神。玉华略云：木遇三孤，乃寡妇之煞。"));
    }
};
