public class d31 extends KeEntry{
	public d31() {
        set(4, new LiuReKe("乙未日第一局　干上辰", "课体：伏吟，自信，斩关，稼穑，游子。", "课义：三传日辰，总数财神，丑未白虎，大吉乘辛。", "解曰：三传纯土，及日辰阴阳，皆为日财。总而数之，遍地财神，宜若取之不穷矣。然发用空亡，已为虚而不实之象。又昼未夜丑，皆乘白虎。末传大吉，旬遁辛鬼。鬼乘夜虎，其凶弥甚。孰谓财多无害也哉。", "断曰：自信之卦，局合稼穑，斗罡临日发用，既号斩关。三传俱属季神，亦名游子。静则伏匿而未发，动则艰阻而不宁。末传虎乘遁鬼，占者必因财致讼，因食伤身。知几君子，能远弃其财，急流勇退，乃可永贞而无咎。", "天时：湿蒸而无雨，后有风雷。家宅：昼占主宅有伏尸，夜占多蛇鼠穴。婚姻：不成。胎产：主胎伏不动，后忽动不安，防损漏。功名：宜捐财得微职，或屯田开垦效力。求财：始无得，终有祸。疾病：主脾病，初起不妨，久者不吉。失脱：难获。出行：犹豫未定。行人：流落未归。争讼：主为田土之事，宜解散。兵战：宜屯田固守，交锋无利。", "毕法云：传财化鬼财休觅，支乘墓虎有伏尸，干支乘墓各昏迷。课经云：此卦不宜求财，为借钱还债不明。订讹云：游子并斩关为绝迹课。指窍云：四季稼穑，辰为五阳之促，又为更新白虎主丧服，惟乙日不妨。虎蛇主虚耗。　"));
        set(3, new LiuReKe("乙未日第二局　干上卯", "课体：昴星，掩目，励德。", "课义：支上旬首，干上旬尾，首尾纡回，虎视可畏。", "解曰：午为旬首，临干支上；卯为旬尾，临于干上，首尾纡回不断。倘逢灾难，未易解脱也。况卦名虎视，所投不利。闭口之禄不可守，昴星之财不可取，归干支上，坐墓守困而已。", "断曰：昴星之卦，亦名冬蛇掩目，乃暗昧忧惊之象。春日占之，卯禄正旺，宜守分知足，自见安宁。又干支互乘死气，卯虽旺禄，名为闭口，变而为六害。昼乘六合，当主自己煎熬。所喜末助初财，然亦不宜妄动。", "天时：阴晦沉寒，久而得霁。家宅：主家有土工，厅堂多尘埃，或有怪异官非。婚姻：有阻不成。胎产：主婢妾私孕。功名：有官者转职增俸，余无利。求财：有得，得力于奴仆，终防有耗。疾病：主伤腰膂，或中虚。失脱：可获。出行：驿马临第四课，主忽有远行。行人：即至。争讼：有解。兵战：少利，遇辰则吉。", "毕法云：首尾相见始终宜，人宅皆死各衰羸，旺禄临身徒妄作，魁度天门关隔定。断验：丁丑六月未将，代占一职方请开边。曰：昴星虽无蛇虎入传，只宜静守。贵居本位，驿马旬空，天门魁隔，中传断桥。凡事有阻，不利举动。况朱雀犯岁君，必撄上怒。日临死地，辰临败地，后必有祸。疏上果谪戍，壬午岁起，任边事以失机典刑。"));
        set(2, new LiuReKe("乙未日第三局　干上寅", "课体：昴星，掩目，元胎。", "课义：元胎衰替，孟临季位，支嗣空亡，长生无气。", "解曰：凡元胎卦，必四孟相加，故止有生胎、病胎、绝胎之名。此卦三传，俱坐衰地，由孟临季位而发传。但取干支上神，不由地盘故也。巳为嗣息，临支落空，徒为虚脱。亥虽长生，但昴星发用无力，兼人衰方，被丑土克制，不足恃也。", "断曰：昴星之卦，柔日名为冬蛇掩目，主惊忧不定，进退无凭之象。始见顺利，旋成乖隔。因三传俱坐克脱之乡，且自上生下，全无旺相也。喜初传昼夜皆乘水将，可以助亥育干。", "天时：久雨难晴，占雨即得。家宅：主虚耗，必为子孙喜事多费。婚姻：昼占吉而龙神空陷，主男家不孟就。胎产：孕妇多病，产速而不长进。功名：昼占或得父荫，或文字之力。求财：不利。疾病：不安，久病忌。失脱：不获。出行：有惊忧。行人：淹滞。争讼：易解。兵战：敌兵空营而去。", "毕法云：不行传者考初时。指掌赋云：昴星阴阳无克，从至阴处讨出消息来，俯视者忧近。古鉴：丑将占贼曰：元武克日，必主破财。青龙克酉，贼本合败，却被旺亥反制巳火，贼得水中伏匿。戌为来路，亥为去方，皆在西北。酉为贼本家，必属鸡。酉六亥四，遁得乙八丁六。酉无气不倍，当于二十四里外，门前有林木，屋后有水处捕之，果验。"));
        set(1, new LiuReKe("乙未日第四局　干上丑", "课体：重审，稼穑，励德，游子。", "课义：干就支所，财宜急取，稍缓遭昏，丑辛可惧。", "解曰：乙课在辰，辰就支上，是为人就财所，速取为佳。若就留连，则必为未所墓，而遭其昏滞也。况丑遁辛鬼，临干发用，有因财致祸之象，可不惧乎。", "断曰：此重审之卦，合稼穑之局。事非顺利，多历艰辛。循理则亨，犯上不吉。四课墓覆而不备，三传恃势而递刑。昼占蛇乘遁鬼，大有惊恐不宁。赖末传未虎冲破丑墓，是为以凶制凶，聊得其平耳。携金告贵，纳粟奏名则吉。", "天时：湿蒸霾雾，晴阴不定。家宅：人往就宅，昏晦不宁。婚姻：夜占可合，亦见刑冲。胎产：不实。功名：夜战利　宜捐赀而得之。求财：宜见机急取，恋则生灾。疾病：求神则吉，但防尊长。失脱：难获。出行：不果。行人：昼占至，夜占得空信。争讼：主为妻财，或酒食起衅。兵战：宜防伏兵。", "毕法云：宾主不投刑在上，传财化鬼财休觅。课经云：三宫皆土，兄弟以力相伤，为恃势刑。断验：庚辰生人，戊申戌将占财产，曰：日上见财乘龙，又临行年，主进田产，不用钱买。中传朱雀，有人争竞。末传未作天后，应归还内戚。日就支财，旋得众赀。却因门前有坟侵界兴讼，财化为鬼。若非春占乙旺，则不惟破横得之财。即在己者，亦须破尽也，皆验。"));
        set(0, new LiuReKe("乙未日第五局　干上子", "课体：元首，曲直，淫u。", "课义：贵临乙木，支乘干禄，仔细推之，无礼反目。", "解曰：子为旦贵，临于日干；卯为日禄，加干支上，乍视之可谓美矣。然细推之，干支上下，子未卯辰互作六害，又干上子，与支上卯，彼此相刑，无礼反目，甚可恶也。", "断曰：元首之卦，局合曲直，以尊制卑。三传比日，又且旦幕贵人，临日阴阳。日禄长生，临辰阴阳，宜无不吉。然交车六害，俯丘仰仇，子卯无礼，后合不正，往往自招悔吝，先吉后凶也。旺禄临支，昼合夜鬼，会木伤支，人虽强旺，宅必倾颓。", "天时：多风有雨，长养禾稼，亦主虫莠。家宅：防有奸私暗昧，及有伏尸。婚姻：不用。胎产：胎安，防难产。功名：得贵得禄，然美中不足。求财：有禄者不宜。疾病：不妨，然难全愈。失脱：在木器内，或在林中。出行：仕宦吉，宜用舟楫。行人：即归。争讼：难解。兵战：胜，宜委粮诱敌。", "毕法云：权摄不正禄临支，贵虽坐狱宜临干，宾主不投刑在上，彼此猜忌害相随，合中犯煞蜜中砒。指南：己丑年未将，占地方安否。曰：子为游都临干，卯为贼符临支，发用木局为舟楫，主有贼船东来。然初中休囚，末传太阳月建，城邑无虞也。至己亥日，果有贼自东来，水陆并进，人民骇走，系有奸人勾引。官兵一出，贼果奔溃。"));
        set(11, new LiuReKe("乙未日第六局　干上亥", "课体：重审，六仪，四绝。", "课义：守有生路，解忧散虑，昼贵空亡，夜贵临墓。", "解曰：亥为长生，临于日上，守之则得其益。若动而之传，午脱日气，昼乘天空空脱之神，惟宜解忧散虑而已。中传财丑遁辛鬼，亦不可取。末传夜贵坐墓，昼贵坐空，皆不足恃也。", "断曰：重审之卦，事多不顺，起于阴小，喜三传递生以生日。又旬仪发用，支仪居末，亦可逢凶化吉，得意外之遇。但卦名四绝，止宜结绝旧事，不可妄作图新。", "天时：晴雨得宜，可望丰岁。家宅：主有老阴人作耗，或左旁小巷坍塌。婚姻：宜赘婿。胎产：主老婢孕。功名：主有异遇，孟冬占吉。求财：恐因财致患。疾病：主身弱，防饮食暗伤。失脱：昼夜贼神俱空，难获。出行：不宜谒贵，余亦未利。行人：即至。争讼：此胜彼负。兵战：意外得捷，宜慎其后。", "毕法云：前后引从升迁吉。古鉴：一县令巳生，寅将占前程。曰：亥虽生干，有蛇扰之。功曹作太阴合亥，曹吏为奸。午火临绝乘空，主妻子有灾。申鬼数七，七个月被曹职羁滞，遇运使始得解去。子作贵，加本命巳上，以子为漕运故也。指南：一内监被逮，用戌将占，曰：日上皇恩，支见天赦太岁，贵人生日，子命初末引从，必转祸为福。后果免罪，寻复原职。"));
        set(10, new LiuReKe("乙未日第七局　干上戌", "课体：反吟，无依，稼穑，斩关。", "课义：满目空财，往而不来，宅乘遁鬼，因妻致灾。", "解曰：三传俱财，然落空陷，不足用也。反吟主往来反覆，今俱空绝，则往而不来矣。丑财在支，旬遁辛鬼，反伤乙木。丑为乙妻，而昼乘天后，故当因妻致祸也。", "继曰：此反吟无依之卦，课传纯土，局合稼穑，于变动不宁之中，仍有艰难困苦之意。三传皆落空陷，主事有声无实。虽就必更，纵得还失。吉不成吉，凶不成凶。满目皆财，取之不得，不取又贪，有身入宝山，空手而还之象。", "天时：夜将乘龙，主有雨。家宅：主家道空乏，夫妻不睦。婚姻：不宜，亦不成。胎产：主双胎。功名：无所就。求财：难得。疾病：主三消之症，久则凶。失脱：难寻。出行：不果。行人：未至。争讼：为争空财，旋各解散。兵战：未利。", "毕法云：空空如也事休追，来去俱空岂动移。课经云：旬尾昼乘元，夜乘虎为禄作闭口，不必加旬首也。古鉴：丁丑亥将占会试，曰：三传年命，魁罡俱空，如何敢许甲榜。丑年末日，合而为魁即中，后居官未久，即当丁艰。盖传课纯财，印爻被克故也，后果验。"));
        set(9, new LiuReKe("乙未日第八局　干上酉", "课体：知一，铸印，度厄，励德。", "课义：丁鸡抱蛇，初中虚花，卯虽是禄，元虎相加。", "解曰：酉为旬丁，又作日鬼。夜占乘蛇而临干，为忧疑之象。初传旬空，中传坐空，凡事虚而不实。末传卯虽日禄，然夜占元武，则为盗脱，昼占白虎，则为凶战，宁可取乎，徒有铸印之美名而已。", "断曰：知一之卦，格合铸印。惜初中空陷，神将非佳，恩中有害，触处惊疑。课名度厄，不利长上。在君子为虚声无实，官禄不宁，马空印破。在常人为孤独无成，既防病讼，亦见别离也。惟巳年月占之，仕宦可卜亨利。干乘六合，支乘六害，外和而内不和。", "天时：主有风雷，而雨泽甚少。家宅：夜占人宅不宁。婚姻：不成，虽合不利，宜迟配。胎产：胎神坐空，利产不利胎。功名：考试迟钝，宦途亦有未稳，巳年月吉。求财：往来无济，静守反得。投谒：昼贵坐害，夜贵在卯，不宜干贵。疾病：主惊痫风火，或噤口不食，久病大不利。失脱：难寻，见者不言。出行：改期。行人：久望不至。争讼：防遭曲断。兵战：未利。", "毕法云：害贵讼直遭曲断，传墓入墓分憎爱，常问不应逢吉象。订讹云：夏月巳午时，或值蛇雀太旺，或值空亡，皆为破印损模。指掌赋云：三下贼上为长度厄，越海无舟楫之刑。"));
        set(8, new LiuReKe("乙未日第九局　干上申", "课体：重审，曲直，狡童。", "课义：自生传墓，先醒后雾，夜贵乘罡，昼贵保护。", "解曰：亥为日之长生，未为日之墓库。初亥末未，自生传墓也。经曰：自墓传生，先迷后醒，此则反是。两贵俱临日之阴阳，但夜贵乘罡作鬼，有伤乙木。赖昼贵生乙脱申，贵德相扶，大宜干贵求神，暗中保护也。", "断曰：重审之卦，局合曲直，帘幕临干，长生发用，更逢春占木旺，定主富贵荣华，官禄并至。但合中刑害，先阻后成，又防弟兄连累。干支值绝，止宜告贵，结绝旧事。", "天时：多风雨，草木繁。家宅：夜占宜慎闺门。婚姻：不用。胎产：胎坐空生，防不育。功名：春冬占大吉，昼占考试高第。求财：可得。投谒：两处只有一处得力。疾病：主脾病少食，难愈。失脱：勾克元武，捕之必得。出行：宜从舟楫。行人：即至。争讼：先直后曲，告贵求和。兵战：夜占吉。", "毕法云：贵虽坐狱宜临干，彼此猜忌害相随。课经云：自生传墓，主初谋事时，如花似锦，后反迷惑。指南：戊子年亥将，一官占行藏，曰：木局茂盛，蛇化为龙，必是舟车之职，向后功名远大。因幕贵坐空，故不由科第。而印爻发用，末传皇恩，由门荫出身。正官临日，偏印居支，先升知府，后转司道也。果现任工部，未几升知府。"));
        set(7, new LiuReKe("乙未日第十局　干上未", "课体：重审，稼穑，不备，赘婿。", "课义：辛鬼后墓，休买田土，循环三刑，因财受苦。", "解曰：丑为日财，其占为田上。然旬遁辛鬼，鬼墓在后，能伤乙干，又财化为鬼。若事涉田土，于中取财，则循环于三刑之中，缠绕不脱，必致因财起衅，受诸苦楚也。夜占初传蛇，末传白虎，其凶尤甚。", "断曰：重审之卦，课传皆季，局合稼穑。干支各遭墓覆，事多艰苦，境亦沉迷。虽满目皆财，而妄求必招灾悔。循理守顺，利必自来。昼贵立酉，显者增荣，下秩所忌。又支财就干。最宜速取，缓则化墓矣。", "天时：阴湿不见日色。家宅：主宅来就人，买之不费力，又戌作朱雀加未，主有恶犬伤人。婚姻：主纳妾，或妇人携儿改嫁，昼占吉。胎产：胎神临败克，防有伤损。功名：用财可得，须历艰辛，卑位防谪。求财：自来者急取，往求则致祸。疾病：中停饮食，寒热不安。失脱：龙雀入墓，遗失文书之属。出行：墓覆，必不成行。行人：在彼不能得意，未归。争讼：主因田财而发难端。兵战：俟彼来侵，可胜，我动不吉。", "毕法云：华盖覆日人昏晦，干墓并关人宅废，虎乘遁鬼殃非浅，宾主不投刑在上。指要云：三传稼穑，田土稽停，四季迎财，尚宜守旧。指掌赋云：辰临日而受克，名为赘婿，不能自立其身。"));
        set(6, new LiuReKe("乙未日第十一局　干上午", "课体：重审，涉三渊，间传。", "课义：丁鬼入宅，费用不一，尚可交关，贵不见恤。", "解曰：酉为日鬼，又为遁丁，今居宅上，盗未之气，多缘有所动变，遂致费用甚多。辰与酉合，午与未合，日辰上下交车相合，尚可利于交关。但昼贵入狱，夜贵受克，贵人自顾不给，焉能望其恤我，则干之无益也明矣。", "断曰：重审之卦，顺进间传，日辰皆受脱耗，两贵受制蹉跌。凡所谋为，皆难遂志。有心无力，外美中暌。好事迟成，见灾难散。申子与日课，合为水局。中传戌土，有山之象。又皆入夜方，幽人贞守，山水怡情，为叶其吉。", "天时：久畅得雨。家宅：主多变动耗费，兼防婢仆奸私。婚姻：主养媳养婿，先情后礼。胎产：主非正妻孕，或媵婢，又主女胎。功名：多费难成，三月占可以有得。求财：所得不偿所费。投谒：见贵无益。疾病：主上部虚，宜祈祷鬼神。失脱：勾生元，防捕人纵盗。出行：利北方及巳月。行人：到家，恐复出。争讼：此多脱耗，彼有挑唆，易结难解。兵战：无益，春月吉。坟墓：辰方出水则吉，或辛龙人首可阡。", "毕法云：空上逢空事莫追，鬼乘天乙乃神o；交车相合交关利，人宅受脱俱招盗。课经云：自刑之中，午酉又为四胜煞。凡占自逞其能，各怀邀功逞俊之意。指掌赋云：申戌子名涉三渊，当隐于山林。"));
        set(5, new LiuReKe("乙未日第十二局　干上巳", "课体：遥克，蒿矢，进茹。", "课义：空脱实鬼，庶俗深畏，仕宦宜占，三奇并值。", "解曰：巳为空脱而临日，申乃实鬼而临辰，主有病讼耗费，故庶俗畏之。仕宦则为驿马官星，况三传元遁，得乙丙丁三奇，尤上吉之课也。", "断曰：遥克之课，顺进连茹，祸福俱轻，事主牵连不决，谋望未成。独连珠三奇，幕贵入宅，大利科第。仕宦必有奇遇，富贵非常也。家有客来不可留，西南方防有口舌。", "天时：多雨而寒。家宅：多宾孟之费，防婢妾奸私。婚姻：不用。胎产：生女。功名：主有遇奇，荣贵可期。求财：耗折。疾病：不吉，不止一症。失脱：必系婢仆。出行：赴试上官吉，余不利。行人：忽然而至。争讼：难解，两家俱耗。兵战：秋冬不利。", "毕法云：鬼临三四讼灾随。指南：丁丑年亥将占会试，曰：日鬼皇恩发用，中传河魁天喜。末传长生太阳，支见幕贵官星。又雀生太岁，文字华藻，高捷无疑。嫌干支互绝，官难通显。果举进士以主政终。又：甲申年申将，县令占行止，曰：岁贵劫贼临支，贼符驿马加干，地有兵扰。幸干网逢空，必得解脱，往水乡安稳。又：己丑年午将占病，曰：身加卯为棺，又乘驿马，干支互绝，青龙太常为孝服。戌加酉为纸钱煞，九月节尽矣，皆验。"));
    }
};
