public class d34 extends KeEntry{
	public d34() {
        set(5, new LiuReKe("戊戌日第一局　干上巳", "课体：伏吟，元胎，斩关，孤辰。", "课义：自初至末，迤逦侵伐，昼勾虎蛇，夜免猖獗。", "解曰：初传克中，中传克末，末传克干。三传递克，必受众人之欺。昼占将乘蛇虎，凶不可言。夜将天后青龙，临于中末而助初生，可无虑其猖獗矣。", "断曰：伏吟之课，发用空亡，宜静不宜动之象。末传寅木，助起初传巳火，以育干支。而又三递以克干，所云两面刀者是也。喜空亡无力，夏占则吉。支神昼夜乘元，遗失难免。", "天时：日上巳空，寅申相冲，风雨夜作。家宅：昼虎脱干，必主卑幼脱赚。仕宦：禄空逢绝，占官不吉，幸而末助，己命稍吉。求财：反防失脱。婚姻：干上德空不吉。胎产：子息属阳，母实儿空，不利。疾病：腹痛脾泄之症，禄空不吉。遗失：满目皆元武，须防家贼。出行：陆路稍可，水路不吉。行人：近者立至，远在中途。兵战：昼占不宜。", "毕法云：任信丁马须言动，三传互克众人欺。课经云：三传互克，必有人递相克害，遂使众口雷同，群然攻讦。朝官宜自检束，提防公众弹章。灵辖经云：戊戌日为十恶大败，以禄神空亡故也。秘要云：干支同类，难以求财，惧争夺也。金兰略云：孤辰相克，必主孤单。又曰：白虎重临传送，在外遭殃。"));
        set(4, new LiuReKe("戊戌日第二局　干上辰", "课体：元首，退茹，斩关。", "课义：退入鬼乡，进向空方，守身昏晦，败坏门墙。", "解曰：退而寅卯皆鬼，进而辰巳皆空；守则日墓临身，居则败气临宅。所以身遭昏滞，而门墙败坏也。", "断曰：三传退茹，事多主退。凶则重重，吉亦累累。与其进而履空，不若退而就旺也。日上辰土，与支上酉金，虽为六合，而酉与支戌，实为六害，当有门户之忧。全局官鬼，春夏遇之，所谓鬼贼当时，尚无畏忌。若到秋冬，祸不旋踵矣。", "天时：日上逢空，太阳必见，火上水下，自然不雨。家宅：太常作败气脱宅，必因酒色，以致家道衰微。仕宦：初中皆系官星，末传又作贵人，占官最吉。求财：财爻不见，青龙作鬼，求之不得。婚姻：支干之上神相合，填实可就。胎产：上强下弱，二阴包阳，妊乃成男；母实儿空，恋腹不吉。疾病：酒色过度，脾土受伤，或咳嗽劳伤之症，人空病实，恐难瘥愈。遗失：自己家贼，非婢即仆。出行：人宅相合，行期必愆。行人：天罡加孟，尚未启行。兵战：前后逼迫，未见其利。坟墓：有败死之气，不吉。", "毕法云：华盖覆日人昏晦，宾主不投刑在上，众鬼虽彰全不畏。课经云：三传皆是木神，并来伤干，诚为凶也。不知支上有酉金，足以敌鬼，不为凶咎。此必有宅中之人解祸，谓之家人解祸格。指掌赋云：三传纯官鬼，而兄弟成灾。"));
        set(3, new LiuReKe("戊戌日第三局　干上卯", "课体：重审，励德，极阴。", "课义：交合可以，彼生我忌，破碎初逢，闭口可畏。", "解曰：卯与戌合，巳与申合，交关则可。但申，长生也；卯，官鬼也。彼受申生，而我却忌卯克耳。初传丑为破碎，临于卯而发用。卯为闭口之鬼，乘雀伤干，岂不可畏乎。", "断曰：极阴之课，发用破碎，中财末败，退于幽暗之乡，凡事有抑塞不舒之象。闭口临身，总宜三缄，以保明哲。雀作日鬼，亦防弹章，尤不宜上书言事，伸理冤枉。", "天时：毕宿带丁，雨师发用，必雨。家宅：昼占卯朱克日，主有争讼，白虎临宅，防有丧事。仕宦：雀鬼临干　官员防劾。求财：当因酒食而得。婚姻：破碎发用，不吉。胎产：日受上克，子孙难保。疾病：三传极阴，死神乘干，巳禄空绝，肺经受病，甚危，幸宅上申能制鬼，或因妇人及行路而得良医。争讼：朱雀闭口，讼枉难伸。出行：丁居末传，行期甚速。行人：驿马带虎，入宅即归。兵战：宜守。坟墓：辰上长生，可以发丁。", "毕法云：制鬼之位乃良医。古鉴：酉命丑加卯占终身，曰：三传丑亥酉，课名极阴。土死于卯，败于酉，酉六亥四，其人寿至四十六岁。初传贵人，当享父福。中传临官加丑，亦乘父禄。末传及于本身，便尔败坏矣。中传亥水，末传酉金，当因酒色而败，果验。"));
        set(2, new LiuReKe("戊戌日第四局　干上寅", "课体：元首，元胎。", "课义：寅夜青龙，身初两逢，昼申虽制，白虎有凶。", "解曰：寅乘青龙，一见于日上，一见于初传，两逢日鬼，唯末传之申金，可以制之。奈昼乘白虎，递助寅鬼，仕宦则一岁九迁，而常人反为凶恶也。", "断曰：元胎之课，凡事初有萌芽，倘占赴试，昼将必然及第。青龙作鬼，福里生灾。白虎克官，祸中得助。但昼夜贵人，皆临囚狱之地，不能仰藉宠光，刷幽振滞。", "天时：天罡指阴，夜龙克日，夜必有雨。家宅：夜贵临宅入狱，贵人不利，亦防酒食之咎。仕宦：官星被克，欠利。求财：昼占宜得妇女之财，夜占宜得酒食之财。婚姻：昼占不利，夜占甚吉。胎产：女胎产顺。疾病：睥土受病，或肝经有伤，末传制鬼，必遇良医。遗失：贼乃家人，捕之可获。出行：水陆两途，均非坦道。行人：天罡加季，行者立至。兵战：不利。", "毕法云：帘幕贵人高甲第(昼占)。课经云：干上寅木，作日之明鬼，支上未土，遁出乙木，作日干之暗鬼。如先有人相允，后却不能相顾。一系明作无情，一系暗中寡义，彼此各怀恶意也。玉女通神诀云：顺道用传母前子，失礼还从子传母，顺即为和逆即疑，此是三传真诀语。"));
        set(1, new LiuReKe("戊戌日第五局　干上丑", "课体：遥克，蒿矢，炎上。", "课义：火局生身，夜虎临寅，末并宅上，丑午凶神。", "解曰：三传火局生身，本为吉兆。但寅夜乘虎作鬼，支及末传午火，又遁甲鬼，昼乘白虎。午为羊刃，丑为破碎，丑午六害，其凶不待言矣。", "断曰：蒿矢之课，为力虽轻，支干相害，彼此不无猜忌。格名炎上，多虚少实，诸占皆有美中不足之象。夜占虑其始，昼占虑其终。", "天时：三传火局生日，天罡指阳，自然晴朗。家宅：尊长相虐，不安之甚。仕宦：日贵临身，夜贵登天，占官可望。求财：财爻不见，青龙又隐，恐不能得。婚姻：支干相害，占婚不吉。胎产：昼占胎神乘虎甚凶，夜占不畏；但酉为子孙，产乃生女。疾病：心经有病，肺家患深，昼占难愈。遗失：物非盗窃，不过婢妾所遗。出行：陆路利昼不利夜，水路利夜不利昼。行人：天罡加孟，尚未启行。兵战：夜贵两占，皆不甚利。", "毕法云：虎乘遁鬼殃非浅，合中犯杀蜜中砒。课经云：午加戌父母爻上，乘虎坐墓，主父母墓中生白蚁，或兴祸端。如父母在堂，主有疾病之灾，更作月内死神、死气尤凶。神将论云：未临亥为贵登天门，神藏煞没，无不亨利。指掌赋云：寅戌午为华明，彰精光于天表。玉成歌云：遥克当传主远寻。"));
        set(0, new LiuReKe("戊戌日第六局　干上子", "课体：重审，不备。", "课义：我投于彼，两边契义，夜虎临寅，昼占无畏。", "解曰：日为我，辰为彼，以日加于辰，是我投于彼也。巳与戌有内辛之合，岂不两相契合乎。寅乃日鬼，临于末传。而夜乘白虎，诚可畏矣。若昼占则上乘天后，自坐墓地，全然无气，复何畏之有哉。", "断曰：不备之课，事不周全，必有缺陷。三传自下克上，子水受伤，是伤其财也。虽则临干，取之防有惊恐。初末引从昼贵，可以告贵用事，亦利科名。夜占催官符至，到任极速。但五行俱入墓中，止宜结绝旧事。", "天时：青龙带水升天，奈三传递克子水，有雨不大。家宅：人来就宅，又复生宅，居必壮丽。仕宦：夜占白虎作催官使者，青龙贵人，皆乘日上，占官必得。求财：财来就人，不求自至。婚姻：以夫就妻，填实甚吉。胎产：上强下弱，产必生女。疾病：心经受病，或齿痛之疾，夜占则系脾胃受伤之症。出行：陆路甚坦，水路亦吉。行人：天罡加仲，尚在中途。兵战：昼占吉，夜占凶。", "毕法云：权摄不正禄临支，支干值绝凡谋决。课经云：子加巳正月占，主孕喜之事，然终不吉。缘胎神加干，临绝受克，恐防损坠。唯占产则可云当日便生也。肘后经云：用神若还害天乙，是为四闭凶不测(子害未)。"));
        set(11, new LiuReKe("戊戌日第七局　干上亥", "课体：反吟，元胎，无依，斩关，寡宿。", "课义：我去彼绝，凡占歇灭，病讼皆凶，行人被截。", "解曰：火生寅而绝于亥，水土生申而绝于巳，巳加于亥，是自投于绝域也。况来往皆空，凡占皆主歇灭。巳为德禄生气，今值旬空而逢绝克，故病讼皆凶。往来俱陷于绝，则行人必有阻而截之者矣。", "断曰：反吟之卦，自刑又复自冲，宾主不浃之象。中传居日，初末他乡，往来于绝地，谓之去绝反吟，诸占不吉。惟僧道则甚相宜，缘四大皆空也。", "天时：太虚明净，天地相通，万里无云之象。家宅：室如悬磬，空空如也。仕宦：官贵不见，德禄皆空，占官不吉。求财：财爻空绝，求之不得。婚姻：彼此皆虚，媒妁难凭。胎产：胎必堕，产母吉。疾病：脾胃受伤，新病吉，久病凶。遗失：元武临宅，家贼难防，元武打洞，恐不能捕。出行：虚话而已。行人：天罡加季，行人立至。兵战：不能成功。", "毕法云：空空如也事休追，来去俱空岂动移。秘要云：来去俱空之卦，鬼空尤妙，然亦宜有制，否则有虚挠之凶，为我难见彼之象故也。心镜云：四课无形，事不出名，纵然出也，恐是虚声。光明经云：水临巽户主腰灾，火遇亥神防走失。"));
        set(10, new LiuReKe("戊戌日第八局　干上戌", "课体：元首，斩关，不备。", "课义：昼虎甲午，申类最苦，夜贵当权，初末拱护。", "解曰：午上遁出甲木，昼占白虎乘羊刃而伤干，又克初传。长生之申，既被午克，复被土墓，最为痛苦也。夜占则贵人临于寅火生地，甚有权势。况初末之申午，又为引从而拱护，占官者，吉可知矣。", "断曰：元首之课，履正而亨。午生丑，丑墓申，申克卯，卯克戌，是午之暗鬼，流祸甚长也。长生入墓，长上应灾。末火克金，子息告匮，又名不备，诸事当有缺欠。", "天时：天罡指阴，水运乎上，有雨。家宅：宅与上神相合，家道必成。仕宦：青龙发用，白虎催官，占官甚宜。求财：龙虽发用，被丑墓午克，恐不能得。婚姻：干支相合，可以成就。胎产：女孕，产速。疾病：肺经受病，春占不吉。遗失：元武不见，乃遗落道途，并非贼窃。出行：行者恋宅，行期必愆。行人：天罡加孟，尚未启行。兵战：昼占甚利，后宜谨慎。", "毕法云：虎乘遁鬼殃非浅，传墓入墓分憎爱。纂要云：戌加巳，卯加戌，此为干支相会，可以共谋成事。秘要云：日鬼乘太常，入宅克宅。若十月占，主有内孝服。玉成歌云：墓神加日身灾滞，支往临干事体危。又云：日鬼加临辰两课，定然官吏到门庭。指掌赋云：丑遇天空为矮子，会申而为和尚。"));
        set(9, new LiuReKe("戊戌日第九局　干上酉", "课体：元首，励德，u女。", "课义：寅并甲午，昼夜皆虎，酉金来救，火局焚去。", "解曰：寅乃日鬼，午遁旬鬼，昼夜皆乘白虎，并来伤干。独干上之酉金为救，又被三传之全火焚毁。占官之外，皆非吉征。", "断曰：元首之卦，本为顺利，但干作金局，支作火局，以火制金。凡占利卑幼而不利尊长，利他人而不利己身。又交车相害，须防面是心非。传虽炎上，却喜会作生气发用，经所谓三传纯父母，不求安而身自安也。占者倘能处安思危，则无不善矣。", "天时：三传皆火，毕宿受制，有风无雨。家宅：夜虎克宅，遁虎复伤，凶患难防。仕宦：催官发用，占官甚吉，兼有威权。求财：财爻不见，求之不得。婚姻：女家太盛，配不相宜。胎产：上强下弱，产乃生男。疾病：昼占则肺经受病，病深难瘥，夜占则脾经受伤，病浅易痊。遗失：元武不见，乃婢妾所失。出行：陆路甚坦，水路难行。行人：天罡加孟，尚未起身。兵战：昼夜俱不为吉。", "毕法云：虎乘遁鬼殃非浅，鬼临三四讼灾随。课经云：子加申为胎坐长生，恐子恋母腹，宜占胎孕，不利占产。指掌赋云：寅午戌为炎上，以发达为名。心印赋云：三合支辰为眷亲，岁中必定别阴人。云霄赋云：火炽伤金，边塞驰驱之子。三才赋云：将神沐浴之处相逢，凡事露机。"));
        set(8, new LiuReKe("戊戌日第十局　干上申", "课体：遥克，弹射，元胎。", "课义：长生须弃，财引逢鬼，昼贵入宅，夜为破碎。", "解曰：申为水土长生，临于干而可守。却贪弹射之财，因而引入中传之鬼乡，所谓因财致祸也。昼占则为贵人入宅，能伏诸煞。倘用夜将，则为碎破临家，其耗费殆无穷尽矣，但当坐守。昼申敌鬼，庶可安宁。", "断曰：遥克之课，当有远方财动。但见生不生，反为脱气。盖因申金陷空，虽三传递生，而末传既空，到底不能结实。", "天时：青龙与水母升天，奈逢空陷，出旬始雨。家宅：昼贵入宅，家必构讼。仕宦：龙与德禄皆空，不吉。求财：财爻虽现，取必遭患。婚姻：昼占青龙空陷，如年命填实可成。胎产：女胎，母实儿空，占产不吉。疾病：脾胃受病，或气促伤残，见救不救，病势难安。遗失：元武不见，自失于途。出行：水陆两途，昼占可行。行人：天罡加季，行者立至。兵战：昼占稍可，夜占甚凶。", "毕法云：脱上逢脱防虚诈，龙加生气吉迟迟。课经云：日干上申金，昼乘青龙，遇九月占，乃是龙加生气。虽目下未即峥嵘，却能徐徐发福。玉女通神诀云：顺道用传母前子，失礼还从子传母，顺即为和逆即疑，此是三传真诀语。天官汇函云：朱雀临亥是非张，水火相刑夫妇伤。"));
        set(7, new LiuReKe("戊戌日第十一局　干上未", "课体：重审，间传，u女，向阳。", "课义：夜贵身边，两子害焉，罡塞鬼户，昼贵登天。", "解曰：未乃夜贵临身，被支与发用二子相害，所谓害贵讼直遭曲断也。辰为天罡，寅为鬼户，辰加寅上，为罡塞鬼户。丑为贵人，亥为天门，以丑加亥，为贵登天门。神藏煞没，凡有谋为，无不遂意也。", "断曰：课名u女，必有阴私。干支之上神不和，人己各怀猜忌。旺财乘虎发用，而生中传寅鬼，必因财上生灾。昼蛇克日，难免惊忧。末传入墓，又属旬空，向后到底少益。", "天时：天罡加寅，子水被脱被克，有风无雨。家宅：支干之上神相害，人宅不安之象。仕宦：夜贵临身，昼贵登天，功名可得。求财：昼占有妇人之财，夜占见凶恶之财。婚姻：上神相害，不吉。胎产：下强上弱，产乃生女。疾病：肾经受伤，心经不足，不能即愈。遗失：家奴作贼，可向东南捕之。出行：夜占陆路甚吉，昼占水路可行。行人：天罡加孟，尚未启行。兵战：昼占犹可，夜占不吉。", "毕法云：彼此猜忌害相随，害贵讼直遭曲断，罡塞鬼户任谋为。指掌赋云：子寅辰向三阳，而所望光明。神将论云：子乘天后，临戌发用，主妇人有淫邪之事。不占妇人，须有私通之事。纂要云：传子寅辰，先合后嗔，阴私内乱，口舌频频。"));
        set(6, new LiuReKe("戊戌日第十二局　干上午", "课体：重审，进茹，三奇。", "课义：罗身网宅，动则遭厄，财在家中，多贪疲役。", "解曰：午为戊干之罗，亥为戌支之网，凡占值此，乃为网罗兜裹，身宅不得亨快。静则守旺，动则为刃。亥乃日财，临支发用，若守其宅上之财，亦足自给。苟肆其贪求之心，则未免财多身弱，不胜其疲役矣。", "断曰：进茹之课，格合三奇，宜无不顺。但支干之上神，各居自刑，支干之阴神，又复相害。宾主不投，人己相忌，惟宜谨守。三传皆财，一切起居饮食，皆宜撙节。恐因财致祸，因食致病。", "天时：青龙升天，三传皆水，天罡指阴，时雨滂沛之象。家宅：但宜谨守，灾患自无。仕宦：大宜谨饬，亦恐丁艰。求财：家财可守，外财难得。婚姻：干支之上神自刑，不吉。胎产：下强上弱，妊乃成女。疾病：肾经与膀胱受病，或心血不足。遗失：为家奴所窃，向正南捕之。出行：昼占陆路可行，夜占水路可行。行人：天罡加仲，尚在中途。兵战：只宜谨守，不可妄动。", "毕法云：所谋多拙逢罗网，尊崇传内遇三奇，宾主不投刑在上。指掌赋云：亥子丑为龙潜，阳光在下，空怀宝以迷邦。又云：传见妻财利益多。又云：三传纯妻财，而父母克害。心印赋云：子丑相加事必成，更逢吉将转欢欣。"));
    }
};
