public class d35 extends KeEntry{
	public d35() {
        set(7, new LiuReKe("己亥日第一局　干上未", "课体：伏吟，自信，杜传。", "课义：元临财上，贼累相向，虎蛇入未，暗中惆怅。", "解曰：亥乃日财，宅上重重是财，却重重见武，是累逢盗贼也。未为己身，既带乙木之暗鬼，又乘蛇虎之凶神。且课传三刑，虽不见其伤损，而暗中有不惆怅乎。", "断曰：自信之课，课传皆阴，利私谋而不利公干。发用杜传，事防有阻，改图可以成就。但中末二传，昼夜互乘蛇虎。而支上亥财，又被众土分夺，总宜清心寡孟，守分为安。", "天时：天地不动，晴占则晴，雨占则雨。家宅：满屋皆贼，财源不聚，人口亦恐不宁。功名：贵禄不见，不能有成。求财：劫之者众，夜占防失，若阴人之财，昼占可得。婚姻：将凶，不吉。胎产：阴极则阳生，况胎乘阳神，产必弄璋。疾病：膀胱受伤，或肾经湿症，不妨。出行：蛇虎当道，盗贼满途。行人：丁马不见，行者不至。兵战：彼此皆诈，利主不利客。坟葬：丁龙从右入首则吉，水太旺，穴中恐有水。", "毕法云：虎乘遁鬼殃非浅，任信丁马须言动。课经云：辰加辰，谓之两勾夹墓。纂要云：申贵逆行，白虎乘丑，乃贵人嗔怒之象，不可干谒。盖丑乃天乙本家，不宜见虎也。壬经云：伏吟干支，各居其位。日辰只有两课，用日则舍阴，用辰则舍阳。天地不备，阴阳独刑，实为不足之体。"));
        set(6, new LiuReKe("己亥日第二局　干上午", "课体：元首，退茹，斩关，励德。", "课义：弃禄尤宜，关隔卑微，魁居宅上，百无所依。", "解曰：午为日禄，昼乘天空，弃之他求，未为不可。但天魁临亥发用，谓之魁度天门。事主关隔，昼夜乘阴，卑微之象。亥为本日支神，而魁塞之，关隔尤甚，故曰百无所依也。", "断曰：退茹之课，干支拱传，破败居中，不如退守日禄，尚为有益。真朱雀临干，土年占之，最利功名科第。", "天时：天上乘空，水母与毕宿相加，夜当阴雨。家宅：破败居中：未见昌盛。仕宦：官禄临身吉。求财：关隔临财，不能入手。婚姻：媒妁难通。胎产：占孕成女，占产艰滞。疾病：脾胃受病，隔食隔气，虽担时日，不防。出行：关梁隔阻，陆路可通。行人：魁度天门，必有阻隔。兵战：只宜谨守，不可袭远。", "毕法云：旺禄临身徒妄作，魁度天门关隔定。古鉴：巳将午时占，曰：时与日干六合，事必主外。命上乘未加常，与发用俱得子爻之休气，主其子在外酒食过度致病。又丁卯占，曰：命用见合，中传酉乘元武，因私通人家婢妾致疾。盖酉为婢妾，元武临酉为窥户，本身属土，被酉耗败，故必顿也。又壬戌占曰：戌为奴，酉为婢，元武窥户，卦得斩关，乃逃亡之象。主家中奴婢逃亡，得马姓人，于西方捉获。缘日上午为马姓，与日相助克酉，酉在西方，与午相破故也。"));
        set(5, new LiuReKe("己亥日第三局　干上巳", "课体：遥克，蒿矢，间传。", "课义：六阴俱全，空脱，破败丁酉，嗣婢熬煎。", "解曰：四课三传，六阴俱备，昏昧之甚。干上巳火空亡，支上酉金脱气，所以也。土败于酉，又为破碎，遁出旬丁，临于宅上，非子嗣之破败，即婢妾之奢靡，煎熬岂能免乎。", "断曰：遥克之课，日财助起初传之鬼而伤干，有将钱买祸之象。却喜蒿矢无力，发用坐于空乡，不足畏也。宜年命上乘土神，有以制水则吉，有以生水则凶。盖亥水虽为财神，而以末助初，实为祸之根，而罪之首也。", "天时：六阴相继，必雨。家宅：败气临宅，家渐衰微。仕宦：官星与青龙皆空，不吉。求财：昼占妇女之财可得，夜占不利。婚姻：昼占龙空，夜占后合，皆为不吉。胎产：课传六阴，男喜也，但母实儿空，四月占尤不吉。疾病：酒色过度，或肺经受伤，难愈。遗失：继母藏匿，或在饮酒处，否则婢女盗去。出行：水陆俱不吉。行人：已在中途。兵战：事多更张，伏兵宜防。", "毕法云：后合占婚岂用媒，六阴相继尽昏迷。六壬断诀云：干上乘马，支上乘丁，饭不得饱，睡不得宁。指掌赋云：卯丑亥为断涧，义利分明。照胆秘诀云：阴阳间克传藏课，内外萦心成虑破。中黄经云：马见巳亥在路歧。"));
        set(4, new LiuReKe("己亥日第四局　干上辰", "课体：元首，斩关，励德，寡宿。", "课义：干上墓神，自末而生，孟脱不脱，宜守家庭。", "解曰：辰乃己土之墓神，覆于日上，主昏迷迟滞。三传自亥水递生育干，当有推荐之喜。但巳属旬空、驿马，未足取信。支上申金，孟脱干气。幸昼夜贵常，与干相比相助，不能脱也。申为长生，坐干支上，而乘常贵以生支，占者当闭户谨守，向后自尔荣昌。", "断曰：元首之课，又名斩关，宜顺时而动。支上乘生，日上乘墓，亦有宅旺人衰之象。三传生墓育干，可以重兴旧事。两常夹墓临干，幸系旬空，尚不至十分沉闷也。", "天时：墓神覆日，龙已升天，必有大雨。家宅：宜谨守家业，不宜外骛。仕宦：官文两空，占官不利。求财：宜得阴私之财。干谒：昼占武职可见，夜占文职可见。婚姻：昼占平平，夜占乃吉。胎产：上强下弱，二阴包阳，产必生男。疾病：肺家有疾，或脾土受制，不药可愈。出行：陆路塞滞，水路甚吉。行人：天罡加季，行者立至。遗失：昼占可寻，夜占为家贼所窃。逃盗：东南朽木之下可捕。兵战：主胜于客，敌自远遁。坟墓：当成金局，丑方出水，大旺人丁。", "毕法云：华盖覆日人昏晦。指掌赋云：三传生日百事宜，日生三传财源耗。太乙经云：占与人期会，天罡临日辰者会。"));
        set(3, new LiuReKe("己亥日第五局　干上卯", "课体：涉害，曲直，度厄，不备，乱首。", "课义：彼己俱乖，弃害就财，循环而已，倘恋灾来。", "解曰：卯克己土，未克亥水，是彼己俱乖也。乃弃卯就亥，财虽可得，不可久恋。盖课体循环，卯乘夜虎，必来相伤故也。", "断曰：课作回环，占事不散，吉事固成，凶事亦就。凡百云为，皆有迟疑阻滞。惟年命上得一金神，可以制凶。", "天时：水被卯脱，风烈而雨微。家宅：田土兴灾，欺之者众。仕宦：官鬼成局，过于旺相，须年命上有火神方吉。求财：木局泄财，自不能聚。婚姻：后合甚多，占婚不吉。胎产：阴极阳生，当是男喜，产亦必速。但防堕胎。行人：天罡加孟，行者未来。遗失：非干贼窃，失于途中。兵战：宾主皆伤，彼此不利。", "毕法云：干支全伤防两损，虎临于鬼凶速速，虎乘遁鬼殃非浅。指南：壬午十月卯将未时，为子占功名，曰：干神归支，传将逆行，应回东省求名。三传纯鬼，鲁都虎鬼克支，贼符将星克干，主贵地不日兵动，且有攻城破邑之事。眷属宜迁他处，目今且防失盗。曰：老母九十大寿，庆后始迁可乎？曰：尊堂寿止八十有九，因乙发用，与地盘巳字合断也。后其人被盗复来，曰：元武乘脱气，居丑命之上，所盗者乃郎君之物。然课传回环，贼必再至，后悉如所占。要览云：亥乘天后，抱卯子而合于未，妇人有私情也。"));
        set(2, new LiuReKe("己亥日第六局　干上寅", "课体：重审，六仪，四绝。", "课义：六关和顺，禄神已尽，嗣及长生，占之有吝。", "解曰：寅与亥合，午与未合，六合交关，非不和顺。但午加于亥，禄入绝乡，且昼乘天空，下受亥克，则禄已竭尽无余矣。申为己土之长生，又为己土之子孙，午克丑墓，悔吝必矣。", "断曰：六仪之课，凡占可以化凶为吉。日德临身，己土受克，美中不足。日禄临支，不能自尊自大。又名四绝，止宜了结旧事，不利图新。寅虽官鬼，赖坐于未土之墓，不能伤干。巳乘虎入墓，父母之坟墓有灾。", "天时：水运乎上，青龙升天，天罡指阴，有雨之象。仕宦：官星临身，日禄临支，功名最利。家宅：禄神临宅，家自富饶。求财：家中自有，不必外求。婚姻：德禄居于干支之上，门户相当。干谒：非为贵人所伤，即为贵人所脱。胎产：下强上弱，二阳包阴，妊乃成女。疾病：脾家有病，或心火受伤，春占可畏。遗失：非贼所窃，不过遗失途中。求谋：凡事皆吉。出行：陆路甚坦，水路亦佳。行人：尚在中途。兵战：宜防虚诈。", "毕法云：权摄不正禄临支。指南：乙酉正月子将巳时占病曰：脾土受病，目今无虑。盖木为官鬼，克脾土故也。止宜平胃清心，切勿健脾理肺。禄临绝地，马入墓乡，又卯临申位，是木被金雕，病人非宜。且年带死败克日，恐至七月有不测之忧耳，果验。"));
        set(1, new LiuReKe("己亥日第七局　干上丑", "课体：反吟，元胎，寡宿。", "课义：来去皆空，长上须凶，妻妾婢媵、常闹家中。", "解曰：巳为己之父母，旬空受制，必主长上有灾。亥为己土之妻财，昼乘蛇，与巳往来冲击。而巳又为双女，既冲复陷，则家中妻妾，岂得安和哉。", "断曰：无依之卦，来去俱空，旺财临绝，黩货非宜。斗鬼相加，占试最吉。孤辰寡宿，尤防骨肉别离。惟九流僧道，不为凶也。巳乘虎加亥，主小儿有咽喉之患。", "天时：巳亥相加，天地相通，昼占则风，夜占则雨。家宅：宅内皆空，多虚少实之象。仕宦：官禄不见，求之甚难。求财：财爻空动，恐无实济。婚姻：男实女空，不吉。胎产：课传无阳，妊乃成女，三传皆空，恐是鬼胎。疾病：肺经之病，或心虚齿痛，不久自愈。出行：陆路可行，水路不吉。行人：天罡加季，行者立至。遗失：元武乘空，无从捕获。兵战：虚张声势，主虚客实。", "毕法云：来去俱空岂动移，空空如也事休追。照胆秘诀云：天倾西北日月随，地陷东南江海归，巳亥中间常缺欠，重求轻得报君知。中黄经云：马见巳亥在路歧。课经云：巳日反吟，三月占，乃生气克日，是幸中不幸。正月占，乃死气生日，是不幸中之幸。神将汇函云：巳乘虎加亥，木器柜上有销坏，换之则吉。"));
        set(0, new LiuReKe("己亥日第八局　干上子", "课体：知一，铸印，斩关，励德。", "课义：墓覆家庭，元虎为邻，贵财系子，昼卜临身。", "解曰：辰为日墓，临干支上，是墓覆家庭，不无昏滞。初巳末卯，虽云引从支辰，但昼夜俱乘元虎，与我为邻，动必生祸矣。干上子水，昼占为贵人之财，临于干上，守之最善。", "断曰：铸印之课，巳空谓之损模。虽末助初生，但初中空陷，凡百谋为，俱费心力。墓虽覆支，喜中传戌冲作救，惜坐空无力耳。前后引从地支，只宜迁修家宅。", "天时：水运乎上，午未日有雨。家宅：支上元白，乘墓克支，虚失虚忧之事。求财：宜得贵人之财。功名：昼占必遇。婚姻：占男昼吉，占女皆凶。胎产：二阴包阳，妊必成男。疾病：昼占则肺患，夜占则脾疾，或因酒食伤胃，不久自愈。出行：昼占陆路甚吉，夜占平平。行人：尚未起身。兵战：主虚客实。", "毕法云：前后引从升迁吉，传墓入墓分憎爱。占验：寅将酉时占开肆，曰：日为财，支为库。今干上有贵财，支上见真库。干上财神，又归宅库，大有益利。但巳为店，上乘元武，恐修葺多费。中传又为店库，财库重重，嫌末传卯木克财。九年之后，须被人争夺，缘巳四戌五故也。又一人占赁屋开肆何如，曰：此屋颓损，大费修葺，赁后不五六年，而店中之赀财累万，后至第九年，因取赎致衅，经官陈论，当被夺取也，果验。"));
        set(11, new LiuReKe("己亥日第九局　干上亥", "课体：涉害，曲直，不备，赘婿。", "课义：财就身宫，恋则为凶，只宜早弃，免被贼通。", "解曰：支财临干，财临身也。取之本无不可，但亥之上神为卯，乃是日干之鬼。若不急弃，则支上之卯，会成木局伤身，祸不免矣。况卯为太冲，上乘元武，皆贼象也。亥卯未一经作合，是与贼通，所得之财，岂能享乎。", "断曰：赘婿之卦，又四课不备，支上乘脱，必无正屋可居，亦俯首于人之象。课传回环，吉凶俱成，最不利于解散忧疑之事。木局传鬼乘财，只可携财告贵，纳粟奏名。", "天时：青龙发用，但水被日克，三传木局，雨少风多。家宅：满盘官局，而身受制，却宜胥役。仕宦：官星甚旺，再得文书，方为全美。求财：禁止贪心，庶可远害。婚姻：神吉将凶，成而不美。胎产：课传纯阴，又曰不备，产必弄瓦。疾病：夜虎乘卯，脾胃受伤，课传皆鬼，病笃难瘥。出行：陆路稍可，水路不吉。行人：天罡加仲，尚在中途。遗失：元武脱宅，盗失难免，贼众太旺，难于捕获。兵战；迭为宾主，名有所伤。", "毕法云：初遭夹克不由己(昼占)。指南：癸未正月亥将未时，有客来占，曰：龙神发用，课传结成官局，来意必占今岁功名，六月即有钦召之应。盖木遇春旺，夏则密茂，将来事业远大。缘岁建皇恩发用，中传天诏，故六月定有佳音也。果于是月奉诏进京，授京营提督，复升戎政。"));
        set(10, new LiuReKe("己亥日第十局　干上戌", "课体：遥克，蒿矢，元胎，励德，斩关。", "课义：巳类为弓，申象箭容，遗弓落箭，所为无功。", "解曰：巳形如弓，乃值旬空。申形似箭，乃为陷空蒿矢，而弓箭皆空，将何以建功立业乎，故曰所为无功。", "断曰：励德之课，君子所宜。寅为官星，临干支上，既得亥水之助，又遁壬水之生。若春冬占之，官爻得气，旺相极矣。夫官为扶身之本，财为养命之源，寅爻得地，而财官双美。课虽蒿矢，其为功也甚大，岂以弓箭之空，遂弃之勿道哉。又干上戌刑未，支上寅合亥，在外者极其经营，居家者极其安乐，亦有自己煎熬，他人逸乐之象。", "天时：天罡指阴，朱雀被墓，阴浓雨少。家宅：官爻临宅，事起家庭。仕宦：官星得气，夜占甚佳。求财：财爻虽见，但取之甚危。婚姻：支干上下相合，事在必成，但恐婢妾居于妻位。胎产：二阳包阴，妊乃成女。疾病：肝家有病，金日可愈。出行：马载白虎，迅速之极。行人：天罡加季，行者立至。兵战：主胜于客，须防偷劫。", "毕法云：夫妇芜淫各有私，宾主不投刑在上。课经云：寅木克干，赖末传申金冲克，为己土之长生，应苦去甘来之喻。玉成歌云：德神动处吉相随，受克遭刑反见危。心印赋云：蒿矢遥神克日干，救神制鬼却为欢。"));
        set(9, new LiuReKe("己亥日第十一局　干上酉", "课体：涉害，间传，见机，出产。", "课义：破败临身，况又乘丁，五阴柔日，凡占杳冥。", "解曰：酉乃土之败气，又为支之破碎。上乘遁丁，其破败耗费，必有不免者矣。课传皆阴，而干上昼合夜后，总为魇翳之神。消沮闭藏，有不可告人者矣。", "断曰：出户之卦，虽自暗出明，而末传空亡。未能出幽离隐。惟天罡塞于鬼户，凡百阴谋，翻能遂意。丑土比肩，克宅发用，家中必有暗损。中传官鬼闭口，尚喜酉制，不至猖獗，必其家有败子，能转移祸福也。", "天时：课传皆阴，毕宿临干，当有雨。家宅：昼占则妻妾扰宅，夜占则同类为凶，皆有耗费。仕宦：贵禄不见，破败加干，不吉。求财：己财不守，不必外求。婚姻：后合占婚，成而不吉。胎产：男喜，防损胎。疾病：肾肺受伤，兼有积块腹疾，四月占更凶。出行：水路不吉。行人：天罡加孟，尚未启行。兵战：未见其利。", "毕法云：后合占婚岂用媒，两贵受克难干贵。课经云：干上酉金作日干之脱气，昼乘天后复脱酉金。谓之脱上逢脱，酉为子孙，亦为婢妾。夜乘天后，必有因酒色而败坏身家者。又云：申加午，子加戌，昼夜两贵，皆被下神所克，不宜干请。不惟不能成福，翻能致祸。心印赋云：丑卯巳为出户，春雷震蛰。"));
        set(8, new LiuReKe("己亥日第十二局　干上申", "课体：元首，进茹，三奇。", "课义：身宅俱贵，三奇又遇，君子宜之，常人反忌。", "解曰：申为夜贵，临于干上。子为昼贵，临于宅上，是身宅俱贵也。丑寅卯作三传，鼠遁则为乙丙丁三奇。君子占之，有迁官进爵之喜，常人则反忧官事矣。", "断曰：连茹之卦，汇征则吉。初传丑土，鼠遁得乙为暗鬼，中传末传为明鬼，是三传全鬼伤干，殊为可畏。然赖申金临干，又为父母，又为子孙，足以制之，所云众鬼虽彰全不畏也。", "天时：四课之上，尽属水神，却被丑土所克，雨不能久。家宅：贵常临于身宅之上，三奇作传，富贵可望。功名：昼夜两占，不拘文武，皆掇巍科。求财：宜得贵人之财。婚姻：朱陈佳偶，极为吉祥。胎产：上强下弱，二阴包阳，必产贵子。疾病：肾水不足之症，或脾土受伤，不日可瘥。出行：陆路甚佳，水路亦吉。行人：天罡加仲，尚在中途。兵战：干支罗网，恐遭困厄。", "毕法云：昼夜贵加求两贵，尊崇传内遇三奇，所谋多拙逢罗网。纂要云：干支之前一辰，临于干支之上，为之天罗地网，值此者，如网罗兜裹，不能亨快。若占人年命上神，冲破支干之网，始为无咎。或遇空亡，又为破罗破网，见亦不害。指掌赋云：三传纯官鬼，而兄弟成灾。"));
    }
};
