public class d39 extends KeEntry{
	public d39() {
        set(1, new LiuReKe("癸卯日第一局　干上丑", "课体：伏吟，稼穑，励德。", "课义：课传八土，癸水受苦，宅中夜贵，可以御侮。", "解曰：传见三土，课见三土。而干之丑，昼乘勾陈。末传之未，夜乘勾陈。是共八土相并，来伤日干。则一泓之癸水，何以堪此。幸宅中卯木夜贵，足以敌土而御侮，所谓众鬼虽彰全不畏也。", "断曰：课名稼穑，又系伏吟。总宜株守，不利动谋。子息乘贵，必有克家之子，家赖以安。惟忌年命上乘申酉，反制救神。中传白虎临官，仕宦最速。", "天时：天地不动，晴占则晴，雨占则雨。家宅：贵人临宅，全鬼化财，富贵之象。仕宦：有官无禄，催官之月，即能赴任。求财：财爻不见，求之无益。婚姻：婿不甚佳，夜占女吉。胎产：长胎发贵，产期尚迟。疾病：肝肾之疾，家中自有良医，治之即瘥。遗失：元武不见，觅之可得。出行：不宜动。行人：近者立至，远者杳然。兵战：客胜，夜占擒敌。坟墓：两贵夹墓，牛眠吉地。", "毕法云：制鬼之位乃良医，六爻现卦防其克。课经云：卯为夜贵临宅，作干之脱气。凡占主被贵人脱赚，或因神o以致耗费。神定经云：辰戌丑未为三传，自相冲刑，倘占捕获，贼不能脱。谓之以凶制凶，以贼捉贼。盖内有四金，可以化鬼故也。秘要云：年命上若有金神，名为否极泰来，又为家人解祸格。"));
        set(0, new LiuReKe("癸卯日第二局　干上子", "课体：重审、退茹、三奇。", "课义：夜禄难守，常占殃咎，昼夜天空，勾陈在丑。", "解曰：子为癸水之禄，昼占乘龙，固为吉庆。而夜占乘元，则难守矣。初传官动，常人主有官讼疾病，所以有咎。发用之丑，既属官星，上乘勾土，而亥之天门，又乘天空，遁出己土，印何累累，绶何若若也，仕宦有不迁擢者乎。", "断曰：连珠三奇，莺鸣谷迁之象。仕宦固佳，常人亦可化凶为吉。干支夹定三传，末传出于干外，名透关格。不利内事，止利外事，皆先难后易。", "天时：青龙临日，三传皆水，天罡指阴，必非晴象。家宅：寅为病地，作脱气临支，人多疾病。仕宦：传得三奇，尊崇之象。求财：宜守自己旺财，不可妄求。婚姻：昼占则为佳婿，卜女不吉。胎产：男孕，未产。疾病：肝经受病或肾水涸竭之症，家有良医。出行：陆路昼占甚吉，水路昼夜防脱。行人：天罡加孟，尚未启行。捕获：在北方。兵战：传遇三奇，昼占更利。", "毕法云：二贵皆空虚喜期，尊崇传内遇三奇。课经云：戌加亥为魁度天门，昼夜皆乘白虎。凡占谋用，尽被阻隔。又辰加巳，昼夜乘蛇，地盘之巳，亦是蛇位，谓之两蛇夹墓。病必积块，以致不救。精蕴云：透关格，号曰当时不时，过后失时，或心力不逮，致已成之事为人破坏。"));
        set(11, new LiuReKe("癸卯日第三局　干上亥", "课体：涉害，极阴，间传，循环。", "课义：昼将俱土，神愆咒诅，传课循环，灾出无路。", "解曰：勾陈天空太常，尽属土将。乘于三传，共伤日干，灾患必生。且发用丑鬼，所司咒诅，夜坐于贵人之上，受贵所克，递伤日干，非阳人暗算，即鬼神为愆。干加干支，为支所脱，课体循环。纵孟避灾，岂能免乎。", "断曰：极阴事必昏晦，间传亦有迟疑，静安动阻之象。", "天时：课曰极阴，天将皆土，浓阴不雨。家宅：勾陈乘鬼临宅，家中争讼难免。仕宦：贵空不吉。求财：求之无益。婚姻：不成，不吉。胎产：女孕，易产。疾病：腹内有疾，或肾虚之症，难愈。出行：水陆俱不吉。行人：干加支，本家人即至。兵战：课名倒拔蛇，作为费力，未见其利。", "毕法云：二贵皆空虚喜期。指南：戌加子占两贵来否，曰：四月必来，以巳贵来合三传，但居官不久。以二贵空陷，传退极阴，格合回环，来而必去也。又干临支受克，来必失意，果应。古鉴：寅将辰时，占逃亡罪人，曰：癸来加卯，又自卯上传出，退归西北，其人隐一买卖人家猪圈内。亥四酉六，四六二十四里，或六十四里，当有孝服。婢在宅前洗酒器，问之便得。盖酉为婢，见太常主孝服，酉加亥为酒器故也，果验。"));
        set(10, new LiuReKe("癸卯日第四局　干上戌", "课体：元首、稼穑、斩关。", "课义：六合交互，脱难入墓，屑就其禄，渐旺门户。", "解曰：卯与戌合，子与丑合，干支交互相合也。白虎乘鬼发用克干，中传又是日鬼，幸而脱于虎口。至末传而又逢日墓，其昏迷不更甚乎。喜旺禄临支，昼乘青龙吉将，倘能屏弃众土，俯就支禄，则子水必能生卯，门户当渐兴旺也。", "断曰：日禄临支，权摄不正，官为佐贰，俸或家食。戌为催官发用，仕宦最宜。嫌末传空亡，向后恐无结束。支干上下相刑于六合之中，外虽和顺，须防胸中荆棘。", "天时：三传皆土，青龙克日，阴而不雨。家宅：龙合生支，宅必见喜。仕宦：催官临身，龙乘日禄，占名最利。求财：宜从本家觅。婚姻：昼占女吉，夜占男吉。胎产：上强下弱，孕乃成男。疾病：肾经膀胱之疾，能食可愈。出行：昼占水路甚吉。行人：末传入墓，行者立至。兵战：利主不利客。", "毕法云：权摄不正禄临支，干乘墓虎毋占病，虎临干鬼凶速速。课经云：干上戌乘白虎克干，赖末传辰土，昼乘蛇冲虎，是以凶制凶。占忧患事，必得人解释。要览云：子禄临卯，被宅所脱，必因起盖房屋，以禄偿债。"));
        set(9, new LiuReKe("癸卯日第五局　干上酉", "课体：涉害，曲直，励德。", "课义：干上旬丁，引往贼营，夜将皆土，解释冤憎。", "解曰：干上丁酉，所谓水日逢丁财动也。若贪此财，而冒昧前取，则三传皆脱，必引干癸入于脱盗之乡矣。夜将皆土伤日，却赖曲直为救，非解冤释憎而何。", "断曰：曲直之课，纯乎盗气，幸干上酉金，足以克木生身，必家有严母，能樽节后人。课传六阴，自昼传夜，惟利阴私，亦防昏滞，此占不利仕宦，惟利常人，秋占更吉。", "天时：课传皆阴，毕宿临日，风雨皆见。家宅：亥为日之同类，加支生支，必赖兄弟之力，光大其门。仕宦：虽有贵人临于课传，奈三传全木克官，占官最忌。求财：财气最旺，宜得贵人与婢妾之财。婚姻：利妻不利夫。胎产：上强下弱，阴极变阳，孕乃生男。疾病：肾水亏损之疾，或木侮脾经，赖酉金生亥克木，必遇良医。遗失：并非贼窃。出行：陆路可行，水路欠利。行人：尚未启行。捕获：昼占贼在东方，夜占贼在正南。兵战：防敌虚诈。", "毕法云：合中犯杀蜜中砒，众鬼虽彰全不畏。课经云：是名脱气为救格，盖三传木局，并来脱干，既无日鬼，岂宜附于众鬼虽彰之例。缘三传天将，皆是土神，并力伤干，反赖三传木局，克制土将，岂不应斯格耶。凡遇此局，只作救神论，切不可作脱气言之。"));
        set(8, new LiuReKe("癸卯日第六局　干上申", "课体：知一，斩关，斫轮，芜淫。", "课义：交互战伐，不受福德，夜虎临身，脱空鬼贼。", "解曰：干上申金克支，支上戌土克干，是交互战伐也。巳为日德，又为昼贵。逢空入墓，无气之极，是不受福德也。申虽日之长生，夜虎昼元，不可守矣。往投三传，卯为脱气，戌为日鬼，巳又逢空，无一佳处。但当弃此三传，再投日干所临之午，乃日之财，经所谓避难求财格也。", "断曰：斫轮之课，本利占官，但发用子孙，克制官星，亦非全吉。长生昼夜，将乘元虎，父母不无疾病遗失之事。又名芜淫，琴瑟必有未调，常人亦防官事临门。", "天时：朱雀发用无雨。家宅：昼占则官事临门，夜占则吉事入宅。仕宦：铸印催官，功名极速。求财：宜得贵人妾妇之财，巳年月利。婚姻：支干上神，昼夜皆有白虎，不吉。胎产：男妊，产速。疾病：肾虚之症，后防不测。出行：水陆皆凶。行人：天罡加仲，尚在中途。兵战：有始无终，宜谨慎。", "毕法云：夫妇芜淫各有私。指南：卯加申占失马，宜向何方寻觅，何日可得，曰：马之阴神，见子乘龙，马必青黑色，三日内必获。缘末传之驿马逢空，必俟出旬乙巳日填实，方能获马。马居戌地，当于西北山冈觅之。后果三日自刘家集得之。"));
        set(7, new LiuReKe("癸卯日第七局　干上未", "课体：反吟，励德，龙战。", "课义：夜贵闭口，宅乘丁酉，动既无财，尤不可守。", "解曰：夜占不可干贵，缘贵人闭口，不肯剖露真情也。丁神临宅，宅不安宁。水日逢丁，本为财动，而乘酉之败气，不惟不能得财，且有耗费。退而归于干上，又逢未土，明鬼暗脱，其不可守也，更甚矣。", "断曰：反吟之课，干被未克，支被酉克，而又各坐于克方，是谓全伤坐克，人宅皆伤之象。初末作脱，中传败气，三传又皆土将，祸患重叠，殆非一端。雀鬼加干，朝官亦防弹劾。盖往来逼迫，进退维艰，而反覆无常，波澜不定也。", "天时：晴占乃雨，雨占乃晴。家宅：宅中必因婢妾不宁。仕宦：雀鬼加干，朝贵防劾。求财：宜得妾妇之财。婚姻：支于全伤，占婚不吉。胎产：女孕，母子不吉。疾病：肺经受病，防有翻覆。出行：中道亦回。行人：行者立至。兵战：反覆不宁，主客皆伤。", "毕法云：两贵受克难干贵，干支全伤防两损。精蕴：未加丑，酉年二月，妻占夫病，本命在丑。曰：妻之年命，上乘华盖，太常作日鬼，又是死气，又是吊客，谓之孝帛盖妻头。夫病必死，倘八月内占之，未为生气，尚有孝服未已，果验。"));
        set(6, new LiuReKe("癸卯日第八局　干上午", "课体：重审，六仪。", "课义：中害末墓，午财勿顾，唯赖申生，夜占虎遇。", "解曰：中传之亥，遁得己土。末传之辰，又是日墓。午财虽则临身，而自末递克，回归干上，又复受克。夜占乘虎，岂能顾恋乎。三传既无可取，赖宅上申金，为日之长生，无如夜乘白虎，宅亦欠宁，不可守也。", "断曰：重审之课，三传自刑，事多不顺。喜六仪发用，干上乘财，支上乘生，乃人宅荣昌之象。初末昼拱巳贵，干支夜拱太常。若年命居于寅子，谋无不遂。午为妻财，受克乘元，非有妻病，即防盗马。", "天时：午火发用。水被库藏，恐不能雨。家宅：长生临宅，夜占乘虎，父母防灾。仕宦：寅命子命人吉。求财：财爻临干，易得，但防惊恐。婚姻：女益男家。胎产：胎神受克，占胎不吉。疾病：肝肺受病，或疮疼首痛，未几即愈。出行：昼占吉。行人：末传入墓，可望即到。兵战：客能胜主。", "毕法云：支乘墓虎有伏尸。课经云：六合乘申加卯，为六片板格。申者，身也。上有六合，下有卯木，以身就木之象。占病不吉。三月占尤的。如不乘六合，九月占病，则卧床而未愈。缘申作生气，卯为床也。若占父母长上之疾，尤为不吉，以父母爻入棺故也。"));
        set(5, new LiuReKe("癸卯日第九局　干上巳", "课体：涉害，从革，绝嗣，孤辰。", "课义：四课被贼，绝嗣亡族，昼将助传，去祸迎福。", "解曰：四课上神，皆被下神所贼，卦名绝嗣。昼将纯土克干，幸三传金局，窃脱土气，转育癸水，化难作恩，岂非去祸而迎福者耶。", "断曰：从革之课，三传克支生干，人虽丰盈，家无正屋，或孟弃其田宅，逃往他方。五阴相继，事主昏迷。墓神覆支，宅非明敞。惟仕宦占此，官印全逢，有革故鼎新之象。", "天时：天罡指阳，三传生日，天晴无雨。仕宦：贵禄皆空，填实即吉。家宅：支乘支墓，宅上欠宁。求财：财爻已空，不吉。婚姻：女子愚蠢多言，不利。胎产：阴极阳生，当生男。疾病：脾土之疾，七月占不吉。出行：水陆两途，均不甚利。行人：末传空，未到。兵战：利客不利主。", "毕法云：传墓入墓分憎爱，水日逢丁财动之。指南：辛未六月未将卯时，为台臣占上疏，曰：“传将逆生，有疏荐人乎？”曰：“非也。有疏参人耳。”曰：“虽则三传递生，嫌其初末逢空，独存中传之岁破，作鬼伤干。又太岁临宅，乘朱雀克日。太岁，君也，岁破，相也。恐得罪于君相，课象不利。”后果以上章荐人，下狱拟配。纂义云：酉乘天空加巳，防婢妾挈财逃走。"));
        set(4, new LiuReKe("癸卯日第十局　干上辰", "课体：重审，斩关，三交。", "课义：禄贵丁财，并墓俱逢，守之昏晦，动则亨通。", "解曰：辰为日之墓神，临于干上，若守此墓，自然昏晦。倘弃而投诸三传，则酉上乘丁，作日之财神。子为日之禄神，卯为干之夜贵，三者皆逢，亨通之至，非动曷能有此。", "断曰：三交之课，支干自刑。又支克干上辰土，干克支上午火，交互克贼，主有参商。倘夫妻行年，再立辰午，卦名解离，非断弦必反目也。所喜午实辰空，传逢轩盖，占官可用。", "天时：墓神覆日，天罡指阴，阴雨之象。家宅：败气发用，脱干克支，室家不和，衰替之象。仕宦：贵禄皆现，占名最吉。求财：水日逢丁，求财必得。婚姻：恐有刑伤。胎产：女孕，母有惊，子难育。疾病：心经受病，夜占肾水亏损，病实人空，难愈。出行：陆路不吉，水路得财。行人：出旬即至。捕获：昼占在艮方炉灶之处，夜占在正北方竹丛中。兵战：彼此两伤，夜占擒敌。", "毕法云：夫妇芜淫各有私，宾主不投刑在上，水日逢丁财动之。课经云：春酉将癸卯日酉加午，临仲发用，斗系丑未，名二烦课。秘要云：午酉递加乘勾陈，宅中主有产妇惊恐，应在寅日。指掌赋云：寅乘元武见巳，为炼丹道人。又云：酉加午上，为宠婢登堂，会六合，必主淫乱。又云：申乘合作医人。"));
        set(3, new LiuReKe("癸卯日第十一局　干上卯", "课体：遥克，蒿矢，励德，不备。", "课义：传及干支，迤逦生上，涸源断根，病讼恶况。", "解曰：三传自上生下，四课自下生上，谓之水涸其源，木断其根也。病因不摄，虚损难医。讼自家庭而起，所谓自恶窝犯者是也。", "断曰：不备之课，课传六阴，心事昏迷，不利公用。蒿矢课而发用蓦越，已属轻浮。又兼落陷，毫无实迹。如年命在寅，上乘天罡，得昼夜贵人拱定，仕必高甲。", "天时：天罡指阳，朱雀发用，中末两传，昼夜皆乘土将，不能得雨。家宅：昼夜贵人各脱干支，必因贵人耗费财物。仕宦：两贵相加，昼夜互见，定中高魁。求财：宅上财空，出旬可得。婚姻：干支皆贵，门楣相称。胎产：昼占六合受克，男孕，产恐不育。疾病：心经受病，指日可愈。出行：昼占水路可行，夜占陆路可行，然失耗皆所不免。行人：驿马归宅，出旬即到。兵战：财爻不备，军资不足。坟葬：朝山好，当发财。", "毕法云：昼夜贵加求两贵。指南：甲子四月申将午时占，曰：时为日胎，必占六甲。问男乎？女乎？曰：干上卯属震，长男之象。又是幕贵，三传四课纯阴，阴极阳生，必产贵儿。且支加干，俯首见子，生更顺利。但课不备，未能足月。问生干何时？曰：六月生。后果一一如断。"));
        set(2, new LiuReKe("癸卯日第十二局　干上寅", "课体：重审，进茹，龙战，斩关，寡宿。", "课义：前路皆空，凡事无踪，未免虚动，病讼俱逢。", "解曰：辰巳午作三传，皆逢空陷。凡事俱无踪迹，虚声而已。干上寅木作病气，脱于发用。天罡夜乘蛇，作鬼墓干。所以病讼之殃，必不能免也。", "断曰：斩关之课，事不由己。逼迫而动，网刃在前，不利动作。干上脱气乘元，昼占难免失脱。支上夜乘蛇，乘墓克干，惊恐当亦不免。若能退守干上寅木，虽为脱气，尚司敌鬼安贫。", "天时：支干各乘罗网，墓神发用，阴云不雨。家宅：支上辰土，克干墓支，人灾宅晦。仕宦：德贵驿马皆空，占官不利。求财：财爻皆空，求无所得。婚姻：支墓干脱，两家不吉。胎产：胎神空绝，占胎不吉。疾病：脾胃受伤，病空可愈。出行：水路有惊，陆路防失。行人：传空未至。兵战：元武临干，须防劫营。", "毕法云：进茹空亡宜退步，所谋多拙遭罗网，干墓并关人宅废(夏占)，来去俱空岂动移。古鉴：戊申九月卯将寅时占葬地，曰：支干皆贵，此地甚佳。盖丑为坟，即癸干之位。其墓在辰，遇空则为虚墓。对酉乘空，亦是虚坟。去此二者，方可以葬，后当发贵。且申酉为今日水母，对冲寅木，主水合星辰。巳又主双，乘雀加辰，为双圆峰，文章星也，生子当应举及第，后皆应。"));
    }
};
