public class d40 extends KeEntry{
	public d40() {
        set(2, new LiuReKe("甲辰日第一局　干上寅", "课体：伏吟，自任，元胎。", "课义：德禄俱空，昼虎鬼凶，火命解祸，土命畏逢。", "解曰：寅乃日之德禄，旬空无力，不能为福。未传申为日鬼，昼占又乘白虎，其像甚凶。若占人年命属火，火能克金，可以解祸。若年命属土，则土反生鬼，逢之岂不可畏。至于金木水年命，皆可以类推而断之。", "断曰：自任之卦，三传皆孟，格合元胎，事主伏匿，情在狐疑。安静则宜，妄动不吉。况禄马空陷，仕宦不利。虎鬼刑克，俗庶尤忌。斩关有奔逃之患，孤辰有离别之忧。虚幻无成，成亦更变，惟僧道占之吉。", "天时：少雨有雷雹或寒冱。家宅：人口衰耗，防有奸逃。功名：恐是虚声，寅月占吉。求财：静守家业为上。婚姻：男家不愿。胎产：婢妾孕，主女胎，宜多服药。疾病：昼占凶。失脱：不出家中，隐匿难寻。出行：改期。行人：未至。争讼：助我者少，昼占不利。捕获：系本家奴隶，赃埋坟土中。兵战：有失众之象。", "毕法云：宾主不投刑在上，虎临干鬼凶速速。纂要云：甲辰日禄马俱空，为十恶大败之一。又干上寅，懒去取财，恐争夺也。神将论云：功曹乘龙为道士，乘蛇主猫狸之怪。六合临辰，家有宰杀。白虎在申为衔牒，天后在申为修容。玉成歌云：天马(五十月寅)驿马为初用，参星白虎动行神(申为参星)。"));
        set(1, new LiuReKe("甲辰日第二局　干上丑", "课体：知一，退茹，重阴。", "课义：进损退益，戌财必失，冬昼火惊，贵人覆日。", "解曰：甲课在寅，进逢卯之空，故曰损。退受子亥之生，故曰益也。末传戌财，昼夜皆乘元武，定主耗失。卯乘朱雀克宅，冬占又为火鬼，其有火惊宜矣。丑乃昼贵，覆临日上，在君子为利见大人，在常占为官事相扰也。", "断曰：知一之卦，事起同类，恩中有害，和衷则吉。人空宅克，合处见刑，宾主不和，求谋未济。又贵作空墓，干禄非宜。秋占方有吉兆，春夏遇之，只可从容守旧。大宜于难中得易，旧处生新，妄动无益也。", "天时：久雨，难晴。家宅：朱雀克宅，冬月防火灾。功名：须得人相助，难中有望。求财：得亦必失。婚姻：易成而不吉。胎产：主婢妾怀妊，或私孕，产即生。疾病：迟愈，无妨。失脱：寻觅不获。出行：更改如意。行人：迟至，或得音信。争讼：案牍迁延，有解终散。捕获：西方女婢作窝。兵战：不利。", "毕法云：魁度天门关隔定。课经云：丑乃日之破碎煞，支上卯，又干之阳刃，宜弃此而就三传以生干，是为舍损就益。又干上丑，财作闭口，与禄作闭口，食神空亡同论，不利占病。指掌赋云：子亥戌为重阴，安嘉遁之贞，宁甘没齿。神将汇函云；亥加子为孩儿，丑遇空为矮子。"));
        set(0, new LiuReKe("甲辰日第三局　干上子", "课体：涉害，间传，悖戾，闭口，励德。", "课义：屈尊就财，末午助来，六阳俱备，申虎尤乖。", "解曰：辰为日财，日往加之，是屈尊以就财也。末传午火又助起初传之财，似乎财不可胜用矣。然课传六阳俱备，利于公用，不宜私有。况中传申乘昼虎，脱财之气，生身之灾，尤为乖戾，不可不慎也。", "断曰：涉害之卦，迟疑不定，退逆间传，事多阻隔。于虽生气临干，夜将白虎，变为败气。若贪戌财，引入鬼脱之乡，必人先以利饵，后却为害。宅上空亡，须防耗失。虎临干鬼，必见灾，元武发用，亦名闭口，凡事不孟告人。", "天时：阴晴不开，得风始霁。家宅：贫耗，又主有忤逆潜逃之事。功名：孟成中阻。求财：得失相半，主有人暗中相助。婚姻：不宜，亦不成。胎产：胎坐长生，胎安而产不利。疾病：不能言语，昼占凶，辰月占主出血。失脱：见者不说，贼人自投网罗。出行：主有阴私逃避。行人：即来。争讼：财贿入官，彼此皆损。兵战：利用虚声慑敌，乃搜捕奸细。坟葬：当发，挂名空职，或佐贰前程。", "毕法云：权摄不正禄临支，虎临干鬼凶速速，闭口卦体两般推，末助初兮三等论。订讹云：戌午火局，中间一申，反成克象，故名悖戾。凡占寅日，方有克应。中黄经云：白虎天后与蛇，克日须当病转加。"));
        set(11, new LiuReKe("甲辰日第四局　干上亥", "课体：蒿矢，元胎。", "课义：干虽乘亥，切勿自怠，病苏讼宽，其忧尚在。", "解曰：甲虽得亥之长生临于日上，不可自足而怠忽也。初传申鬼，昼乘白虎，病讼得此，为像甚凶。幸亥水窃金面生干，中传复能克制，得以苏宽。然元胎卦，四生恋生，恐难杜绝，其为忧患，未尽脱然也。", "断曰：蒿矢之卦，虽曰祸福俱轻，事在远而无畏。然申金发用，加以昼虎，则为矢带金镞，凡事忧在西南。贵人在支，可以墓虎脱鬼，解释冤仇。若夜占则为破碎，家必不完。惟仕宦逢之为催官符，寅年月必见迁擢。", "天时：夏占雷雨，冬占冰雪。家宅：防有口舌，忽然而来。功名：禄空防罚俸，寅月占利。求财：财神闭口，求官无路。婚姻：卦名解离，大有不利。胎产：主生哑儿。疾病：主禁口或咽肿，巳酉月见脓血。失脱：见者不肯言。出行：不利谒贵，利于莅任。行人：在路，有虚惊。争讼：贵人闭口，不肯排解。捕获：在南方，近西向东，井边有唱歌牧羊者。兵战：未见得利。坟葬：主西北方，防水淹及盗损。", "毕法云：夫妇芜淫各有私，虎临干鬼凶速速，闭口卦体两般推。课经云：日上辰生，初传申鬼，转生亥水，为不幸中之幸。袖中金云：第二课发用，乃日上两课自战，作事无力。神应经云：干克支上丑，支克干上亥，名解离卦。"));
        set(10, new LiuReKe("甲辰日第五局　干上戌", "课体：涉害，炎上，斩关，狡童。", "课义：初遭夹克，三传盗日，赖子相制，其象夜吉。", "解曰：戌作初传，既为日财，复乘六合并临，名曰夹克，不由己用；况三传火局全盗日干之气，但见其损，不见其益，所赖者宅上子水，足以制火之脱而生日干；又夜将乘青龙，其象反为吉也。邵南此卦，取子水比日发用，三传水局生干主吉。", "断曰：涉害之卦，局合炎上，占事多历艰难，占人过于刚急。日上发用，坐于空乡，凡百谋为，少成而多变。外合而内暌。况斩关为奔逃之象，狡童有邪僻之私，三传火局，脱干生支，戌财遁庚，内藏暗鬼，占者不可不慎。", "天时：求雨未雨，求晴不久。家宅：子加辰乘蛇，防妇人哭泣，戌加寅乘合，防奸丑不明。功名：不足。求财：只利索债。婚姻：防媒妁相欺。胎产：胎安，产利。疾病：主脾虚肝气旺，宜作福。失脱：难获。出行：宜避难远逃。行人：子午日可到。争讼：先发者失利。捕获：北方水次。兵战：大利，但防粮草不给。", "毕法云：初遭夹克不由己，后合占婚岂用媒，合中犯煞蜜中砒。三车一览云：火局脱气，而反生干上财神，名为取还魂债。又三传脱干生支，亦作屋广人衰论，余占皆我衰他旺之象。曾门定章云：六合者天之私门，天后蔽翳奸邪。寅克戌，又为强逼，下贼其上，过在女人。"));
        set(9, new LiuReKe("甲辰日第六局　干上酉", "课体：知一，四绝。", "课义：去煞留官，仕宦欣欢，常人值此，灾祸多端。", "解曰：甲木以酉为官，以申为煞。今申被午克丑墓，全然无气，止留日上官爻。仕宦占之，定有迁擢之喜。常人最畏官鬼临身，倘值此卦，须忧病讼相缠。日辰上互见朱勾，初传又乘昼虎，总为仕途所欣，常占所忌。", "断曰：知一之卦，五行皆临绝地。恩中有害，绝处逢生。三传递生，官鬼临日，主有人推荐，大利功名。长生临宅，交车相合，凡事得人扶助。当舍近就远，勿惮艰辛，自然前程亨泰也。秋冬占之更吉。", "天时：晴多雨少。家宅：亥加辰，昼将朱雀，主小儿哭泣之事。功名：先难后易，秋冬大吉。求财：迟而有得。婚姻：可合，稍有刑克。胎产：胎神临绝，利产而不利胎。投谒：和好中恐生不足。疾病：虚弱难愈。失脱：防捕役受赇纵贼。出行：宜近出，不利远行。行人：在路，子日到。争讼：宜和解。兵战：防损并防马病，秋占吉。坟葬：长生在支，庆远流长。", "毕法云：交车相合交关利，合中犯煞蜜中砒，宾主不投刑在上。心印赋云：胜光临亥为德乡，婚姻有象不相当，立名四绝病人已，结绝旧事可消详。又云：午加亥上见极阴，胜光为火火为心，心神荧惑生疑虑，纵疑灾害不相侵。"));
        set(8, new LiuReKe("甲辰日第七局　干上申", "课体：反吟，绝胎，孤辰。", "课义：善者既陷，恶者浪荡，所举百事，无些影响。", "解曰：寅为日之德禄，虽在初末，而值旬空。申为日之鬼煞，虽临日入传，而坐空地。是善者不能为善，恶者不能为恶，善恶俱无实也。卦体反吟，日上阴阳，尽归空陷。辰上魁罡，来去无常。虽孟举事，何所据乎。", "断曰：反吟无依之卦，格合元胎，课名四绝。事主呻吟反覆，迟滞无成，暗昧不通，徒劳罔济。况禄马官星，皆临空绝，以求荣福，何异捕风捉影。惟宜解散忧愁，及利九流方外耳。寅年月占之，庶望有成。", "天时：晴雨无常。家宅：不宁，防有阴私。功名：寅年亥月吉，余无成。婚姻：反覆不成。胎产：防系鬼胎。求财：徒劳无得。投谒：不遇。疾病：新病即瘥，久病不吉。失脱：系宅中奴仆所窃，移远就近可获。出行：改期。行人：主未动身，或将至复他往。争讼：解散。兵战：大利。坟葬：东南方近孔道，或河津处佳。", "毕法云：昼夜贵加求两贵，来去俱空岂动移。课经云：支上戌财生干上申鬼，又戌旬遁庚作暗鬼，大不利谒贵求财，即生灾祸。斗魁乘六合临辰，亦名斩关。如占时发用者，名动中不动，寻远在近处，兼中末空亦然。如初见太岁，中末见月建或日辰，亦名移远就近，将缓为速。"));
        set(7, new LiuReKe("甲辰日第八局　干上未", "课体：涉害，度厄，寡宿。", "课义：干禄告免，家h内战，传墓入墓，行人立见。", "解曰：寅为干禄，然上遭墓覆，下受酉克，是为仰丘俯仇，虽发用而不足恃也。宅上神克初传，因而递克，是为三传内战。况中传未既来墓，岂宜末传子水，复入于墓乡乎。凡占不宜，惟占行人，可立见其至耳。", "断曰：涉害之卦，三下贼上，又名度厄。事涉艰辛，情多乖隔。所喜德禄驿马发用，然值旬空，又中末传墓入墓，凡百谋为，俱难成就。况孤辰有别离之象，龙战有乖戾之虞。进退更张，全无定著，在占者见机趋避而已。", "天时：阴晴不定，有风。家宅：防有窝犯斗讼之累及火灾。功名：防为有势力者所扼。求财：当前而不得取。婚姻：不合。胎产：主生女，或私孕。疾病：主脾胃或口齿，新病愈，久病凶。失脱：贼自内出，捕者不力。出行：傍徨不定。行人：立至。争讼：理虽直，防遭曲断；事虽小，恐化为大。兵战：防失众心。", "毕法云：传墓入墓分憎爱，害贵讼直遭曲断。课经云：未贵为墓覆日，丑贵又受寅克，子未相穿，凡占多弄巧成拙。如识时去就，庶无大害。又秋占未为关神，临干入传，遁作旬丁，更凶。又寅加酉禄作空亡坐克方，夜占乘虎，病必绝食。指掌赋云：三下贼为长度厄，越海无舟楫之形。"));
        set(6, new LiuReKe("甲辰日第九局　干上午", "课体：蒿矢，润下，励德。", "课义：笑里有毒，鬼贼满屋，末助初传，午畏水局。", "解曰：申子辰三合，而内课寅午戌，各与之相冲，是为笑里有毒也。申为日鬼，临宅发用。辰为末传，元武并乘，是鬼贼满屋也。末助初以克干，全赖干上午火相救，而全水之局，又来克之。水虽生干，而反克干之救神，终不离合中犯煞之意。", "断曰：蒿矢之卦，申金发用，即为有镞。虽曰祸福俱轻，而为力稍重矣。三传合润下之局，甲木逢之，重重助益，谋事宜必有成。惜乎干上空脱，末助初克，又且合中有冲，鬼临宅上，未见全美。午火受制，子孙不利。", "天时：雨多晴少。家宅：贫耗不宁。功名：夏月昼占吉，不利微员。求财：防能生灾作祸。婚姻：和合中有刑冲。胎产：胎坐长生，占孕安，占产不利。投谒：防遭贵怒。疾病：主虚脱，日德空亡，尤不利尊长。失脱：家中小儿所遗，在北方寻。出行：有事复返。行人：舟行即至，陆行有阻。争讼：彼此耗财，财多者胜。捕获：贼在西方七十里，道旁近水。兵战：昼占吉，夜占不利。", "毕法云：合中犯煞蜜中砒，人宅受脱俱招盗。课经云：辰为日财而上乘申鬼，是为危中取财，况辰土助申克干，为教唆词讼之人，故取财必致官非也。指掌赋云：贵人临卯酉，分前后为励德，庶人吝而君子亨通。"));
        set(5, new LiuReKe("甲辰日第十局　干上巳", "课体：重审，元胎。", "课义：宛转益己，不利于彼，家宅动摇，忧变为喜。", "解曰：申为日鬼发用，似乎可忧。殊不知申生亥，亥复生寅，宛转相生，适足为益于己也。寅克辰土，故独不利于彼。未乃干墓，又遁旬丁，以临宅上，主动摇不安之象。申先克日，而后迤逦相生，非忧变为喜而何。", "断曰：重审之卦，三传皆孟，格合元胎，事多不顺。隐而未著，远而难通，守正则亨，妄为取咎。又神将内战，必因争夺生灾。夜占尤有惊恐，惟喜三传递生。官文禄马，无不入传，犹嫌寅落旬空，必得寅亥月占，方为全美。", "天时：雨中时见日色，出旬始晴。家宅：不宁，防阴小作耗。功名：亥月昼占大利，得人扶持之力。求财：得失相仍，且防有祸。婚姻：昼占吉，恐未能即成。胎产：防损。疾病：渐愈。失脱：主有势家隐庇。出行：考试赴任俱吉，余不利。行人：占弟兄即至。争讼：多虚费，先难后缓。捕获：盗神是未，乃在西南方，有职人员家。兵战：宜慎。", "毕法云：三传递生人举荐。三车一览云：日干生其上神，昼乘太常，上神又生天将，亦为脱上逢脱。又未加支，四月占为月厌飞廉，亦为天日煞。乘旬内丁神，如占人未为本命，必宅中怪物现形，或夜多怪梦也。"));
        set(4, new LiuReKe("甲辰日第十一局　干上辰", "课体：涉害，间传，斩关，赘婿，狡童。", "课义：午火可赖，事不出外，财自然来，昼占可畏。", "解曰：申为日鬼，又乘昼虎，其势甚凶，赖得午以制之。三传循环，不离四课，故事不出外也。辰财临干，是为自然上门。但六合并临，与日夹克，财虽来而不由己用。若昼占助起末传虎鬼，能无畏乎。夜将子虎冲支，家宅亦有惊怪。", "断曰：涉害之卦，顺进间传，凡事必多间隔。辰午申为登三天之课，事干天庭，仕宦必登高位。常占不吉，又格合斩关。贵登天门，罡塞鬼户，六神藏而四煞没，利于逃灾避难，秘计阴谋。赘婿有依人之象，狡童防不正之私。", "天时：主得甘霖。家宅：昼占吉，有喜事。功名：利于显者，不利小位。求财：迟得。婚姻：主作赘婿，夜占不吉。胎产：防有惊恐。疾病：见凶有解。失脱：难寻获，逃者难追。出行：利避难。行人：有阻。争讼：必至上达部院，然终有解。兵战：宜行诡道，勿追败兵，恐有诈。", "毕法云：罡塞鬼户任谋为，后合占婚岂用媒。课经云：春占酉将未时，日辰用神俱旺相，上乘吉将，为三光课。笔尘载越王谓范蠡曰：今三月甲辰时加日i，孤还故乡，得无后患乎。蠡曰：大王勿疑，直视道行，越将有福，吴当有忧。按三月甲辰酉将加未，乃斩关课，大利逃亡也。"));
        set(3, new LiuReKe("甲辰日第十二局　干上卯", "课体：重审，进茹，六仪，升阶。", "课义：静则有旺，动遭罗网，昼卜亡财，夜迎盗党。", "解曰：甲上乘卯，辰上乘巳，静则守其旺地，人宅俱宜。若一妄动，即成罗网，又为阳刃，及生灾祸也。昼占虽遭夹克，而勾陈助土，青龙吉将，不过亡财而已。夜则巳午上乘蛇雀，皆脱干气，是往与盗党相迎也。", "断曰：重审之卦，顺进连茹，宜静守不宜动作，喜旬仪发用。午为正阳，有泰阶之象。从辰巳升之，可以观光利用。惜初传坐空，干上又值旬空，凡有所占，都无诚实。又空财引入盗脱之乡，占者宜安心守分，自获亨通。", "天时：占雨雨少，占晴晴迟。家宅：防有口舌文书之事。功名：待时。求财：财空日脱，反防耗失。婚姻：媒妁无力，不成。胎产：胎旺，昼占防损漏，夜占主生女。疾病：主虚脱，不进饮食。失脱：难寻。出行：宜止。行人：先有信至。争讼：耗财，消释为宜。兵战：我虚彼实，宜坚守。", "毕法云：所谋多拙遭罗网，脱上逢脱防虚诈，初遭夹克不由己。课经云：干上卯，初传纵见财爻，必心多退悔，懒去取财，恐争夺也。又辰加卯，昼夜将皆六合，夹克其财，财必他费，或妻常有病。纂要云：透关格会成火局，常人喜占，仕宦却忌。集义云：年命在卯，名天罗自裹。"));
    }
};
