public class d41 extends KeEntry{
	public d41() {
        set(4, new LiuReKe("乙巳日第一局　干上辰", "课体：伏吟，斩关，六仪。", "课义：叠遇天罡，身动难康，夜被神挠，幸免残伤。", "解曰：天罡临日两课，又作用神，是叠遇也。日干主身，身必摇动，难以安矣。申为日鬼，夜乘天乙，当有神o作祟，必祈禳而后安。幸有中传，与支上之巳，刑克申金，更得六合卯木，亦足制贵人丑土，可免伤残之患也。", "断曰：伏吟之卦，天地未发之象。利于安静，不宜妄动。然天罡主乎变动，临日发用，格合斩关，决无安居之理。勾陈并乘，又主稽留争斗，屈折不伸。占者除奔逃患难之外，每事以退为佳，惟仕宦占之则吉。", "天时：兴云致雨，亦有风雷。家宅：宅吉而人不安。功名：田土之职可望，或纳财得官。求财：恐致争夺生灾。婚姻：不宜。胎产：女胎迟产。疾病：主脾胃之症，不利占父母。失脱：虽近难寻，逃者远去。出行：有碍，不能顺利。行人：将至。争讼：必因争财起衅，或缘妻妾。捕获：不出邑里，在西北方。兵战：宜慎。", "毕法云：宾主不投刑在上，鬼乘天乙乃神o。课经云：辰财发用，又勾陈并临，亦名稼穑。辰属自刑，中传取支，名杜传格。事主有阻，当别寻枝节。指要云：失物不远，访人不出，病主不言语而呻吟。心镜云：虽系伏吟，交车相脱，主彼此有相赚之意。"));
        set(3, new LiuReKe("乙巳日第二局　干上卯", "课体：元首，退茹，不备，蓦越。", "课义：空禄为初，中末仍虚，所谋无效，进步徐徐。", "解曰：卯为日禄，临日发用，似乎可取。然值旬空，何益之有。况中末皆空，所谋之事，必无成效也。但退茹空亡，反宜进步，徐徐俟之，当有改图耳。原本以遥克之辰为初传，今改定。", "断曰：元首之卦，逆退连茹，一上克下，虽曰正大光明，事多顺利。然四课不备，三传皆空，空禄临身，斗罡临宅。身宅不宁，行止未决，宜乘机观变，徐以待时。干加支，贪辰土之财，却被支脱，支又传干遇空，所谓费有余而得不足也。", "天时：风云雷电，空作雨势。家宅：贫而不宁，时有争斗。功名：无中生有，终无实际。求财：合伙营生，防归消折。婚姻：不成。胎产：占胎不实，占产立下。疾病：新病即愈，久病不宜。失脱：东南方可寻。出行：改期。行人：立至。争讼：空中楼阁，旋归消解。捕获：昼在西南，夜在西北。兵战：宜以退为进。", "毕法云：空空如也事休追，脚踏空亡进用宜。课经云：初禄既空，末值蛇虎，乃前后逼迫难进退也。断验：癸丑生人，己卯年戌将占宅，曰：卯来赶日，宅不容人。卯为空禄，寅为兄弟，课中不见父母，勾陈临宅，必同气相争，以致父母离居也，皆验。"));
        set(2, new LiuReKe("乙巳日第三局　干上寅", "课体：重审，间传，极阴，蓦越。", "课义：四课俱空，万事无踪，昼病不食，夜卜则凶。", "解曰：寅卯旬空，子丑坐空。日辰上阴阳神俱空，所占万事皆无踪迹。癸丑发用，是名闭口。昼占乘蛇，占病者止于不能饮食而已。若夜占则白虎乘财助鬼，其凶更甚。盖蛇尚能克鬼，而虎则党于鬼也。", "断曰：重审之卦，逆退间传，为极阴之象。事多不顺，人心疑惑，进退乖离。况卦体全空，用神坐陷，主彼此恍惚，纯属虚声。又旺禄临支，为宅所脱，未免虚耗百端。若占灾患惊忧，亦归消解。总之动不如静，坐胜乎行，毋妄营求也。", "天时：始有风电，继以寒阴，旋皆解散。家宅：主因买宅，以禄偿债，或因喜事多耗。功名：虚声无实。求财：无实际，有虚惊。婚姻：不吉，亦不成。胎产：女胎不实，占产立下。疾病：主噎隔失音，久病大凶。失脱：主阴人所盗，物散难追。出行：不成行。行人：亥日动身，卯日至。争讼：主争空财，彼此虚耗。捕获：昼占西南，井边有狮，夜占正西，女人哭泣。兵战：虚张声势。坟葬：上有空树，下有蛇穴，不吉。", "毕法云：权摄不正禄临支，空空如也事休追。观月经云：四课无形，事不出名，纵然出名，也是虚声。又干禄加支，却被支脱，必因盖屋，以禄偿债。指掌赋云：丑亥酉为极阴，如月隐西山，言三传皆是夜方，不见光明也。"));
        set(1, new LiuReKe("乙巳日第曰局　干上丑", "课体：重审，稼穑，游子，闭口。", "课义：满目财喜，不偿所费，昼虎墓临，闭口无畏。", "解曰：日上阴阳神以及三传，满目无非日财，若可喜矣。然传财太旺，反致亏损。丑既旬尾，戌遁庚鬼，未遁丁神，财虽多而不偿所费也。未为日墓，昼虎临之，诚为凶象。然能闭口谨言，以俟丑上蛇冲破，以凶制凶，可无畏也。", "断曰：重审之卦，三传皆季，局合稼穑。重审多不顺之情，稼穑主艰难之象。况旬丁居末，马见支阴，又合游子之格，主身心摇动，踪迹飘零。闭口临干发用，亦主有怀莫白。支乘空生，又值六害，宅亦虚危。", "天时：阴湿而无雨。家宅：朱雀乘寅，主有口舌文书，事防到官。功名：财旺生官，或以人赀致位，但不利于终。求财：虽多难取。婚姻：有阻。胎产：防生哑儿，或产母失音。疾病：主脾疾不食，难愈。失脱：远去，难获。出行：远游四方，飘蓬无定。行人：思归未得。争讼：主为财起讼，不辩自明，多词有咎。捕获：昼占在南方驿地，夜占在东僧道家。兵战：宜屯田守备，不利攻取。", "毕法云：传财太旺反财亏，闭口卦体两般推。占诀云：旬尾加旬首，财作闭口，与禄作闭口食神空亡同论，大不利占病。订讹云：游子丑戌未为阳传阴，主在外思归。丑加辰为破游，传值墓神煞害，冤家逼迫，值合龙戏驿，万里奋飞。"));
        set(0, new LiuReKe("乙巳日第五局　干上子", "课体：蒿矢，从革。", "课义：末助初鬼，尤赖蒿矢，俯丘仰仇，化合为美。", "解曰：子为昼贵临干，而末传丑财，助初传之酉鬼，以生子水，是谓引鬼为生。尤赖蒿矢无力，虽遇金有镞，在第四课遥克，更无力也。况酉鬼坐丑之墓，临巳之克，为俯丘仰仇，不能为害。则三合成局，适足为美矣。", "断曰：遥克之卦，局成从革，虽曰凡事无力，祸福俱轻。然祸中隐福，害里生恩。目前主有虚惊，过后渐逢好境。鼎新革故，苦尽甘来，贵循理守分而已。", "天时：先燥后雨，变歉为丰。家宅：主妻有喑□之疾。功名：先难后易，得人扶助。求财：不求当自来。婚姻：迟合。胎产：主女胎，防□。疾病：迟瘥。失脱：勾克元可以捕获。出行：东北方吉。行人：主军役吏人，蓦然至门。争讼：用财者胜，得贵人扶持。兵战：连和。坟葬：可发贵。", "毕法云：众鬼虽彰全不畏，贵虽坐狱宜临干。指掌赋云：酉巳丑为操会，已过受时岂失宜。指南：丁卯年辰将，占兄弟乡试曰：蒿矢见金，如箭有镞。朱雀翱翔，贵临贵位，必得两贵周旋推荐，兄弟二人皆中。午命者在前，亥命者在后。榜发果验。冲虚子经论云：巳来酉上，乃是配字，如逢恶煞恶将，必主配军。"));
        set(11, new LiuReKe("乙巳日第六局　干上亥", "课体：重审，四绝。", "课义：仕宦畏逢，常人免凶，夜休投贵，昼稍中庸。", "解曰：申乃官爻，今居末传，初午克之，中丑墓之，是为无气，仕宦之所忌也。常占则为讼宽病减之象，可免乎凶。夜贵入墓，投之岂有所益。昼贵临巳，虽得三传递生，但冲初用而克宅神，犹不失为中平者也。", "断曰：重审之卦，利下不利上，宜后不宜先。虽发用脱干无力，然渐次而进，自近及远。虽迟有成，昼将贵人临支。虽克支而实生干，干又上临长生，凡占皆宜坐守，自尔称心。", "天时：晴雨相间，晚种得利。家宅：好交贵客，必致耗破。功名：迟吉，主众人推荐。求财：利息轻。婚姻：迟合有喜。胎产：胎神临绝，占产立下。疾病：主心疾，迟愈，无妨。失脱：可得，逃亡自归。出行：宜投谒贵人。行人：归迟。争讼：得贵调解。捕获：贼在西北，是军吏之类。兵战：我胜。", "毕法云：三传递生人举荐。课经云：干生初恩也。初生中，中生末，末却克干，是为恩多怨深。古鉴：庚戌生人，戊申午将占讼，曰：本是四绝，不意亥子又来生日，绝中得起。子为乙母，财临子孙，主得母家产业。申为乙祖，丑为申墓，应筑作祖坟。本命庚戌，亦墓于丑，故已亦作生圹于旁。申为僧，见贵人，生子当作大善知识，后一一皆验。"));
        set(10, new LiuReKe("乙巳日第七局　干上戌", "课体：反吟，无依，元胎。", "课义：昼虎防脱，亥水难托，夜被欺诈，奸私见约。", "解曰：初末巳乘昼虎，脱干之气，占人必有虚弱之患。全恃亥水长生，冲克制之。无奈火胜水少，又被干上戌土来克，自救不暇，难以托庇矣。夜占初末乘元，神脱将盗，焉免欺诈之弊。况与中传之六合，相比为奸，如私约也。", "断曰：无依之卦，四绝元胎，反覆呻吟，伏而不通之象。加以魁罡临于日之两课。巳亥冲击，夜元昼虎，仇恶相攻，风波百出矣。干上财乘庚戌，昼夜雀神，止可以财献纳，不利商贾营生。如孟问财，必滋口舌。", "天时：雷电风雨，顷刻万状。家宅：防有奸私惊恐之事，又巳虎主木器，上有锁损。功名：反覆无成。求财：财或自来，得之恐生口舌灾咎。婚姻：不用。胎产：胎动不安。疾病：主胎病，或小儿咽喉之症。失脱：昼占逃者远去，夜占失脱重叠。出行：不利舟车，既出复回。行人：立至，又复有事他往。争讼：中立无患。捕获：昼在西方，有妇人作合；夜在西北，有孩子作合。兵战：宜慎之。", "毕法云：将逢内战所谋危。纂要云：反吟巳亥，巳多主改动，取索财物文章事。又反吟四孟，为绝元胎，蛇惊虎伤，见皆不利。指掌赋云：将克神为外战，灾自外来；神克将为内战　祸由内起。中黄经云：马见巳亥在路岐。"));
        set(9, new LiuReKe("乙巳日第八局　干上酉", "课体：重审，寡宿，励德。", "课义：克身墓宅，昼传夹克，动意难已，凶里财得。", "解曰：乙克于酉，巳墓于戌，主身灾而宅昏也。昼占三传皆受夹克，万事不能由身。辰上河魁为斩关，中传未为旬丁，动摇之象，自不能已。酉寅未子，皆从干上迤逦克去，如依倚酉之官势而取其财，非凶里得财而何。", "断曰：重审之卦，事多不顺，三传递克，必有众人欺凌，以下犯上之象。干支上神六害，彼己怀猜。寡宿发用，主有别离。夜占空上逢空，尤主无成，方外占之则吉。", "天时：先风后雨。家宅：主昏晦，防奸私。功名：先难后利。求财：恐致灾咎。婚姻：不合，合终乖异。胎产：占胎防损，占产甚易。疾病：见凶有解。失脱：系文书之属，申日获。出行：先涉惊险，后得利益。行人：先有信来。争讼：先负后胜。捕盗：当是本家奴仆，或西方僧道之类。兵战：防众心不附。", "毕法云：三传递克众人欺，夫妇芜淫各有私。课经云：干上酉被巳克，支上戌被乙克，乃干支互克，为解离卦。指南：己巳年戌将，一官占出差，曰：禄临戌上，似当得北差。然守土论禄，钦差论马。今驿马长生居午，必得南差。曰：明日拈阄，先后孰利。答曰：后拈利，盖初中空亡，末见贵人生日故也。明日果他人先拈，得大同解饷。而此官次拈，得九江钞关。"));
        set(8, new LiuReKe("乙巳日第九局　干上申", "课体：重审，从革。", "课义：交合虽逢，鲜克有终，夜占散祸，昼卜屯凶。", "解曰：辰与酉合，巳与申合，交车相合，交关则利矣。但申酉比而为鬼，以克日干，故为鲜克有终之象。夜占天将，蛇火克酉，龙木克丑，元水克巳，全局受制，且贵德临身，足以散鬼之祸。昼占则将受神克，且纯金带虎，其凶愈甚也。", "断曰：重审之卦，局合从革，先天后革，有阻隔艰难之象。金局克干，众人欺侮。夜占虽可消凶，而格合狡童，须防奸慝。昼夜贵人，临于于之两课。幸干上所乘德神，纵有众鬼，全然不畏。讼宜告贵，病可求神。", "天时：昼占燥旱，夜占有雨。家宅：昼占防有奸私，夜占防有惊恐。功名：主得人推荐，但中墓末克，防有后阻。求财：利以资财告贵，或纳粟求官。婚姻：不宜合。胎产：胎安产不利。疾病：夜占有解，昼占凶，尤不利小儿。失脱：勾神克元可寻。出行：利上官赴任。行人：即至。争讼：我得贵助，虽凶终解。捕获：昼占西南方，喜事孟宴；夜占正西方，女人有惊。兵战：宜和。", "毕法云：交车相合交关利，合中犯煞蜜中砒，贵虽坐狱宜临干，鬼乘天乙乃神o。课经云：酉加巳发用，将得六合，亦曰三交课。指掌赋云：酉丑巳为献刃，远近俱被其伤。又云：末生中，中生用，名荣盛而多人推荐。"));
        set(7, new LiuReKe("乙巳日第十局　干上未", "课体：知一，稼穑，游子。", "课义：携钱随鬼，纳粟偏宜，孟求平善，请祷神o。", "解曰：三传皆财，生起支上之鬼，祸患必从宅中而出。若贪财无厌，鬼即随之。惟宜以财求贵，纳粟奏名，则为帘幕临宅，可显门闾也。占病为鬼天乙，必是家堂神像不肃。宜虔心祈禳，方获平善。", "断曰：知一之卦，恩中有害，信中有疑。与人共事，和衷则吉。墓神乘丁，覆日发用。夜将塍蛇，至为凶恶，反赖中刑末冲，以杀其势。如年命在未，名为天罗自裹，其凶更甚。或年命上神有制，即不为咎。", "天时：阴晦蒸湿。家宅：主家神作祟，或恶犬吠人，四月占有凶怪。功名：纳赀得官，或告贵得升擢。求财：灾随财至。婚姻：昼占可合，然终有刑克。胎产：胎临败克，防损。疾病：主脾症湿症，占父母病大凶。失脱：当于西南土中寻觅。出行：远游漂泊，遇贵得官。行人：流荡难反。争讼：主为田土之事，以财告贵得胜。捕获：贼倚势家，捕之无益。兵战：宜屯田固守。", "毕法云：干墓并关人宅废：传财化鬼财休觅。纂要云：干上未，四月占，为月厌、大煞、天日、飞廉，况属丁神、墓神，若并临年命，为凶怪格。玉成歌云：墓神加日身灾滞。又云：日鬼加临辰两课，定然官吏到门庭。"));
        set(6, new LiuReKe("乙巳日第十一局　干上午", "课体：重审，间传，涉三渊。", "课义：两贵虽值，俱不可恃，所费百出，住宅移徙。", "解曰：夜贵发用，昼贵作末，两贵虽然相值，然申临午遭败，子入狱被克，俱不足恃。干上午，支上未，干支皆为上神所脱，故耗费多。未受午生，巳受乙生，宅脱人气，为宅旺人衰，作速迁移。", "断曰：重审之卦，顺进间传，凡事始逆终顺，难中生易。虽涉渊见阻，勾留不遂，恼悔相仍。然动必有合，任其自然，终无大咎。墓临支上乘丁，如经商贩卖，必在彼处受人愚弄。孟弃难舍，孟求不得，所谓支坟财并旅程稽也。如占宅舍，必有怪物现形，梦魂惊悸。二月占之，尤为不吉。", "天时：久晴后雨。家宅：贫耗不宁。功名：遇而不遇。求财：轻微。婚姻：可合，恐因娶而耗家。胎产：胎安，昼占可生贵子。投谒：不得力。疾病：虚损有救。失脱：时时不免。出行：近行安，远行有阻。行人：未至。争讼：宜和。捕获：昼在南方，夜在北方。兵战：将无权，防粮储耗散。", "毕法云：支坟财并旅程稽，脱上逢脱防虚诈，两贵受克难干贵，罡塞鬼户任谋为。课经云：干上午脱，昼乘天空，名脱空神。凡占皆无中生有，全无实迹，不足取信也。又未加巳临宅，巳为塍蛇，亦为两蛇夹墓，主凶。指掌赋云：申戌子为涉三渊，当隐于山林。"));
        set(5, new LiuReKe("乙巳日第十二局　干上巳", "课体：弹射，进茹，不备，蓦越。", "课义：泥弹化石，射之有力，止宜守困，前迫后逼。", "解曰：弹射逢土发用，便为有丸。况未乘昼虎，中末俱金，是泥丸化石矣。四课虽远射之，必有力也。若贪此未墓之财，因而引入申酉鬼乡，为祸不浅。况退而逢空，进而被克，前后逼迫，岂不难乎。不如坐守得支加干，虽为脱气，犹有天将青龙六合，同类相助，亦能制鬼，可免灾殃也。", "断曰：遥克之卦，顺进连茹，事多忧疑，进中有退。况四课不备，二阴争阳，未免伤于缺陷。财动生官，惟仕宦占之大为吉兆，常人遇此，必有蓦然之惊。辰临日脱日，主有虚耗遗失。惟宜甘心守困，不妄进退，斯灾去福生矣。", "天时：忽风忽雨，有时晴霁。家宅：主口舌，自西南而至，忌留宾客。功名：大利，或因财而得。求财：快意，然防是非。婚姻：不吉。胎产：主生贵女。疾病：见凶，宜慎饮食起居。失脱：自己懒寻。出行：利东北，不利西南，宦游反是。行人：路有惊恐。争讼：为争财而起，重重见官。兵战：宜慎。", "毕法云：传财化鬼财休觅，前后逼迫难进退。曾门经云：此射虎伤人格也。诀曰：猛虎来相害，张弓免祸殃，心忙手未稳，却把别人伤。指掌赋云：未申酉为回春，若午夜残灯，盖因东南之气减故也。"));
    }
};
