public class d42 extends KeEntry{
	public d42() {
        set(5, new LiuReKe("丙午日第一局　干上巳", "课体：伏吟，元胎，励德。", "课义：禄财及生，昼总无成，始如花锦，后若浮萍。", "解曰：初巳日禄也，中申日财也，末寅长生也。乍看三传，如花似锦。岂知昼将巳乘天空，申乘元武，寅属空亡，好事无而恶事有。若萍梗之浮泛，讫无成就而已。", "断曰：元胎之课，禄马交驰。若寅年月建占之，大是休征。但巳为破碎，而羊刃坐干支上，不无刑伤破耗。况伏吟自信，乃伏而未发之象。凡占动则滞，静则享也。末传逢空，凡事多无结束。", "天时：四课发用纯火，大晴之象。家宅：长生落空，防长上灾，课得元胎，家中应有孕妇。功名：传中有禄有马，应主迁职改官。求财：干支同类，恐有争夺。婚姻：干支比和，男女和合，恐难偕老。胎产：胎上神属阳，主生男。疾病：症属心经，占父母病不吉。争讼：宅带羊刃，主有刑责。出行：旺禄临干，羊刃临支，末复逢空，家居胜于出外。行人：天罡加季，行人立至。兵战：宜进不宜退。", "毕法云：宾主不投刑在上，旺禄临身徒妄作。课经云：伏吟卦六丙日，有禄有马，惟忌空亡。天官汇函云：天官临巳，主有水虫。勾陈临巳，为管钥神。若占囚禁，则有出狱之象。玉成歌云：德神动处吉相随。又云：伏吟举动心无遂，刚主行人到户庭。"));
        set(4, new LiuReKe("丙午日第二局　干上辰", "课体：元首，退茹，不备。", "课义：脚踏空亡，休恋生方，向前一步，食禄荣昌。", "解曰：三传虽是生气，但皆空陷，见生不生，不如无生。若恋生则脚踏空陷，凶不可言矣。惟进前一步，俯就支上旺禄，乃得荣昌也。", "断曰：退茹之课，值空被脱，主有虚耗之事。魁度天门，又名不备。事多阻隔，亦未周旋。天罡临干，人难安享。日禄临支，官宜佐贰，或分俸家食，或因人成事。若孟独断独行，非其占矣。", "天时：雨师退空，主晴。家宅：德禄守支，占宅甚吉。功名：退空猛力，庶得前程。求财：费力。婚姻：退茹，四课不备，不成。胎产：二阴包阳，孕乃成男，支上脱干上，占产必易。疾病：火症、齿痛、呕血，不妨。争讼：昼占勾空，夜占勾与干比，宜和。谋望：退空，则进有可图，但不无费力。出行：结伴同行，庶免阻滞。行人：驿马临门，即归。捕获：昼占贼在坤方酒饭店中。兵战：退无可守，宜进，攻其不备。", "毕法云：权摄不正禄临支，脚踏空亡进用宜，来去俱空岂动移，魁度天门关格定。指掌赋云：卯寅丑联芳悔吝，须知否极泰来。精蕴云：卯寅丑皆空，占父母长上病最不吉，惟占子息病无畏。占讼理亏，必问官不肯主张，总缘生我者空亡故也。"));
        set(3, new LiuReKe("丙午日第三局　干上卯", "课体：重审，退间，极阴。", "课义：生空变败，与人六害，夜贵惠财，昼贵难赖。", "解曰：火生干木，而败于卯。今所生既空，止留败气，故变为败，况又与支上辰作六害乎。末传夜贵乘财，得拜贵人之惠。中传昼贵坐丑受克，不足赖也。", "断曰：间退之课，事有疑阻，卦名极阴，利阴谋而不利公干。支乘实脱，干乘空生，凡事止有美名，终无实济。朱雀安巢，且闭口落陷。一切文书，占之不吉。末传贵财，克日上神。若逢旺相年月，当发大财。", "天时：水爻坐克，浓阴无雨。家宅：干支上神六害，人宅不宁。功名：官爻坐克，青龙落空不利。求财：宜借贵人之财，营运可以得利。婚姻：夜占支上天罡白虎，女子性恶貌丑，且与干上六害，不宜成。胎产：干支上神六害，女孕，母恐不保。疾病：肾经受病，末传死酉，冲克干上空木主凶。谒贵：当于阴贵人家见之。谋望：事干两贵。出行：驿马投墓，未必果行。行人：未回。兵战：不利。", "毕法云：空上逢空事莫追，彼此猜忌害相随，昼夜贵加求两贵。指掌赋云：丑亥酉为极阴，如月隐西山。壬髓经云：当门台土，出行有阻。纂义云：空来助力，仲春方吉。过此之时，更变不一。占验云：辰乘虎临支，主移居。"));
        set(2, new LiuReKe("丙午日第四局　干上寅", "课体：蒿矢，三交。", "课义：四课无形，生如不生，遥克既陷，好恶无成。", "解曰：寅卯旬空，亥子落空，四课无形也。寅卯俱生气，既系旬空，则不能生矣。亥子俱鬼，遥伤日干，而陷空乡，则不能伤矣，所以好恶皆无成也。", "断曰：蒿矢之课，祸福本轻。况四课全空，毫无实像。唯宜解散忧疑，如望成事，改旬再谋可也。中传财坐鬼乡，末为羊刃，不惟事无成就，抑且伤财。经所谓费有余而得不足者是也。", "天时：水爻落空，无雨。家宅：长上宜慎起居，财物不免虚耗。功名：官爻空陷，防有不测。求财：财见鬼乡，求之无益。婚姻：不吉亦不成。胎产：子母皆空，难保，无益。疾病：新病即安，久病难治。逃亡：往北方寻，难获。出行：未必成行。行人：耽恋淫u，未想回家。兵战：有失众之象。", "毕法云：费有余而得不足，空空如也事休追。课经云：寅加巳夜将乘青龙，三月占，青龙乘生干之神，又作月内生气，主徐徐发福。所谓龙加生气吉迟迟也，惜是空亡，不能久远。曾门经云：三交相因，家匿罪人。玉成歌云：三交吉凶皆因内。纂义云：子加卯为用，夜乘六合，主奸淫口舌之事。"));
        set(1, new LiuReKe("丙午日第五局　干上丑", "课体：重审，炎上，u女，三奇。", "课义：自墓传生，遁甲戊庚，得意浓处，掉臂长行。", "解曰：戌者丙之墓，寅者丙之长生。三传戌午寅，是自墓传生也。以五虎元遁，则戌遁戊，午遁甲，寅遁庚，是谓三奇，卦之最吉者。但三合相会，而午丑相害，乃合中犯煞。凡事得意浓处，即当见机，掉臂而行可也。", "断曰：炎上之课，光焰升腾之象。遁得三奇，更能化凶为吉。占者始虽抑郁，终必亨通。但交车六害，丑实寅空，且中传羊刃，昼乘白虎，害干上之丑，而末传又是空亡。宜知进知退，不可妄想贪多，自贻伊戚。", "天时：火空则明，当是大晴。家宅：支上长生，将带龙合，春占主有财喜。功名：昼占朱雀画翔，帘幕入课，考试必中。求财：不宜贪多。婚姻：女性淫悍，不能齐眉。胎产：胎上神属阳，孕必生男，支上神临空，产必甚易。疾病：肝经受病，或头疼腹痛，新疾即痊。逃亡：斩关，必已远去。出行：陆路不吉，水路甚佳，俟旬空填实更佳。行人：天罡加孟，尚未启行。兵战：我实彼空，我胜于敌，但勾陈生元，将不得力。", "毕法云：尊崇传内遇三奇，干墓并关人宅废，合中犯煞蜜中砒。纂义云：传来炎上又逢空，夏月占之妻妾凶。指掌赋云：戌午寅为就燥，行合中庸。要览云：戌乘天后临寅，奴婢逃失。"));
        set(0, new LiuReKe("丙午日第六局　干上子", "课体：知一，四绝。", "课义：三传内战，昼占灾殄，告贵徒然，交关眷恋。", "解曰：三传皆是下克，谓之内战。既内战矣，而昼占蛇鬼发用，岂能免于灾殄乎。昼贵入狱，夜贵坐空，是告之无益也。干支上神子丑相合，则眷恋之情，乌能已矣。", "断曰：知一之课，事宜专一，不能两就。三传迤逦伤干，必有众口攒攻。本为凶象，喜干上子鬼作初，却生末传寅木长生，谓之引鬼为生。而支上之丑，又能救之，夫复何畏。但末传寅空，所忧者文书无力耳。", "天时：神后临干发用，云浓雨小。家宅：用神与支冲克，若非移居，或主修造。功名：干支遇临官帝旺，爵禄峥嵘之象。求财：专心求之可获。婚姻：干支上神六合，可成而吉。胎产：二阳包阴，孕必生女，子与母合，占产不利。疾病：脾胃受病，难于速愈。争讼：干上子鬼发用，主胜客负。出行：水路佳。行人：人宅上神相合，可以即归。捕获：盗神是卯，在东方竹木丛中，近寺观，有舟车往来之处。兵战：捷音可奏。", "毕法云：众鬼虽彰全不畏，制鬼之位乃良医。山阴道士云：丙日胎神在子，正月占，非妻孕则妾孕，乘元则主私孕，如空则鬼胎。课经云：丙日胎神在子，养神在丑，正月血支血忌皆并，养神克胎神，占产生速，占胎有损，十二月占亦然。"));
        set(11, new LiuReKe("丙午日第七局　干上亥", "课体：反吟，三交。", "课义：守皆遇鬼，交互不美，壬子居中，仕宦宜矣。", "解曰：干被亥克，支被子克，守之则皆鬼也。子又克丙，亥又克午，非交互不美乎。中传之子，旬遁得壬，官星最旺，仕宦得此为吉，常人岂其宜乎。", "断曰：反吟之课，坎离交易，凡事反覆无常。午为羊刃，见于初末，喜子水克制，虽凶不凶。但夜占乘武，问财大忌。如求官见贵，须正七四十月，子午为天马方吉。", "天时：水上火下，主雨，若久雨则反晴。家宅：昼占天乙克支，宅神不安，日鬼临支，宅有鬼祟。功名：雀鬼临干，防有弹劾。婚姻：干支上神交克，不可成婚。胎产：三传俱阳，干上属阴，孕当生女，干支比和，产迟。疾病：巳午加亥子为寒热格，主痨病或心疼，须防反覆。逃亡：夜占可获。失脱：赃物未动，中传制元，贼当败露。出行：日辰俱乘官鬼，水陆皆恐不宜。行人：夜占，先有信至。兵战：主强宾弱。", "毕法云：鬼乘天乙乃神o，干支全伤防两损，夫妇芜淫各有私。课经云：亥加丙为明鬼，支上神遁旬内之壬为暗鬼。又支干各受上克，各坐克方，是为全伤坐克。常问必主病讼，如已见凶灾，反宜结绝旧事。心镜云：无依是反吟，逃者远追寻，守官须易位，结友也分襟，所为多重覆，占病两般侵。"));
        set(10, new LiuReKe("丙午日第八局　干上戌", "课体：六仪，斩关，比用。", "课义：两蛇夹墓，凶灾可恶，干鬼临支，长生木蠹。", "解曰：戌为丙之墓，昼夜乘蛇，而巳又为蛇之本宫，故曰两蛇夹墓，临于干上，凶晦之象也。干鬼临支克支，幸得天罡发用，制鬼破墓，庶为救神。奈末传寅木，复往克之，是凶灾在戌与亥。而戌与亥之得以肆行无忌者，诚在寅也。然则寅虽为干支之长生，宁非干支之蠹神哉。", "断曰：旬首发用，名曰六仪。凡百谋为，我心实获。但中传酉财，昼占乘朱投网，恐因财而生祸。末传寅空，有始无终，必出旬填实，方为有济。", "天时：水升火伏，亥鬼又克干支，必雨。家宅：昼占贵乘鬼坐宅，宅神不安，墓神覆日，人多昏晦；夜占朱雀入宅，恐有官非口舌。婚姻：干支上神，及龙后相克，不吉。胎产：干克支，凶。疾病：两蛇夹墓，应有积块，以致不救，或患头疯。争讼：干贵入狱，当被囚禁，恐难脱免。行人：朱雀入宅，主先有信。兵战：干乘墓，支乘鬼，主客皆凶。", "毕法云：鬼乘天乙乃神o，两蛇夹墓凶难免。指南：二月戌加巳，有陈蔡两姓占推升总戎曰：先推陈，后推蔡，盖亥命属陈，辰命属蔡。今亥贵作官星临支，而辰阴发用，故知先推亥命者，而次及辰命者耳，果验。玉历经云：凡有求干，必至两三次方成。"));
        set(9, new LiuReKe("丙午日第九局　干上酉", "课体：重审，从革，斩关。", "课义：两贵心灰，夜将助财，禄为破碎，宅惹尘埃。", "解曰：昼贵临未，夜贵临巳，俱被下克，是以心灰。夜将皆土，助起全金，作日之财，似可贪也。其如巳禄为破碎，墓又覆宅，徒费经营，终归乌有，不过空惹尘埃而已。", "断曰：从革之局，传财太旺，反致财亏。即有奇赢，亦横暴所得，非义取也。寅为长生，夜占带虎入墓，加以旺财来克，恐于父母不利。三合成金，俦侣不一，利于合伙营生。", "天时：晴占则阴，雨占则晴。家宅：支上坐墓，昼带蛇，鬼祟作耗，人宅不宁。功名：昼占朱雀昼翔，帘幕临干发用，末传德禄递生，考试甚利。求财：可得。婚姻：财旺可联。胎产：胎坐长生，占孕吉，产不利。疾病：死神临干发用，支上墓神复作六害，甚凶。争讼：昼占官爻夹克，勾乘日解，临门可解。出行：宜结伴行。行人：朱临干发用，不日有信。兵战：戌乃墓神，酉复来害，利我不利彼。坟葬：穴情龙气甚旺，但恐费财消耗。", "毕法云：两贵受克难干贵，彼此猜忌害相随，合中犯杀蜜中砒。课经云：戌加午是墓神覆宅，如戌为月将，却名太阳照宅，屋必向阳，事多吉庆。占验：子将加申，占睢州被围。曰：课传从革，刑干害支，春占金局，乃返射肃杀之气。戌命长生被克，全无化解，干乘死气，支乘墓神，又干支年命上神俱遭刑克墓害，被戮何疑乎，果验。"));
        set(8, new LiuReKe("丙午日第十局　干上申", "课体：元胎，比用。", "课义：昼贵鬼魔，夜贵相过，财马入课，荐我者多。", "解曰：亥为昼贵，乃日之官鬼，是鬼魔也。酉为夜贵，作财临支，必有阴贵相过而惠我者矣。干上申金发用，为财为马，又兼三传递生，荐我者岂不多乎。", "断曰：知一之课，事多未决。六合乘马，自干上发用作财。中传官爻，末传长生，占官者百事咸宜。末寅虽被初克，却为明克暗助，大得攀援之益也。但寅值旬空，必须填实，方为全美。", "天时：水坐长生，罡又指阴，必雨。家宅：人宅平安，但解离课，夫妻恐有反目。功名：帘幕出传，传又递生，考试必中。求财：远行经运有利。婚姻：不吉。胎产：胎上神阴，主女；胎神坐于死地，干支互克，产凶。疾病：肺经受病，干支比和，难愈。争讼：干支互克，六合发用，终当和解。出行：驿马则财，动则获利。行人：昼朱乘丁，夜朱入宅，有信。捕获：昼占贼在东南土窑中。兵战：胜负各半。", "毕法云：夫妇芜淫各有私。课经云：干上申为解离课。占者如夫年立午上见寅，妻年立子上见申，乃上下互相克贼，天地解离，各有异心。指南：三月酉将加午，占真定城池安危。曰：初传财爻内战，又乘相气冲克空末，干支被两阴神所克，支又克支上神，主居民心散，兵马内变，左右献城之象。且末传寅为幽燕，初传申马冲克，燕京亦有他虞，俱验。"));
        set(7, new LiuReKe("丙午日第十一局　干上未", "课体：重审，间传，涉三渊，死奇。", "课义：支马干丁，客主欢迎，鬼呼病者，动静无成。", "解曰：支上申为驿马，干上未为丁神，丁马在课，其动也必矣。又午未巳申，交车相合，主客欢迎之象也。但中传戌墓，末传子鬼，如鬼在墓而招呼者然。占病不宜，静则为未所脱，动则引入墓鬼，或动或静，岂有成乎。", "断曰：间传而进，事有阻滞。交车相合，彼此相投。但卦名涉三渊，必因财而动，以致昏迷，所谓利令智昏也。", "天时：水爻坐克，密云不雨。家宅：日辰生合，人宅安宁。功名：官爻入墓，养晦之象。求财：不宜贪而多取。婚姻：天将不吉，婚非佳偶。胎产：胎神坐墓作鬼，占胎不安，子恋母腹，占产不吉。疾病：甚凶。争讼：六合乘马发用，有人讲和。盗逃：元武入墓，干上勾复制之，可获。出行：水陆咸宜，利有攸往。行人：驿马入宅即归。兵战：干上生支上，马作合临支发用，当有使来议和。", "毕法云：初遭夹克不由己，后合占婚岂用媒。心镜云：天上三奇日月星，日为福德月为刑(日奇，月将也。月奇，月宿也。日出，则奸盗止，鬼神潜，恶兽伏，病者愈，故为福。月夜则奸盗不止，鬼神不潜，恶兽不伏，故为刑)。星是死奇为北斗，更互加之各有灵。加孟所生忧父母，临仲为身及弟兄。季上见之妻与子，并辰月内日应旬(奇加日，凶吉在旬内，加辰在月内)。岁上吉凶言一载(奇加太岁，吉凶在年内)，临死日照免危倾。"));
        set(6, new LiuReKe("丙午日第十二局　干上午", "课体：弹射，进茹，不备。", "课义：午未禽财，妻妾怀胎，食伤治病，胃腕宜开。", "解曰：从魁作朱雀，则为禽财。午未皆火，酉其财也，主有博斗之利。申为妻，丑加子为腹胎，故来意必占妻孕。三传重金，传财太旺。占病则因饮食所致，故治宜开胃也。", "断曰：进茹之课，干支上乘罗网，虽名壮基，凡事静守则宜，动则变为网刃。遥克弹射，申金发用，支干及初，俱在合乡。而末传又助初财，凡占婚孕财帛，皆吉。", "天时：火上水下，主晴。家宅：四课财神发用，交秋获财。功名：中传酉，昼夜将临朱贵，可白衣而入翰林。求财：传财甚旺，可得。婚姻：可成。胎产：胎上神属阴，孕女。干支相合，产迟。疾病：伤食淹缠。争讼：六合而官不见，宜和。出行：人宅相合，未能动身。行人：末传入墓，行人即至。盗逃：勾阴制武，可获。兵战：主利。", "毕法云：所谋多拙遭罗网，全财病体难担荷。课经云：丙与午，本相邻近，上神又作六合，凡占主有变换。彼我共谋，神和道合之象。秘要云：丙午日干支同类，不宜问财，以比肩劫财故也。壬髓经云：三传皆财忧父母。指掌赋云：申酉戌曰流金，似霜桥走马。"));
    }
};
