public class d43 extends KeEntry{
	public d43() {
        set(7, new LiuReKe("丁未日第一局　干上未", "课体：伏吟，稼穑。", "课义：处静而动，奴仆休宠，夜嫁娶费，行人继踵。", "解曰：初并干支丁神，共有三重。伏吟得此，处静而动。戌墓在末，恃势刑干。戌为奴仆，乘天后厌翳，故休宠之也。太常天后脱日，必主婚姻喜庆嫁娶之费。墓在末传，行人归速，不旋踵而至矣。", "断曰：自信之卦，上乘丁神，占者有不能郁郁久居之象。但三传逼迫，全无和气，谋望未能如意。昼占当有宴会，夜占当有文书。若丑月占，太常乘月破，有孝服人，可以获财成事。武职人占更吉。", "天时：伏吟有丁，阴晴不定。家宅：宅上不安，昼占有孝服，夜占防火烛。功名：守静安分为佳，妄动有损。求财：可得麦舟之赠。婚姻：传内三刑不吉。胎产：男孕产迟。疾病：病因伤食，翻胃吐逆，然可治。争讼：彼此不利。出行：三传不吉，动不如静。行人：先有信，不久当归。遗失：不出本家。捕获：不出邑里。兵战：彼我相当。", "毕法云：任信丁马须言动。诀要云：日辰刑冲，事成恍惚。指掌赋云：三传纯子孙，不求财而财自至。玉成歌云：伏吟举动心无遂，刚主行人到户庭。"));
        set(6, new LiuReKe("丁未日第二局　干上午", "课体：八专，帷簿。", "课义：四课四虎，受惊受惧，费力千般，未免如故。", "解曰：四虎列于四课，其惊与惧，不可言也。初传空卯，乃日之败气，无中生有，经营可谓费力矣。然且不能行传，而复归于日上。则向之惊且惧者，其能释然于中乎，则亦犹然如故而已。", "断曰：八专之课，事情专一。干支上神，俱属自刑。凡事谨饰，不可以无礼相干。旺禄临干，虽昼占乘虎，而受克于午火，复夹克于丙火，纵有可畏，不能为害。惟宜素位而行，自当丰亨豫大。", "天时：课传皆火，大晴。家宅：旺禄临支自刑，昼占带虎，防有兄弟丧。功名：禄带白虎，守静方吉。求财：宜守本分，久后利益。婚姻：夫妻难免刑伤，不宜成。胎产：男孕，恐子母皆损。疾病：心经受病，恐入膏肓。争讼：两造相当，事当和息。出行：水陆皆所不宜。行人：不久当归。捕获：元武克勾，捕盗反有所伤。兵战：彼此相持，无分胜负。坟葬：支上午禄，带虎自刑，子孙当出武臣，不免刑戮。", "毕法云：权摄不正禄临支，魁度天门关隔定，旺禄临身徒妄作。精蕴云：卯为旬空，夜占乘空，亦为空上逢空，凡事不能作实，仲春占之则吉。心鉴云：日值八专为两课，阴阳并值不分明，不修帷簿何存礼，夫妇占之总不贞。"));
        set(5, new LiuReKe("丁未日第三局　干上巳", "课体：八专，帷簿。", "课义：破碎遁鬼，四马载起，昼将如逢，传皆陷矣。", "解曰：丑为破碎，旬遁癸鬼而伤日干。中末干支，巳火四重，上乘驿马，其动不容己矣。然初丑陷于空卯，如逢昼将，巳乘天空，则三传俱陷，全无定踪。虽日夜奔驰，飘蓬萍梗而已。", "断曰：八专之课，逆数发用，本自无中生有，又陷空亡，益无凭据。初传脱气，与支刑冲。昼乘朱雀，必因口舌而生文书。夜乘勾陈，必因土田而致争讼。", "天时：传课皆火，大晴。家宅：主有动移之事。功名：干支中末，助初克官，防有丁艰黜落。婚姻：帷簿不修，不宜成。胎产：孕主男，产不易。疾病：久病甚凶，新病即愈。争讼：彼此两损。出行：游都乘丁马临干支，水陆俱须防盗。行人：丑日有信。捕获：昼占在正西，夜占在正南。兵战：失众须慎。坟葬：破碎发用，支上复乘丁马，主有移阡修改之事。", "毕法云：空空如也事休追。课经云：天祸卦，立冬日是丁，前一日是丙，丙为金绝之末日。丙课在巳，丁日占事，未上见巳，是末日临也。观月经云：四立干上神，分明末日临；此名天祸卦，乖角竟相侵；火动烧人死，水临劫盗深；木因梁屋事，土动为争论；金则兵戈乱，闹处起衰声。"));
        set(4, new LiuReKe("丁未日第四局　干上辰", "课体：八专，帷簿，斩关。", "课义：火生四土，夜将白虎，昼贵难靠，宅昏人苦。", "解曰：一丁火而生四重辰土，夜乘白虎，其脱耗惊恐，何可当也。初传昼贵，又陷空乡，兼被寅脱，力绵难靠。支被墓，故宅昏；干被脱，故人苦也。", "断曰：两课无克，是名帷簿不修。官贵空陷，夜占又被辰虎克制，利常人不利仕宦。天罡昼乘青龙，课合斩关，利于远行，当主西南得朋。初传虽财，中末子孙脱盗，求财不吉。德神夜乘朱雀，占音信必至。", "天时：青龙升天，主雨。家宅：辰虎墓支，人宅不宁。功名：官星落陷，禄复逢元，难免折挫。求财：利远动求之。婚姻：支墓覆宅临干，不吉。胎产：占孕成男，占产子母皆防有损。疾病：病主遗漏风瘫，难愈。争讼：两败俱伤。出行：利有攸往。行人：德神发用，夜占乘朱，先有信音。兵战：宜守不宜战。坟葬：支墓覆支脱日，主多耗散。", "毕法云：鬼乘天乙乃神o。指南：盛姓寅命辰将未时，占被逮进京，曰：盖因月将青龙，加临干支，勾陈生日，官鬼空陷，其事必辩雪，到京公讼自解也。又周姓占，亦得此课，曰：己丑命上见日墓，年乘三刑，与寅命相去甚远，乌能无罪，后周果赐死，盛以畔贼破城脱归。"));
        set(3, new LiuReKe("丁未日第五局　干上卯", "课体：元首，曲直。", "课义：昼将脱干，好恶中半，勿恋传生，宜更宜换。", "解曰：三传木局生干，昼将皆土脱日，赖有发用卯木克去，故曰好恶中半。但初中及干上卯木空陷，见生不生，反为败神，败则何可恋也，是以更换为宜。", "断曰：传会三合，事关伙众。凡占顺利，始略有阻，盖曲直之义乃尔也。但三传不离四课，为回还格。事主循环，不能摆脱，生神既空，尊长不能见惠，贵德复陷，朋友不能相扶。虚誉沸腾，实情阒寂，", "天时：三合主阴。家宅：人旺宅窄，却难迁移。功名：日德作官，夜与龙合，填实便佳。求财：结伴经运为佳。婚姻：官坐空亡，支受卯克，不能偕老。胎产：传课纯阴，孕乃成男，三合贵顺，产迟生易。疾病：胸胁多风，久难愈，新即痊。争讼：先发者胜。出行：课体回环，不能成行。兵战：客利主不利。", "毕法云：空上逢空事莫追，眷属丰盈居狭宅。课经云：卯加亥，三传生日干而克支辰，是为人旺弃宅，主无正屋可居，纵然仕宦多是寄居，或孟逃亡，遂弃家室。照胆秘诀云：用空传复支与干，旧久重新废孟完。指掌赋云：卯亥未为先春，未萌先动非时过。"));
        set(2, new LiuReKe("丁未日第六局　干上寅", "课体：比用，孤辰。", "课义：昼贵临罡，夜贵空方，寅纵生身，熟视不臧。", "解曰：初传夜贵，投于绝而坐空。末传昼贵，临于罡而墓克。凡占干谒，皆为不利。干上寅木，虽是长生，奈系旬空，又被支墓，见生不生，熟视竟何益哉。", "断曰：财神发用，贵德入传，龙乘生气加干，目下虽属旬空，出旬填实，财亦可取，贵亦可望。但三传酉辰亥，皆属自刑，或恐自心懊恼，弄巧成拙。惟当坦然任运，不急功利，斯无咎矣。", "天时：昼占亥水夹克，夜占亥水内战，阴而不雨之象。家宅：龙合长生，临于干支，人宅y亨。功名：三传自刑，贵人入狱，禄遭夹克，被黜无疑。求财：难得。婚姻：干支相生比和，大吉。胎产：孕主男，产不易。疾病：肝胆脾胃受病，或目疼腹痛，久难治，新易瘥。出行：水陆皆可。行人：驿马入墓，未归。捕获：昼占于二十里外，土门擒之。兵战：客利主不利。坟葬：初年不利，后当发福。", "毕法云：龙加生气吉迟迟。占验：戌将卯时占病，曰：此手足不举之症，课传全无一点生气。因日禄临绝地，驿马投墓乡。又行年游魂，子巳相加，合为死字。三传死墓绝，安能有救乎。何以知其病在手足？因卯加申，戌加卯，故主风颠发搐之症。然则坏在何日，曰：久病应在卯字，卯加申，申日子时当绝矣，果然。"));
        set(1, new LiuReKe("丁未日第七局　干上丑", "课体：反吟，八专，井栏射，励德。", "课义：丁生四丑，凡占殃咎，四癸暗伤，惟宜闭口。", "解曰：一丁火而生四丑土，盗脱滋甚，凡占多咎。丑遁旬癸，暗伤丁火，惟能闭口，可以免祸也。", "断曰：井栏之课，卒属无依，发用巳为驿马。昼将乘空，丑乃破碎，遁癸作鬼，是明脱暗克也。昼占丑乘太阴，脱上逢脱，事防虚诈。若夜将则乘勾陈，共逢八土，忧事之来，岂一端哉。若年命在卯乘酉金，类成财局，或年月填实寅卯，可以制土泄水，主能化凶为吉。", "天时：晴占则阴，雨占则晴。家宅：宅旺人衰。功名：不利。求财：寅卯生人利。婚姻：女命过刚不吉。胎产：传课极阴，胎上神阳，男子之祥，占产不易。疾病：病在表，主气促伤残，愈而复发，淹缠不断。争讼：原被皆伤。出行：马临绝地，干支逢破碎，不利。行人：马乘空临绝，在外受人欺诈，往来无依。兵战：利主不利客，寅卯日可以偷营。", "毕法云：脱上逢脱防虚诈。课经云：丑加未，或未加丑，作年命日干者，占考试必中魁元。缘丑中有斗，未中有鬼，合而为魁故也。巳午加亥子，如或克日，为寒热格，主痨病。纂义云：年命在亥，主有血痢。巳乘天空，主病眼咽喉肿痛。"));
        set(0, new LiuReKe("丁未日第八局　干上子", "课体：比用，铸印。", "课义：克干畏支，狐假虎威，略举动足，踏翻祸机。", "解曰：子遁壬水，昼占又乘天后，并力伤干。赖有未土克害子水，不能伤日，故曰克干畏支。然则未犹虎也，丁犹狐也，止宜坐守，不利动谋。若丁神一离未土，即被子克，举足之顷，而祸机动矣，可不畏哉。", "断曰：铸印之课，凶事利空，吉事忌空。盖破模走炉，凡事无成也。三传传墓入墓，主有晦滞。如遇卯年月占，则卯不以空论，乃腾骧王路，进职迁官之象。", "天时：作雨势而不成。家宅：鬼合临支，人口不宁。功名：铸印乘轩，遇卯填实甚佳。求财：不利。婚姻：可成，但乾受坤制，妻必专权。胎产：男孕，恐防有损，占产必速。疾病：病主肾竭，卯年月占凶。争讼：鬼旺讼凶。出行：未必果行。行人：孟归而未果。捕获：盗神是未，当在西南方酒食店内，或是本家人。兵战：主利于客。", "毕法云：须忧狐假虎威仪，两蛇夹墓凶难免。课经云：胎神临于干支，正月占，非妻孕则妾孕。如空乘死气，必是鬼胎。天官论云：巳乘天空加子，灶破惊人；子乘六合临支，主有邪魔鬼祟。子乘六合加未，当有树影入井，致人灾异。"));
        set(11, new LiuReKe("丁未日第九局　干上亥", "课体：重审，曲直，回环。", "课义：昼将脱日，全赖传力，贵德三传，夜占何益。", "解曰：昼将皆土，盗脱干气。幸赖贵德临身，会起木局，以克制之。如夜占太阴，乘鬼发用，中末空陷，何益之有哉。", "断曰：曲直之课，先曲后直。干支上乘德贵，三传三合，凡占皆利。惟中末二传，旬空陷空，必须填实为妙。秋占则为真空，最为不吉。春占利于暗地托人，可以成事。格合回环，有心吴身越，不能奋飞之象。", "天时：传课皆阴，水爻发用，有雨。家宅：贵德在宅临干，万事和合。功名：官鬼作德贵发用，大吉。求财：春占得人周助之力。婚姻：干乘官，支乘财，门户相当，但恐不能偕老。胎产：男子之祥，产亦安吉。疾病：病在头面，支上日鬼作贵，家神作祟，宜祷禳之。争讼：先曲后直。谋望：出旬可成。出行：回环课，不行，行亦中途即返。行人：贵德发用，得意而归。兵战：主客强弱相等，主有不利。", "毕法云：鬼乘天乙乃神o，两贵受克难干贵，干支值绝凡谋决。课经云：子加申为胎坐长生，利占孕不利占产。纂要云：支干上临亥，皆是绝神，所谓干支值绝也，不宜占食禄事。干支相同，又为互绝，宜退换屋字，或兑替差遣，交代职任等事。"));
        set(10, new LiuReKe("丁未日第十局　干上戌", "课体：八专，帷簿，斩关。", "课义：昼墓救应，不幸中幸，反伤夜贵，幸中不幸。", "解曰：干支中末四戌，墓干刑支，诚为凶课，不幸孰有甚焉。然赖此以制鬼，反为救神，可谓不幸中幸矣。若夜占则又伤害贵人，非幸中不幸哉。", "断曰：干支同位，是名八专，有八家同井，诸侯会盟之象。两贵拱干，德神发用，虽墓覆干支，而旬遁庚财，刑开丑中财墓，若求财则可取也。占者凡事重叠，忧喜再来。戌加未乘蛇，西南坟墓有惊。戌蛇临支，酉月占主有血光。墓神克贵，动必昏晦。", "天时：水坐长生，有雨。家宅：墓覆干支，人宅俱晦。功名：成功异路，显擢士林。求财：宜得阴贵人之财。婚姻：帷簿不修，恐非良匹。胎产：胎上神阴，女孕，产易。疾病：腹痛脾泄，难愈。争讼：必见囚禁，方结。出行：只利家居。行人：未回，昼占有信。兵战：客胜于主。", "毕法云：华盖覆日人昏晦。课经云：凡八专遇后合元入传，为帷薄不修。盖重门树塞，以限内外。讲堂设帐，以别男女。今阴阳共处，男女混杂，又遇阴私之神，乃门墙生茨之兆。占验：庚午年丑将加戌占，曰：此占必是显宦，盖发用官贵日德，而贵人又居岁君日禄旺位，非寻常之官。惜乎干支乘墓，禄马空陷，太阳入山，岂能久任乎。果一大冢宰，于次年而请归。"));
        set(9, new LiuReKe("丁未日第十一局　干上酉", "课体：重审，间传，凝阴。", "课义：丁死于酉，破碎癸丑，两贵加临，事危闭口。", "解曰：丁火死于酉，而丑又遁旬癸，作破碎克日，极凶之象。虽两贵入传，且见于干支之上。所谓遍地贵人，贵多反无依靠矣。破碎为闭口，临危谨言可也。", "断曰：凝阴之卦，利小人不利君子，利私营不利公干。末助初财，主暗中有人相助。但干支皆乘自刑，虽化出德神，究为日鬼。人已交接之际，或恐因财起祸，或恐尚口启羞，致彼此各怀疑虑。惟占功名，不在此例。", "天时：卦象极阴，有雨。家宅：酉财入宅，临干发用，兴隆之象。功名：贵人带德大利，帘幕日德朱雀并见，考试必中高魁。求财：宜得贵人之财。婚姻：良缘可缔。胎产：胎乘元后，恐为私孕，占产不易。疾病：肺家受病，淹缠难愈。争讼：重审课，当覆审。出行：往东方吉。行人：即有信。捕获：昼占盗神是辰，主大眉粗须，著黄衣，渔猎人。兵战：主利于客，然干支皆乘死酉，大宜谨慎。", "毕法云：课传俱贵转无依，昼夜贵加求两贵，宾主不投刑在上。课经云：酉临年命日干，占试必高中。缘酉为幕贵，又为从魁也。指掌赋云：酉亥丑乃凝阴，而忧不可解。玉成歌云：昼夜贵入传共见，或同日德动高尊。"));
        set(8, new LiuReKe("丁未日第十二局　干上申", "课体：重审，进茹。", "课义：得财失财，戌月怀胎，伤风肺喘，讼绝复来。", "解曰：干支初中俱财，传入戌库，身旺财弱可求，财旺身弱反失，故为得财失财也。申者妻也，九月占生气在申，妻必怀胎。申为虎之本家，金属于肺，为火神夹克，故主伤风肺喘。连茹卦又末助初传，动止牵连，所以讼绝而复来也。", "断曰：进茹之课，干乘天罗，支乘地网，谋为不免蹇滞。初传申财，夜占则夹克，昼占则内战。况传课皆财，旺则化鬼，须防因财得祸也。末戌乘后，临于死地。若占女人夙疾，必应增剧。凡占主旧事重新，蔓延不绝之象。", "天时：财旺主阴。家宅：春夏占财喜，秋冬占人灾。功名：财旺生官，仕人最利。求财：得防复失。婚姻：妻财太旺，妇刚夫柔。胎产：女孕防堕，占产防病。疾病：病在肺经，秋占凶。争讼：结而复兴，经年累月。出行：水陆皆利，名利皆可，但须稍迟。行人：有阻滞，不久先有信归。捕获：夜占盗神是丑，在北方阴贵家。兵战：支生干上，干克支上，利客不利主。坟葬：德贵临墓，穴能发贵。", "毕法云：所谋多拙遭罗网，全财病体难担荷，初遭夹克不由己，将逢内战所谋危。课经云：丑加子为腹胎，来意必占妻孕。如天盘之丑，落空坐空，占产则生速，腹空故也。占孕必损。玉成歌云：子丑相加事必成。"));
    }
};
