public class d44 extends KeEntry{
	public d44() {
        set(4, new LiuReKe("戊申日第一局　干上辰", "课体：伏吟，元胎。", "课义：迤逦不利，败也萧何，末助初生，成也萧何。", "解曰：寅在末传，能成能败，故以萧何喻之。败者初克中，中克末，末克干。成则末助初传，而能生干也。", "断曰：干支上下，交车相合。德禄发用，中传长生，末传驿马，明助巳火为生气。虽三刑互克，而一德足以消万忧。诸占y吉，凡事人来暗助。然能助我，又能克我，是为两面刀也。须开诚布公，乃为善用。课中城吏全逢，禄马交动，求官最利。但寅属旬空，春占方吉。", "天时：火爻发用，大晴。家宅：课名元胎，家有妊人，干支生合，人宅安宁。功名：德禄临身，夜占龙官乘马，未仕者得官，现任者升转。求财：合本营生，春占则吉。婚姻：吉而难成。胎产：男孕产速。疾病：肺家受病，不妨，虽危有救。出行：驿马生德扶干，水陆皆吉。行人：夜占朱雀发用，先有信，旋当得意而归。捕获：不出邑里，当是健奴军吏。兵战：利客不利主。坟葬：支乘长生，上下六合，兴旺之地，且出长寿。", "毕法云：交车相合交关利，宾主不投刑在上，上下皆合两心齐，三传互克众人欺。课经云：交车长生，大宜合本营生。纂要云：昼占三重白虎作长生，乃不幸中幸。夜占三重青龙作日鬼，乃幸中不幸。又云：六戊伏吟，三传递克，全无和气，不可作甲戊庚三奇。"));
        set(4, new LiuReKe("戊申日第二局　干上辰", "课体：元首，退茹，斩关。", "课义：脚踏空亡，岂容退步，肯舍危疑，青云得路。", "解曰：寅卯本旬空亡也，子丑次旬空亡也，故名脚踏空亡。墓神覆日，危疑甚矣。然退而虎鬼为害，三传空陷，几何而不蹈陷阱之中乎。惟舍危疑之墓，向前一步，既不罹祸，又得其禄，是青云得路也。", "断曰：斩关之课，本为动象，连茹空亡，亦宜进取。禄居旺位，即为德神，进前一步，即可相逢。大宜勇力精进，慎毋因遁，以滋后悔。惟官爻值空，求官见贵，未遂所愿。若春占，或占人年命，及岁月填实，仍为吉象。", "天时：火上水下，晴有小风。家宅：朱雀发用，防有口舌。功名：官空不利。求财：宜远动求之。婚姻：三传空，不成。胎产：二阴包阳，男子之祥，产速不易，却喜无妨。疾病：暴疾即痊，久病凶。争讼：未讼不成，已讼即散，不终讼也。出行：墓神覆日，昼占马带蛇，行未能果，且亦不利。行人：近出即归，远出未回。捕获：西方女婢家。兵战：三传空，不能如意。", "毕法云：脚踏空亡进用宜，来去俱空岂动移。神定经云：辰戌作旬首，临年命日干，必中魁元，唯甲戌甲辰二旬有之。三车一览云：三传卯寅丑，皆作日鬼。幸遇鬼空，足可脱灾避难，不宜守旧。缘干上乘墓，宜于三传之外，向前觅禄也。"));
        set(3, new LiuReKe("戊申日第三局　干上卯", "课体：重审，极阴，励德。", "课义：占身卜宅，总是上克，祸及常流，仕宦宜得。", "解曰：干为身，支为宅，身被卯克，宅被午克，皆上克也。常人见之，能免官非灾患乎。仕宦则最喜官星临干克干，昼乘朱雀文书却为吉也。", "断曰：极阴之课，日辰各乘上克，彼己遭咎，事防不测。卯木虽空，末传酉金生亥水，中传亥水生卯木，暗有鬼神相助，占官则吉，余占不佳。如春占午为火鬼，夜将上乘蛇克宅，当防火烛之惊。", "天时：课体极阴，阴云之象。家宅：人宅俱灾。功名：官空不吉。婚姻：干支俱受上克，不吉。胎产：阴极阳生，男孕恋母，难产。疾病：病因心火，或心痛目昏，淹缠难愈。争讼：有解，不见输赢。出行：有阻不能成行。行人：驿马坐墓，尚未能归。捕获：在正西方二十四里，或四十二里道旁。兵战：不实。坟葬：乾龙落脉，成金局，可以发贵。", "毕法云：干支全伤防两损。课经云：干上卯朱雀作日鬼克干，朝绅防弹章，临年命者亦然。指掌赋云：丑亥酉为极阴，如月隐西山。壬髓经云：当门台土，所行有阻。心机独悟云：天乙发用是贵人，利为干谒庆财因，君子拜官迁远职，小人争竞入公门。"));
        set(2, new LiuReKe("戊申日第四局　干上寅", "课体：元胎，比用，不备。", "课义：尊就卑旁，彼己不臧，两贵俱怒，可脱凶殃。", "解曰：干为尊，支为卑，干往加支，乃尊就卑也。戊被寅克，申被巳克，故彼己不臧。昼夜两贵入狱，故曰俱怒。寅鬼旬空，虽临身发用，有难可避，有生可逃，非凶殃可脱乎。", "断曰：干乘克害，支乘六合，自己熬煎，他人逸乐之象。然三传递生官鬼，初中空亡。爱我者恐为善之不终，害我者亦操刃之未割。", "天时：初中空陷，而末申作水长生，亥日当有风雨。家宅：干支相合，人宅康泰。功名：城吏全逢，官星乘马，升迁之象。求财：亥月日可得。婚姻：甚佳，宜觅良媒。胎产：胎防损，或是鬼胎，占产难生。疾病：齿痛或呕血，久病须祈禳。争讼：有救，当解释。出行：传课生合，甚吉。行人：即归。捕获：当在西北饭店空房。兵战：避难逃生之格，不利。", "毕法云：权摄不正禄临支，避难逃生须弃旧，干支全伤防两损。古鉴：酉加子占赴任，曰：寅作青龙，官星也，奈值空亡。逼日干之巳，就宅而作朱雀。宅神又加亥而作末传，任当虚赴。巳与申合，禄在支上，只可申上权摄，不得正任。待陈姓人去，方得到正任。行年午在酉上作蛇，临行主妇人大病，一一皆验。"));
        set(1, new LiuReKe("戊申日第五局　干上丑", "课体：润下，斩关。", "课义：干乘旬尾，支上旬首，递互暗谋，四季财喜。", "解曰：干上癸丑，支上甲辰，首尾相见，谋事有成，解事不脱。然丑，支墓也；辰，干墓也。交互乘墓，如我孟网人，反被人网，岂非递互暗谋乎。三传合成财局，秋冬财旺，身弱难任。四季土旺，则能胜其财矣，故可喜也。", "断曰：润下之课，行多顺利。况格合一旬周遍，传课又逢三六相合，何事非宜，何求不遂乎。惟水局而所乘之将又皆水兽，传财太旺，反致财亏，亦忧长上之疾。末辰自刑，凡事不可任性。发用子乘蛇，主妇人哭泣之事。", "天时：课体润下，晴占则雨，雨占愈大。家宅：人宅交互乘墓，虽在顺境，亦不舒畅。功名：财旺生官大利。求财：财乘青龙可得。婚姻：琴瑟调和。胎产：男孕产易。疾病：脾家之病，不久自愈。争讼：无甚输赢，未能即结。出行：水路防小人。行人：得意，当有妇人同行，然妇人能为我作财，亦能为我致疾。捕获：在正南马房，或炉冶铁匠之地。兵战：三六合局，得众之象。", "毕法云：首尾相见始终宜，传财太旺反财亏，万事喜忻三六合，干支乘墓各昏迷。要诀云：昼将父爻，上乘白虎坐墓，必父母墓生白蚁；如父母在，主病灾；更作月内死气死神，占父母病尤亟。天官论云：干上丑为贵人临身，宜干贵成事。"));
        set(0, new LiuReKe("戊申日第六局　干上子", "课体：涉害，度厄。", "课义：巳申为美，以致无礼，涉险登危，全无畏矣。", "解曰：戊课在巳，巳与申合，所以为美。上乘子卯相刑，是和合中致无礼也。三传互克，而初传之子，回归干上，又受其克，财之险危甚矣。幸末传鬼虎，旬空坐墓，全然无气。虽历险危，亦何畏哉。子为江湖临干，卯为舟车临宅，故有登涉之喻。", "断曰：涉害之课，事主艰危，三下克上，长上有厄。初遭夹克，财不由己。末传马载虎鬼，最忌吊丧问疾。惟初末二传拱贵，宜托庇于贵人，方为无咎。", "天时：青龙乘子受克，起有小雨，大风而止。家宅：宅损人衰。功名：官空不利。婚姻：干支各乘死气相刑，不吉。胎产：胎坐绝受克，主有损，产防厄。疾病：脾土受病，喜鬼空无害。争讼：三传互克，又虎鬼甚凶，必遭刑讯。出行：水路差可，不如家居。行人：夜占主有病，寅酉月日当得信。捕获：盗神是亥，当在北方近水楼阁中，或点水旁地名。兵战：不利。", "毕法云：宾主不投刑在上，人宅皆死各衰羸，三传互克众人欺。课经云：干上子正月占，主孕。喜土生在寅也，支之胎神，作月内生气，亦主孕。又若胎神临妻之年命与支尤吉。秘要云：马载虎鬼，其祸甚速。占讼必得罪于远方，喜寅空无害。"));
        set(11, new LiuReKe("戊申日第七局　干上亥", "课体：反吟，元胎。", "课义：虎鬼空排，切勿贪财，如贪亥水，生起寅来。", "解曰：亥为日财，寅为虎鬼，上下相合，交车六害。幸而空绝，不能为祸，不过空排而已。然亥虽日财，与寅六合，有虎豹在山之势。若贪其财，而遂生起寅木，变为虎鬼，必反受其殃矣。占者当凛贪财受祸之戒。", "断曰：反吟之课，来去俱空，吉固不成，凶亦无准。干支值绝，止宜结绝财物之事，或携财告贵，占病求神，皆可用也。凡事反覆，贫富有乘除，祸福有倚伏。塞翁得失，取鉴不远矣。", "天时：青龙入庙，亥水临绝，风大雨小。家宅：虎鬼在宅，防有丧服。功名：城吏全逢，催官发用，甚吉，必填实始应。求财：宜索债。婚姻：干支值绝，不吉。胎产：下强上弱，女孕，干支上合下害，产凶。疾病：当是翻胃，防有反覆。争讼：两伤。出行：不宜行。行人：立至。捕获：主逃奴、逃兵作盗，在西方垒土之处。兵战：防失众。", "毕法云：上下皆合两心齐，干支值绝凡谋决；来去俱空岂动移，昼夜贵加求两贵。课经云：戊日反吟三月占，生气克日主病，为幸中不幸。死气生日主生，为不幸中幸，占妻病必死。若干支上神作月内死气，妻死尤速。邵南诗云：空亡用起事无成，要遂须当是别旬。"));
        set(10, new LiuReKe("戊申日第八局　干上戌", "课体：元首，斩关。", "课义：发用虽凶，赖系旬空，如论夜将，三传无踪。", "解曰：发用官鬼，却系空亡，又被申制，全然无气矣，虽凶不凶也。如用夜将，初末既已空陷，而丑又乘天空，复何踪迹之可寻哉。", "断曰：元首之卦，先忧后喜。斩关非安居之象，空用有阻滞之忧，大抵凶事可散，吉事难成。必当养晦待时，方为有济。", "天时：主晴。家宅：骨肉防有刑伤。功名：官爻空，防解任，填实乃利。婚姻：防妇刑夫，不吉。胎产：震宫发用，主长男，但恐难育，占产甚易。疾病：脾虚，腰腿痿弱，可愈。争讼：先发者当遭刑责。出行：陆路胜于水路，但恐未必果行。行人：不久当归，归须仍出。兵战：两相刑伤。", "毕法云：干支乘墓各昏迷。指南：丑加申占前程，或曰：卯与戌合，为大六合。六合加戌，为小六合。喜末传月将贵人，定然片言人相。余曰：不然。太阴临卯空，不能成名，此必旧事重新举行者。盖以旧太岁发用，且四墓覆生，主已废复行，沉而又举。嫌初中龙官空战，朱雀阴见元墓，二月还宜谨慎旨意，必不能佳。后果以改授上疏见驳，几至察处。"));
        set(9, new LiuReKe("戊申日第九局　干上酉", "课体：元首，润下，励德，六仪。", "课义：破碎虽遇，钱财满路，彼己乘脱，递互坐墓。", "解曰：干上虽乘破碎，然三传水局，钱财固满路也。但戊为酉脱，申为子脱。而又人坐宅墓，宅坐入墓，不无耗散忧疑耳。", "断曰：润下之卦，事动众谋，或托人作合。六仪发用，且三传递生，必有将伯之助。败气临干，占者当有破败之子。", "天时：主雨。家宅：元武发用，倘有近水房屋，应遭盗贼。求财：可得。婚姻：男女皆败，将来不吉。胎产：孕生女，润下易产。疾病：伤风肾竭之症，难愈。争讼：主牵连下贱，未能散释。投谒：不利。出行：元武发用，中传游都，须防盗贼。捕获：盗在正西道旁。兵战：破碎临干，不吉。", "毕法云：干墓并关人宅废，人宅坐墓甘招晦。占验：寅加戌占病，曰：酉临干为败气，病因少阴。木火作虎，肺脾二经受病，元墓发用为收魂煞，又纯财生卯为死气克日，甲戌冬不保矣。且元武发用乘相气，传归宅上，防一北方陈姓少年来作贼也，悉验。古鉴：占宅曰：恋一妾，复置一妾，精气衰耗，财为家贼所偷。宅上子水作蛇，主子横逆。辰土又耗子水，所以多招是非。酉为脏毒，今年太岁酉值水败。明年巳月，破碎重见，当死矣，果然。"));
        set(8, new LiuReKe("戊申日第十局　干上申", "课体：蒿矢，元胎，不备。", "课义：蒿属朽木，逢申委镞，格居三会，两贵履狱。", "解曰：寅乃旬空，木空则朽。发用蒿矢，是朽木作矢也。然日上乘申作末，是有镞矣，射之能不伤乎。支来加干，末传又归干上，格名三会，凶吉皆成。丑未两贵，临于辰戌，是名履狱，干贵尤非所利。", "断曰：蒿矢之课，祸福皆轻，干支下合上害，各自乘脱。三传互刑，德禄绝陷。凡事必多猜忌，且有诗张。又名不备，吉凶断不十全。末克初传，秋占无虑，惟仕宦不佳。", "天时：寅为风，乘马作虎，主大风。家宅：平安，但须耗财，元胎课家有孕妇。功名：官空禄陷，幸龙乘长生，目下不利，终吉。求财：久后自得。婚姻：干支上神相害，又不备课　不吉。胎产：二阳包阴，孕女难产。疾病：病属肝脾二经，或头风难愈。争讼：讼有解，先告少弱。出行：不利。行人：三会格，立至。捕获：盗在西南井旁饭店。兵战：有失众之虞。坟葬：戌方出水，作艮坤向，可以发财，异日须防盗坟。", "毕法云：宾主不投刑在上，彼此猜忌害相随，上下皆合两心齐。课经云：干上申，青龙乘生干之神，若作月内生气，主徐徐发福。集要云：辰临日生日，凡事不待我求，彼自上门顺应，不劳余力。"));
        set(7, new LiuReKe("戊申日第十一局　干上未", "课体：重审，间传，向三阳，u女。", "课义：虎载财走，不得人手，用碎己心，笑破人口。", "解曰：发用之子，日财也。上乘白虎，惊危而不敢取。若更恋此财，必引而入于鬼墓之乡，其财反为支上戌土所得矣，几何而能入手乎。是己心枉自用碎，不能有济。人却毫不费力，而安然享之，岂不笑破他人之口哉。", "断曰：三阳之卦，贵登天门。罡塞鬼户，神藏煞没，谋为罔不如意。财爻发用，财可问也。官乘虎为马作中传，为催官使者，官可问也。但入空乡，填实乃吉。干支下合上刑，客主难投。宅上昼夜将乘元武，穿窬宜谨。末传六合，昼占u女，此阴小失礼，男女不正之象。郑卫之风，不可防欤。", "天时：课名三阳，有风无雨。家宅：宅上元武刑干，宜防盗贼，秋月尤甚。功名：官空传墓，非宜。求财：干支上神相刑，财被夺去。婚姻：不宜。胎产：孕女产易。疾病：脾痛脾泄，难愈。争讼：后强于先，终当和解。出行：水路防失脱。行人：未回。捕获：北方近水人作盗。兵战：防偷营劫粮。", "毕法云：用破身心无所归。精蕴云：初财坐戌受克，缘恋财引入鬼乡，犹幸空亡。谚云，争似不来还不往，也无欢笑也无愁，其斯之谓欤。"));
        set(6, new LiuReKe("戊申日第十二局　干上午", "课体：昂星，转蓬。", "课义：元戌加酉，奴偕婢走，既撞网罗，奸逃束手。", "解曰：戌为奴，酉为婢，戌加酉为用，上乘元武，必奴与婢奸而逃走也。然干支各乘前辰旺气，名为罗网，有不束手就擒者乎。有官人防丁内艰。", "断曰：昂星之课，亦名转蓬，关梁闭塞，行李稽留之象。祸从外起，止宜家居，守静方为吉征。中末传归支干，所当待人三反自讼，而不讼人，有忠恕之道焉。", "天时：主大晴。家宅：元加宅阴，防家人行盗。功名：难免风波之险，常怀疑惧之心。求财：防有走失。婚姻：天罗地网，不吉。胎产：孕男，产易。疾病：症属肺经，多惊可危。争讼：干支上乘罗网，主遭刑责囚禁。投谒：刑在上，不美。出行：不利。行人：不归。兵战：宜守不宜进。", "毕法云：所谋多拙遭罗网，宾主不投刑在上。古鉴：酉将申时占考弓马。曰：午为马，申为箭，巳为弓，全备乃吉。身上有马而无弓，马带阳刃，色黄者不可骑。况所走之地有厌翳，莫令新服人在旁，须先解厌。支干上自刑，宾主不相得。年上见申，箭不得地。况午见酉，酉见午，为四胜煞，各逞其能。午作青龙，仅得兵部垂顾。初戌作元，又乘害气，如何得中。"));
    }
};
