public class d45 extends KeEntry{
	public d45() {
        set(7, new LiuReKe("己酉日第一局　干上未", "课体：伏吟，龙战。", "课义：静躁不常，失脱须防，动之逢虎，闭口财昌。", "解曰：伏吟主静，干及中传，未遁旬丁，末复来冲，其象又动。酉乘元武，在支发用，失脱之虞，不可不防。中传昼乘白虎，动则必有祸端。末传丑乃旬尾，中有癸水为财，闭口谨言，默而取之，庶无惊恐，而无不昌也。", "断曰：龙战之卦，事多改革，义主乖违。初传夜将天后，事起妇人。三传昼占，皆乘元武虎蛇，尤加惊恐。孟行难行，孟止难止。占者得此，反覆无定，灾祸不一。惟干支拱于暮贵。如用夜将，则告贵用贵，所最宜耳。", "天时：晴占将阴，雨占将晴。家宅：龙战课，夫妻年或立酉上，主室家离散。如兄弟年立酉上，主争财异居。功名：当有改动。求财：不得。婚姻：有阻。胎产：男孕不安，产亦不易。疾病：病属肺，喘咳劳伤，反覆难愈。争讼：屈不得伸。出行：游都、鲁都，乘丁冲刑，有盗贼之忧。行人：即至。捕获：昼占当是家人作贼。兵战：不利。", "毕法云：前后引从升迁吉，信任丁马须言动。三车一览云：巳日申贵逆行，白虎乘丑，乃贵人怒恶之象。凡贵官值此，必招贵人嗔怒。如白虎临丑亦然，盖丑乃天乙本家，不宜见虎也。"));
        set(6, new LiuReKe("己酉日第二局　干上午", "课体：昴星，励德。", "课义：昼将辰阴，课传两申，四虎共聚，禄位空存。", "解曰：辰之阴神，昼将白虎，课名虎视，是为二虎。支上之申并末传之申，是四虎也。四虎相聚，惊危灾祸，岂浅鲜哉。况干禄临身，又乘天空，虚诈不实，何足守也。", "断曰：柔日昴星，又为魁度天门，凡占皆有稽滞惊惶之象。喜上神相生，将伯有助，可以化凶为吉。长生临支，上乘太常，家有婚姻喜庆之事。发用为奴，将乘太阴为婢，作支之六害，应主奴婢害宅。", "天时：大晴。家宅：夜贵作长生坐宅，旺禄临干，大吉。功名：虎视逢虎，不吉。婚姻：支上长生，干上禄吉。胎产：三传纯阳，孕女，产易。疾病：肺经受病，或因饮食而起，极险。争讼：先发者胜。投谒：宾主不和。出行：陆路差可。行人：有信。捕获：夜占可捕，昼占捕役与贼相通。兵战：有惊险，宜慎。坟葬：龙虎相生，上乘长生，大吉。", "毕法云：旺禄临身徒妄作，虎视逢虎力难施，帘幕贵人高甲第。集要云：此名虚一待用格，盖戌午本为火局，止少一寅，吉凶之事，必待寅日方应。课经云：昼占帘幕临支，考试定然中式。纂义云：申常临支，当有一人送物来家。"));
        set(5, new LiuReKe("己酉日第三局　干上巳", "课体：蒿矢，狡童，不备。", "课义：破碎乘巳，三传无益，舍此归家，终受疲役。", "解曰：干上巳火，虽曰生气，但为破碎之神。发用旬空，中陷空乡，末传亥财能助初鬼，且夜占乘元，岂利取财。乃舍之而趋归本家，则干加支而被支脱，非惟无益而又有损，自始至终，疲役甚矣。", "断曰：蒿矢之卦，发用空亡，福浅祸轻。传课五阴，三传自昼而夜，尽为昏迷。且天将后蛇合武，利小人而不利君子。昼占狡童，亦恐夫妇异心，各怀邪妄。惟喜上神青龙生干，忧可释而惊可解也。", "天时：课传皆阴，主阴。家宅：干临支而遭败脱，人衰宅旺之象；头上复乘破碎，主有虚耗。功名：不利。求财：无益。婚姻：不备之课，又属纯阴，不吉。胎产：阴极阳生，孕主男产，有厄。疾病：脾经之病，或起于饮食惊疑，以致虚脱，有解。争讼：可和。出行：青龙生气乘干，陆路吉。行人：干来入宅，即归。捕获：昼占盗神是未，在西南沽买家。兵战：干临支而为支脱，支脱干而阴不备，主客皆不利。", "毕法云：六阴相继尽昏迷，龙加生气吉迟迟。课经云：干上巳，六月占巳为生气，昼将青龙，乘生干之神，徐徐获福。指掌赋云：卯丑亥名断涧，义利分明。集义云：卯丑亥三旬空亡，乃舍益求损之象，名为抱鸡不斗。"));
        set(4, new LiuReKe("己酉日第四局　干上辰", "课体：元首，高盖。", "课义：禄来生身，交互忻忻，夜传可用，昼将更旬。", "解曰：午乃旺禄临支，交车相合，而又三传递助生身，非交互忻忻乎。但夜贵午乘太阴，所以为美。若昼将中末既尔空陷，而发用复乘天空，则必更旬而后可用也。", "断曰：高盖乘轩之卦，乃富贵荣昌之象。三传自末生身，凡谋皆吉。但支则受克，干则被墓，辰午又自刑，且夜占两勾夹干，三传刑冲空陷。大抵初虽交合，后恐不利耳。", "天时：火空则明，大晴。家宅：宅上得禄生干，亨吉。功名：发用旺禄生干，又高盖乘轩，大吉。求财：出旬再图，恐不易得。婚姻：可卜偕老。胎产：孕主女，产易生。疾病：心经之疾，难愈。争讼：可不终讼。投谒：宾主不投。出行：水路利于陆路。行人：未回。捕获：夜占盗神是寅，捕者当在东方树林空地。兵战：客主皆不利。", "毕法云：权摄不正禄临支，不行传者考初时。课经云：辰加干为交车三合，凡交关用事。必有奸私，或相交涉二三事。神定经云：干上辰虽与支生合而却墓干，支上午虽与干生合而却克支，纵有生旺之名，反作衰败空耗矣。壬髓经云：初传克末成者罕，末克初传事可成。"));
        set(3, new LiuReKe("己酉日第五局　干上卯", "课体：涉害，从革。", "课义：身既受克，破碎伤宅，三传全脱，劳心费力。", "解曰：卯为日之鬼，临干克干。巳乃破碎煞，临支克支，身宅俱受伤矣。三传全金，盗脱干气，凡所作为，徒劳心力而已。盖课中有鬼，则子孙为救神，课中无鬼，则子孙为脱气。此课干上虽鬼，却为旬空，既空不烦金制，故为脱气而不作救神也。", "断曰：传会三合，事关众人，课名从革，谋多更改。卯加巳乃日干之明鬼，而发用支上之巳遁乙，为日干之暗鬼。动辄遇鬼，不惟无益，犹有受人脱赚之虞。如占交易等事，后必参商。若先有不和，后却相合。", "天时：水空主晴。家宅：破碎克宅，宅主崩损。宅空鬼临干，人多虚病。功名：官空从革，当遭革职。求财：徒费心力。婚姻：不吉。胎产：有堕胎之患。疾病：病属心经，身患虚怯，初起可愈，久病甚凶。争讼：干支各被上克，两家皆被刑责。出行：行意不决。行人：久出恐因女色丧身。捕获：昼占盗神是巳，当是炉匠师巫之类，向系积贼。兵战：不利。", "毕法云：干支全伤防两损，虎临干鬼凶速速。纂要云：日干之鬼，上乘白虎者，凡占凶祸速极。惟鬼值空亡，或坐于鬼方，或坐于生方，及虎之阴神能制虎者，虽目下有灾，后却无畏也。"));
        set(2, new LiuReKe("己酉日第六局　干上寅", "课体：蓦越，斩关。", "课义：初财中禄，不从所孟，闭口临绝，暗财可逐。", "解曰：初传之亥，乃日财也。入墓而与蛇内战。中传之午，乃日禄也，临绝而受克乘空，皆不从所孟也。末传丑乃旬尾，遁癸为财，若能闭口随时，其中可以暗逐。", "断曰：蓦越之课，五行投墓，止宜解忧释惑　结绝旧事。日德临身，却为空鬼，将复乘空，所谓空上逢空也。天罡临支，虽与支生合，乃是墓神覆宅。若求生意，总属虚无。", "天时：五行投墓，主晴。家宅：墓神覆宅，阴财内战，主因酒食而起惊疑。功名：官空不利。婚姻：不成。胎产：二阴包阳，男孕产顺。疾病：昼占肺经受病，宜泻心；夜占脾经受病，宜泻肝。争讼：即散。出行：水陆皆不宜。行人：即回。捕获：盗在正东，与人口角。兵战：彼此不利。", "毕法云：空上逢空事莫追。指南：丙子三月，戌将卯时刑部被逮，占出狱曰：目今不能脱难，交四月甲戌日巳时方可出狱。其人曰：指日即出矣。曰：发用驿马坐墓，且赤鸟犯岁君，如早上疏，旨意必驳。三月司马上疏，旨意果驳。四月上疏，依拟脱难。盖因四月建巳，冲初传墓中驿马，方有出狱之应耳。三才赋云：驿马入垣，所占迅速。中黄经云：马见巳亥在路岐。"));
        set(1, new LiuReKe("己酉日第七局　干上丑", "课体：反吟，龙战。", "课义：俱空俱鬼，何劳金制，昼将元合，逢私口闭。", "解曰：初末俱鬼，幸得旬空，不待金制，其鬼自消。倘用昼将，元合临门，天后居日，必主阴私之事，往来勾引。癸丑在干，惟宜闭口，免致灾。", "断曰：柔日反吟，事非一端。三传卯酉，应主门户动摇。祸自外来，事从下起，然来去皆空。虽反覆靡宁，究为孟动不动之象。常人相宜，仕宦最忌。", "天时：传课皆阴，阴而无雨。家宅：支空乘元，失脱难免。功名：宜退不宜进，恐有风波之险。婚姻：昼将后合元武，女子不洁。胎产：课传皆阴，胎上乘阳，孕主男，产防厄。疾病：病有两症相侵，久病大凶。争讼：不成讼。出行：无依卦，不吉。行人：即回。捕获：昼占酉为盗神，主西方女人作线，难获。兵战：三传全空，不利。", "毕法云：来去俱空岂动移。纂要云：反吟课，如三月占，生气克日主病，为幸中不幸。死气生日主生，为不幸中幸。又云：卯加酉，昼占鬼乘元武脱气发用，来意必占失脱。课经云：九丑己酉日丑加干，四仲发用，凡四仲时占丑临干，加四仲上发用为九丑。"));
        set(0, new LiuReKe("己酉日第八局　干上子", "课体：涉害，绝嗣。", "课义：课传内战，昼贵相眷，虎随遁鬼，绝嗣尊殄。", "解曰：三传四课，皆被下贼，夹克内战，非有窝犯之私，必有家庭之讼。昼贵临干作财，似得贵人眷顾之意。然末传之巳，遁鬼乘虎，灾殃必甚。况卦名绝嗣，若占长上，定主灾殄也。", "断曰：涉害之课，凡事艰难，必有稽迟岁月，历尽风霜之象。占者得此，所谓苦尽甘来也。三传不离四课，义主循环。德神坐宅，昼贵临身，可以凶化为吉。占官者当从微而显，迤逦迁转，不能一蹴登于要津也。", "天时：传课下贼，内战主阴。家宅：德入宅贵临身，人宅俱宁。功名：主有迁转。求财：昼占贵人之财可得，但夹克恐不能多。婚姻：青后比和，课传皆吉，昼占尤妙。胎产：孕主男，产易生。疾病：虎乘遁鬼甚危，然喜寅空，久病难，治暴病不妨。争讼：可解。出行：有阻。捕获：昼占在西方邮亭左右，造车匠人家。兵战：昼占吉，夜占凶。", "毕法云：虎乘遁鬼殃非浅。课经云：子为昼贵临身，朱雀乘戌克贵，不可告贵请托，必贵人忌惮，而不肯用事。若占文书事，尤非所宜。曾门经云：四下贼上，法曰绝嗣。亡其先人，孤独无嗣，宜为主人利天后起。邵公诗云：鬼在鬼方无所畏，鬼居鬼上不须忧。"));
        set(11, new LiuReKe("己酉日第九局　干上亥", "课体：曲直，重审，狡童。", "课义：利刃置蜜，舐之无益，只宜慷慨，凶灾自释。", "解曰：亥为日财，临身可羡。三传全木，而亥乃木之长生。取之则引起众鬼，如蜜在刀上，舐之必有害也。不若慷慨以投卯，反与鬼混合为一，免罹于祸。故凡携金祷贵，散财济众，凶灾皆可释也。", "断曰：曲直之课，事多冗杂。三传全鬼，止利占官。亥财贪生，须防欺诈。中末空陷，事亦鲜终。夜将初合末后，是名狡童，乃男诱女之象。夏月占此，主有阴私不正之事。春占木旺，空亦无碍。", "天时：传课皆阴，中末空陷，阴而无雨。家宅：人宅平安，但不聚财。功名：木局伤干，止宜纳粟求官。婚姻：不宜。胎产：胎坐长生，又作旦贵，孕生贵子，但产不速。疾病：脾经受病，或腿足痿痹，难愈。争讼：先曲后直，防遭械。出行：陆路更吉。行人：三合即归。捕获：昼占在东方，喜庆宴会家。兵战：不利。", "毕法云：用破身心无所归，六阴相继尽昏迷。指掌赋云：亥卯未为曲直，当举直错枉。云霄赋云：天门木户，相见两和(卯加亥)；醴酒元浆，相调两便(未加亥)。"));
        set(10, new LiuReKe("己酉日第十局　干上戌", "课体：蒿矢，三交，斩关。", "课义：弓矢斯张，蒿箭何防，那堪堕矢，惊恐无伤。", "解曰：发用蒿矢逢空，矢已堕矣。中午旺禄，虽坐于空，末传之镞，尽被火克，灰飞烟灭，复何伤之有。", "断曰：蒿矢祸福皆轻，虽交车六害，刑干脱支，未为凶象。况阴神互作六害，虽危有救。发用官鬼临子，逢春或改旬，破财求官甚吉。", "天时：大晴。家宅：日鬼作支之阴辰发用，不宁。功名：主有虚惊。求财：出旬再图。婚姻：昼占可用，夜占更吉。胎产：二阴包阳，孕男，产速。疾病：肾经受病，或病起饮食，宜治脾。争讼：鬼空，讼可解。出行：马恋长生，恐不成行，行则陆路吉。行人：天罡加季即至。捕获：昼占盗在正南空马房中。兵战：不利。坟葬：冬占发财发贵。", "毕法云：空上逢空事莫追。指南：己卯六月未加辰占病，曰：太岁发用作日破，目今无妨，但医神发用克日，用药不当。木火为虎鬼，脾肺受病，未能脱体，须平肝清心，可以渐愈。卯为死我门，酉为生我户。宜避初就末，可向空门求接命延年之术，否则壬午春，必有他虞矣。支上见胎神，家有妊妇。当二男一女，以行年推之，丁未生女，壬子、甲寅皆生男，后悉验。"));
        set(9, new LiuReKe("己酉日第十一局　干上酉", "课体：元首，不备，间传，闭口，出产。", "课义：阴私叠有，独存闭口，昼虎遁鬼，两贵皆丑。", "解曰：传课纯阴，而后合元武叠见，定主阴私之事，不一而足。中末皆空，独存初传癸丑。闭口之卦，事有难言。如用昼将，末传巳火，遁乙乘虎，灾殃必重，纵空难解。昼贵入狱，夜贵临午，两贵皆伤，俱不可干，所以丑也。", "断曰：间传之课，进步艰难。五阴相继，不利公干。酉支脱己干之气，却生所乘之亥水，而己土反取以为财，是即毕法所谓取还魂债也。占者得此，索取旧逋为宜。但既脱又空，耗失之事，亦不能免。", "天时：纯阴课，主阴。家宅：干支各乘脱气，元武又动，防虚耗及盗贼。功名：不利，且宜谨言。求财：索债可得，但须费力。婚姻：后合重叠入传，不吉。胎产：孕防损，产不利。疾病：闭口卦，又为出户，甚凶，若昼占虎乘遁鬼，禄复乘空，必不能愈。争讼：可以讲和。出行：不利。行人：驿马入宅，即至；远者酉亥月到。捕获：夜占在北方武臣贵家。兵战：利诡诈，不利声讨。", "毕法云：两贵受克难干贵，虎乘遁鬼殃非浅，脱上逢脱防虚诈。酉从魁也，为亚魁星。天盘酉临年命日干，占试必中。指撑赋云：丑卯巳为出户，春雷震蛰。秘要云：诸占于酉日取应。"));
        set(8, new LiuReKe("己酉日第十二局　干上申", "课体：重审，三奇，进茹。", "课义：进进退退，先忧后喜，讼禁病脾，求财可以。", "解曰：三传水局脱支，作日之财，但被戌土刑干，致进退维艰。然干乘长生，财终可取，所以先忧后喜也。末传闭口，讼事所忌。传财太旺，病则在脾，而求财则大可用也。", "断曰：课传三奇，万事和合，刑杀亦所不忌，卦之最吉者也。主士有奇遇，官必超擢，庶民亦消祸获福。惟宅上逢害，奴婢恐有阴私动摇耳。干上长生，昼占乘常，来人当占婚姻之喜，或有赏赐财帛之事。", "天时：水爻发用，有雨。家宅：人宅平安，须防奴婢奸逃。功名：连珠三奇，当以异政超擢。求财：三传皆财，求之必得。婚姻：良姻可谐。胎产：孕产并利。疾病：主腹痛脾泄，当得良医。争讼：当解散。出行：水陆皆利。行人：即回。捕获：北方寻之可得。兵战：利用奇兵取胜。", "毕法云：尊崇传内遇三奇。课经云：丑加子为腹胎，胎在腹中也。如天盘之丑，作空坐空，占产生速，占孕必损。纂要云：申为夜贵加干，朱雀乘午克之，不可告贵干托，必遭贵人忌惮，占用文书事尤忌。纂义云：丑年月占，主小口有灾。"));
    }
};
