public class d47 extends KeEntry{
	public d47() {
        set(10, new LiuReKe("辛亥日第一局　干上戌", "课体：伏吟，杜传，斩关。", "课义：生意特达，投初被脱，丁未凶动，旅情未遏。", "解曰：干并中末皆生，是生意特达也。支并初传脱气，是投初被脱也。自信乘丁，静中主动。乃末逢丁未，又属三刑。克贼用神，祸患之侵，殆不免乎。然不守中戌而进逢旬丁，必因生意，孟赴前途，所以旅情未能遏也。", "断曰：伏吟之卦，天地如一，四伏未发，本为静象，而乘丁则动矣。但杜传课，又自刑三刑，传中和气却少，谋为未必称心。喜河魁为印，太常为绶，印绶临身生身，宅上奇神发用，末得天后青龙。占者主因父母官鬼而动，应有娶妻获财之喜。凡事初凶终吉，祸福相倚也。", "天时：水爻发用，中末皆土，阴而无雨。家宅：人口平安，但亥用脱干，夜占乘元，防有盗贼之虞。功名：金日逢丁，禄遭破碎，不利。求财：始益终损。婚姻：龙后乘丁，不吉。胎产：孕主男子，克母脱母，主产难，，疾病：疾在心经，虚怯有救。争讼：金日逢丁凶。出行：杜传，宜静不宜动。行人：先有信。失脱：昼占元在门，夜占元在宅，家中人所窃，不必远寻。捕获：昼占在正西，夜占在西北。兵战：不利。", "毕法云：信任丁马须言动，金日逢丁凶祸动。课经云：任信丁马，如占访人，必出外干事，倘先允许，后必改易，故名无信无任也。"));
        set(9, new LiuReKe("辛亥日第二局　干上酉", "课体：元首，斩关，退茹，不备。", "课义：禄乘元虎，结好不忤，夜奴婢失，相生交互。", "解曰：旺禄临身，昼元夜虎，未免惊忧耗费。幸金水相生，相与结好，而意不忤也。戌为奴，酉为婢，夜将天空在末，元武居中，或因奴婢而有失脱。然酉生亥水，戌生酉辛，又交互相生，夫复何患。", "断曰：退茹之课，格名失友。凡百孟行不行，人情欠美。斩关虽有动象，而干加支，支加干，课体循环，名曰回环。又兼魁度天门，面前六害，事多阻隔。且干上酉虽生支，而却败支。支上戌虽生干，而自克支。虽有生旺之名，反成衰败。占者利退不利进，宜静不宜动。", "天时：毕宿临干，主阴。家宅：太常入宅克宅，主有孝服灾祸，五月占戌乃死气，尤的。功名：酉为亚魁在干，试必高中。求财：费力。婚姻：不吉。胎产：二阳包阴，孕主女，支上脱日上，产速而易。疾病：心肝二经之病，淹缠难退。失脱：昼占元武临干，婢女窃去。争讼：回环课，不解。出行：退茹又关隔，主不行。行人：回环课，当归。捕获：斩关，利于出奔，占捕须远。兵战：不宜。", "毕法云：魁度天门关隔定，旺禄临身徒妄作。指掌赋云：戌酉申为返驾，主行肃杀之道。括囊赋云：日临辰位，招两姓以同居。"));
        set(8, new LiuReKe("辛亥日第三局　干上申", "课体：元首，间传，顾祖。", "课义：未虽助初，赖此旬无，徒取怨憎，权称虚拘。", "解曰：用乃日鬼，末传助初克日，定有教唆问讼之人。幸赖旬空不能作恶，谚所谓枉做恶人，徒取怨憎者也。然则午之所以为官，寅之所以为财，凡损我益我之事，皆不过权宜之称许，而虚拘之礼文而已，何尝有实意哉。", "断曰：元首发用。理本顺从。间传而退，事有艰阻。午官又得末助，仕宦占之，利见大人之兆。干支上神，交车六害，禄临支而被支脱，当因起盖房屋，以禄偿逋。空寅助鬼，财非我有。宜携财谒贵，纳粟奏名。或求神祷病，以免灾殃。", "天时：角星指阳，主晴。家宅：身宅防失脱，不宁。功名：大吉。求财：为贵人所不足，难得。婚姻：财空不吉，亦不成。胎产：胎亡神阴，孕主女，传逆产难。疾病：肝经之病，须得西南方医人治之。争讼：有人唆讼，然末传空，支亥又制官，有救。出行：支见元虎，干上阴空，远行不利。行人：顾祖格，当归。捕获：不寻自回。兵战：昼占差吉，夜占不利。坟葬：主山高，白虎亦得气，但发福不久耳。", "毕法云：权摄不正禄临支。课经云：午为寅之子孙，寅为午之长生，自午传寅，乃子回顾母，名曰顾祖格。指掌赋云：午辰寅为顾祖，而喜气和平。纂义云：成合之期，戌月日应。"));
        set(7, new LiuReKe("辛亥日第四局　干上未", "课体：元首，元胎，蓦越，励德。", "课义：初马丁杂，投虎空脱，君子犹，常流怎活。", "解曰：丁火临干，鬼马发用，金日逢之，必主凶动。因取中传之财，引入亥虎空脱之乡。虽君子尚有，而况常流值此，有能免于死亡刑狱乎。", "断曰：元胎之课，日鬼乘马，自第四课发用，名曰蓦越，占主蓦然而来。或有惊疑，或有窃盗。幸巳为日之长生，又为德神，所谓一德当头，万事无忧。况干支各乘上生，凶中有解。但课名励德，君子当随时省察，勿愧厥心，斯为元吉。", "天时：天罡指阴，主阴。家宅：未乘天后加戌，主妇人有疾。功名：大利。求财：财爻不实，迟或犹可。婚姻：财空不宜。胎产：阴包阳主男，干脱支，产不易。疾病：心脾肾三经有病，主魂梦不安，宜谢土。争讼：初势汹涌，后却有解。出行：宜慎。行人：即归。捕获：难获。兵战：宜慎。", "毕法云：金日逢丁凶祸动，虎乘遁鬼殃非浅。指南：乙丑十月卯将午时占，曰：先锋属病神，上乘卯财，当占阴人之病。传得元胎，又四课德鬼发用，食神乘空，主胸膈不能饮食。禄临死地，今幸子爻制鬼无妨，明夏太岁生鬼可虑。且夫占妻不宜财空，又辛日亦不宜占病，以辛作亡神故也。"));
        set(6, new LiuReKe("辛亥日第五局　干上午", "课体：涉害，曲直，度厄。", "课义：宅并身灾，鬼掠钱财，携金告贵，此其宜哉。", "解曰：辛被午克，亥被未克，夜又白虎临之，身宅所以俱灾也。三传纯木，乃日之财，却生干上午火作官，是我之财，为鬼所掠矣。然午为日贵，而携金谒之，关节可通，此其宜也。若爱财则化鬼矣，其能免于祸乎。", "断曰：涉害之课，事主两端。三上克下，卑幼不利。面前六合，传会三合，财神外观可喜。但干支全伤，却恐两损。丁神自宅上发用，辛日逢之，祸机已动。幸中末空陷，凶中有解。未虽虎乘遁鬼，究为干之生气。且天乙临身，投以苞苴，事亦可谐。", "天时：午火临干，丁神发用，全传生之，主晴。家宅：丁神入宅，动摇不宁，如正月夜占，鬼作死气乘常，丁又乘虎克支，必有内外孝服。功名：官贵临身，虽逢丁神，三传全财，生官大利。求财：恐是虚花。婚姻：后合在传，不宜。胎产：干上神阳，主男，三合课，产难。疾病：病属，心脾二家，甚险，宜求神。争讼：全财化鬼，丁神发用，大凶，然幸有解。出行：不利。行人：迟归。捕获：昼占在正西，夜占在东南。兵战：有惊忧。坟葬：发财之地。", "毕法云：贵虽坐狱宜临干，鬼乘天乙乃神o，干支全伤防两损，虎乘遁鬼殃非浅，不行传者考初时。摘要云：六辛日午加干，虽是日鬼，却是贵人，不可作鬼祟看。当是香火正神，神佛岳府。夜占当有新化灵席作祟。"));
        set(5, new LiuReKe("辛亥日第六局　干上巳", "课体：重审，四绝。", "课义：采葛寻脑，幽隐难考，夜元克身，丙午又造。", "解曰：干上巳鬼，夜将乘元，支上午火，旬遁得丙，俱来伤日，为害甚矣。然细考之，巳害犹浅，而午尤甚。盖午官发用，迤逦生至末传，午克丑墓，使申金全然无气。譬之采葛者，而寻其脑，午为厉阶矣，岂不幽隐而难考哉。", "断曰：四绝之课，五行入墓，止宜结绝旧事，不利新谋。干上虽乘巳鬼，却为日德。贵又坐宅，占官最吉。末传兄弟，丑墓午克，且作鲁都劫煞。干上元武又乘驿马，主自己或兄弟，必因盗贼而起官司。", "天时：火爻发用，主大晴。家宅：午贵加宅，宅中有愿未完。功名：四绝卦，禄又陷空，不吉。求财：只宜索逋。婚姻：四绝卦，不宜成姻。胎产：胎神临宅，受克作旬空，占孕占产俱畏。疾病：病在心肾二经，凶不可言。争讼：官鬼临身发用，势极凶险，幸为日德，有解。出行：所如猝难称意，驿马乘元，又逢鲁都官鬼，须防讼盗。行人：迟归。捕获：昼占即西方捕役作贼，夜占在北方。兵战：主客俱不利。", "毕法云：鬼乘天乙乃神o。纂义云：事多i塞，动遭阻隔，了结旧案，是名四绝。指掌赋云：贵常同入官乡，当朝宰执。邵公诗云：鬼在鬼方无所畏，此鬼原来无避忌。"));
        set(4, new LiuReKe("辛亥日第七局　干上辰", "课体：反吟，元胎，斩关。", "课义：两贵受克，生值绝域，浊气所生，失十得一。", "解曰：午为昼贵而临子，寅为夜贵而临申，俱受下克，干贵无力矣。巳乃辛金长生，投绝受克而作初末，诸占不吉。惟赖干上天罡浊气来生，无如辰土所生，实为亥水所脱，故云得一失十也。", "断曰：无依之卦，反覆无常。干支上神，交互相克，事无实据。宅上之巳，本为日德，又为长生。发用作马，遁得乙财，若出外求谋，亦薄有财喜。", "天时：久雨主晴，久晴当变，方晴复雨之象。家宅：宅上鬼马发用，主有动移之事；昼占天后临绝，妻女灾危；夜占元武，防婢走失。功名：马乘德鬼作长生，大利。求财：龙坐长生，大利。婚姻：不宜。胎产：绝胎卦，胎神又空，占孕防损，占产速。疾病：心肾二经之病，以女色不正而得，虽淹滞，有解。争讼：反覆淹滞。出行：马发用作长生，可获财喜。行人：不来。捕获：元武恋生，不获。兵战：不利。", "毕法云；两贵受克难干贵。课经云：巳午加亥子克日，主痨病或心患病痈。克应经云：老幼占病得元胎，为后世投胎之兆，最凶。纂义云：支马乘元，若水局月占之，主有走失逃亡。巳时占，主宅舍动移之事。夜占天后临巳加绝，主阴人有灾。"));
        set(3, new LiuReKe("辛亥日第八局　干上卯", "课体：重审，励德，孤辰。", "课义：来情占失，夜贵无力，斫朽木轮，所谋阴慝。", "解曰：卯乃日财，临于干上。昼占乘元发用，是财物逼迫，来意定占遗失也。夜贵仰丘俯仇，贵人无力，干之何益。卯加金为用，名曰斫轮。然卯值旬空，朽木难雕，宜改科易业。末墓蛇虎，干支上神，俱乘元武阴后，所谋阴慝，夫复何疑。", "断曰：重审之卦，事须三思。用空名曰孤辰，当防离别。大约有虚名而无实济，惟僧道则相宜也。中申陷空，惟留末传丑墓，遁癸闭口，凡有谋为，须待春风消息。若逢卯年卯月占之便佳。", "天时：天罡指阴，主阴。家宅：辰卯相害，人宅恐有不宁。功名：初中皆空，惟留末墓，俟卯填实，生起官星。末冲丑墓，乃佳。求财：得亦还失。婚姻：财恐不成。胎产：二阴包阳，男子之样，胎神临空受克，占孕必损，占产即生，疾病：初中空陷，末墓乘虎，大忌。争讼：原被皆忌。出行：春占吉。行人：朱乘中传之申，音书已在途中。捕获：难获。兵战：不可轻动。", "毕法云：朽木难雕别作为，不行传者考初时。括囊赋云：申乘朱雀，音书必起于程途。金兰略云：孤神上克，必主孤单。照胆秘诀云：后合元阴与酉卯，加临课位遭强盗。"));
        set(2, new LiuReKe("辛亥日第九局　干上寅", "课体：知一，曲直。", "课义：夜虎丁火，勿恃传课，交合何益，无财有祸。", "解曰：用未遁丁，夜乘白虎，金日逢之，凶祸动矣。况三传俱财，全财则化鬼。干上寅，支上卯，虽交车相合，而四课皆空，恃之何益。若贪其财，不惟财不可得，而祸即随之矣。", "断曰：曲直之课，整体冗杂，又名知一，首鼠两端。三传三合，且干支上神交关相合，日辰阴阳，又自为合，欢忻和合之象。惜为空亡，惟留中传一亥，凡百谋望，凶则可解吉则难成。倘值春占。或遇卯年可喜。", "天时：龙虽升天而角星指阳，主晴。家宅：人宅亨嘉，但丁神自第四发用，主长上文书之事，蓦有虚惊。功名：不利。求财：财神空亡，求之反费己财。婚姻：淫u卦，不宜。胎产：干上神属阳，主男；胎坐长生，孕吉产迟。疾病：传课皆空，即愈，久病可危。争讼：财空官陷主即解散。出行：宜慎。行人：即归。捕获：武恋长生坐宅，易获。兵战：防有失。坟葬：龙虎皆陷，元神空亡，葬之不古。", "毕法云；金日逢丁凶祸动，传财化鬼财休觅，交车相合交关利。指南：甲申十二月子加酉占功名。曰：干乘绝气，支见死神，贵既空亡，禄亦受制，官星陷矣。谁与居位，营垒空矣；谁与御侮，财星空矣；谁与生官，太阳西坠。冬木逢空，岁在大梁，不能保矣。盖明岁行年酉为自刑，当破坏木局，果验。"));
        set(1, new LiuReKe("辛亥日第十局　干上丑", "课体：蒿矢，元胎，励德。", "课义：马上张弓，矢箭申空，幸脱孤矢，虎墓须逢。", "解曰：巳为弓，乘马而遥伤日干，故曰马上张弓。申为箭，蒿矢得金，是为委镞。幸巳落空乡，又被末传亥水相制，全然无气。中传之申，夜乘天空，亦无足虑，则亦幸脱于弧矢者也。惟干上墓虎加临，凶恶殊甚，倘年命得逢寅卯，犹可为救。", "断曰：蒿矢之课，祸福俱轻。至第四课发用，尤为无力。华盖覆日，事主昏迷。昼乘白虎，须防仇人陷害。", "天时：早晴，午后风且雨。家宅：人口不宁，年命上得寅卯，或寅卯年占吉。功名：已得者不利，未得者不遇。求财：不能。婚姻：不宜，亦不成。胎产：胎空坐死地，难保。疾病：丑墓乘虎临干，马临绝地，难愈，本命木有解。争讼：可解。出行：水路差可，然驿马乘后合克干，身须保重，若近女色，尤危。行人：不来。捕获：难获。兵战：防失众。", "毕法云：干乘墓虎无占病。吴越春秋：七月辛亥寅时吴伐齐，辛上丑，亥上寅，巳神克日为用。辛，岁位也。亥，阴前之辰也。合，壬子岁前合也，行师决胜。但德在合，斗系丑。丑，辛之本也。大吉为白虎而临辛，功曹为太常而临亥。大吉加辛为九丑，又并白虎，前虽胜，后必大败。"));
        set(0, new LiuReKe("辛亥日第十一局　干上子", "课体：涉害，间传，u女，出户，闭口。", "课义：财德既空，墓虎独存，病绝药食，恐丧其魂。", "解曰：中传卯财，末传巳德，俱属空亡。独存初传墓虎，又为闭口。占病定然药食不进，恐丧其魂者，言必死也。", "断曰：涉害之卦，凡事疑滞，孟行不行。发用闭口，似物闭藏，机关莫测。干支上神六合，三传自墓传生，而末又助初生，大约先凶后吉，亦有恩中生害之象。凡占以退为进，以晦为明，不当恃才尚智，贻悔后来。", "天时：天罡指阳，主晴。家宅：春占干墓并关，宅应隳废，墓虎伤支，宅有伏尸，或邻舍有棺鬼作祟。功名：寅卯年月最利。求财：无利。婚姻：不宜。胎产：涉害，胎孕逾期；闭口，恐生哑子。疾病：主肾虚气促，或痰气格塞哑，或禁口痢，或喉塞不食。失脱：人见而不肯言。争讼：闭口，屈不得伸。出行：有阻。行人：不归。捕获：不得。兵战：宜慎。坟葬：龙虎值空，未为全吉，然墓作生气，逢寅卯亦发。", "毕法云：不行传者考初时，空上逢空事莫追，支乘墓虎有伏尸，干墓并关人宅废。课经云：干上子脱辛，昼占乘天空，是名脱空神。凡占皆无中生有，全无实迹，不足取信。如旬空乘天空亦然。纂义云：丑虎乘墓加支，宅有伏尸，或邻舍有棺椁，不无鬼祟。指掌赋云：丑卯巳为出户，春雷震蛰。"));
        set(11, new LiuReKe("辛亥日第十二局　干上亥", "课体：元首，进茹，不备，闭口。", "课义：故意相亏，狐假虎威，己财既失，意外何希。", "解曰：支来加干，上门相脱，喜得坐下戌土敌之，亥惧而不敢脱，是辛犹狐而戌犹虎，即狐假虎威之喻也。中末寅卯，己之财也。乃作空亡，己财则既失矣，意外之获，尚何可希冀乎。", "断曰：退茹空亡，大宜进步，又值网刃，不宜向前。大约进退维谷，动静两难之象。且干上逢脱，夜将乘元，是为脱盗。支上子亦脱气，凡事尽被脱耗虚诈不实也。", "天时：主阴。家宅：人口衰惫，且有耗财之患。功名：财空，官星不见，不吉。求财：己财且失，何暇外求。婚姻：龙后乘墓，不佳。胎产：孕生男，但闭口，当生哑子，占产不易。疾病：心经之病，不能饮食，或不能言语，凶而有救。失脱：主见者不肯言。争讼：冤不能伸。出行：进茹空亡，不宜前往。行人：未归。捕获：难获。兵战：不利。坟葬：不能生长草木，将来家道渐贫。", "毕法云：须忧狐假虎威仪，脱上逢脱防虚诈，所谋多拙遭罗网，干墓并关人宅废，不行传者考初时。断镜云：亥将戌时占此课，定主失脱。缘干上亥为日之盗神，将乘元武。又初传为日之墓，中末日财，又系旬空。纵太阳照武，奈戌时太阳已归地下，其贼难获，果验。"));
    }
};
