public class d48 extends KeEntry{
	public d48() {
        set(11, new LiuReKe("壬子日第一局　干上亥", "课体：伏吟，励德，三奇，杜传。", "课义：德禄幸临，贪心再成，孟其兴旺，无礼遭刑。", "解曰：亥为德禄，临干发用，何其幸也。如不守其禄，而贪心未已，前就中传之子，以冀兴旺，则末传卯子无礼相刑，刑狱之遭，其能免乎。", "断曰：卦得三奇，祸消福长之象。况传课皆逢旺禄，凡谋俱吉。惟归计值空，事恐更变难成。且课体伏吟，宜静而不宜动耳。", "天时：晴占则晴，雨占更甚。家宅：德禄临干，旺神坐支，人宅y亨。功名：传课重逢旺禄，大利。求财：干支同类，恐有争夺。婚姻：支干比和，且乘旺禄，大吉。胎产：男子之祥，伏吟，占产尚迟。疾病：病属肾经，未能即愈。争讼：田庐致讼，可和。出行：不果。行人：未动。捕获：夜占即家中人作贼。兵战：防诈。", "毕法云：旺禄临身徒妄作。古鉴：辛卯人，戊申十一月卯加卯占家宅。曰：宅上帝旺，财物兴隆。中传是子，子边作亥似孙字，今年子及孙当进学。末传门户上有不宁，乃是户役事。门上不合安符，主不时吵闹。财禄甚稳，家当大发。但身有胸胱疝气，及阴肿水气之忧。尚有四四一十六年寿，当以阴肿而亡。盖亥数四，身与初传重见，旺则相倍之故也。其家果挂一天师，时有怪异，去之即安，余亦皆验。"));
        set(10, new LiuReKe("壬子日第二局　干上戌", "课体：退茹，斩关，不备，元首。", "课义：先疑后遂，佯输诈退，病讼逢斯，昼夜皆晦，", "解曰：戌土临干发用，昼夜上乘白虎，此壬所最怕者也。先受此惊疑，然后从而得酉申之生遂，所谓佯输诈退也。若占病讼，岂能当此虎鬼昼夜相临乎。", "断曰：连茹而退，凡事以退为进，斯能受益。魁度天门，虽有阻滞，而临干发用，却为斩关，非安居之象。但格合失友，课名不备。又虎乘干鬼，人情必然欠美，事体必然未周。多主孟行不行，先难后易之象。", "天时：天罡指阴，主阴。家宅：德禄坐宅，宅旺而窄，人亦平安，防有大惊。功名：青龙值旺，已仕者主升转，未仕者，不日得官赴任。求财：无益。婚姻：不吉。胎产：胎上神阳，主男，产迟而难。疾病：魁度天门，乘虎作鬼，主肾气大虚，以致隔食隔气，法宜治脾。投谒：有阻。争讼：不利。出行：不大遂意。行人：归迟。捕获：昼占盗在西南，夜占盗在正北。兵战：当失众。", "毕法云：权摄不正禄临支，魁度天门关隔定，干乘墓虎无占病，虎临干鬼凶速速。课经云：日干禄神加临支辰上者，凡占不自尊大，受屈折于他人，非邻国之游士，亦幕府之客臣。纂要云：壬水临支就旺，大宜家居食禄。"));
        set(9, new LiuReKe("壬子日第三局　干上酉", "课体：元首，斩关，间传。", "课义：宅上为初，昼占难居，叵耐末午，助鬼趑趄。", "解曰：戌土临宅发用，昼将白虎，若占家宅，凶恶难居。末传之午，虽为日财，却助初鬼，壬干见之，自趑趄而不敢进步，故尔叵耐也。", "断曰：间传之课，进步艰辛。又名斩关，事难安贴。干支六害，而干则受败，支则受克，人宅不宁之象。末财生助初鬼，止宜携财告贵，切勿妄意求财，必将致祸。昼占酉乘太常生日，当因喜庆婚姻，以致破财，或开丝铺酒肆，以为生计。", "天时：主晴。家宅：虎鬼临支发用，干支俱受其克，人宅不宁，小口灾更甚，功名：催官发用，末财又助，已得者升迁，未得者任速。求财：无益。婚姻：干支上乘六害，不吉。胎产：三传纯阳，主女；干上脱支上，产速而易。疾病：肝肾二经之病，昼占甚凶，夜占有解，总宜求神作福。争讼：有教唆人，主略占先。出行：斩关课，最利。行人：即至。捕获：昼占盗在正南，道旁马舍。兵战：不利。", "毕法云：虎临干鬼凶速速，虎乘遁鬼殃非浅。课经云：戌加子发用，夜将为一喜一悲，干上酉乘空生日，为不幸中幸。支上戌乘龙克日，为幸中不幸。断镜云：戌虎临支，寅月占主房舍不安。虎临四季加子，主小口灾病，占讼有主唆。年命制末则吉，生初则凶。"));
        set(8, new LiuReKe("壬子日第四局　干上申", "课体：比用，三交，高盖。", "课义：夜遁戊虎，财亡妻苦，中末皆空，败坏门户。", "解曰：干上申，长生也。遁得戊土，夜乘白虎，反为伤日。发用午火，昼后夜元，又被末传子水冲制，是妻财受伤矣。中末空陷，且子卯无礼相刑，兼之壬水败于酉，支上乘之，门户之所以败坏也。", "断曰：知一之课，亦名比邻。事有两歧，起于同类。有始无终，恩中生害之象。且三交作传，事体牵连，须防有人阻破。幸日辰上神，交互相生，循分守拙，最为安妥。", "天时：火用水空，主晴。家宅：败神临支，夜虎遁鬼，人宅俱不能安宁，家中忌隐匿私人。功名：高盖课，大利。求财：无益。婚姻：不吉。胎产：胎神临败地，孕防损；阳包阴，主女，支上神脱干，产速。疾病：病属肝经，或因酒食而起，甚凶。争讼：夜占虎乘遁鬼，甚凶，然可讲和。投谒：比邻，人可得见。出行：有阻。行人：未归。捕获：贼在邻近。兵战：宜和不宜战。", "毕法云：虎乘遁鬼殃非浅，不行传者考初时。太乙经云：壬子日魁加辰，胜光临子为用，将得蛇，此为日辰并伤神将也。但辰伤妇，谓辰伤其阳神神将者。又曰内有微气往助之者，为妇死也。"));
        set(7, new LiuReKe("壬子日第五局　干上未", "课体：涉害，曲直，励德。", "课义：丁鬼临身，暗戊加辰，财如刃蜜，夜卜为。", "解曰：水日逢丁，临身之财，极易取也。但乘未土，为日之鬼。申乃长生在支，却遁戊土乘虎，二者皆美中不足，故以刃蜜喻之。至于夜占，则天将皆属土克干，尤甚矣。", "断曰：见机之课，乃趋安避危，先难后易之象。事须见机，思所变计。若守旧不改，愈加稽滞矣。况局成曲直，动则如意，静则不宁。贵人立于酉上为励德，亦主迁动。但干上神，交车六害，末传德禄陷空，凶易散而吉难成耳。", "天时：曲直带丁马，主大风。家宅：干支上神交车六害，三传又脱盗干支，人宅不宁。功名：不利。求财：壬干乘丁，惜乘六害，难得，春占吉。婚姻：水日逢丁，妻财已动，春占大吉，余不甚佳。胎产：胎上神属阳，弄璋之兆，产迟。疾病：症主肝木，因风而起，难愈。争讼：虽凶，有解。出行：中末空陷，有始无终；未必去，去亦不佳。行人：归迟。捕获：昼占是东方本家人。兵战：不宜。", "毕法云：虎乘遁鬼殃非浅，水日逢丁财动之。断镜云：壬子日太常临未加亥，支上申乘白虎，三月占又是死气，乘虎入宅，主内外孝服。指掌赋云：未卯亥为正阳，遵发生之意。又云：三传纯子孙，不求财而财自至。"));
        set(6, new LiuReKe("壬子日第六局　干上午", "课体：重审，四绝，u女。", "课义：夜申遁鬼，长生无气，或卜财亡，或缘妻婢。", "解曰：申乃壬水长生，遁戊为鬼，被初克中墓，则长生无气矣。午为壬水妻财，被水将夹克，占遇此课，非为财亡，则缘妻婢之事也。", "断曰：重审之课，事多不顺，事由妇人而起。面前六合，三传递生育干。占主先以财交引荐，后致祸灾。初遭夹克，财不由己而费用。中作腹胎，妻应怀孕而忧虞。初末虽引从地支，然被丁未克害，当亦不能安坐享福也。", "天时：火爻夹克，天罡指酉，主阴。家宅：旬丁乘鬼，入宅克宅，主宅不宁。功名：三传递生，可得，恐有反覆。求财：费力。婚姻：恐非佳偶。胎产：胎神临绝受克，占孕危，产易。疾病：症主肝肾二经，淹缠难愈，宜作福。争讼：可和。投谒：干支上神六合，宾主和谐。出行：吉。行人：在途。捕获：昼占在东方舟车中，夜占在正南园田内。兵战：能知机，可小用。", "毕法云：三传递生人举荐，初遭夹克不由己，六爻现卦防其克，虎乘遁鬼殃非浅。邵先生口议云：凡太岁未加子，或子加未，主井中有祟。克应经云：凡宅上见鬼，如为岁月破，主破家破祖破田畜小口妻财之应。"));
        set(5, new LiuReKe("壬子日第七局　干上巳", "课体：反吟，励德。", "课义：两贵无力，博奕大获，满目钱财，身心疲役。", "解曰：巳贵临亥，卯贵临酉，昼夜俱被克制，故尔无力。干支各自乘财，交互克取，如博奕角胜之类，可以大获。但钱财虽然满目，若秋冬遇之，身旺财弱，虽遍地蛇虎，而以凶制凶，求财何忧不得。春夏则财旺而身弱矣。蛇虎羊刃，来去冲迫，大费奔驰筹画，所以身心疲役也。", "断曰：无依之卦，反覆不常之象。凡占主有惊疑，美中不足。又干支相同，为借钱还债格。求财非宜，问官大利。然巳为破碎煞，又系德丧禄绝，止宜循理守正，不可恣意妄行。", "天时：晴占则阴，雨占则晴。家宅：游都临＊干支上，夜乘元武发用，防有咨贼之惊。功名：德丧禄绝，主有不测，幸财旺生官，虚惊而已。求财：可得，秋冬更稳。婚姻：反吟课，不成。胎产：胎上神阳，主男，产顺。疾病：心经之症，新病即痊，久病凶。争讼：先发者少利。出行：干乘游都劫煞，支复逢元，防盗。行人：在途。捕获：昼占在西南方，门墙城阙之所。兵战：防劫掠。坟葬：在道路旁，可以发财。", "毕法云：两贵受克难干贵，传财化鬼财休觅。神定经云：反吟刑冲，有始无终，吉未得吉，凶未得凶。"));
        set(4, new LiuReKe("壬子日第八局　干上辰", "课体：重审，铸印，斩关。", "课义：两贵引从，再复旧俸，久处沉滞，忽然擢用。", "解曰：支之华盖，作干墓神，覆于日上，主人昏晦。亥禄为墓所掩，幸初末两贵，引从日干。而中传之戌，复来冲开辰墓，然后禄神得以无恙，是再复旧俸也。凡人之久处沉沦，一旦擢而用之者，其象如此。", "断曰：重审之课，事多不顺，先暗后明，三传铸印。有位者占此，喜溢眉端。庶人占之，却生灾祸。又主得上人提携，或两处贵人引荐成事，难中有救之象。如八月占，辰为太阳，尤为喜庆非常也。", "天时：天罡指阴，主雨。家宅：人亨宅旺。功名：遇卯戌，升迁立至。求财：末助初财，求无不遂。婚姻：可成。胎产：铸印课，又胎上乘禄，主生贵子，占产不易。疾病：心肝二经之病，久病凶，新病即愈。争讼：甚凶，幸有解。投谒：甚利。出行：水陆咸宜，铸印卯空，俟卯戌月日吉。行人：先有信至。捕获：昼占贼在西南方，因饮食与人口角。兵战：宜慎。", "毕法云：前后引从升迁吉，华盖覆日人昏晦。观月经云：富贵天乙卦，发用最为良。四月申加卯，壬子入本乡，富贵兼权印，福禄从天降。要览云：末传卯居旬后，谓之铸印不成，必勉强而后可得。"));
        set(3, new LiuReKe("壬子日第九局　干上卯", "课体：重审，曲直，斩关。", "课义：夜将难受，木局为救，昼卜俱空，占官虚谬。", "解曰：夜将贵勾太常皆土，壬水难受，却赖木局为救神。但三传盗气，初末空亡，而昼将又乘天空，虚脱甚矣。惟常占喜木局制鬼，仕宦则剥官煞旺，竟成虚谬也。", "断曰：重审主有忧虞，斩关主有移动。局名曲直，其性曲折而直遂，有进退未决之象。发用水日逢丁，当因官鬼之财而动。未卯空陷，惟留中传德禄，兼之仪神入宅，和合可望。但丁为日财，其实是鬼，火又生之，并土将克干，名子母鬼，主有骨肉解破其事。", "天时：天罡指阳，亥水夹克，主晴。家宅：干支上神六害，人宅不宁。功名：不利。求财：虚而不实。婚姻：不吉，亦不成。胎产：胎上乘阳，主男，干支上六害，产有厄。疾病：心肾二经受病，久病凶，新病即愈。争讼：先发者不利。出行：驿马居天中，天罡加四仲，道路阻塞。行人：初传陷空，末遇天中，当因贵人官鬼之财阻滞。捕获：贼在南方，斩关难获。兵战：先动少利。", "毕法云：众鬼虽彰全不畏，彼此猜忌害相随，合中犯煞蜜中砒，水日逢丁财动之。断鉴云：木虽福德，究脱壬气，干事不宜托人。"));
        set(2, new LiuReKe("壬子日第十局　干上寅", "课体：弹射，三交。", "课义：课无涯岸，执弓妄弹，末刃虎元，凶存吉散。", "解曰：寅卯旬空，巳午陷空，四课皆空，浩无涯岸。又弹射发用，是弓虽张而亡弹者也。日财临空，中酉坐克，独存末传羊刃。昼虎夜元，吉则散而凶则存，何利之有。二贵皆空，求贵者业蒙许允，后却被人搀越。", "断曰：弹射无丸，事属影乡。三交之课，隐蔽欺私。干支上神，交互相脱，当有欺诈虚耗之事。昼蛇临财，财有虚惊。夜空临酉，婢女欺诳。凡事总当持正，素履无咎。", "天时：火空毕见，子水独实，主雨。家宅：家匿阴私，虚耗不免。功名：已仕者，有迁转之机，未仕者难得。求财：不惟不得，且防有失。婚姻：不吉，亦不成。胎产：孕产皆不利。疾病：心肾二经之症，新病即愈，久病则凶。争讼：朱勾陷空受克，可以解散。出行：谨慎为要。行人：不至。捕获：昼占在东南炉冶贵人家。兵战：有失众之虞。", "毕法云：人宅受脱俱招盗，二贵皆空虚喜期。课经云：支干上皆乘脱气，占病必因屋宅费用致心气虚惫，补元可愈。占式云：元武临亥，渭武入贼乡，必有失脱。午加卯用，主托人失节。登歌云：一弹打双鸿，须知两用心，有谋虽所中，所望不全成。"));
        set(1, new LiuReKe("壬子日第十一局　干上丑", "课体：重审，励德，间传，六仪，u女，登三天。", "课义：自墓传生，交合相并，劣猿难靠，羸马难乘。", "解曰：初传辰乃日墓，末传申乃长生，是自墓传生也。子支与干上丑合，壬干与支上寅合，是交合相并也，凡事先迷后醒，交关最利。但发用空亡，中传之午属马，坐墓受脱则羸矣，何可乘乎。末传之申属猴，被午克制则劣矣，讵足靠哉。", "断曰：登三天课，至高至危之象。重审发用，百凡犹豫。幸属六仪，可以化凶为吉。占者主初值艰难，后主成合。", "天时：角星指阳，时而有风。家宅：元后乘马，临宅脱干支，须防奸盗，如八月昼占，有内外孝服。功名：青龙乘生内战，主有升迁。求财：始艰，终可得。婚姻：后合不宜。胎产：孕女，产不利。疾病：登三天，难愈。争讼：当至朝省，至凶之地。出行：水路防盗。行人：未归。兵战：不利。", "毕法云：二贵俱空虚喜期，罡塞鬼户任谋为，干墓并关人宅废，制鬼之位乃良医。古鉴：卯加丑占身命，曰：此课于细微中出入，却能显焕。宅居西北，渐出东南，主有财有寿。盖壬子皆在西北，主幽燕之地，转出东南旺盛之方。宅上寅为子息值空，初传辰为鬼，又临子嗣空亡之地，中传得孙为财，末又长生，临于财及子孙之上，故主有寿有财。子虽先死，得孙送老也，后皆验。"));
        set(0, new LiuReKe("壬子日第十二局　干上子", "课体：知一，进茹，不备。", "课义：支神作旺，来临干上，倘孟改谋，迭遭欺诳。", "解曰：面前六害，进茹皆空。喜支加干上，彼来就己而作旺神，则有兴隆之象也。倘进而别谋，则空脱墓鬼俱全，兼值网罗，岂不迭遭欺诳乎。", "断曰：知一之卦，事起同类。四课不备，事不周全。连茹而进，三传俱空，止宜退守。干上之旺，支上之合，使壬水不被传木全脱，乃全身远害之道。凡占不宜妄动妄行，亦不利托人干事。", "天时：旺水临干，却坐绝地，大风而略飘细雨。家宅：宅旺人亨，如八月昼占，鬼乘常作死气克宅，防有内孝服。功名：昼占凶，夜占吉。求财：不可得。婚姻：不成，亦不吉。胎产：孕女，产迟而难。疾病：心肾二经之症，初病淹缠，久病危。争讼：彼胜。出行：利于速行。行人：将至。捕获：盗在近邻，女人作线。兵战：利客不利主。", "毕法云：进茹空亡宜退步，所谋多拙遭罗网。三车一览云：壬子日，干上子，支上丑，支干本相邻近，而上神又作六合，凡占主有变换，彼我共谋求合之事。九门杂占云：支干上子丑六合，又乘太常，是为牛女相合，大宜占婚。缘丑中有牛宿，子中有女宿也。然壬子又是芜淫，恐未必成。"));
    }
};
