public class d52 extends KeEntry{
	public d52() {
        set(5, new LiuReKe("丙辰日第一局　干上巳", "课体：伏吟，斩关，元胎，励德。", "课义：禄财长生，空虎纵横，静中须动，所作无成。", "解曰：初传巳为德禄，遁丁发用。中传申为日财，末传寅为长生，又乘驿马，三传皆吉。但嫌天空与元虎，昼夜纵横耳。伏吟传见丁马，静中已动。三传自初递克，长生又属三刑，生意息矣。凡所作为，岂能成就。", "断曰：伏吟之卦，本为静象，传遇丁马，动意蹶然。若能安心坐待，则末助初传，禄神正旺，占官大吉。天罡乘龙临支，当因喜事，宅中欠宁。", "天时：雨占即晴。家宅：日上乘禄，支上乘龙，财喜重重之象。功名：禄马全逢，主大利。求财：阴私之财可得，防有惊恐。婚姻：女美性刚，可就。胎产：天罡作子息爻，男妊，产迟。疾病：脾土受病，或遗漏风痈之症，未愈。出行：吉。行人：立至。捕获：难寻。兵战：宜防虚诈。坟葬：龙神得气，丑方出水则吉。", "毕法云：任信丁马须言动。指南：戊子八月辰将，先辰时，后酉时，占两人乡试，曰：辰时者前列，酉时者次之。盖缘天罡为领袖之神，从魁幕贵在后故也。及排辰时者，三传巳申寅，干乘德禄，支见月将青龙，禄马入传。酉时者，三传午丑申，太岁空战，幕贵入墓，榜发。辰时者，果中二十二名。酉时者，止中副榜，二者皆以时断中也。"));
        set(4, new LiuReKe("丙辰日第二局　干上辰", "课体：伏吟，退茹，斩关，不备。", "课义：还嗣息债，长生宁耐，然后散忧，退空无害。", "解曰：发用之卯，克支生干，支却临干受土，是得之于彼，而与之于此也。辰为丙火之子，非还嗣息之债而伺。寅为日之长生，上带白虎，虽有惊危，然孟受生，大宜守耐。末传丑空，可以解忧散虑。退虽逢空，且喜脱气，又在长生，有生而无害也。", "断曰：三传联茹而退，末遇空亡，反宜进步，凡占先起者吉，后起者凶。败气临支，非有卑幼灾伤，即有门庭消耗。求官者，始如春花，后如秋叶，总属有头无尾之象。", "天时：青龙虽升天，被初中所克，不雨而风。家宅：卯木克宅生干，弃宅益人，人亦消耗。功名：课传不见贵禄官星，占名欠利。求财：青龙临干作脱气，反有耗费，不能聚财。婚姻：支干相害，占婚不吉。胎产：上强下弱，不备逢阴，孕乃成男，支干作害，微灾难免。疾病：肝胆之疾，病来生人，不能即愈。出行：水陆两途，皆不吉利。行人：天罡加孟，尚未启行。捕获：昼占当在西南饮食，夜占在北方阴人家。兵战：得失相半。", "毕法云：彼此猜忌害相随，喜惧空亡：乃妙机，踏脚空亡进用宜。课经云：丙辰日，辰加巳，卯加辰，支干上神作六害，主彼此各相猜嫌，主客互有机械。太乙经云：占与人期会，天罡临口辰者，会在日辰前为已过，会在日辰后为未至。括囊赋云：木逢勾虎，必栋折于三刑。"));
        set(3, new LiuReKe("丙辰日第三局　干上卯", "课体：重审，寡宿，间传，极阴。", "课义：两相交会，各有利害，昼夜贵加，亥水休赖。", "解曰：寅卯辰巳，两课全会。但卯辰相害，寅巳相害，不无彼此猜忌之嫌，是各有利害也。初传虽属空亡，而酉加于亥，昼夜贵加，凡占宜干两贵，方为有济。所虑昼贵，坐于空乡，不能赖其弘益耳。", "断曰：寡宿发用，凡事无凭，吉凶不就。又曰极阴，愈退愈暗之象。支上克支生干，人则盛而宅则衰。惟年命在子，两贵拱夹，占此最为吉利。", "天时：课名极阴，日乘勾空，阴云之象。家宅：龙合克宅，必因吉庆生事。功名：两贵相加，一贵用力，必因妻财得贵。求财：财爻已空，求之无益。婚姻：女益男家，但恐不睦。胎产：女孕，产凶。疾病：肝经之疾或肾水有亏，不药自瘥。出行：水陆两途，吉凶相半。行人：驿马临支，行人即至。捕获：昼占在正南马道，夜占是公吏凶人。兵战：防失众。", "毕法云：昼夜贵加求两贵，彼此猜忌害相随。课经云：丙日胎神在子，正月占主妻孕；然正月血支血忌皆在丑，并养神克胎，占产则速，占孕有损。若十二月占，血支血忌在子作胎神，占产亦速，占孕亦损。惟血忌作空亡，产孕皆不畏矣。秘要云：此课凡事于巳日有克应。"));
        set(2, new LiuReKe("丙辰日第四局　干上寅", "课体：遥克，蒿矢，元胎，三奇。", "课义：破碎临庭，耗散财婚，昼占告贵，不语沉吟。", "解曰：丙以申金为妻财，墓于破碎之丑，而临支上。昼乘元武，必因婚娶而耗散其财。亥为昼贵，发用上乘闭口，故告贵者，必沉吟不语，有理难伸也。", "断曰：课遇三奇，干上长生，夜乘青龙，必当徐徐发福。但发用蒿矢，得福亦轻。中传财坐鬼乡，求财欠利。末传德禄，春夏占之，吉无不利。", "天时：亥乘贵人发用，水母复又生亥，青龙临日，先大雨而即晴。家宅：财库作破，临宅上乘朱勾，必因口舌争讼，大费财帛。功名：贵人发用，德神居末，占名甚利。求财：宜得阴私妇女之财。婚姻：占男则吉，占女则凶。胎产：二阴包阳，妊乃成男，母破儿生，占产甚吉。疾病：脾胃之疾，人生病破，不药自愈。出行：陆路甚吉，水路耗费。行人：将及到门。兵战：昼占甚吉，有辟地千里之象。", "毕法云：闭口卦体两般推，龙加生气吉迟迟(三月占)。课经云：亥加寅，乃旬尾加旬首，为闭口卦。如更值初末上下六合，则气塞乎中。占病即哑，或患禁痢，或咽肿痰厥。占胎定是哑儿。占失脱，纵人见盗去，亦不肯言。求人说事，人皆闭口。纂要云：干上龙乘长生，当耐心静守，自然亨泰，不可妄动。指掌赋云：癸为闭而丁为动，闭主死而动主生。"));
        set(1, new LiuReKe("丙辰日第五局　干上丑", "课体：重审，润下，励德，寡宿。", "课义：课传没溺，凶灾可释，外勾里连，夜多淫u。", "解曰：鬼能作祸，遇空即解。今课传尽皆空陷，若没溺者然，凶灾何难消释乎。三传水局克干，盖由宅上之子发用，合辰申而为鬼，是家中之人，外勾里连，以生祸也。支上二课，将乘合后元武，夜占不免淫u之事矣。", "断曰：润下之课，事关水利，或亲族牵连，有动而不息之象。众鬼相侵，必有数人作伙谋害。幸丑土能敌，不能为祸。发用官空，占官不吉，须用水之时，方能入手。", "天时：三传皆水，昼将亦水，即有大雨。家宅：子月占家长有忧，财上乘元，昼占财耗。功名：贵禄不现，官星又空，占者欠利。求财：反有耗费。婚姻：天作之合，但女子不佳。胎产：妊当育女，干支上神相合，产期必愆。疾病：肾与膀胱之病，病空即愈。出行：不吉。行人：尚未启行。捕获：夜占贼在东北大木林中。兵战：昼占惊恐，夜占稍吉，但恐劳而无功。", "毕法云：空空如也事休追，万事喜欣三六合，将逢内战所谋危。课经云：三传俱鬼，则能克去劫财，若遇二空独存一财，则安稳无破，亦谓之全鬼变财。集义云：三传水局，干上丑与子合，经所谓三六相呼见喜忻，总然带恶不成嗔也。况干上丑土自能敌水，凡谋可成。又子加辰乘蛇，主妇人哭泣。"));
        set(0, new LiuReKe("丙辰日第六局　干上子", "课体：知一，四绝。", "课义：中末虚无，独用午初，动逢羊刃，昼贵归庐。", "解曰：丑申空陷，故曰虚无，独用初传午火而已。鬼居午上，既不可守，若孟动谋，又逢初传羊刃，是动静皆不可也。亥水临辰，贵人入狱，既不得地，临于支上，故曰归庐，告之不能蒙益。", "断曰：四绝之课，五行入墓，止宜结旧，不可图新。三传空陷，克我之神皆不得位，不过虚声而已。丑加午乘朱勾，主有田宅争竞。午加亥乘白虎，主有火灾。亥加辰，夜乘雀，主有小儿哭泣。财爻坐墓无气，主有耗失。", "天时：水母已空，午火发用，晴而不雨。家宅：官贵临宅，必有讼狱。功名：贵人坐狱，占名不吉。求财：财爻已空，求之不得。婚姻：干上临空，女占不：占。胎产：二阳包阴，离为中女，胎神遇空，占产不吉。疾病：肾经受病，七月占不吉。出行：水陆俱不吉。行人：尚在中途。捕获：昼占勾陈受克，不能擒获。兵战：初传坐于绝地，中未皆空，闻见皆虚。", "毕法云：鬼乘天乙乃神o，不行传者考初时。课经云：昼将亥贵加支，乃鬼乘天乙。占病必是家堂神像不肃。宜修建功德，安慰宅神，庶几无咎。中黄经云：丑入午传多诅咒。照胆秘诀云：戌加卯上是合乡，信至人归见福昌。玉成歌云：日鬼加临辰两课，定然官吏到门庭。又云：勾雀同传主讼争。"));
        set(11, new LiuReKe("丙辰日第七局　干上亥", "课体：反吟，斩关，励德，元胎。", "课义：满地皆丁，动遇贵成，夜占不动，鬼墓难兴。", "解曰：巳为日之德禄，遁丁而为初末，则满地皆丁矣。亥贵临身，动则以贵为官，仕宦所以遇之而成也。倘或夜占，则静而不动。干上亥水，为日鬼而克之，支上戌土，乘两蛇而墓之。一星丙火，岂复能兴乎。常庶占之，皆所不利也。", "断曰：反吟之课，发用旬丁，至动不宁之象。干上亥水，旬遁得癸。夜乘朱雀，是明暗二鬼，并力相伤。朝贵尚防章劾，常人岂免灾。", "天时：天地相通，日上克日，晴雨不常；家宅：蛇虎满宅，惊恐之患，反覆互见。功名：德禄往来，官贵临身，功名独利。求财：财爻不见，求之无益。婚姻：反吟课，占婚不利。胎产：女孕难育。疾病：脾胃受病，肾水大亏，峻补命门方愈。出行：中途而回。行人：行者立至。捕获：昼占在西，夜占在北，皆有外淫。兵战：事多更变，防有虚惊。坟葬：回头顾祖则吉。", "毕法云：鬼乘天乙乃神o，制鬼之位乃良医。纂要云：亥为日鬼，却赖支上戊土克亥，则戌土便是良医、或是本家之人能医，或得家堂宗神保佑。除占病外，凡占虽值危难，灾患之中，必得人分解，诚为救神。纂义云：年命加戌，占宅西北上有破罐。"));
        set(10, new LiuReKe("丙辰日第八局　干上戌", "课体：重审，斩关，六仪。", "课义：妻财虽美，恋之害己，众口一词，乘马遭耻。", "解曰：支上酉金，遁得辛金，乃丙之妻财。若恋之不舍，反被辰土盗脱，必有害于己矣。三传寅克未，未克子，子克丙，必被众口攻讦。初传之寅木，虽为驿马，上则受未土之墓，下则受酉金之克，俯仰丘仇，适所以遭其耻也。", "断曰：旬首发用，号曰六仪。辰日支仪，亦居寅上，是六仪与支仪相并。凡占吉庆，不忌刑杀。干支上神，戌酉相害，必然宾主不和。华盖覆日，百事昏晦。", "天时：墓神覆日，青龙入庙，阴云之象。家宅：财神临宅合宅，家道必丰，或恐修治房屋以致脱耗。功名：必待末传官星填实，方为有济。求财：可因贵人文书得财。婚姻：支干相害，占婚不利。胎产：二阳包阴，女孕，难产。疾病：病有积块，四月占不吉。出行：恐不能起身。行人：人已起身，中道有阻。捕获：昼占在西南，夜占在西北。兵战：夜占稍吉。", "毕法云：两蛇夹墓凶难免，末助初生三等论。课经云：戌加干，昼夜天将皆乘蛇，地盘之巳，亦是蛇之位，故名两蛇夹墓，占病难救。如年命临戌，天罗自裹，凶灾尤甚。或亥为年命乘罡，则以虎冲蛇，庶得少延。心印赋云：寅加酉上为用初，中末未与子上居，行人远至及文书。"));
        set(9, new LiuReKe("丙辰日第九局　干上酉", "课体：重审，从革。", "课义：交关利己，昼贵莫倚，夜将助才，金多生水。", "解曰：巳与申合，辰与酉合，申酉俱作日财，此交关之有利于己者。亥作昼贵，临未被克，不可依倚。幸夜将纯土，助起金局，财气极旺。然恐金多生水，化而为鬼也。", "断曰：从革之课，三传全财，利于功名，而不利于父母。宜于求财，而不宜于构讼。干上酉财，遁辛合日，但遭夹克，不能由己。帘幕临干，试必高第。", "天时：水母被墓，天罡指阳，不能得雨。家宅：妻财作病气入墓，防妻有病。功名：三传生官，不求自至。求财：宜得贵人妇女之财。婚姻：财旺生官，必定和谐。胎产：女孕难产。疾病：昼占脾病，夜占肾病，防停食翻复。出行：未能动身。行人：当有信来，尚在中途。捕获：昼在南方，夜在东方。兵战：有始无终。坟葬：发财损丁。", "毕法云：两贵受克难干贵，合中犯杀蜜中砒。课经云：子加申，胎神坐于长生之上，大宜占孕，不利占产。酉为帘幕，临于日上，大宜占试。倘年命再加酉上，必掇巍科。缘酉为从魁故也。纂要云：三传作财，天将夜皆乘土，生起财神，占利必能遂意。指掌赋云：酉丑巳为献刃，远近俱受其伤。"));
        set(8, new LiuReKe("丙辰日第十局　干上申", "课体：重审，元胎。", "课义：财贵长生，传内俱陈，迤逦相荐，夜变成。", "解曰：申为日财，亥为日贵，寅为丙火长生，三传全值。初传申金生水，中传亥水生木，末传寅木生干，迤逦相生，主人举荐。夜占初蛇末虎，故变为悔也。", "断曰：三传脱支生干，眷丰宅狭，财神临干合干，身裕财充之象。", "天时：三传生日，晴光普照。家宅：人多屋少。功名：财旺生官。求财：得后防惊。婚姻：女益乎男，内助得人。胎产：女孕产顺。疾病：脾胃之疾，宜节饮食。行人：行者立至。兵战：昼吉夜忌。", "毕法云：三传递生人举荐。指南：酉将午时占官运，曰：传将财官，驿马城吏，递互相生，大吉之兆。曰此人向撄重罪下狱，生全为幸，矧有他望。曰月德发用，中绝末牛，谓之绝处逢生。支上皇恩化戌，今虽谪戍，后必显达。果被赦，寻迁司空。古鉴：戊申年卯加子占自身，曰：金生干水，亥生干申，寅生干亥，宅阴得辛，辛年十月，冲得身动。当往东二百里为寅地，又转西北为亥地，复入原处，终年六十九矣。盖宅阴戌中有辛，岁逢辛亥冲巳，十月建亥，故于是时宜动。然始为蛇扰，后为虎拒，往复不远，终归故地。若非蛇作丁神，恐亦难去。寿止六十九者，将为身，时为命，卯将子时，乃卯六子九之数，悉验。"));
        set(7, new LiuReKe("丙辰日第十一局　干上未", "课体：重审，励德，间传，涉三渊，天狱。", "课义：初财末官，仕宦忻欢，壬戌干覆，元子偷瞒。", "解曰：初传之申，乃日之财；末传之子，乃日之官。仕宦遇之，无不欣然矣。但中传戌墓，遁得壬水，遥克日干。末传子水为鬼，夜乘元武。传财化鬼，其为偷盗而欺瞒者，实多也。常人占之，止宜携财告贵，洁豆求神。", "断日：涉三渊之课，不利前行。喜干支上神作合，宾主情怡。支阴合日，干阴合支，彼此相怀，暗中莫逆。惟午加辰上，谓之朱雀入勾陈，凡占讼狱，其动非细，至难解释。末传子鬼可畏　幸干上未土可敌。但当坐守，凡吉凶事，应于辰日。", "天时：水母被克，天罡指阳，日晴夜雨。家宅：青龙与六合生宅，喜气盈庭之象。功名：财旺生官，即可到手：求财：宜速取之，迟则有变。婚姻：干支上神相合，秦晋可谐，胎产：孕成女，母子相合，胎吉产凶。疾病：心经或眼目之症，不能即愈，出行：陆路防口舌，水路吉。行人：中途有阻。捕获：昼占是东方屠户出身，夜占是绿林积贼，难获。兵战：利主不利客。", "毕法云：罡塞鬼户任谋为。课经云：辰加寅为罡塞鬼户，不论在传与不在传，能使众鬼不能窥觑。宜阴谋私祷，合药书符，无不亨利。指掌赋云：申戌子涉三渊，当隐于山林。集义云：申加午用。主炉火之事。"));
        set(6, new LiuReKe("丙辰日第十二局　干上午", "课体：别责，三奇，不备。", "课义：贵及旺龙，昼占即逢，鬼贼羊刃，夜卜须凶。", "解曰：初传亥水，昼乘贵人。中末及干上之午火，皆为日旺。昼皆乘龙，体用俱吉。若逢夜卜，则贵人为太阴而作鬼，青龙为六合而作刃，动必罹其凶矣。", "断曰：别责之课，又名芜淫。阴阳不备，止宜脱节离根，别寻头绪，凡事皆当借径而行。又干神临支生支，乃不自尊大，受屈于人之象。占者守则有余，动则末利。", "天时：青龙升天，初传亥水克干，日中乃雨。家宅：日禄生支，廪有余粟。功名：官星乘昼贵发用，占名可许。求财：青龙乘旺临身，宜守本分之财。婚姻：干上乘旺，支有德禄，可云佳偶。胎产：不备在阴，孕乃成男。疾病：心经受病，或齿痛。出行：罗网临身，必有羁绊。行人：日临于支，即至。捕获：夜占在北方可捕。兵战：昼占则吉，宜柔而胜之。", "毕法云：所谋多拙逢罗网。课经云：干上乘干前一辰，支上乘支前一辰，名天罗地网。守己则为支干乘旺，动谋即为罗网缠身。倘有官人占遇天罗，主丁父服，遇地网主丁母忧。括囊赋云：日临辰位，招两姓以同居。六壬占诀云：干临支生支，名历虚格。盖我上他门，愿尽心力，所为历虚也。凡占利于他人，不利自己。主炉火之事。"));
    }
};
