public class d55 extends KeEntry{
	public d55() {
        set(7, new LiuReKe("己未日第一局　干上未", "课体：伏吟，稼穑。", "课义：支干相逢，中末刑空，无异独足，昼虎三重。", "解曰：己课寄未，未即己也。以未加己，乃是支干相逢。中传丑土遇空，末传戌亦空陷。三传四课，独有一未，并无他神相耦，何以异于独足哉。昼占三重白虎，病讼尤不能免，不特谋望，不成已也。", "断曰：独足之课，事有难行。中传空亡，又名断桥折腰，必有中阻。商贾逢之，宜水路舟船，或小车独载，以应独足之意。干支同位，传内辗转相刑，凡占皆无和气。", "天时：天地不动，支干相并，晴占则晴，雨占则雨。家宅：蛇虎满前，人宅罹患，至凶之象。功名：寅卯子午年命人吉。求财：劫财重重，不利。婚姻：家世相当，必系旧亲，然无吉象。胎产：女孕，腹空易产。疾病：肾水受伤，二八月占凶。遗失：物在家内。出行：不宜。行人：中路有阻。捕获：伏于西北娼妓家。兵战：主客相等，未见其利。", "毕法云：不行传者考初时。课经云：伏吟取刑为传，今中传空亡，不能刑至末传，故前后进退皆难。如占他事，必先得人允许后不践信。若占人年命是巳，则丁马交加，其动甚急，年命上乘魁罡，亦有动象。秘要云：蛇虎入宅，非惊即失。"));
        set(6, new LiuReKe("己未日第二局　干上午", "课体：八专，帷簿。", "课义：从军食禄，方才享福，惟忌昼空，占病必哭。", "解曰：午火属离，有王用出征之象。又为日之旺禄，临于干支之上，故宜食从军之禄，而享其福也。昼占午禄乘空，又非旺禄之比。若占病人，则禄空必致绝食而死矣。", "断曰：八专之课，尊卑共室，人宅不分，多有失礼之象。喜中末二传，及干支上神重叠相生。若仕宦夜占，又当旺相，则名通利达，必历要津，常人须防文书口舌。", "天时：日辰上乘天空朱雀，昼夜晴明。家宅：家道饶裕，昼占防有诓骗。功名：夜占官文甚旺，一岁九迁。求财：文书甚旺，不求自至。婚姻：帷簿不修，占婚最忌。胎产：胎之阴神属阴，孕女，天魁临亥，产迟。疾病：心经受病，或口不能言之疾，难愈。遗失：在中堂觅取。出行：人宅相恋，不能动身。行人：得意即回。捕获：在西北方。兵战：主客相同，势均力敌。", "毕法云：魁度天门关隔定。课经云；戌为天魁，亥为天门，戌加亥为魁度天门，诸占皆有阻隔。纂要云：日之禄神，复作日之旺神，临于干上者，切不可舍此而别谋动作。初传卯鬼可畏，不可凭依。中末仍归日上，所谓到处去来，不如在此也。汇函云：午火乘雀，名为真朱，文书必达朝廷。"));
        set(5, new LiuReKe("己未日第三局　干上巳", "课体：八专，帷簿，孤辰。", "课义：破碎作空，费耗贫穷，丁马遍布。一世飘蓬。", "解曰：发用丑为破碎，又作空亡。昼蛇夜虎，破碎耗散，必至贫穷而后已也。干支中末，巳火四重，既是旬丁，又为驿马。昼占上乘青龙　动而不止，了无定踪，岂能免一世之飘蓬乎。", "断曰：八专之课，劫财发用，凡占不免耗费。又名帷簿不修，占者总宜正己持身，家庭清肃，方免无事。孤辰发传，僧道相宜，绝气临干，宜结旧事。四六月占之吉。", "天时：发用土神，中末乘龙生日，不能有雨。家宅：宅上乘青龙六合生支，宅中必有喜庆事。功名：青龙驿马甚众，当得美差。求财：青龙生日，宜有往来行动之财。婚姻：门楣相当，必宜速就。胎产：女孕，昼占吉，夜占产迟。疾病：肾经受病，齿牙成疾，未愈。遗失：婢女窃。出行：水陆两途，昼夜皆吉。行人：马在干支，即可抵家。兵战：昼夜俱不甚利。", "毕法云：干支值绝凡谋决，龙加生气吉迟迟(六月占)。课经云：青龙乘生干之神，又作月内之生气者，虽目下未即峥嵘，却能徐徐发福。此例喻君子孟施惠于人，不言而有待，尤耐岁寒。照胆秘诀云：用空传复支与干，旧久重新废孟完。三才赋云：巳亥逢驿马并驾，在路奔腾。玉成歌云：父母临干忧子孙。"));
        set(4, new LiuReKe("己未日第四局　干上辰", "课体：八专，斩关，励德。", "课义：财神闭口，不得人手，四墓乘勾，昏迷难受。", "解曰：亥乃日之财神，又是旬尾加首发用，是财神闭口也，坐于寅木之上，畏之而不敢取，岂能入手乎。四重辰墓，业已昏迷。又加以太常勾陈之土，积累于上，其昏迷之状，必有汶汶^^而难受者矣。", "断曰：支干之上，皆乘自刑，宾主不投，人已不浃。又干支乘墓，人昏它晦，凡占皆不亨快　如年命在丑，仗戌冲辰，破其网墓，乃为可救。若年命在辰，天罗自裹，事难摆脱矣。", "天时：日上乘墓，太阳被掩，水又被墓，浓阴不雨。家宅：人昏宅暗，诸事欠利。功名：不吉。求财：暗取妇人之财，然恐生患。婚姻：两愚相耦。胎产：女孕，产凶。疾病：遗漏疯瘫，咽喉肿痛之疾，十一月占凶。遗失：见者竟不肯言，昼占武空难缉，夜占可获。出行：水陆两途，皆不甚利。行人：天罡加干支，行者立至。兵战：不宜妄动，守正则亨。坟葬；乾龙小可发财。", "毕法云：干支乘墓各昏迷，宾主不投刑在上，闭口卦体两般推。纂要云：干支全被上神墓者，如人在云雾中行，我孟昏昧人，却被人先昏昧我也。太乙经云：占与人期会，天罡临日辰者会，天罡大吉加午未者，唤人必来。玉华略云：水逢四季，为鳏者之刑。"));
        set(3, new LiuReKe("己未日第五局　干上卯", "课体：元首，曲直，狡童。", "课义：虎乙夜占，木局为传，常人仕宦，总受。", "解曰：水土生申死卯，又遁乙鬼，夜乘白虎，临干发用而克干，常人必罹病讼。仕宦遇之，以官星作死气，则白虎不作催官，故亦不免于也。", "断曰：曲直之课，先曲后直，干支各乘死气，止宜休息，不利动谋。中传虽财而成鬼局，贪才取祸。曲直作鬼，主有枷之患。", "天时：三传克日，先雨后风。家宅：死气克宅，夜占极凶。功名：官乘死气，春占甚利。求财：当有妇女说合取财。婚姻：占女不吉。胎产：神死将凶，母子不吉。疾病：昼占乃肾经之疾，夜占乃脾土之疾，十月占凶。出行：不利。行人：即至。捕获：当是徒配人。兵战：里勾外连，防人侵害。", "毕法云：人宅皆死各衰羸。指南：丁丑年午将戌时，占推升抚院得否，并日后结局若何。曰：目今会推必遂，但结局不佳耳。用合干支，传成官局，推升必矣。但干支死伤，丧吊全逢。又贵履天罗，斗系日本。行年酉金，冲破官局，末后大有不如意事。后果以流贼犯界，遣将守御，全军覆没，丁艰而归。课经：辛巳年丑将加巳，占陈留县被贼安危，曰：木局克身，人宅皆逢死气，又为丧门死神。引中传天后入墓，主妻财受惊，大凶之课。后果遇贼，家口丧失殆尽。"));
        set(2, new LiuReKe("己未日第六局　干上寅", "课体：知一，四绝，无禄。", "课义：鬼宿鬼门，卦号离魂，酉金勿恃，生祸之根。", "解曰：寅为鬼门，未乃鬼宿，寅未相加，卦号离魂，有人入鬼门之象，占病甚凶。酉能制鬼，似可恃为救神。不知初生末传，迤逦助寅，反为生祸之根。是寅之临于未者，酉实迫之也，宁可恃乎。", "断曰：四绝之课，三传自刑，占病占讼，皆非所宜。四上克下，名曰无禄，凡谋不顺，卑幼有伤。巳火长生，乘虎而临于墓，父母亦防刑克。离魂之格，并防有鬼邪魇魅。", "天时：寅木克日，风霾蔽天，翌日得雨。家宅：病气克宅，染病者多。功名：官临身宅，功名到手。求财：财爻入墓，四季占之得。婚姻：不吉。胎产：女孕，即产。疾病：金土受病，九月占凶。出行：不吉。行人：马与支合，即至。捕获：在西北方。兵战：互有胜败。", "毕法云：干支全伤防两损，夫妇芜淫各有私。指南：己巳腊月丑将午时占，曰：正时胜光，值事天空，必为章奏而占。曰：吉凶若何？曰：四课克下，名为无禄。况贵乘旬空，龙神下克，主在上者不足。轻则降罚，重则削权。又财官禄马。俱入墓绝，以急流勇退为佳。朱雀乘天喜，还得好音归里，后果准冠带闲住而归。"));
        set(1, new LiuReKe("己未日第七局　干上丑", "课体：反吟，井栏射，八专，无亲。", "课义：初值丁马，动意难舍，守空撞空，渐为贫者。", "解曰：巳为驿马，又值旬丁，用作初传，动不容己。丑土作空亡，而临于干上，是守空也。既而入传，是撞空也。四课三传，尽皆空陷。惟余一丁，岂非贫乏之象。", "断曰：反吟之课，格合井栏。如井上架木，易欹易斜，不能长久。事多重叠，喜忧两来。凡事旁求易就，直道难容，有大事化小，小事化无之象。", "天时：日上逢空，青龙又陷，不能得雨。家宅：龙后皆空，家徒壁立之象。功名：申酉年命人吉。求财：无所得。婚姻：支干尽空，占婚不吉。胎产：阴变为男，腹空易产。疾病：脾肺之疾，新病吉，久病凶。出行：丁马虽动，中末空亡，不能起程。行人：天罡加季，行人立至。捕获：在西北猪厕左右。兵战：主客皆劳而无功。", "毕法云：不行传者考初时。课经云：己未日干上丑，支上亦丑，乃是四课全空格。经曰：四课无形，事不出名，纵然出也，也是虚声。订讹云：此课如遇吉神良将，凡事半遂，尤喜见青龙救护。精蕴云：丑加未，未加丑，作年命日干者，占试必中魁元。缘丑中有斗，未中有鬼，合而为魁也。秘要云：未土无克，以未宫己土遥射亥宫壬水为用，全是冲散，故名无亲。"));
        set(0, new LiuReKe("己未日第八局　干上子", "课体：知一，铸印，励德。", "课义：财既双空，丁马虚逢，卯鬼宜助，凶祸难容。", "解曰：子水为财临宅，空而无用。发用之巳火，丁马相并，又落空陷，是虚逢而已。末传卯鬼，宜助初传之丁马，但昼夜上乘元虎，凶祸必动而难容也。", "断曰：铸印之课，占官则吉。夜占帘幕临干，占试必中。胎神临干支干之上，占妻必孕。末传助起初传巳火生干，必得旁人暗助。但生气入墓，不利生计及长上之事。", "天时：巳火生日，晴而不雨。家宅：贵人临宅，平庶必遭官讼。功名：得差得位。求财：财空难觅。婚姻：两空不吉。胎产：胎损产吉。疾病：肺与膀胱有病，当即愈。出行：求名吉，商贾不利。行人：尚未启行。捕获：昼占贼在西南，夜占贼在正西，防走脱。兵战：守则利。", "毕法云：胎财生气妻怀孕。指南：辛未四月酉将辰时占功名。曰：月内定转掌垣，因月建虎马发用，又是铸印乘轩故也。但嫌贵空，恐居官难于久任。果转吏科掌垣，后以武场事降谪。占验：辛巳十月寅将酉时占起复。曰：仲冬月必有起官之征，盖干上贵人虽空，幸乘进气，交仲冬子水司令，填实旬空，便当擢用。且虎马丁神发用，作岁君生日，又四墓覆生，已废复兴之象，起官何疑，至期果推补兵曹。"));
        set(11, new LiuReKe("己未日第九局　干上亥", "课体：重审，曲直，三奇，狡童。", "课义：三木并立，合成曲直，取则为灾，不取可惜。", "解曰：四课之上，两得卯木。三传之内，又见卯木。课传相并，是三木并立也。三传作合，会成曲直，是全鬼伤干矣。初传亥水，乃是日财。干支发用，上乘闭口，孟待不取，财在眼前，急不能舍。取之又惧其化而为鬼，占者止宜以财告贵，纳粟奏名。", "断曰：曲直之课，类成官局，春占大利求官。明暗二鬼，坐于干支。夜乘白虎，常人不免人宅两灾。", "天时：三传克日，水上火下，风雨连绵之象。家宅：干支自克，防夫妻反目，亦防阴小越礼。功名：官星甚旺，发用三奇，功名大就。求财：取之防祸。婚姻：支干自刑，不利。胎产：阴极阳生，孕男产迟。疾病：脾土受病，或肾水不足，秋占可畏。出行：心孟归而不能。捕获：伙盗难擒，秋占方获。兵战：昼占凶，夜占吉。", "毕法云：虎乘遁鬼殃非浅。断验：辛未四月申将辰时占。曰：此课占功名，将来远大非常。传将木局，内成官星，喜木命丁马恩星以化之，逢凶化吉之象。且木逢初夏，正在荣旺之际，又蛇化为龙，将来自然事业日新，功名显赫也。后果晋秩大司马。"));
        set(10, new LiuReKe("己未日第十局　干上戌", "课体：八专，斩关，励德。", "课义：一位财星，四戌来争，争之不已，致讼遭刑。", "解曰：发用之亥水，乃是日财，课传之中，独此一水。而课传四重戌土相争此亥，是人多财少，争必不止。戌上乘雀主讼，且又戌未相刑，构讼遭刑，其能免乎。", "断曰：斩关之课，利逃亡而不利伏匿。贵人立于卯酉，曰励德，利君子而不利小人。若己土寄生干寅，则亥为绝神，亦宜结绝财物事。干支同位，又名懒于取财，惧争夺也。", "天时：朱雀乘墓，昏晦之象。家宅：宅内三刑皆全，刑伤难免。功名：贵禄不见，占官不利。求财：有争。婚姻：昼占可用。胎产：胎上乘酉，私胎育女。疾病：金水之疾，五月占不吉。遗失：夜元乘财，物必贼窃，宜向东北捕之。出行：水陆两途，俱各中平。行人：天罡加季，行者立至。兵战：宜谨密，全功。坟葬：龙脉不真，朝山不甚高耸，且多散乱。", "毕法云：贵人蹉跌事参差。课经云：昼贵临于夜地，夜贵临于旦方，如告贵干谒，多不归一。集义云：戌作朱雀加未，主犬子吠人。照胆秘诀云：日刑杀到忧妻妾。云霄赋云：内室专权，有仙女跨云之象(巳到寅宫)。三才赋云：魁罡临处，多生词讼。"));
        set(9, new LiuReKe("己未日第十一局　干上酉", "课体：八专，独足。", "课义：此系独足，脱欺叠逐，舟车宜驾，婢逃归屋。", "解曰：三传干支，皆在未上。七百二十课之中，惟此一课，名曰独足。酉为败气，而又脱己土之气，重叠逢之，甚不利也。既止一足，非舟车不能行动，惟利水行。酉为婢，逃归屋者，喻不能远去而走脱也。", "断曰：独足之课，寸步难移。又己土生酉，夜占乘后，亦为脱上逢脱。申加午，子加戌，两贵受克，不利干谒。惟宜守己，尚尔费力。", "天时：三传皆毕，天不能晴。家宅：败神脱宅，贫耗之象。功名：占官不利，试中亚魁。求财：不得。婚姻：酉乘后合最忌。胎产：胎不成，产不足。疾病：肺病可愈。遗失：财上乘元，家贼所窃，宜向西方，近池厕处觅之。出行：不宜。行人：未至。兵战：不利。", "毕法云：两贵受克难干贵。古鉴：男命戊申巳加辰，占子息曰：三传皆酉，为少女。与日辰合，算其五数，当生五女。行年上见卯为长男，与元武相冲，男不成胎。酉为大肠，又为女色，主好色而得肠风。酉数六，未数八，寿止四十八而已，果验。又：同日占讼，曰：两边俱不得力，官当自行，干贵无益。后果县宰潜往定验矣。又同日占生理曰：酉为兑为泽，六合为船，不利于陆。若舟行，利可十倍，皆验。"));
        set(8, new LiuReKe("己未日第十二局　干上申", "课体：八专，帷簿。", "课义：申类子孙，却是长生，慈鸟反哺，夜贵尤荣。", "解曰：申为土之子孙，又作土之长生，为子孙者能生养其父母，若慈鸟之反哺也。夜占申又作贵，而生日干，其荣益甚矣。", "断曰：八专之课，比肩发用，昼夜上乘蛇虎，未免虚惊。昼占太常乘长生，临于干支，家有婚姻之喜，或开彩铺酒店。夜占贵人入宅，当有贵人喜庆宴会，或赏赐之类。申为夜贵临身，雀乘午火相克，不可告贵，必忌惮不肯用力。", "天时：虎乘风伯发用，风多雨少之象。家宅：家有喜庆费财。功名：贵人临身，夜占吉。求财：先费后得。婚姻：日后好。胎产：胎防损，产易。疾病：肾经受病，医之愈。出行：水陆俱佳。行人：在途。捕获：在西方。兵战：羊刃乘虎蛇发用，昼夜皆凶。", "毕法云：所谋多拙逢罗网，帘幕贵人高甲第(昼占)。指南：丁丑八月巳将辰时占瓯卜，曰：虽有公卿推荐，恐不能拜。盖因虎刃自他处发用，当突有秦人任风宪兵刑之职者，不由词馆入阁。且中末干支年命俱见罗网，是秦晋梁益之人，在中阻隔。又夜贵居本命之上，必赋归来，后果应。"));
    }
};
