public class d56 extends KeEntry{
	public d56() {
        set(8, new LiuReKe("庚申日第一局　干上申", "课体：伏吟，元胎。", "课义：昼逢四虎，动无少阻，丁马入传，行人商旅。", "解曰：申禄本是虎乡，干支及发用，共有三申，昼又乘虎，故曰四虎也。伏吟本静，中寅财马，末巳遁丁，其动象略无少阻，如行人之在商旅也。", "断曰：自信之课，上乘旺禄，本宜静守，但金日逢丁，岂能安享。三传自刑，财爻化鬼，大宜纳粟奏名，不宜求财黩货。末传巳与日合，上乘勾朱，常人必有官讼，不可解散。仕宦占之，官运方荣，扬历正未有艾。", "天时：青龙入庙，不能得雨。家宅：白虎临禄入宅，病讼不免。功名：官禄马俱动，不日迁擢。求财：人多财少，争夺有讼。婚姻：昼占不吉。胎产：占孕防堕，占产防病。疾病：肝经有疾，或头痛难忍，难愈。遗失：不出本家。出行：求官吉，求财不吉，防遇盗。行人：刚日顺吟，行者即回。捕获：是奴仆勾伙。兵战：白虎刑冲，兵占不吉。", "毕法云：信任丁马须言动。课经云：庚申日伏吟，丁神在传，占访人必出外干事，先许后改。集义云：天后发用，家中有患头风之妇。末传克初，先凶后吉。纂要云：财官禄马，天城天吏俱全，白虎入庙，蛇生角，勾陈捧印，若有岁月扶合，仕宦大吉。"));
        set(7, new LiuReKe("庚申日第二局　干上未", "课体：八专。", "课义：初遭网罗，障难无挪，秋冬事贵，得助极多。", "解曰：发用酉金，为日之罗网。倘若动谋，则裹身绕宅，其为障蔽险难，无可挪移也。秋占酉为旺神，夜未又为贵人，临于干支中末，必得三处贵人，相助成功，不劳心力矣。", "断曰：八专之课，事多重叠。干支之上，各乘生气，又交互相生，凡占皆有利益。昼占帘幕临日，试必高中。贵人入宅生支，应得上人照拂，或有神佑。未上乘空，家有眢井，亦防酒食生谤。六月占此，名利定然通达。", "天时：火上水下，晴而不雨。家宅：夜贵生宅，必得贵人扶助。功名：夜占最吉，昼占利于考试。求财：生之者众，不求自得。婚姻：夜占甚吉。胎产：胎上乘寅，属阳，男孕，易产。疾病：脾土之疾，饮食起病，二月占不吉。遗失：当是右邻，或前邻婢女窃去。出行：不能动身，出则吉。行人：昼占未回。兵战：夜占将帅有功，昼占不能取胜。坟葬：生死均安，初年有克。", "毕法云：互生俱生凡事益，帘幕贵人高甲第(昼占)。课经云：庚申日午加申，作支干之阴神，克支克干。春占则午为火鬼，夜将乘蛇伤宅，难免火灾。玉成歌云：天空在未井多怪，兼主人遭疾病缠。秘要云：午年命人，上乘丁神临朱勾，必有口舌官非。"));
        set(6, new LiuReKe("庚申日第三局　干上午", "课体：元首，励德，间传，顾祖。", "课义：若取寅财，生起祸来，财官绶印，君子宜哉。", "解曰：寅为日财，临于末传。若孟取之，则助起初传午鬼，为祸不小。占者止宜纳粟求官，最为元吉。若常人遇之，财爻化鬼，伤干克支，彼此两损矣。", "断曰：顾祖之课，事宜复旧，间传而退，有阻方成。官讼则寅为教唆之人，捕盗则寅为窝藏之主，皆不宜占。干支皆败，不宜交易。夜蛇加午临干，若无孕妇，主有阴人离散。", "天时：青龙升天克日，昼占有雨。家宅：人宅皆有败气，夜占尤有惊恐。功名：财官两旺，占官最吉。求财：取之生患。婚姻：昼占吉。胎产：男孕，产逆。疾病：心经受病，或肝木受伤，甚凶。遗失：西南方道上七十五里，有弄猴者，问之即是。出行：动静皆可，不宜商贾。行人：戌日当到。兵战：防有侵凌，气均力敌。坟墓：祖坟则吉，不利新阡。", "毕法云：干支全伤防两损。古鉴：己酉生人，九月卯将巳时占讼，曰：顾祖课原有讼根，金日得巳时，为天网课。末传寅木为胥吏，生起午官来克日干，主一吏人不足，始终被害。行年在子，上乘戌土，会寅木午火自去烧身，患必自招。午为火针，必遭刺配。且年上见戌为军，当一千八百里。缘午数九，支干两见，会成火局，旺相十倍也。"));
        set(5, new LiuReKe("庚申日第四局　干上巳", "课体：元首，元胎。", "课义：夜克昼生，丁马俱迎，末之亥水，能败能成。", "解曰：巳火发用，昼将勾能生干，夜将雀能克干。巳遁旬丁，又为驿马，其动自不容己。末传亥水，克巳救干是其成也，生寅助鬼是其败也。", "断曰：元胎之课，气象维新。巳为长生官星，而三传又递生之，官途坦顺，一路荣华。常人则金日逢丁，凶祸难免。旬尾加于旬首，缄默可以保身。德入天门，占试必然得意。", "天时：朱勾乘火发用，不雨而晴。家宅：勾雀克宅，初传鬼动，病讼不免。功名：有人举荐，赴任最速。求财：当隔手取之。婚姻：财官六害，尚有阻隔。胎产：男孕，即产。疾病：心经之疾，淹缠难愈，须防反覆。出行：愈远愈好，出门时有阻。行人：已在半路。捕获：西南方酒食店中。兵战：彼此主将皆强，防有本国兵卒泄机。坟葬：日久发福，初葬不利。", "毕法云：干支全伤防两损，金日逢丁凶祸动。课经云：三传年命日辰，逢旬内六丁神者，如乘勾陈，必被官讼勾追而动。如乘月内死气，必亲族在外府郡，报死亡而动。如乘贵人，必贵人差往而动。如乘玄武，必孟逃亡而动。蛇雀尤急。惟有官人占之，则赴任极速，反不孟占人行年上神克去丁神，若常人则又宜制其丁神也。"));
        set(4, new LiuReKe("庚申日第五局　干上辰", "课体：重审，润下，斩关。", "课义：盗气传逢，初中幸空，独有辰土，生气无穷。", "解曰：申子辰会成水局，以盗干气。初传子水逢空，中传申金落陷，是水局已破。独存辰土，坐于长生之上而生干，则生生不已，宁有穷尽耶。", "断曰：润下之课，有动而不息之形；孤神发用，有茕茕孑处之象。占者家有虚耗，子不成业，凡事皆有损无益。辰土属于父母，昼夜乘元，失脱不免。总宜静守，生意盎然。若或妄动，即名泉源涸竭，虚费百出矣。", "天时：龙神昼夜皆空，出旬方雨。家宅：干支各自受生，人宅两旺。功名：龙禄皆空，官星不现，占名不吉。求财：宜守家财。婚姻：支干自刑，占婚不吉。胎产：坎局生男，占胎有损。疾病：脾土之疾，医不得力。遗失：元武生干支，且传得回环，虽失必得，北方寻之。出行：近家处防盗失。行人：未能动身。兵战：虚惊虚喜。坟葬：水法甚合，但来龙系假脉，亦有空陷。", "毕法云：互生俱生凡事益。壬髓经云：干上神生干，支上神生支，主各有生意，彼此和顺，或两家合本营生尤应。纂要云：甲戊庚三日，丑未临亥，为贵登天门，神藏煞没，凡谋亨利，无阻无碍。照胆秘诀云：用空传复支与干，旧久重新废孟完。指掌赋云：子申辰为仰元，守凝寒之困。"));
        set(3, new LiuReKe("庚申日第六局　干上卯", "课体：知一，龙战。", "课义：丁鬼中遇，子戌可拒，却祸除殃，卯财任取。", "解曰：巳为日鬼，上乘遁丁，居于中传，为祸非浅。所可拒之而无畏者，上则子水克之，下则戌土墓之，全然无气，殃祸无忧矣。官鬼既伤，独存卯木，为日之财神，居于干上，可以任意取之，而无害也。", "断曰：知一之课，事有两歧，干支坐墓，自甘招晦。四月占之，妻财生气，必然怀妊。五行入墓，百事皆宜休息。", "天时：青龙带水升天，巳日有雨。家宅：鬼墓发用，四下贼上，防受家奴克制。功名：官星受制不吉，四月占利。求财：宜得门户之财。婚姻：财旺，男占可就。胎产：男孕，防损胎。疾病：肝家风疾，脾肺亦伤，可医。出行：路上防盗失。行人：尚在中途。捕获：西北方积盗，现有官司。兵战：微有益利，尤宜加谨。", "毕法云：人宅坐墓甘招晦，二贵皆空虚喜期。占验：申将丑时，占携妻子远行吉凶若何。曰：男女远行，俱不得意。中途被劫，死于他乡，有沉溺破舟之虞。盖因干支入于空墓，中传劫杀乘丁，刑克干支。末传死气，子加巳又为死字。且壬戌加卯发用，是河井相加。又卯受干克，主车船破坏，其祸必矣。后果近江西百余里，男女五人，皆被盗而死。"));
        set(2, new LiuReKe("庚申日第七局　干上寅", "课体：反吟，元胎，六仪。", "课义：四财一禄，夜虎趋逐，四马并进，奔驰反覆。", "解曰：寅为日财，两见于日辰，两见于初末。申为日禄，独现于中传，是四财一禄也。如用夜将，寅财上乘白虎，又为驿马，来往反覆，动坐鬼乡，其奔驰迫促之状，不可言也。", "断曰：反吟之课，德丧禄绝，凡占止宜结绝旧事，不宜求财问禄。犹幸六仪发用，昼占天后生财，虽于反覆不定之中，尚可沾其余润。如占妻病，吉少凶多矣。", "天时：龙虎交冲，必有疾风暴雨。家宅：夜占白虎临于干支，人宅必见惊忧。功名：德禄逢绝不吉。求财：满目皆财，不能安享，常有风波。婚姻：男占吉，有伤克。胎产：孕凶，产吉。疾病：脾土受伤，须防翻覆。遗失：旧处寻觅。出行：有阻。行人：马临干支，行者立至。捕获：西方军隶，合伙作贼。兵战：夜占有惊，防有败衄。", "毕法云：二贵皆空虚喜期，干支值绝凡谋决。集义云：庚申日反吟，支干之上，各乘绝神，宜结绝财物之事。玉成歌云：反吟占事休言定，往复双双两事因，常古须主身摇动，不动人情有怨心。指掌赋云：传见六仪，病将瘥而狱自出。"));
        set(1, new LiuReKe("庚申日第八局　干上丑", "课体：八专，帷簿。", "课义：四员昼贵，全然无气，纵有浮财，夜占可畏。", "解曰：丑为日贵，支干中末，四重丑土，皆遇旬空，全然无气矣。发用卯财，本非课中所有，浮泛不实。如遇夜占，则太常天空，重重皆土神覆日，昏滞之象，大可畏也。", "断曰：八专之课，顺数卯神发传。妻当别娶，财必改求，凡事皆有节外生枝之象。又名帷簿不修，尤防淫u。日阴克日，而日上戴墓，家中当有小人暗地扼~，而彼昏不知也。", "天时：日墓临干，昏晦不雨。家宅：人宅皆不亨快，且防病讼。功名：贵空不吉。求财：甚少。婚姻：支干皆空，不利。胎产：胎财发用，当得长男，腹空产易。疾病：脾胃受病，正二月占不吉。遗失：前堂与厨房寻之。出行：虽有贵人结伴，不宜动身。行人：尚无归意。捕获：西方女婢，或银铁匠之类，正有讼事。兵战：功不能成。坟葬：空穴无气。", "毕法云：干乘墓虎无占病。指南：甲申年酉将辰时占京师安危。曰：贼自西山出奇，用骡车木韫，先攻西南，后攻东北。必有内变。盖贼符自戌发用，克中末并干支贵人。而天空临寅，此地疏虞，贼必由此而入。两阴神作虎鬼，克干克支并伤岁，均乃左右献城之象。贼果明攻张掖门，暗逾东直门，城中鼎沸，开门降贼。"));
        set(0, new LiuReKe("庚申日第九局　干上子", "课体：元首，润下，励德。", "课义：脱空满前，水作三传，昼夜水将，耗盗缠绵。", "解曰：三合水局，脱干之气，又兼初末空亡，而昼夜天将。俱乘龙蛇元武，无非水兽，名曰枝断其根，流消其源。又是三合，不惟耗盗之虞，而亦缠绵无已也。", "断曰：润下之课，流而不息。龙门之险，始于滥觞，有江河日下之势。金生干巳，而死于子。干支以及末传，三子坐守，皆属死气，止宜休息，不利动谋。", "天时：水局空二，不能多雨。家宅：元武临于宅阴，防有盗失。功名：剥官煞多，占官不吉。求财：子孙生财，可以有望。婚姻：支干皆空，不吉。胎产：孕损产速。疾病：肾水不足，以致脾肺虚泄，峻补方愈。出行：孟动不动。行人：近者即到，远防不测。捕获：西南七十五里僧道家。兵战：彼此各乘死气，休兵养士为上。", "毕法云：人宅皆死各衰蠃，脱上逢脱防虚诈。课经云：干上乘子，支上亦乘子，全乘死气，不宜吊丧问疾。如更乘月厌，死墓凶神，占病必死。又干上子，夜将上乘青龙，乃庚干生上神子水，子水又生青龙，三传并来盗日，凡占尽被脱耗，虚诈不实之象。集义云：子加申乘龙，主望远处医僧。"));
        set(11, new LiuReKe("庚申日第十局　干上亥", "课体：八专，帷簿，寡宿。", "课义：凡事缄默，可脱灾厄，事关贵人，甘受岑寂。", "解曰：亥为旬尾，临于干支之上，复作中末两传。凡事退守缄默，仗此制鬼，则灾患可免。发用丑土，乃是昼贵，旬空覆狱，不受其生，反被其墓。若孟与贵图谋，枉受岑寂而已。", "断曰：帷簿不修之课，尊卑共室，男女不分，事多重叠，忧喜两来。凡占大宜谨之又谨，方免后悔。干支中末，亥水四重，盗脱干支。少有所动，耗费无极。常人尚可，仕宦最忌。", "天时：墓神发用，日上乘亥，先晦后雨。家宅：朱勾脱干脱支，口舌争讼难免。功名：不利。求财：暗财可觅。婚姻：不吉。胎产：女孕，孪生。疾病：肾经与膀胱之疾，或闭口不食，六八月占凶。遗失：失于猪栏秽厕之中。出行：有阻，防官讼是非。行人：问子孙即来。捕获：西南方缙绅旧家。兵战：诈而不实。坟葬：穴中泄气，不吉。", "毕法云：人宅受脱俱招盗，宾主不投刑在上。纂要云：干上脱干，支上脱支。更有支上脱干，干上脱支。凡占遇此，人被脱赚，家被盗窃。如占病体，必因起盖屋宅费用，以致心气脱弱，而成虚惫。宜服补元调气之剂，自然痊愈。肘后经云：初来克末凶还甚。要录云：当门台土，出行有阻。指掌赋云：丑遇天空为矮子。"));
        set(10, new LiuReKe("庚申日第十一局　干上戌", "课体：重审，斩关，间传，向三阳，u女。", "课义：戌覆宅身，驿马虚寅，动被虎盗，末逢丙辰。", "解曰：河魁覆于身宅，中有驿马，其动自不能已。初传子水旬空，昼乘白虎，以盗于气。末传辰土，遁鬼伤干，虽动亦何益乎。不如坐守干上戌旺之气，尚有生意也。", "断曰：向阳之课，贵登天门，罡塞鬼户，可以利有攸往。但发用既空，中传财马亦陷，动则生灾，不可造次。戌为鬼墓，临于干支，昼夜乘元，应有匪人，暗中觊伺，所当思患而预防也。孤辰发用，惟于方外相宜。", "天时：水神空，无雨而晦。家宅：元武入宅，宜防盗失，子息防病。功名：不显。求财：求多，得少。婚姻：男女皆有暗疾。胎产：阳极生女，防有暗病。疾病：水来克火，缠绵不愈，五月占尤不吉。出行：不能动身，亦防盗失。行人：马陷未来，或七日到。捕获：武阴是子，当于北方近水女人家捕之。兵战：须防失众。", "毕法云：互生俱生凡事益，罡塞鬼户任谋为。课经云：罡塞鬼户不论在传不在传，能使众鬼不敢窥觑，宜逃灾避难，吊丧问病，合药书符。玉女通神诀云：用神若还害天乙，是为四s凶不测(夜占)。指掌赋云：子寅辰向三阳，而所望光明。"));
        set(9, new LiuReKe("庚申日第十二局　干上酉", "课体：八专，帷簿，三奇。", "课义：守旺则昌，动则有伤，两酉为刃，灾害非常。", "解曰：干上酉金，为日之旺气，守之则人宅皆昌，彼此受益。倘或一动，而逢初传之亥，脱盗其酉，则酉为羊刃，中末皆逢伤害，必非寻常可比矣。", "断曰：帷簿不修之课，内不隔而外不遇。事多重叠，忧喜两来。守正则家有余财，妄动则身无完体。且太常持刃，当因酒色丧身。占者切宜惊醒，以礼自持。", "天时：毕宿临日，水神发用，可望雨泽。家宅：人宅皆乘罗网，安享则吉，亦防盗失。功名：防有内服丁艰。求财：不得。婚姻：课体不吉，防有越礼。胎产：胎上乘罡，男孕防堕。疾病：肺经咳嗽，木气不舒，四月占不吉。出行：不宜动身。行人：即到。捕获：在西北方江海之旁。兵战：各乘罗网，互有杀伤。", "毕法云：所谋多拙逢罗网。古鉴：丁卯四十三岁，妻戊寅三十二岁，卯将寅时占产，曰：庚为子，申为母，见酉为羊刃，是面前羊刃也。太阴兼之，其凶尤甚。母是申，以酉为破碎，乃面前进步破碎也。夫之行年居酉，是见妻子皆受破碎也。妻之行年上见寅，又兼鸡嘴。干支一处，羊刃破碎，又是自刑。先且自害，不全其身，后果不利于母。"));
    }
};
