public class d6 extends KeEntry{
	public d6() {
        set(8, new LiuReKe("庚午日第一局　干上申", "课体：伏吟，元胎，自任。", "课义：昼德乘虎，中财休取，献纳尤宜，不利商贾。", "解曰：申乃德禄，昼占乘虎，不可守也。中传之寅乘蛇作财，已不可取，而况寅上又乘遁鬼乎。末传巳火又来伤干，守之无益，动必大伤。不如静守惊危之德禄，还可以聊生也。庚午伏吟，干支拱定夜贵人。若年命在内，大宜告贵用事，得以成就，拱日禄亦然，", "断曰：自任之卦，元胎为用，旺相则吉，休囚则凶。惟宜守己待时，不宜妄动。如有必不得已而动，则又有功。", "天时：占晴不晴，占雨不雨。功名：昼占利于武职，或有节钺之任。求财：迟得，不可贪多。家宅：优游静守，不宜躁动；若强出头，必有祸患。婚姻：夜占性躁，恐有刑伤。胎产：双生男喜。官讼：防有刑责，和解则吉。出行：马已动矣，陆行甚利。行人：近者即刻可到。遗失：在家中旧地再寻。捕获：尚在本境。疾病：肺家受病，巳午日防增病。出兵：昼占不胜，夜占稍吉。", "毕法云：旺禄临身徒妄作，任信丁马须言动。庚午伏吟子作虎，冲支上午，为狮兽冲宅，必有对邻兽头冲其本家，或道路上有狮子冲宅，以致家道衰替。如对邻陷于空亡，便不足畏也。丑加丑，亦谓之两贵夹。"));
        set(7, new LiuReKe("庚午日第二局　干上未", "课体：遥克，逆茹，蒿矢。", "课义：交关且尔，可疑蒿矢，蛇雀空逢，五火焚毁。", "解曰：午未申巳交互相合，然以未合年而悔，以申合巳而疑，则虽交车相合，而尚多不足之处也。午火伤庚，其力虽微，而三传引入鬼乡，三传俱火，夜占又逢蛇雀，五火齐发，庚金焉能免其焚毁哉。折蒿为矢，力弱难伤，缓而且轻。然引入鬼乡全是火局，则微金受烁矣。惟仕宦占之，则官星虽旺，赖干上未土化煞生身，方为官文全美。昼占帘幕贵人临干，占试事必隽。", "断曰：蒿矢之卦，兼逆连茹，其始虽有凶勇之势，愈久愈微，盖忧喜皆末实也；祸由外作，兵利为主。", "天时：课传全是火土，久雨当日即晴。家宅：宅坐长生，主人宅兴旺，夜占防火烛。功名：甚利，以退为进。求财：合伙营生则利。婚姻：可成，女益于男。眙产：属女，孕则稳，产则易。疾病：病势甚急，防有血症，惟养脾补肾为妙。出行：恐有阻，不能动身。行人：尚未归。遗失：当在家中东南。捕获：在西北方，楼台池水之旁。出兵：可以取胜，昼占则吉。", "毕法云：帘幕贵人高甲第。玉成歌云：水乘火将皆惊恐，勾雀同传主讼争。指掌赋云：三传克日，众鬼难堪。"));
        set(6, new LiuReKe("庚午日第三局　干上午", "课体：元首，间传，顾祖，回环。", "课义：幸名顾祖，亡门欺侮，可恨者寅，暗助初午。", "解曰：午发用，间退至寅，名为顾祖，其名可谓幸矣。但支午临干克庚，又为上门乱首，是敢于欺侮者也。弃而投中传之生，而辰遭夹克，又不足投。更进而就末传之财，乃寅木遁鬼乘蛇，暗助初传午火，反为伤庚，尤为可恨也。庚遂移而投戌上以受生，亦可谓之避难逃生。干上午，支上辰，乃干支与初中传，拱地盘之夜贵，宜告贵成事。助刑戕德格：乃六处有神作支之自刑，又作干鬼，又结连三传为鬼，来伤庚德。", "断曰：见机之卦，又名顾祖，有功成身退之象。顾祖诗曰：顾祖迎亲复旧庐，求财谋望始图维；惟有庚日不宜见，鬼来又向病中居，", "天时：龙神发用，宜有雨。求财：到手之后须防后祸。婚姻：不吉，不孝公姑。胎产：胎临长生，恐其难产。疾病：脾家受克，不能即愈。争讼：有人唆讼，防遭遣责。出行：驿马陷空，未能起程。行人：末回。捕获：在正东方，难获。遗失：为人带去。出兵：夜占将星克武，可以全胜。", "毕法云：我求彼事干传支。课经云：末传寅生起初传午火而克庚金，乃教唆词讼之人也。或为公曹、吏人、道士，或为胡须人，或属虎人，或其姓从木，再详天将言之。"));
        set(5, new LiuReKe("庚午日第四局　干上巳", "课体：元首，元胎。", "课义：昼生夜克，讼凶官吉，亥喻萧何，贵不悯恤。", "解曰：巳临庚上，昼乘勾陈，则土将能生。夜乘朱雀，则火将为克，天将生克，吉凶分途矣。常人占讼，则官兴而凶。仕人占宦，则官成而吉。亥为末传，来克初巳，固能消灾免祸。然生寅助巳，亦未始非亥也。所谓成也萧何，败也萧何，皆亥之一神为之矣。两贵入狱，求之则怒，岂有悯恤之心哉。", "断曰：元首之卦，理宜从顺。元胎之课，事宜从新。苟能事事顺理，必有革故鼎新之象。", "天时：朱勾发用，龙神入庙，不能得雨。家宅：女人当坐喜，亦防火烛。婚姻：干上官，支上财，三传财生官，主女益男家，来即受胎。胎产：是男喜，亦平安，但有病，产时略艰难。疾病：肝家有火，初病易治，久病难痊。求谋：有人在中间撮合，便孟干中取利。出行：水陆平安，但恐有病，亦防有阻。行人：马犹落空，不能即至。遗失：有人欲破败之。捕获：隐东南贵人家。出兵：主胜客，恐旷日持久，频有书信往来阵前。", "毕法云：金日逢丁凶祸动。课经云：巳加庚，卯加午，为丁神临宅，是为人宅罹祸。盖日上神克日，而辰上神乘丁，又克日故也。丁神乘卯，卯为日财。占者必因妻而起祸，否则因财而起祸。卯为胎财，若十月占，死气在卯，毕法所谓胎财死气损胎推也。"));
        set(4, new LiuReKe("庚午日第五局　干上辰", "课体：知一，炎上，斩关。", "课义：自来生身，切勿信凭，倘遇夜卜，总是幽冥。", "解曰：辰来生支，昼夜皆乘元武，恐反虚盗，不足凭也。戌午空陷，不能会火局以克我，是彼虽有虚声，而无实祸也。寅为财神，坐于鬼方，亦不敢取，是吉凶皆无所就矣。至于夜占遇之，愈加幽冥。然而大体不吉，岂独夜占为然哉。干上辰土生庚金，支上寅木生午火。只宜坐待其时，倘动其干支，即坐于脱气之乡，谓之乐里生忧。戌为六合，临寅奸丑，阴私不明，奴婢逃走。戌为孟上神，故不论涉害。", "断曰：炎上之课，斩关可通，有旧事翻新之象。三传全鬼，反为不畏，当有鬼神暗中默佑，转祸为福。", "天时：炎上局，自戌传寅，主晴。家宅：宅多暗昧之神，家有阴私之事。功名：自墓传生，官星正旺。求财：财神临支守虎，难以到手。婚姻：一派私淫之象，断不宜成。胎产：阴私婢妾之孕，恐是病胎。疾病：昼占当是肺气受病，宜清邪火，急切难愈。出行：宜从陆路，亦防盗贼。行人：他往未到。遗失：已为人取去。捕获：在北方道路旁，或在僧道家。出军：贼从西转东，其势正盛。", "毕法云：众鬼虽彰全不畏，后合占婚岂用媒。课经云：三传虽全伤日干，不知反生干上辰土，育养庚金，是谓传鬼为生格。"));
        set(3, new LiuReKe("庚午日第六局　干上卯", "课体：比用，龙战，官爵。", "课义：长生虽在，全无倚赖，丑为干墓，卯作支败。", "解曰：巳乃长生，落于空亡，又被初传戌墓，末传子克，其不足倚赖明矣。丑为干墓，而临干支。卯为支败，而临于干。彼此交互，俱无美意，是四课三传，皆无益矣。况初中俱是空陷，吉凶不成，尤不宜占父母病也。干上卯生支，支上丑生干。虽有生旺之名，反成衰败之象。盖以卯败午，而丑墓庚也。不可作耳生格。亦有干神生干，支神生支者，名俱生格，主彼此各有生意。六庚日卯加申，乃胎临本日，占产可言当日便生，为胎神克绝也。", "断曰：比用之卦，从违宜审，又名龙战。君子修德则吉，小人违德则凶。凡事止有虚声，不足深信。", "天时：阴霾未雨。家宅：外面尽好，中实破败；凡事虚花，全无实际。婚姻：心虽孟成，宜防刑耗。疾病：向有旧病，医之可以除根。求财：些少不多。功名：不能如意，夜占当有同姓人提拔。胎产：男喜，占产即生。逃亡：自归。行人：有阻未至。出行：未能动身。行师：为主者胜。", "毕法云：金日逢丁凶祸动，胎财生气妻怀孕。照胆秘诀云：戌加卯上是合乡，信至人归见福昌。"));
        set(2, new LiuReKe("庚午日第七局　干上寅", "课体：反吟，涉害，元胎。", "课义：财虽可绝，七虎排列，切勿取财，秋占焚燕。", "解曰：寅财临申，巳投绝地，况兼七虎相持，岂可取乎。秋占火鬼是子，昼将乘蛇克宅，主有火灾，宜以井底泥涂灶禳之。干支拱昼贵，大宜干贵。又昼夜两贵相加，必求两贵人而后成；或干涉两贵人，或谒贵不见，往见别贵；或贵人在宅，自会别贵，大概不止一贵人也。庚日申为本命，反吟课为人入鬼门，占病必死。财遁丙鬼，必因财致祸，因食伤身　因妻成讼，占者切宜谨慎。", "断曰：无依之课，又曰元胎，大抵人情暗中不顺，反覆后成，纵有吉神吉将，凡百艰难。", "天时：昼占主雨，夜占主风。家宅：主有变更之事，亦防有孝服。求财：反覆不定，须防祸福。婚姻：夜占女子美貌，昼占不吉。胎产：生亦不育。疾病：病不一症，医不一方，昼占是火克肺金之病。行人：未至。出行：恐防有阻，半途而返。捕获：不可得。遗失：原在旧地。出师：杀气太重，止宜和好。", "毕法云：虎乘遁鬼殃非浅，宾主不投刑在上。课经云：庚日反吟伏吟，皆是地盘旬首上神乘元武，为闭口课。玉成歌云：反吟占事休言定，往复双双两事因；常占须主身摇动，不动人情有怨心。"));
        set(1, new LiuReKe("庚午日第八局　干上丑", "课体：比用，用墓，闭口。", "课义：生旺财气，三传皆值，夜元雀虎，未财衰替。", "解曰：初传辰土生干，中传酉金为日之旺神，末传寅木作日之财爻。三传有生有旺，兼有财气，故曰生旺财气，三传皆值。细视干上丑墓，弃之诚是。但投初传，则辰落空亡，不可相倚。再就中传，又逢羊刃网罗，亦不可近。再投末传之财，又带丙鬼，复乘凶将，真衰替至极之象也，", "断曰：蓦越之课，事皆忽起，患由内生，占必仓卒，情必疏远。脚踏空亡，只宜进步，不宜退缩。凡占多系虚惊，究竟终无实济。", "天时：天罡指亥，水运乎上，箕毕相会，风雨大作。家宅：午乘白虎作干之阴神，亦是催官使者，仕宦安居，常人防病讼。求财：财气虽有，恐取之致祸。婚姻：寅乘天后，又系日之妻财，与日冲克不吉。胎产：上强下弱，胎神属阳，孕必生男。功名：禄败官害，墓神覆日，占官不吉。疾病：病神克宅，病者必多。遗失：元武盗宅，被窃难寻。出行：陆路有墓，乘贵可行，水路空陷，虚费必多。行人：天罡加孟，行者未发。出师：利客不利主。", "毕法云：人宅受脱俱遭盗，脚踏空亡进用宜，三传互克众人欺，墓神覆日人昏晦，虎乘遁鬼殃非浅(昼占)。课经云：庚午日，丑加申，干上丑脱支，支上亥脱干。彼此相脱，真有东手财来，西手财去之象。"));
        set(0, new LiuReKe("庚午日第九局　干上子", "课体：涉害，润下，闭口，励德，斩关。", "课义：脱干伤支，占失无疑，人身多病，宅渐崩颓。", "解曰：申子辰水局，脱日干之庚金，去克支辰之午火，所以占人多病，占宅崩颓也。大凡全脱，不止于病，更主遗失财物，故曰占失无疑。再若脱上逢脱，则事事虚诈，令人不可思拟。", "断曰：见机之课，须斟酌时宜，见可便行，最忌迟疑。若操刀不割，必致自伤。支干上神，自相脱骗，真似掩耳盗铃之状。若不见机，终必损己。更详发用，乃系干阴。占事必从外起，患从外生，凡百皆主无益而有损。", "天时：青龙带水升天，更兼润下，大雨无疑。功名：子孙爻现，占官不吉。求财：虚耗百出，焉能有得。婚姻：夜占青龙临干，可称佳婿；戌乘午上，女颇不佳。胎产：孕必生男，昼将损母，夜将伤儿。疾病：必系虚弱之症，或是怔忡心疾。遗失：两元脱干，盗尽财物。出行：中传龙乘驿马，庚申同宫，如乘飞骥。行人：天罡加仲，已在中途。出师：以日为我，辰为敌。日上作申子辰水局，辰上作寅午戌火局，以水制火，直如摧枯拉朽耳。盗贼：元武乘辰，必系目大眉粗，须长凶相。盗神乘申，必在西南方，近州县城关之所，远则村野冲要之地，三叉路口，或邮亭马舍之间。", "毕法云：脱上逢脱防虚诈，六阳数足须公用。课经云：庚午日子加申，课传皆阳，凡占利公干，而不利私谋。指掌赋云：辰申子为呈斗，玩阴阳于天象。"));
        set(11, new LiuReKe("庚午日第十局　干上亥", "课体：重审，高盖，三交，伏殃，二烦。", "课义：财内藏丁，无礼相刑，必然凶动，休倚贵屏。", "解曰：卯乃日财，暗藏丁火，潜克庚干，子卯为无礼之刑，必有横逆相加，故曰凶动。昼贵居戌，夜贵居辰，皆云入狱。且昼贵临夜地，夜贵临昼地，又曰贵人蹉跌。或有干请，何能相助。占者勿恃为屏障而肆志，庶能免患。", "断曰：网罗发用，捕获相宜，羊刃与勾雀相并，临于酉上。婢媵防灾，见财即取，必至受伤。或妻妾相竞，祸起萧墙，家破后已。缘课传皆冲，转转相破放也。", "天时：毕宿发用，天罡指丑，龙蛇在传，必主大雨。功名：官星不现，贵人入狱，而且蹉跌，占官不吉。求财：财神虽现，嫌其化鬼，不宜取之。家宅：门户破败，居亦不久：婚姻：干上乘空，男家不吉；支上乘破，女家不吉。胎产：胎神系卯，必产娇娃，子午相冲，妊必动摇，疾病：虚痨之症，难禁丁克，亥为庚之病地，恐难脱体。出行：末传乘丁，必主行动，无甚好处。行人：天罡加季，末传乘丁，行人立至。遗失：不见元武，定非窃盗，不遗于酒地，定失于儿曹。出师：干上乘脱，支上乘败，末传乘丁，谨防慎守。", "毕法云：金日逢丁凶祸动，贵人蹉跌事参差；宾主不投刑在上，传财化鬼财休觅。神应经云：庚午日亥加干，末传见卯，上乘厂神，的是因妻而凶动，由财而祸起。"));
        set(10, new LiuReKe("庚午日第十一局　干上戌", "课体：涉害，间传，乱首，斩关，魄化，芜淫，涉三渊，飞魄。", "课义：中末空坠，自招其祸，事绪萦纡，病者难可：", "解曰：中传戌土为鬼之墓，末传子水为干之脱。鬼墓既空，见脱不脱，庚既无患矣。乃自投干支上，受午火之燔炙，岂非自招其祸耶。由干而传干支，由支而传于初，由初以及于中，又传作支干之阴神，真是四课不离于三传，三传不离于四课。头绪甚多，萦纡转辗。病者自不能脱然，忧疑自不能释然也。", "断曰：乱首之课，五伦悖逆，忧患难消。吉事易成，仇讼不释。若逢吉煞，可以避难，大宜书符合药。缘贵登天门，神藏煞没，罡填鬼户，魑魅潜消也。", "天时：课传皆阳，天罡临寅，必主大晴。功名：禄临支上，权摄不正。求财：财神不现，恐无所得。家宅：禄居宅上，日德相并，宅美财丰之象。婚姻：男颇不佳，女甚美好。胎产：孕必生男，产母安吉。疾病：病缘自致，有德可解。出行：乘马人宅，不能远行。行人：天罡加孟，马未出宅，中传空墓，行人迟滞。遗失：元武乘空，失物难有。出师：利主不利客。", "毕法云：权摄不正禄临支，罡塞鬼户任谋为。课经云：庚午日戌加干。干支与初中传，皆拱地盘之夜贵。宜占官禄升迁之事，所谓前后引从升迁吉也。"));
        set(9, new LiuReKe("庚午日第十二局　干上酉", "课体：昴星。", "课义：失系来情，未免虚惊，无中生有，然后安宁。", "解曰：发用旬空，上乘元武，又是人宅受脱。占者来情，必是失物。戌为鬼墓，所以的有虚惊。但戌不应传　因刚日昴星，始得用传，故曰无中生有。又曰安宁者何，缘庚金得初中父母爻来生也。", "断曰：虎视转篷，吹毛求疵之象。隐之愈密，索之愈力，更加以天罗地网，凶不能逃，祸不能避。心迷意惑，进退乖离。诸占不吉，只宜占官禄之事。缘支于拱地盘夜贵，又拱天盘之禄故也。", "天时：天地不动，晴日占晴，雨日占雨。功名：前后引从，贵禄何患不得。求财：财神不见，从何而得。家宅：人宅受脱，空戌乘二武发用，遭窃无疑。婚姻：天后得旺，夜占女吉，昼占从魁临干，太常亦乘旺气，虽不甚吉，亦不可以言凶。胎产：刚日昴星，必主生女。疾病：鬼墓发用，占病不吉。出行：前罗后网，不能行动。行人：天罡虽则加仲，两合相控，罗网相阻，必不能至。遗失：两武盗宅，必不能得。出师：出师不利，恐遭罗网。", "毕法云：前后引从升迁吉，所谋多拙逢罗网。百章云：庚午日酉加庚，酉为羊刃网罗，太常主宴会酒食之事。三煞相并，必主孟上操戈，席中争斗。"));
    }
};
